  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
  <style>
    :root{
      --bit0:#D3D3D3;
      --bit1:#000000;
      --bg:#FFFFFF;
      --arrow:#808080;
      --tab:#E0E0E0;
