    <div class="row">
      <label for="slider-time">time</label>
      <input type="range" id="slider-time" min="5" max="25" step="0.5" value="15">
    </div>
    <div class="row">
      <label for="slider-vx">initial velocity in x</label>
      <input type="range" id="slider-vx" min="1" max="8" step="0.1" value="2.5">
    </div>
    <div class="row">
      <label for="slider-dt">Δt</label>
      <input type="range" id="slider-dt" min="0.05" max="0.4" step="0.01" value="0.15">
    </div>
  </div>

  <div id="canvas-container">
    <div class="frame">
      <!-- p5.js canvas will be created here -->
      <div id="p5-holder"></div>
    </div>
  </div>

  <!-- CDN for p5.js -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    // Constants for physics
    const g = 9.8;     // m/s^2
    const y0 = 10;     // initial height (m)
    const vy0 = 0;     // initial vertical velocity (m/s)
    const x0 = 0;      // initial x (m)

    // Canvas/plot settings
    const CANVAS_W = 700;
    const CANVAS_H = 400;
    const PADDING = 40;

    // Utility: read current slider values
    function getParams() {
      return {
        k: parseFloat(document.getElementById('slider-k').value),
        totalTime: parseFloat(document.getElementById('slider-time').value),
        vx: parseFloat(document.getElementById('slider-vx').value),
        dtFlash: parseFloat(document.getElementById('slider-dt').value)
      };
    }

    // Simulate the trajectory and strobe points (in meters)
    function simulate(params) {
      const { k, totalTime, vx, dtFlash } = params;

      let currentTime = 0;
      let currentX = x0;
      let currentY = y0;
      let currentVy = vy0;

      let nextStrobe = 0; // absolute time for next strobe
      const pathPoints = [];
      const strobePoints = [];

      const dtSim = 0.01; // time step to sample path (seconds)
      const EPS = 1e-9;

      // Helper to compute position within current arc
      function posAtLocal(t) {
        const x = currentX + vx * t;
        const y = currentY + currentVy * t - 0.5 * g * t * t;
        return { x, y: Math.max(0, y) };
      }

      // Prime the arrays with the very first point at t = 0 (start)
      pathPoints.push({ x: currentX, y: currentY });

      while (currentTime < totalTime - EPS) {
        // Time of flight until y returns to 0 for this arc
        const disc = currentVy * currentVy + 2 * g * currentY; // >= 0 always
        const vyImpactMag = Math.sqrt(disc);
        const tFlight = (currentVy + vyImpactMag) / g; // positive root

        const arcDuration = Math.min(tFlight, totalTime - currentTime);

        // Sample path during this arc
        let t = 0;
        while (t < arcDuration - EPS) {
          const p = posAtLocal(t);
          pathPoints.push(p);
          t += dtSim;
        }
        // ensure last point of arc segment included
        const pEnd = posAtLocal(arcDuration);
        pathPoints.push(pEnd);

        // Stroboscopic flashes during this arc
        while (nextStrobe <= totalTime + EPS && nextStrobe <= currentTime + arcDuration + EPS) {
          if (nextStrobe >= currentTime - EPS) {
            const tLocal = nextStrobe - currentTime;
            const sp = posAtLocal(tLocal);
            strobePoints.push(sp);
          }
          nextStrobe += dtFlash;
        }

        // If we truncated the arc (hit totalTime), stop
        if (arcDuration < tFlight - EPS) {
          currentTime += arcDuration;
          break;
        }

        // Otherwise, update for next bounce
        currentTime += tFlight;
        currentX += vx * tFlight;
        currentY = 0;
        currentVy = Math.sqrt(k) * vyImpactMag; // new upward velocity
      }

      // Determine extents for scaling
      let xMax = 0, yMax = y0; // ensure at least initial height visible
      for (const p of pathPoints) {
        if (p.x > xMax) xMax = p.x;
        if (p.y > yMax) yMax = p.y;
      }
      // Also consider that motion continues horizontally with vx for totalTime
      xMax = Math.max(xMax, vx * params.totalTime);

      return { pathPoints, strobePoints, xMax, yMax };
    }

    // p5.js sketch (global mode)
    function setup() {
      const cnv = createCanvas(CANVAS_W, CANVAS_H);
      cnv.parent('p5-holder');
      pixelDensity(1);
      noLoop(); // static; redraw only on slider input
      redraw(); // initial render
    }

    function drawAxes(chartW, chartH) {
      // Axis color and style
      stroke(100, 100, 150);
      strokeWeight(1);

      // Axes
      line(0, 0, chartW, 0);      // x-axis
      line(0, 0, 0, chartH);      // y-axis

      // Ticks
      const ticks = 10;
      const tickSize = 6;
      for (let i = 1; i <= ticks; i++) {
        const x = (i / ticks) * chartW;
        const y = (i / ticks) * chartH;
        // On x-axis: ticks down (negative y due to flipped coord system)
        line(x, 0, x, -tickSize);
        // On y-axis: ticks left (negative x in this flipped frame is still left)
        line(0, y, -tickSize, y);
      }
    }

    function draw() {
      const params = getParams();

      // Run physics to get points in meters
      const { pathPoints, strobePoints, xMax, yMax } = simulate(params);

      // Drawing setup
      background(0, 0, 0);
      push();
      translate(PADDING, height - PADDING); // origin at bottom-left
      scale(1, -1); // y up

      const chartW = width - 2 * PADDING;
      const chartH = height - 2 * PADDING;

      // Axes
      drawAxes(chartW, chartH);

      // Scaling from meters to pixels
      const sx = chartW / Math.max(1e-6, xMax * 1.05);
      const sy = chartH / Math.max(1e-6, yMax * 1.05);

      // Draw continuous trajectory (red line)
      noFill();
      stroke(255, 0, 0);
      strokeWeight(2);
      beginShape();
      for (const p of pathPoints) {
        vertex(p.x * sx, p.y * sy);
      }
      endShape();

      // Draw stroboscopic points (yellow dots)
      noStroke();
      fill(255, 255, 0);
      for (const p of strobePoints) {
        ellipse(p.x * sx, p.y * sy, 8, 8);
      }

      pop();
    }

    // Attach slider event handlers to trigger redraw
    function attachSliderHandlers() {
      ['slider-k', 'slider-time', 'slider-vx', 'slider-dt'].forEach(id => {
        const el = document.getElementById(id);
        if (el) {
          el.addEventListener('input', () => {
            // Trigger a single re-render (draw runs once because of noLoop)
            redraw();
          });
        }
      });
    }

    // Make sure handlers are set once DOM is ready
    document.addEventListener('DOMContentLoaded', attachSliderHandlers);
  </script>
</body>
</html>
