<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
  <title>Angle Bisectors on the Circumcircle</title>
  <style>
    body {
      margin: 0;
      font-family: Arial, Helvetica, sans-serif;
      background: #ffffff;
      color: #000;
      -webkit-font-smoothing: antialiased;
      -moz-osx-font-smoothing: grayscale;
      user-select: none;
    }
    header {
      max-width: 840px;
      margin: 24px auto 12px auto;
      padding: 0 16px;
      text-align: center;
    }
    #canvas-container {
      width: 100%;
      display: grid;
      place-items: center;
      padding: 12px 0 32px 0;
    }
    /* The canvas will be created by p5 and assigned this id */
    canvas#p5-canvas {
      box-shadow: 0 0 0 1px #ddd inset;
      background: #F0F0F0;
      border-radius: 4px;
    }
  </style>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.2/p5.min.js"></script>
</head>
<body>
  <header>
    <h1>Angle Bisectors on the Circumcircle</h1>
    <p>
      Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'.
      Then AA' ⟂ B'C', BB' ⟂ A'C', and CC' ⟂ A'B'.
    </p>
  </header>

  <div id="canvas-container">
    <!-- Placeholder canvas; replaced by p5-created canvas with the same id in setup -->
    <canvas id="p5-canvas" width="500" height="500"></canvas>
  </div>

  <script>
    // Geometry and interaction using p5.js
    let cx, cy, R;
    let angleA, angleB, angleC; // radians
    let dragging = null; // 'A' | 'B' | 'C' | null
    const hitTol = 15;
    const COL_SLATE = '#6A5ACD';
    const COL_ORANGE = '#FFA500';

    function setup() {
      // Remove placeholder canvas and create a p5 canvas with the same id
      const placeholder = document.getElementById('p5-canvas');
      const parent = document.getElementById('canvas-container');
      if (placeholder && placeholder.parentNode) {
        placeholder.parentNode.removeChild(placeholder);
      }
      const cnv = createCanvas(500, 500);
      cnv.parent(parent);
      cnv.id('p5-canvas');
      pixelDensity(1);
      angleMode(RADIANS);
      strokeCap(ROUND);

      cx = width / 2;
      cy = height / 2;
      R = 200;

      // Initial angular positions
      angleA = radians(210); // 7π/6
      angleB = radians(330); // 11π/6
      angleC = radians(90);  // π/2
    }

    function draw() {
      background('#F0F0F0');

      // Update dragging if active
      if (mouseIsPressed && dragging) {
        const ang = Math.atan2(mouseY - cy, mouseX - cx);
        if (dragging === 'A') angleA = ang;
        if (dragging === 'B') angleB = ang;
        if (dragging === 'C') angleC = ang;
      }

      // Compute primary points
      const A = angleToPoint(angleA);
      const B = angleToPoint(angleB);
      const C = angleToPoint(angleC);

      // Derived angles via vector averaging (robust against wrapping)
      const angleAprime = avgAngles(angleB, angleC);
      const angleBprime = avgAngles(angleA, angleC);
      const angleCprime = avgAngles(angleA, angleB);

      // Derived points on the circumcircle
      const Ap = angleToPoint(angleAprime);
      const Bp = angleToPoint(angleBprime);
      const Cp = angleToPoint(angleCprime);

      // Draw circumcircle
      noFill();
      stroke(COL_SLATE);
      strokeWeight(4);
      circle(cx, cy, R * 2);

      // Triangle ABC (slate)
      stroke(COL_SLATE);
      strokeWeight(4);
      drawSegment(A, B);
      drawSegment(B, C);
      drawSegment(C, A);

      // Extended bisectors and triangle A'B'C' (orange)
      stroke(COL_ORANGE);
      strokeWeight(4);
      drawSegment(A, Ap);
      drawSegment(B, Bp);
      drawSegment(C, Cp);

      drawSegment(Ap, Bp);
      drawSegment(Bp, Cp);
      drawSegment(Cp, Ap);

      // Points
      drawInteractivePoint(A.x, A.y); // A
      drawInteractivePoint(B.x, B.y); // B
      drawInteractivePoint(C.x, C.y); // C

      drawCalculatedPoint(Ap.x, Ap.y); // A'
      drawCalculatedPoint(Bp.x, Bp.y); // B'
      drawCalculatedPoint(Cp.x, Cp.y); // C'

      // Labels (radially outward from center)
      drawLabel('A', A);
      drawLabel('B', B);
      drawLabel('C', C);
      drawLabel("A'", Ap);
      drawLabel("B'", Bp);
      drawLabel("C'", Cp);

      // Cursor feedback
      const near = pickPoint(mouseX, mouseY, A, B, C);
      if (dragging) {
        cursor('grabbing');
      } else if (near) {
        cursor('pointer');
      } else {
        cursor('default');
      }
    }

    function mousePressed() {
      const A = angleToPoint(angleA);
      const B = angleToPoint(angleB);
      const C = angleToPoint(angleC);
      dragging = pickPoint(mouseX, mouseY, A, B, C);
    }

    function mouseReleased() {
      dragging = null;
    }

    // Helpers
    function angleToPoint(ang) {
      return {
        x: cx + R * Math.cos(ang),
        y: cy + R * Math.sin(ang)
      };
    }

    function avgAngles(a1, a2) {
      const s = Math.sin(a1) + Math.sin(a2);
      const c = Math.cos(a1) + Math.cos(a2);
      // If both s and c are near zero (opposite angles), fall back to simple average
      if (Math.abs(s) < 1e-8 && Math.abs(c) < 1e-8) {
        return (a1 + a2) / 2;
      }
      return Math.atan2(s, c);
    }

    function drawSegment(P, Q) {
      line(P.x, P.y, Q.x, Q.y);
    }

    function drawInteractivePoint(x, y) {
      // Circle
      stroke('#000000');
      strokeWeight(1);
      fill(COL_ORANGE);
      circle(x, y, 12);

      // Crosshair inside
      const r = 4.5;
      stroke('#000000');
      strokeWeight(1);
      line(x - r, y, x + r, y);
      line(x, y - r, x, y + r);
    }

    function drawCalculatedPoint(x, y) {
      stroke('#000000');
      strokeWeight(1);
      fill(COL_ORANGE);
      circle(x, y, 12);
    }

    function drawLabel(txt, P) {
      const dx = P.x - cx;
      const dy = P.y - cy;
      const mag = Math.max(1, Math.hypot(dx, dy));
      const off = 14;
      const ox = (dx / mag) * off;
      const oy = (dy / mag) * off;
      noStroke();
      fill('#000000');
      textSize(16);
      textAlign(CENTER, CENTER);
      text(txt, P.x + ox, P.y + oy);
    }

    function pickPoint(mx, my, A, B, C) {
      if (dist(mx, my, A.x, A.y) <= hitTol) return 'A';
      if (dist(mx, my, B.x, B.y) <= hitTol) return 'B';
      if (dist(mx, my, C.x, C.y) <= hitTol) return 'C';
      return null;
    }
  </script>
</body>
</html>
