</head>
<body>
  <div id="container">
    <div id="controls">
      <label for="slider-steps">carbon cycle steps</label>
      <input type="range" id="slider-steps" min="0" max="7" step="1" value="0">
    </div>
    <div id="canvas-container"></div>
  </div>

  <!-- p5.js CDN -->
  <script src="https://cdn.jsdelivr.net/npm/p5@1.9.3/lib/p5.min.js"></script>
  <script>
    // Globals
    let stepValue = 0; // current slider value
    const LABEL_SIZE = 14;
    const LINE_H = Math.round(LABEL_SIZE * 1.25);
    let tooltipDesc = null; // description to be shown when hovering any label

    // Descriptions for tooltip
    const DESCRIPTIONS = {
      photosynthesis: "Plants use sunlight, water, and carbon dioxide to create food, releasing oxygen.",
      respiration: "Animals and plants break down sugars for energy, releasing carbon dioxide.",
      waste: "Decomposers break down dead organic matter, returning carbon to the soil and atmosphere.",
      fossils: "Over millions of years, buried organic matter can turn into fossil fuels like coal and oil.",
      extraction: "Humans extract fossil fuels from the ground for energy.",
      emissions: "Burning fossil fuels releases large amounts of carbon dioxide into the atmosphere.",
      diffusion: "Carbon dioxide dissolves into and is released from bodies of water like oceans."
    };

    // Connect slider
    const slider = document.getElementById('slider-steps');
    slider.addEventListener('input', () => {
      stepValue = parseInt(slider.value, 10);
    });

    function setup() {
      const cnv = createCanvas(600, 500);
      cnv.parent('canvas-container');
      textFont('Arial, Helvetica, sans-serif');
      textSize(LABEL_SIZE);
      textAlign(LEFT, TOP);
      stepValue = parseInt(slider.value, 10);
    }

    function draw() {
      // Reset tooltip each frame
      tooltipDesc = null;

      // Static Scene
      drawStaticScene();

      // Reveal dynamic processes based on slider
      if (stepValue >= 1) drawCellularRespiration();
      if (stepValue >= 2) drawOrganismsWasteDecay();
      if (stepValue >= 3) drawFossilsAndFossilFuels();
      if (stepValue >= 4) drawPhotosynthesis();
      if (stepValue >= 5) drawFossilFuelEmissions();
      if (stepValue >= 6) drawFossilFuelExtraction();
      if (stepValue >= 7) drawCO2Diffusion();

      // Tooltip (if any)
      if (tooltipDesc) {
        drawTooltip(tooltipDesc);
      }
    }

    // ---------- Static Scene ----------
    function drawStaticScene() {
      noStroke();
      // Sky
      fill('#4169E1');
      rect(0, 0, width, height);

      // Sun (top-right)
      fill('#FFD700');
      ellipse(width - 70, 70, 90, 90);

      // Cloud (upper-middle)
      fill(255);
      const cx = 300, cy = 90;
      ellipse(cx, cy, 80, 50);
      ellipse(cx - 35, cy + 10, 70, 45);
      ellipse(cx + 35, cy + 10, 70, 45);

      // Grass (middle)
      fill('#228B22');
      rect(0, 250, width, 150);

      // Soil (bottom)
      fill('#8B4513');
      rect(0, 400, width, 100);

      // Water (bottom-right corner overlap) — quarter circle
      fill('#0000CD');
      noStroke();
      ellipse(width + 60, height + 60, 300, 300);

      // Trees
      // Tree 1
      drawTree(100, 260);
      // Tree 2
      drawTree(300, 260);

      // Sheep
      drawSheep(200, 240);

      // Factory
      drawFactory(470, 220);

      // Fossil deposits (small black circles in soil)
      fill(0);
      const deposits = [60, 130, 210, 290, 360, 430];
      deposits.forEach((dx, i) => {
        ellipse(dx, 445 + (i % 2) * 18, 12, 12);
      });
    }

    function drawTree(x, yTop) {
      // x: trunk left, yTop around grass top
      // Trunk
      noStroke();
      fill('#8B4513');
      rect(x, yTop, 26, 120);
      // Canopy
      fill('#006400');
      ellipse(x + 13, yTop - 20, 80, 55);
    }

    function drawSheep(x, y) {
      // Body
      noStroke();
      fill(255);
      ellipse(x, y, 48, 30);
      ellipse(x - 18, y - 12, 30, 22);
      ellipse(x + 16, y - 12, 28, 20);
      // Head
      ellipse(x + 26, y - 4, 20, 18);
      fill(0);
      ellipse(x + 30, y - 7, 3, 3); // eye
      // Legs
      stroke(0);
      strokeWeight(2);
      line(x - 12, y + 14, x - 12, y + 24);
      line(x - 2, y + 14, x - 2, y + 24);
    }

    function drawFactory(x, y) {
      // Building
      noStroke();
      fill('#696969');
      rect(x, y, 90, 70);
      // Stacks
      fill('#808080');
      const stackW = 12;
      rect(x + 12, y - 20, stackW, 40);
      rect(x + 34, y - 20, stackW, 40);
      rect(x + 56, y - 20, stackW, 40);
      // Simple smoke puffs
      fill('#9e9e9e');
      ellipse(x + 18, y - 24, 24, 16);
      ellipse(x + 40, y - 28, 26, 18);
      ellipse(x + 62, y - 24, 24, 16);
    }

    // ---------- Dynamic processes ----------
    function drawPhotosynthesis() {
      // Arrow from sky (350, 80) to tree (300, 180)
      stroke(0); strokeWeight(2);
      drawArrow(350, 80, 300, 180);
      // Label
      noStroke(); fill(0);
      drawLabelWithHover("Photosynthesis", 360, 120, DESCRIPTIONS.photosynthesis);
    }

    function drawCellularRespiration() {
      // Arrow from sheep (200, 240) to sky (250, 180)
      stroke(0); strokeWeight(2);
      drawArrow(200, 240, 250, 180);
      // Label
      noStroke(); fill(0);
      drawLabelWithHover("Cellular\nRespiration", 150, 180, DESCRIPTIONS.respiration);
    }

    function drawOrganismsWasteDecay() {
      // Arrow from ground below sheep (220, 280) down to (220, 350)
      stroke(0); strokeWeight(2);
      drawArrow(220, 280, 220, 350);
      // Label
      noStroke(); fill(0);
      drawLabelWithHover("Organisms' Waste\nand Decay", 240, 320, DESCRIPTIONS.waste);
    }

    function drawFossilsAndFossilFuels() {
      // Arrow from waste area (220, 370) down to soil (220, 440)
      stroke(0); strokeWeight(2);
      drawArrow(220, 370, 220, 440);
      // Label
      noStroke(); fill(0);
      drawLabelWithHover("Fossils and Fossil\nFuels", 240, 440, DESCRIPTIONS.fossils);
    }

    function drawFossilFuelEmissions() {
      // Arrow from factory (480, 200) to sky (430, 120)
      stroke(0); strokeWeight(2);
      drawArrow(480, 200, 430, 120);
      // Label
      noStroke(); fill(0);
      drawLabelWithHover("Fossil Fuel\nEmissions", 440, 150, DESCRIPTIONS.emissions);
    }

    function drawFossilFuelExtraction() {
      // Arrow from soil (450, 420) to factory base (480, 280)
      stroke(0); strokeWeight(2);
      drawArrow(450, 420, 480, 280);
      // Label
      noStroke(); fill(0);
      drawLabelWithHover("Fossil Fuel\nExtraction", 400, 350, DESCRIPTIONS.extraction);
    }

    function drawCO2Diffusion() {
      // Two arrows between water and sky
      stroke(0); strokeWeight(2);
      drawArrow(520, 250, 550, 400); // into water
      drawArrow(570, 400, 540, 250); // out of water
      // Label
      noStroke(); fill(255);
      // Put label on a soft blue bubble over water to mimic the snapshot
      fill('#1e90ff'); noStroke();
      const rX = 460, rY = 410, rW = 110, rH = 32, rad = 16;
      // Rounded rectangle
      drawRoundedRect(rX, rY, rW, rH, rad);
      fill(255); noStroke();
      drawLabelWithHover("CO₂ Diffusion", 480, 420, DESCRIPTIONS.diffusion, true);
    }

    // ---------- Helpers ----------
    function drawArrow(x1, y1, x2, y2) {
      // Line
      stroke(0);
      line(x1, y1, x2, y2);
      // Arrowhead
      const angle = Math.atan2(y2 - y1, x2 - x1);
      const size = 10;
      push();
      translate(x2, y2);
      rotate(angle);
      fill(0); noStroke();
      triangle(-size, -size * 0.5, -size, size * 0.5, 0, 0);
      pop();
    }

    // Draw label, compute its bounding box, and set tooltip on hover.
    // If onBubble is true, don't change fill (label already on colored bubble)
    function drawLabelWithHover(textStr, x, y, description, onBubble = false) {
      const lines = String(textStr).split('\n');
      const w = computeTextBlockWidth(lines);
      const h = lines.length * LINE_H;

      // Draw text
      if (!onBubble) { fill(0); }
      noStroke();
      for (let i = 0; i < lines.length; i++) {
        text(lines[i], x, y + i * LINE_H);
      }

      // Hover detection
      if (mouseX >= x && mouseX <= x + w && mouseY >= y && mouseY <= y + h) {
        tooltipDesc = description;
      }
    }

    function computeTextBlockWidth(lines) {
      let w = 0;
      push();
      textSize(LABEL_SIZE);
      for (const ln of lines) {
        w = Math.max(w, textWidth(ln));
      }
      pop();
      // add a tiny padding margin for the bbox
      return w + 2;
    }

    function drawTooltip(textStr) {
      const pad = 10;
      const maxW = 260;
      const lines = wrapText(textStr, maxW);
      let w = 0;
      for (const ln of lines) w = Math.max(w, textWidth(ln));
      const h = lines.length * LINE_H + pad * 2;

      let tx = mouseX + 14;
      let ty = mouseY + 14;

      // Keep tooltip within canvas bounds
      if (tx + w + pad * 2 > width - 4) tx = mouseX - (w + pad * 2) - 14;
      if (ty + h > height - 4) ty = mouseY - h - 14;

      // Background
      fill(0, 0, 0, 150);
      noStroke();
      drawRoundedRect(tx, ty, w + pad * 2, h, 8);

      // Text
      fill(255);
      let y = ty + pad;
      for (const ln of lines) {
        text(ln, tx + pad, y);
        y += LINE_H;
      }
    }

    // Simple word wrap for a given max width
    function wrapText(str, maxWidth) {
      const words = str.split(/\s+/);
      const lines = [];
      let line = '';
      for (let i = 0; i < words.length; i++) {
        const word = words[i];
        const test = line.length ? line + ' ' + word : word;
        if (textWidth(test) <= maxWidth) {
          line = test;
        } else {
          if (line.length) lines.push(line);
          // If a single very long word exceeds maxWidth, force break
          if (textWidth(word) > maxWidth) {
            let chunk = '';
            for (const ch of word) {
              if (textWidth(chunk + ch) <= maxWidth) {
                chunk += ch;
              } else {
                lines.push(chunk);
                chunk = ch;
              }
            }
            line = chunk;
          } else {
            line = word;
          }
        }
      }
      if (line.length) lines.push(line);
      return lines;
    }

    function drawRoundedRect(x, y, w, h, r) {
      // p5 has rect with radii, but use custom to ensure compatibility
      rect(x, y, w, h, r);
    }
  </script>
</body>
</html>
