      <input type="radio" id="radio-circle-off" name="circle" value="off" checked>
      <label for="radio-circle-off">off</label>
      <input type="radio" id="radio-circle-on" name="circle" value="on">
      <label for="radio-circle-on">on</label>
    </div>
  </div>

  <!-- Right: Visualization Canvas -->
  <div id="canvas-container">
    <!-- p5.js canvas will be inserted here -->
  </div>

</div>

<script>
  // ---------- Global State ----------
  const state = {
    t: 0.4,
    showPosition: false,
    showVelocity: false,
    showAcceleration: true,
    coords: 'rectangular',
    showCircle: false
  };

  // ---------- DOM bindings ----------
  const sliderTime = document.getElementById('slider-time');
  const btnResetTime = document.getElementById('btn-reset-time');
  const selectCoords = document.getElementById('select-coords');

  // position toggle
  document.querySelectorAll('#toggle-position input[type="radio"]').forEach(el=>{
    el.addEventListener('change', ()=>{
      state.showPosition = document.getElementById('radio-position-on').checked;
      requestRedraw();
    });
  });

  // velocity toggle
  document.querySelectorAll('#toggle-velocity input[type="radio"]').forEach(el=>{
    el.addEventListener('change', ()=>{
      state.showVelocity = document.getElementById('radio-velocity-on').checked;
      requestRedraw();
    });
  });

  // acceleration toggle
  document.querySelectorAll('#toggle-acceleration input[type="radio"]').forEach(el=>{
    el.addEventListener('change', ()=>{
      state.showAcceleration = document.getElementById('radio-acceleration-on').checked;
      requestRedraw();
    });
  });

  // circle toggle
  document.querySelectorAll('#toggle-circle input[type="radio"]').forEach(el=>{
    el.addEventListener('change', ()=>{
      state.showCircle = document.getElementById('radio-circle-on').checked;
      requestRedraw();
    });
  });

  sliderTime.addEventListener('input', ()=>{
    state.t = parseFloat(sliderTime.value);
    requestRedraw();
  });
  btnResetTime.addEventListener('click', ()=>{
    state.t = 0.4;
    sliderTime.value = '0.4';
    requestRedraw();
  });
  selectCoords.addEventListener('change', ()=>{
    state.coords = selectCoords.value;
    requestRedraw();
  });

  // ---------- p5.js sketch ----------
  let cnv;
  // world bounds and margins
  const world = {min: -2.5, max: 2.5};
  let scalePX = 1; // pixels per world unit
  let margin = 30;

  function setup(){
    const {w, h} = containerSize();
    cnv = createCanvas(w, h);
    cnv.parent('canvas-container');
    pixelDensity(1);
    noLoop();
    textFont('Arial');
  }

  function windowResized(){
    const {w, h} = containerSize();
    resizeCanvas(w, h);
    requestRedraw();
  }

  function containerSize(){
    const el = document.getElementById('canvas-container');
    const bounds = el.getBoundingClientRect();
    return {w: Math.floor(bounds.width), h: Math.floor(bounds.height)};
  }

  function requestRedraw(){ redraw(); }

  // Coordinate transforms
  function updateScale(){
    const innerW = width - 2*margin;
    const innerH = height - 2*margin;
    const range = world.max - world.min;
    scalePX = Math.min(innerW, innerH) / range;
  }
  function worldToScreen(x, y){
    // Cartesian up is positive; canvas y is down
    const sx = width/2 + x * scalePX;
    const sy = height/2 - y * scalePX;
    return {x: sx, y: sy};
  }
  function len(v){ return Math.hypot(v.x, v.y); }
  function normalize(v){
    const L = Math.hypot(v.x, v.y);
    if(L === 0) return {x:0, y:0};
    return {x: v.x/L, y: v.y/L};
  }
  function add(a,b){ return {x:a.x+b.x, y:a.y+b.y}; }
  function sub(a,b){ return {x:a.x-b.x, y:a.y-b.y}; }
  function mul(v, s){ return {x:v.x*s, y:v.y*s}; }
  function perpCCW(v){ return {x: -v.y, y: v.x}; }

  function draw(){
    clear();
    background(255);
    updateScale();

    drawAxes();
    drawCurve();

    const t = state.t;
    // Kinematics
    const r = {x: Math.cos(t), y: Math.sin(2*t)};
    const v = {x: -Math.sin(t), y: 2*Math.cos(2*t)};
    const a = {x: -Math.cos(t), y: -4*Math.sin(2*t)};

    // point P
    const P = r;

    // velocity and acceleration magnitudes
    const speed = Math.hypot(v.x, v.y);
    const eT = normalize(v);
    const cross = v.x*a.y - v.y*a.x;        // scalar z-component of v x a
    const kappa = (speed !== 0) ? (cross / Math.pow(speed,3)) : 0;
    const signk = (kappa >= 0 ? 1 : -1);
    const eNccw = normalize(perpCCW(eT));
    const eN = {x: eNccw.x*signk, y: eNccw.y*signk};

    // osculating circle
    const rho = (Math.abs(kappa) > 1e-6) ? 1/Math.abs(kappa) : Infinity;
    const C = add(P, mul(eN, (rho===Infinity?0:rho)));

    // draw position vector (origin -> P)
    if(state.showPosition){
      const origin = {x:0, y:0};
      drawArrow(origin, sub(P, origin), {color:'#111', weight:2});
      drawTextAt(worldToScreen(P.x, P.y), 'r(t)', {dx:8, dy:-8});
    }

    // draw velocity (from P)
    if(state.showVelocity){
      const scaleV = 0.8; // world units
      const vecV = mul(eT, scaleV);
      drawArrow(P, vecV, {color:'#8B0000', weight:2.5});
      drawTextAt(worldToScreen(P.x+vecV.x, P.y+vecV.y), 'v', {dx:6, dy:-6, color:'#8B0000'});
    }

    // draw acceleration (from P)
    if(state.showAcceleration){
      const eA = normalize(a);
      const scaleA = 0.9;
      const vecA = mul(eA, scaleA);
      drawArrow(P, vecA, {color:'#9333ea', weight:2.5}); // purple
      drawTextAt(worldToScreen(P.x+vecA.x, P.y+vecA.y), 'a', {dx:6, dy:-6, color:'#9333ea'});
    }

    // draw basis according to coordinate system
    drawBasis(P, eT, eN);

    // draw osculating circle
    if(state.showCircle && isFinite(rho)){
      const centerS = worldToScreen(C.x, C.y);
      const Rpx = rho * scalePX;
      push();
      noFill();
      stroke(120);
      strokeWeight(1);
      drawingContext.setLineDash([5,4]);
      circle(centerS.x, centerS.y, 2*Rpx);
      drawingContext.setLineDash([]);
      pop();
    }

    // point P as small dot on curve
    push();
    const Ps = worldToScreen(P.x, P.y);
    noStroke();
    fill(10);
    circle(Ps.x, Ps.y, 6);
    pop();

    // Axis labels
    drawAxisLabels();
  }

  // ---------- Drawing helpers ----------
  function drawAxes(){
    const range = {min: -2.2, max: 2.2};
    // grid
    push();
    stroke(200);
    strokeWeight(1);
    drawingContext.setLineDash([4,6]);
    for(let g=-2; g<=2; g++){
      const p1 = worldToScreen(g, range.min);
      const p2 = worldToScreen(g, range.max);
      line(p1.x, p1.y, p2.x, p2.y);
      const q1 = worldToScreen(range.min, g);
      const q2 = worldToScreen(range.max, g);
      line(q1.x, q1.y, q2.x, q2.y);
    }
    drawingContext.setLineDash([]);
    pop();

    // axes
    push();
    stroke(0);
    strokeWeight(1.5);
    // x-axis
    const x1 = worldToScreen(world.min, 0);
    const x2 = worldToScreen(world.max, 0);
    line(x1.x, x1.y, x2.x, x2.y);
    // y-axis
    const y1 = worldToScreen(0, world.min);
    const y2 = worldToScreen(0, world.max);
    line(y1.x, y1.y, y2.x, y2.y);

    // ticks -2..2
    textSize(12);
    fill(0);
    noStroke();
    for(let t=-2; t<=2; t++){
      if(t===0) continue;
      // x ticks
      const tx1 = worldToScreen(t, 0);
      stroke(0);
      strokeWeight(1);
      line(tx1.x, tx1.y-4, tx1.x, tx1.y+4);
      noStroke();
      textAlign(CENTER, TOP);
      text(t, tx1.x, tx1.y+6);

      // y ticks
      const ty1 = worldToScreen(0, t);
      stroke(0);
      strokeWeight(1);
      line(ty1.x-4, ty1.y, ty1.x+4, ty1.y);
      noStroke();
      textAlign(RIGHT, CENTER);
      text(t, ty1.x-6, ty1.y);
    }
    pop();
  }

  function drawAxisLabels(){
    push();
    fill(0);
    noStroke();
    textSize(16);
    textAlign(LEFT, CENTER);
    const xLbl = worldToScreen(2.2, -0.18);
    text('x(t)', xLbl.x, xLbl.y);
    textAlign(CENTER, TOP);
    const yLbl = worldToScreen(0.18, 2.2);
    text('y(t)', yLbl.x, yLbl.y);
    pop();
  }

  function drawCurve(){
    push();
    noFill();
    stroke('#1d4ed8'); // blue
    strokeWeight(3);
    beginShape();
    const steps = 400;
    for(let i=0; i<=steps; i++){
      const tt = 2*Math.PI*i/steps;
      const x = Math.cos(tt);
      const y = Math.sin(2*tt);
      const p = worldToScreen(x, y);
      vertex(p.x, p.y);
    }
    endShape();
    pop();
  }

  function drawArrow(originW, vecW, opts={}){
    const color = opts.color || '#000';
    const weight = opts.weight || 2;
    const dashed = opts.dashed || false;

    const startS = worldToScreen(originW.x, originW.y);
    const endW = add(originW, vecW);
    const endS = worldToScreen(endW.x, endW.y);

    push();
    stroke(color);
    strokeWeight(weight);
    if(dashed) drawingContext.setLineDash([6,6]);
    line(startS.x, startS.y, endS.x, endS.y);
    drawingContext.setLineDash([]);

    // arrowhead
    const dx = endS.x - startS.x;
    const dy = endS.y - startS.y;
    const ang = Math.atan2(dy, dx);
    const ah = 9; // px
    const aw = 5;
    translate(endS.x, endS.y);
    rotate(ang);
    fill(color);
    noStroke();
    triangle(-ah, -aw, -ah, aw, 0, 0);
    pop();
  }

  function drawTextAt(screenPos, txt, opt={}){
    const dx = opt.dx || 0, dy = opt.dy || 0;
    const color = opt.color || '#111';
    push();
    noStroke();
    fill(color);
    textSize(14);
    textAlign(LEFT, BOTTOM);
    text(txt, screenPos.x + dx, screenPos.y + dy);
    pop();
  }

  function drawBasis(P, eT, eN){
    const basisLen = 0.6; // world units
    let b1 = null, b2 = null, l1 = '', l2 = '';

    if(state.coords === 'rectangular'){
      b1 = {x: 1, y: 0};
      b2 = {x: 0, y: 1};
      l1 = 'î';
      l2 = 'ĵ';
    }else if(state.coords === 'normal-tangential'){
      b1 = eT;
      b2 = eN;
      l1 = 'ê_T';
      l2 = 'ê_N';
    }else if(state.coords === 'polar'){
      const r = P;
      const er = normalize(r);
      const eθ = perpCCW(er);
      b1 = er;
      b2 = eθ;
      l1 = 'ê_r';
      l2 = 'ê_θ';
    }

    if(b1 && b2){
      const v1 = mul(b1, basisLen);
      const v2 = mul(b2, basisLen);
      drawArrow(P, v1, {color:'#000', weight:1.8, dashed:true});
      drawArrow(P, v2, {color:'#000', weight:1.8, dashed:true});

      const tip1 = worldToScreen(P.x+v1.x, P.y+v1.y);
      const tip2 = worldToScreen(P.x+v2.x, P.y+v2.y);
      drawTextAt(tip1, l1, {dx:4, dy:-4});
      drawTextAt(tip2, l2, {dx:4, dy:-4});
    }
  }

  // Initialize derived controls from default DOM state (already set in HTML)
  // Ensures state matches UI on load.
  state.showPosition = document.getElementById('radio-position-on').checked;
  state.showVelocity = document.getElementById('radio-velocity-on').checked;
  state.showAcceleration = document.getElementById('radio-acceleration-on').checked;
  state.showCircle = document.getElementById('radio-circle-on').checked;
  state.coords = selectCoords.value;
</script>
</body>
</html>
