      </div>

      <div class="row">
        <label for="slider-stiffness">stiffness</label>
        <input id="slider-stiffness" type="range" min="0.1" max="10.0" step="0.01" value="1.0" />
        <span id="stiffness-value" class="value">1.00</span>
      </div>

      <div class="row">
        <label for="slider-mass">mass</label>
        <input id="slider-mass" type="range" min="0.1" max="10.0" step="0.01" value="1.0" />
        <span id="mass-value" class="value">1.00</span>
      </div>

      <div class="row time-controls">
        <label>time</label>
        <div class="buttons">
          <button id="btn-step-back" class="icon" title="Step back">◀</button>
          <button id="btn-play-pause" class="icon" title="Play/Pause">▶</button>
          <button id="btn-step-forward" class="icon" title="Step forward">▶|</button>
        </div>
      </div>
    </div>

    <!-- Visualization Container -->
    <div id="viz">
      <div id="viz-flex">
        <div id="plot-canvas-container"></div>
        <div id="animation-canvas-container"></div>
      </div>
    </div>
  </div>

  <script>
    // Global simulation state
    const state = {
      amplitude: 0.3,
      stiffness: 1.0,
      mass: 1.0,
      time: 0.0,
      isPlaying: false,
      omega: Math.sqrt(1.0 / 1.0),
      history: [], // {t, y}
      xMin: 0,
      xMax: 25,       // dynamic
      dtPlay: 0.05,   // time step during play
      dtStep: 0.1     // step when using step buttons
    };

    // UI elements
    const elAmp = document.getElementById('slider-amplitude');
    const elK   = document.getElementById('slider-stiffness');
    const elM   = document.getElementById('slider-mass');

    const elAmpVal = document.getElementById('amplitude-value');
    const elKVal   = document.getElementById('stiffness-value');
    const elMVal   = document.getElementById('mass-value');

    const btnPlayPause   = document.getElementById('btn-play-pause');
    const btnStepBack    = document.getElementById('btn-step-back');
    const btnStepForward = document.getElementById('btn-step-forward');
    const btnReset       = document.getElementById('btn-reset');

    function fmt(n, digits=3){ return (+n).toFixed(digits); }

    function updateOmega(){
      state.omega = Math.sqrt(state.stiffness / state.mass);
    }

    function setFromSliders(){
      state.amplitude = parseFloat(elAmp.value);
      state.stiffness = parseFloat(elK.value);
      state.mass      = parseFloat(elM.value);

      elAmpVal.textContent = fmt(state.amplitude,3);
      elKVal.textContent   = fmt(state.stiffness,2);
      elMVal.textContent   = fmt(state.mass,2);

      updateOmega();
      // Reset time/history and pause
      state.time = 0;
      state.history = [];
      state.isPlaying = false;
      btnPlayPause.textContent = '▶';
      // Reset x-range
      state.xMin = 0;
      state.xMax = 25;
    }

    elAmp.addEventListener('input', setFromSliders);
    elK.addEventListener('input', setFromSliders);
    elM.addEventListener('input', setFromSliders);

    btnPlayPause.addEventListener('click', () => {
      state.isPlaying = !state.isPlaying;
      btnPlayPause.textContent = state.isPlaying ? '||' : '▶';
    });

    function addHistoryPoint(t){
      const y = state.amplitude * Math.sin(state.omega * t);
      state.history.push({t, y});
      // Keep chronological order in case of stepping back
      state.history.sort((a,b)=>a.t-b.t);
      // Extend x-axis if needed
      if (t > state.xMax) state.xMax = Math.ceil(t / 25) * 25 + 0.00001; // small epsilon
    }

    btnStepForward.addEventListener('click', () => {
      if (state.isPlaying) return;
      state.time += state.dtStep;
      addHistoryPoint(state.time);
    });

    btnStepBack.addEventListener('click', () => {
      if (state.isPlaying) return;
      state.time = Math.max(0, state.time - state.dtStep);
      addHistoryPoint(state.time);
    });

    btnReset.addEventListener('click', () => {
      // Reset sliders to defaults
      elAmp.value = 0.3;
      elK.value   = 1.0;
      elM.value   = 1.0;
      setFromSliders();
    });

    // Initialize displays
    setFromSliders();

    // ===================== Plot sketch =====================
    const plotSketch = (p) => {
      let W = 800, H = 380;
      const margin = {left:60,right:24,top:20,bottom:40};
      const yMin = -1, yMax = 1;

      function parentW(){
        const el = document.getElementById('plot-canvas-container');
        return Math.max(320, el.clientWidth);
      }

      p.setup = () => {
        W = parentW();
        const el = document.getElementById('plot-canvas-container');
        H = Math.max(300, el.clientHeight || 380);
        p.createCanvas(W, H).parent('plot-canvas-container');
        p.pixelDensity(1);
      };

      p.windowResized = () => {
        W = parentW();
        p.resizeCanvas(W, H);
      };

      function xToPx(x){
        return p.map(x, state.xMin, state.xMax, margin.left, W - margin.right);
      }
      function yToPx(y){
        return p.map(y, yMax, yMin, margin.top, H - margin.bottom);
      }

      function drawGridAndAxes(){
        // Background
        p.background(255);
        // Plot frame
        p.noFill();
        p.stroke(210);
        p.rect(margin.left, margin.top, W - margin.left - margin.right, H - margin.top - margin.bottom);

        // Grid y lines
        p.textAlign(p.RIGHT, p.CENTER);
        p.textSize(12);
        const yTicks = [-1,-0.5,0,0.5,1];
        p.stroke('#e6e6eb');
        for (const yy of yTicks){
          const py = yToPx(yy);
          p.stroke('#e6e6eb');
          p.line(margin.left, py, W - margin.right, py);
          p.noStroke();
          p.fill('#555');
          p.text(fmt(yy, yy===0?0:1), margin.left - 8, py);
        }

        // Grid x lines (step 5)
        const step = 5;
        p.textAlign(p.CENTER, p.TOP);
        for (let x=0; x<=state.xMax+1e-9; x+=step){
          const px = xToPx(x);
          p.stroke('#e6e6eb');
          p.line(px, margin.top, px, H - margin.bottom);
          p.noStroke();
          p.fill('#555');
          p.text(fmt(x,0), px, H - margin.bottom + 8);
        }

        // Axes
        p.stroke('#666');
        // y=0 axis
        p.line(margin.left, yToPx(0), W - margin.right, yToPx(0));
        // x=0 axis
        const px0 = xToPx(0);
        p.line(px0, margin.top, px0, H - margin.bottom);
      }

      function drawWave(){
        if (state.history.length < 2) return;
        p.noFill();
        p.stroke('#6363B2');
        p.strokeWeight(2.8);
        p.beginShape();
        for (const pt of state.history){
          p.vertex(xToPx(pt.t), yToPx(pt.y));
        }
        p.endShape();
        p.strokeWeight(1);
      }

      p.draw = () => {
        // Evolve time if playing
        if (state.isPlaying){
          state.time += state.dtPlay;
          addHistoryPoint(state.time);
        }
        drawGridAndAxes();
        drawWave();
      };
    };

    // ===================== Animation sketch =====================
    const animSketch = (p) => {
      let W = 220, H = 420;
      const margin = {left:16,right:16,top:16,bottom:20};
      const yMin = -1.2, yMax = 1.2;

      function parentW(){
        const el = document.getElementById('animation-canvas-container');
        return Math.max(160, el.clientWidth);
      }
      function parentH(){
        const el = document.getElementById('animation-canvas-container');
        // Taller aspect
        return Math.max(360, el.clientHeight || 420);
      }

      p.setup = () => {
        W = parentW(); H = parentH();
        p.createCanvas(W, H).parent('animation-canvas-container');
        p.pixelDensity(1);
      };

      p.windowResized = () => {
        W = parentW(); H = parentH();
        p.resizeCanvas(W, H);
      };

      function yToPx(y){
        return p.map(y, yMax, yMin, margin.top, H - margin.bottom);
      }

      function drawAxis(){
        const axisX = W/2;
        p.stroke('#666');
        p.line(axisX, margin.top, axisX, H - margin.bottom);
        // labels at -1,0,1
        p.textAlign(p.LEFT, p.CENTER);
        p.noStroke();
        p.fill('#555');
        p.textSize(12);
        const ticks=[-1,0,1];
        for (const t of ticks){
          p.text(fmt(t,0), axisX+8, yToPx(t));
        }
      }

      function drawSpringAndMass(){
        p.stroke(0);
        p.fill(255);
        p.background(255);

        // Vertical axis first (so elements draw over it)
        drawAxis();

        const centerX = W/2;
        const topAnchorY = margin.top + 10;

        // Current mass position (normalized)
        const y = state.amplitude * Math.sin(state.omega * state.time);
        const yPix = yToPx(y);

        // Mass block dimensions (pixels)
        const blockW = Math.min(70, W*0.65);
        const blockH = 36;

        // Anchor (small horizontal bar)
        p.stroke(0);
        p.strokeWeight(2);
        p.line(centerX - 24, topAnchorY, centerX + 24, topAnchorY);

        // Spring geometry
        const topSpringY = topAnchorY + 2;
        const bottomSpringY = yPix - blockH/2;
        const springLen = Math.max(20, bottomSpringY - topSpringY);
        const turns = 12;
        const zigAmp = Math.min(14, W*0.22);
        const dy = springLen / (turns*2);

        p.noFill();
        p.stroke(0);
        p.strokeWeight(2);
        p.beginShape();
        p.vertex(centerX, topSpringY);
        for (let i=0;i<turns*2;i++){
          const yy = topSpringY + dy*(i+1);
          const xx = centerX + (i%2===0 ? zigAmp : -zigAmp);
          p.vertex(xx, yy);
        }
        p.vertex(centerX, bottomSpringY);
        p.endShape();

        // Mass block
        p.fill('#6363B2');
        p.noStroke();
        p.rectMode(p.CENTER);
        p.rect(centerX, yPix, blockW, blockH, 6);

        // Center marker (y=0)
        p.stroke('#999');
        p.strokeWeight(1);
        const y0 = yToPx(0);
        p.line(centerX - blockW*0.8, y0, centerX + blockW*0.8, y0);
      }

      p.draw = () => {
        // No time update here; handled in plot sketch to keep a single source of truth.
        drawSpringAndMass();
      };
    };

    // Create p5 instances
    const plotP5 = new p5(plotSketch, 'plot-canvas-container');
    const animP5 = new p5(animSketch, 'animation-canvas-container');
  </script>
</body>
</html>
