  .cell-off { background-color: var(--off-color); }

  .cell span {
    pointer-events: none; /* ensure clicks toggle the cell */
  }
</style>
</head>
<body>
  <div class="app">
    <div class="panel">
      <!-- Control Panel Section -->
      <div class="controls">
        <span>distance function</span>
        <button id="btn-manhattan" class="metric-btn active">Manhattan</button>
        <button id="btn-euclidean" class="metric-btn">squared Euclidean</button>
        <button id="btn-chebyshev" class="metric-btn">Chebyshev</button>
      </div>

      <!-- Grid Area Section -->
      <div id="grid-container"></div>
    </div>
  </div>

<script>
  // Grid dimensions
  const ROWS = 9;
  const COLS = 7;

  // State variables
  let currentDistanceFunction = 'manhattan'; // 'manhattan' | 'euclidean' | 'chebyshev'

  // Initialize gridState (9x7), all false except two sources to match snapshot
  const gridState = Array.from({ length: ROWS }, () => Array(COLS).fill(false));
  gridState[2][1] = true; // row 2, col 1
  gridState[6][4] = true; // row 6, col 4

  // References
  const gridContainer = document.getElementById('grid-container');
  const btnManhattan = document.getElementById('btn-manhattan');
  const btnEuclidean = document.getElementById('btn-euclidean');
  const btnChebyshev = document.getElementById('btn-chebyshev');

  // Create grid elements dynamically (63 cells, each with a <span>)
  const cellElements = []; // 2D array of cell DOM elements
  function initGrid() {
    for (let r = 0; r < ROWS; r++) {
      const rowEls = [];
      for (let c = 0; c < COLS; c++) {
        const cell = document.createElement('div');
        cell.className = 'cell';
        cell.dataset.row = r;
        cell.dataset.col = c;

        const span = document.createElement('span');
        span.className = 'value';
        cell.appendChild(span);

        cell.addEventListener('click', onCellClick);
        gridContainer.appendChild(cell);
        rowEls.push(cell);
      }
      cellElements.push(rowEls);
    }
  }

  // Event handlers
  function onCellClick(e) {
    const r = Number(e.currentTarget.dataset.row);
    const c = Number(e.currentTarget.dataset.col);
    gridState[r][c] = !gridState[r][c];
    render();
  }

  btnManhattan.addEventListener('click', () => setDistanceFunction('manhattan', btnManhattan));
  btnEuclidean.addEventListener('click', () => setDistanceFunction('euclidean', btnEuclidean));
  btnChebyshev.addEventListener('click', () => setDistanceFunction('chebyshev', btnChebyshev));

  function setDistanceFunction(mode, buttonEl) {
    currentDistanceFunction = mode;
    // Update active button styles
    [btnManhattan, btnEuclidean, btnChebyshev].forEach(b => b.classList.remove('active'));
    buttonEl.classList.add('active');
    render();
  }

  // Compute the distance transform for the whole grid
  function computeDistances() {
    const sources = [];
    for (let r = 0; r < ROWS; r++) {
      for (let c = 0; c < COLS; c++) {
        if (gridState[r][c]) sources.push([r, c]);
      }
    }

    const distances = Array.from({ length: ROWS }, () => Array(COLS).fill(Infinity));
    for (let r = 0; r < ROWS; r++) {
      for (let c = 0; c < COLS; c++) {
        if (sources.length === 0) {
          distances[r][c] = Infinity;
          continue;
        }
        let minD = Infinity;
        for (const [sr, sc] of sources) {
          const dr = Math.abs(r - sr);
          const dc = Math.abs(c - sc);
          let d;
          switch (currentDistanceFunction) {
            case 'euclidean':
              d = dr * dr + dc * dc; // squared Euclidean
              break;
            case 'chebyshev':
              d = Math.max(dr, dc);
              break;
            case 'manhattan':
            default:
              d = dr + dc;
          }
          if (d < minD) minD = d;
          if (minD === 0) break; // early exit if on a source
        }
        distances[r][c] = minD;
      }
    }
    return distances;
  }

  // Render function: updates numbers and cell styling
  function render() {
    const distances = computeDistances();
    for (let r = 0; r < ROWS; r++) {
      for (let c = 0; c < COLS; c++) {
        const cell = cellElements[r][c];
        const valueSpan = cell.firstChild;
        const isOn = gridState[r][c];

        cell.classList.toggle('cell-on', isOn);
        cell.classList.toggle('cell-off', !isOn);

        const d = distances[r][c];
        valueSpan.textContent = Number.isFinite(d) ? d : ''; // show blank if no sources
        cell.title = `r:${r}, c:${c}`;
      }
    }
  }

  // Initialize and draw
  initGrid();
  render();
</script>
</body>
</html>
