<script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

<script>
(function(){
    // Grab elements
    const btnModelEP = document.getElementById('btn-model-ep');
    const btnModelAR = document.getElementById('btn-model-ar');
    const btnTmaxLow = document.getElementById('btn-tmax-low');
    const btnTmaxHigh = document.getElementById('btn-tmax-high');

    const sliderN = document.getElementById('slider-n');
    const sliderTmin = document.getElementById('slider-tmin');
    const sliderTmax = document.getElementById('slider-tmax');
    const sliderTref = document.getElementById('slider-tref');
    const sliderParam = document.getElementById('slider-param');

    const labelN = document.getElementById('label-n');
    const labelTmin = document.getElementById('label-tmin');
    const labelTmax = document.getElementById('label-tmax');
    const labelTref = document.getElementById('label-tref');
    const labelParamValue = document.getElementById('label-param-value');
    const labelParamName = document.getElementById('label-param-name');

    const fitResults = document.getElementById('fit-results');

    // Helpers
    const clamp = (v, min, max) => Math.min(Math.max(v, min), max);
    function isEPActive(){ return btnModelEP.classList.contains('active'); }
    function formatIntDot(x){
        // thousands with trailing dot
        return Number(Math.round(x)).toLocaleString(undefined, {maximumFractionDigits:0}) + '.';
    }
    function formatR2(x){
        return x.toFixed(3);
    }
    function ensureMathJax(){
        if(window.MathJax && MathJax.typeset){
            MathJax.typeset();
        }
    }

    // Button group handling
    function toggleGroup(selectedButton, groupButtons){
        groupButtons.forEach(btn => btn.classList.remove('active'));
        selectedButton.classList.add('active');
    }

    // Model equations
    function yEyring(Tk, TrefK, a){
        // y = (T/Tref) * exp( a*(1/Tref - 1/T) )
        return (Tk / TrefK) * Math.exp(a * (1.0/TrefK - 1.0/Tk));
    }
    function yArrhenius(Tk, TrefK, a){
        // y = exp( a*(1/Tref - 1/T) )
        return Math.exp(a * (1.0/TrefK - 1.0/Tk));
    }

    // Data generation
    function linspace(a, b, n){
        if(n <= 1) return [a];
        const arr = new Array(n);
        const step = (b - a) / (n - 1);
        for(let i=0;i<n;i++) arr[i] = a + step*i;
        return arr;
    }

    function generateData(n, TminC, TmaxC, TrefC, a, model){
        const Tcs = linspace(TminC, TmaxC, n);
        const TrefK = TrefC + 273.15;
        const ys = [];
        for(let i=0;i<Tcs.length;i++){
            const Tk = Tcs[i] + 273.15;
            ys.push(model === 'EP' ? yEyring(Tk, TrefK, a) : yArrhenius(Tk, TrefK, a));
        }
        return { Tcs, ys };
    }

    // Fit using grid search
    function fitParameter(Tcs, ys, TrefC, fitModel){
        const TrefK = TrefC + 273.15;
        const n = ys.length;
        let minRSS = Infinity;
        let bestA = 0;

        // Precompute Kelvin temps
        const TKs = Tcs.map(tc => tc + 273.15);

        // Grid search from 0 to 10000 step 1
        for(let a=0; a<=10000; a++){
            let rss = 0;
            for(let i=0;i<n;i++){
                const yhat = (fitModel === 'EP') ? yEyring(TKs[i], TrefK, a) : yArrhenius(TKs[i], TrefK, a);
                const r = ys[i] - yhat;
                rss += r*r;
            }
            if(rss < minRSS){
                minRSS = rss;
                bestA = a;
            }
        }

        // r^2
        const meanY = ys.reduce((s,v)=>s+v,0)/n;
        const ssTot = ys.reduce((s,v)=>s + (v-meanY)*(v-meanY), 0);
        const r2 = ssTot === 0 ? 1 : 1 - (minRSS/ssTot);

        return { aFit: bestA, r2: r2 };
    }

    // Plot drawing
    function drawPlot(Tcs, ys, TminC, TmaxC, TrefC, fitModel, aFit, genModel){
        // Data points: blue circles with green fill
        const tracePoints = {
            x: Tcs,
            y: ys,
            type: 'scatter',
            mode: 'markers',
            name: 'data',
            marker: {
                size: 9,
                color: 'rgba(34,197,94,1)', // green fill
                line: { width: 2, color: 'rgba(31,119,180,1)'} // blue outline
            }
        };

        // Fit curve
        const nCurve = 200;
        const xs = linspace(TminC, TmaxC, nCurve);
        const TrefK = TrefC + 273.15;
        const ysFit = xs.map(tc => {
            const Tk = tc + 273.15;
            return (fitModel === 'EP') ? yEyring(Tk, TrefK, aFit) : yArrhenius(Tk, TrefK, aFit);
        });
        const traceFit = {
            x: xs,
            y: ysFit,
            type: 'scatter',
            mode: 'lines',
            name: 'fit',
            line: { color: 'rgba(34,197,94,1)', width: 3 } // solid green line
        };

        const title = (fitModel === 'AR' ? 'Arrhenius' : 'Eyring–Polanyi') +
                      ' model fit to ' +
                      (genModel === 'AR' ? 'Arrhenius' : 'Eyring–Polanyi') +
                      ' data';

        const layout = {
            title: { text: title, font: { size: 16 }},
            margin: { l: 60, r: 20, b: 50, t: 60 },
            xaxis: { title: 'T °C', zeroline: false },
            yaxis: { title: 'k(T) / k(Tref)', zeroline: false },
            legend: { orientation: 'h', x: 0.5, xanchor: 'center', y: -0.15 }
        };

        Plotly.newPlot('plot-container', [tracePoints, traceFit], layout, {responsive: true});
    }

    // Update labels
    function refreshValueLabels(){
        labelN.textContent = sliderN.value;
        labelTmin.textContent = formatIntDot(sliderTmin.value);
        labelTmax.textContent = formatIntDot(sliderTmax.value);
        labelTref.textContent = formatIntDot(sliderTref.value);
        labelParamValue.textContent = formatIntDot(sliderParam.value);
    }

    // Main update
    function update(){
        // Button logic handled outside via active classes

        // Dynamic label for parameter based on active model
        if(isEPActive()){
            labelParamName.textContent = '\\(a_{EP}\\)';
        }else{
            labelParamName.textContent = '\\(a_{Ar}\\)';
        }
        ensureMathJax();

        // T_max upper limit logic
        if(btnTmaxLow.classList.contains('active')){
            sliderTmax.max = 1200;
        }else{
            sliderTmax.max = 6000;
        }
        // Constrain slider values
        let tmin = Number(sliderTmin.value);
        let tmax = Number(sliderTmax.value);
        let tref = Number(sliderTref.value);
        const tmaxMax = Number(sliderTmax.max);

        // Keep in overall ranges
        tmax = clamp(tmax, Number(sliderTmax.min), tmaxMax);
        // Ensure Tmin < Tmax
        if(tmin >= tmax){
            // Try to pull Tmin down first, else push Tmax up if possible
            tmin = Math.max(Number(sliderTmin.min), tmax - 1);
            if(tmin >= tmax){
                tmax = clamp(tmin + 1, Number(sliderTmax.min), tmaxMax);
            }
        }
        // Ensure Tref within [Tmin, Tmax]
        tref = clamp(tref, tmin, tmax);

        // Apply corrected values back to sliders
        sliderTmin.value = tmin;
        sliderTmax.value = tmax;
        sliderTref.value = tref;

        refreshValueLabels();

        // Read all current states
        const n = Number(sliderN.value);
        const aParam = Number(sliderParam.value);
        const genModel = isEPActive() ? 'EP' : 'AR';
        const fitModel = isEPActive() ? 'AR' : 'EP';

        // Generate data
        const data = generateData(n, tmin, tmax, tref, aParam, genModel);

        // Fit using the other model
        const fit = fitParameter(data.Tcs, data.ys, tref, fitModel);

        // Update results text
        const fitLabel = fitModel === 'AR' ? '\\(a_{Ar}\\)' : '\\(a_{EP}\\)';
        fitResults.textContent = ''; // clear to let MathJax re-typeset fresh content
        fitResults.innerHTML = fitLabel + ' = ' + formatIntDot(fit.aFit) + ' \\(\\quad r^2 = ' + formatR2(fit.r2) + '\\)';
        ensureMathJax();

        // Draw plot
        drawPlot(data.Tcs, data.ys, tmin, tmax, tref, fitModel, fit.aFit, genModel);
    }

    // Event listeners
    btnModelEP.addEventListener('click', () => { toggleGroup(btnModelEP, [btnModelEP, btnModelAR]); update(); });
    btnModelAR.addEventListener('click', () => { toggleGroup(btnModelAR, [btnModelEP, btnModelAR]); update(); });

    btnTmaxLow.addEventListener('click', () => { toggleGroup(btnTmaxLow, [btnTmaxLow, btnTmaxHigh]); update(); });
    btnTmaxHigh.addEventListener('click', () => { toggleGroup(btnTmaxHigh, [btnTmaxLow, btnTmaxHigh]); update(); });

    [sliderN, sliderTmin, sliderTmax, sliderTref, sliderParam].forEach(sl => {
        sl.addEventListener('input', update);
    });

    // Initial render
    window.addEventListener('load', () => {
        update();
    });
})();
</script>
</body>
</html>
