<body>
  <div id="controls-container">
    <span id="n-display">6</span>
    <button id="btn-n-3">3</button>
    <button id="btn-n-4">4</button>
    <button id="btn-n-5">5</button>
    <button id="btn-n-6">6</button>
    <button id="btn-n-7">7</button>
    <button id="btn-n-8">8</button>
    <button id="btn-n-9">9</button>
    <button id="btn-n-10">10</button>
  </div>

  <div id="canvas-container"></div>

  <script>
    // Global state variable n (3..10), default 6
    let n = 6;

    // Colors
    const COLORS = {
      yellow: '#fefd00',
      red: '#ee1f24',
      blue: '#0048a1',
      stroke: '#000000',
      bg: '#FFFFFF'
    };

    // UI initialization
    document.addEventListener('DOMContentLoaded', () => {
      // Wire up buttons
      [3,4,5,6,7,8,9,10].forEach(val => {
        const btn = document.getElementById(`btn-n-${val}`);
        btn.addEventListener('click', () => updateN(val));
      });
      // Initialize to default (6)
      setActiveButton(6);
      document.getElementById('n-display').textContent = '6';
    });

    function updateN(value) {
      n = Math.max(3, Math.min(10, Number(value)));
      document.getElementById('n-display').textContent = String(n);
      setActiveButton(n);
      // Redraw scene
      if (typeof drawScene === 'function') drawScene();
    }

    function setActiveButton(value) {
      [3,4,5,6,7,8,9,10].forEach(v => {
        const b = document.getElementById(`btn-n-${v}`);
        if (b) b.classList.toggle('active', v === value);
      });
    }

    // p5.js sketch (global mode)
    let cnv;

    function setup() {
      const container = document.getElementById('canvas-container');
      const w = container.clientWidth;
      const h = container.clientHeight;
      cnv = createCanvas(w, h);
      cnv.parent('canvas-container');
      noLoop(); // We'll redraw explicitly on state changes
      drawScene(); // initial render for n=6
    }

    function windowResized() {
      const container = document.getElementById('canvas-container');
      resizeCanvas(container.clientWidth, container.clientHeight);
      drawScene();
    }

    // Core rendering routine
    function drawScene() {
      if (!window.width || !window.height) return;
      background(COLORS.bg);

      // Layout parameters
      const cx = width * 0.25;  // polygon center x
      const cy = height * 0.5;  // polygon center y

      // Radius scaled to available height
      const R = Math.min(150, Math.min(width, height) * 0.40);

      // Angles and steps
      const angleStep = PI / n;

      // Draw 2n-gon sliced into 2n isosceles triangles (Yellow, Red, Blue repeating)
      stroke(COLORS.stroke);
      strokeWeight(2);
      const colorSeq = [COLORS.yellow, COLORS.red, COLORS.blue];

      for (let j = 1; j <= 2 * n; j++) {
        const a1 = (j - 1) * angleStep - PI / 2; // rotate upright
        const a2 = j * angleStep - PI / 2;

        const x1 = cx + R * Math.cos(a1);
        const y1 = cy + R * Math.sin(a1);
        const x2 = cx + R * Math.cos(a2);
        const y2 = cy + R * Math.sin(a2);

        fill(colorSeq[(j - 1) % 3]);
        beginShape();
        vertex(cx, cy);
        vertex(x1, y1);
        vertex(x2, y2);
        endShape(CLOSE);
      }

      // Compute rearranged rectangle dimensions from polygon
      const s = 2 * R * Math.sin(angleStep / 2); // base of triangle (chord)
      const a = R * Math.cos(angleStep / 2);     // apothem

      const rectW = s;
      const rectH = 2 * a;

      // Start position for rectangles (right side)
      const startX = width * 0.55;
      const startY = (height - rectH) / 2;
      const gap = Math.max(16, Math.min(36, width * 0.02));

      // Draw full rectangles: floor(n/2)
      const fullCount = Math.floor(n / 2);
      for (let k = 0; k < fullCount; k++) {
        const x = startX + k * (rectW + gap);
        drawFullRectangle(x, startY, rectW, rectH);
      }

      // If n is odd, draw one "half-rectangle"
      if (n % 2 === 1) {
        // Place after the full rectangles
        const x = startX + fullCount * (rectW + gap);
        // To visually match the screenshots, we render a thinner "half" version.
        const halfW = rectW * 0.5;
        drawHalfRectangle(x, startY, halfW, rectH);
      }
    }

    function drawFullRectangle(x, y, w, h) {
      const cx = x + w / 2;
      const cy = y + h / 2;

      // Triangles meeting at the rectangle center:
      // Top (yellow)
      noStroke();
      fill(COLORS.yellow);
      triangle(x, y, x + w, y, cx, cy);

      // Bottom (red)
      fill(COLORS.red);
      triangle(x, y + h, x + w, y + h, cx, cy);

      // Left (blue)
      fill(COLORS.blue);
      triangle(x, y, x, y + h, cx, cy);

      // Right (yellow)
      fill(COLORS.yellow);
      triangle(x + w, y, x + w, y + h, cx, cy);

      // Outlines
      stroke(COLORS.stroke);
      strokeWeight(2);
      noFill();
      rect(x, y, w, h);
      line(x, y, cx, cy);
      line(x + w, y, cx, cy);
      line(x, y + h, cx, cy);
      line(x + w, y + h, cx, cy);
    }

    // Half-rectangle for odd n
    function drawHalfRectangle(x, y, w, h) {
      const cx = x + w / 2;
      const cy = y + h / 2;

      // Colors hardcoded to match instructions for n=7:
      // Top: Blue, Bottom: Red
      noStroke();
      fill(COLORS.blue);
      triangle(x, y, x + w, y, cx, cy);

      fill(COLORS.red);
      triangle(x, y + h, x + w, y + h, cx, cy);

      // Outline and center connectors for clarity
      stroke(COLORS.stroke);
      strokeWeight(2);
      noFill();
      rect(x, y, w, h);
      line(x, y, cx, cy);
      line(x + w, y, cx, cy);
      line(x, y + h, cx, cy);
      line(x + w, y + h, cx, cy);
    }
  </script>
</body>
</html>
