      <div id="canvas-workspace"></div>
    </div>
    <div id="phase-space-panel">
      <h3 id="phase-space-title">robot phase space, path length = 0.00 rads</h3>
      <div id="canvas-phase-space"></div>
    </div>
  </div>

  <script>
    // ---------------------- Global shared state ----------------------
    const WORK_W = 400, WORK_H = 400;
    const PHASE_W = 400, PHASE_H = 400;

    // Robot parameters
    const base = { x: 200, y: 200 };
    const L1 = 75;
    const L2 = 50;
    const linkR = 10; // capsule radius

    // Obstacles default
    const defaultObstacles = [
      { x: 200, y: 100, radius: 15 },
      { x: 200, y: 300, radius: 15 }
    ];

    // Configurations (angles in radians)
    const defaultStartConfig = { q1: -1.5, q2: 0.5 };
    const defaultGoalConfig  = { q1:  1.5, q2: 1.0 };

    // Dynamic state
    let obstacles = JSON.parse(JSON.stringify(defaultObstacles));
    let startConfig = { ...defaultStartConfig };
    let goalConfig  = { ...defaultGoalConfig };

    // Interaction flags
    let isDraggingObstacle1 = false;
    let isDraggingObstacle2 = false;
    let isDraggingStart = false;
    let isDraggingGoal = false;

    // Phase-space grid/state
    let Q = 2; // resolution power
    let N = 40; // grid dimension (will be set by Q)
    let cellSize = PHASE_W / N;
    let cObs = []; // boolean collision grid
    let distField = []; // BFS distance
    let maxDist = 0;
    let startCell = { ix: 0, iy: 0 };
    let goalCell = { ix: 0, iy: 0 };

    // Path data
    let pathCells = []; // [{ix,iy}]
    let pathAngles = []; // [{q1,q2}]
    let pathCumLen = []; // cumulative arc length
    let totalPathLen = 0;

    // Animation state based on slider P
    let P = 1.0;
    let currentConfig = { q1: defaultStartConfig.q1, q2: defaultStartConfig.q2 }; // interpolated on path

    // HTML elements
    const sliderQ = document.getElementById('slider-q');
    const sliderP = document.getElementById('slider-p');
    const valueQ = document.getElementById('value-q');
    const valueP = document.getElementById('value-p');
    const titlePhase = document.getElementById('phase-space-title');
    const btnReset = document.getElementById('btn-reset');

    // ---------------------- Utility functions ----------------------
    function clamp(v, a, b) { return Math.max(a, Math.min(b, v)); }
    function mod(a, n) { return ((a % n) + n) % n; }

    function setResolutionFromQ(q) {
      Q = q;
      // N grows with Q, but cap for performance
      N = Math.min(200, 10 * Math.pow(2, Q));
      cellSize = PHASE_W / N;
    }

    function angleNormalize(a) {
      // -> [-PI, PI)
      let x = (a + Math.PI) % (2 * Math.PI);
      if (x < 0) x += 2 * Math.PI;
      return x - Math.PI;
    }
    function angleDiffShortest(a, b) {
      // shortest signed diff b - a
      let d = angleNormalize(b - a);
      return d;
    }
    function angleLerp(a, b, t) {
      let d = angleDiffShortest(a, b);
      return angleNormalize(a + d * t);
    }

    // Mapping between angle [-PI,PI] and grid indices [0..N-1]
    function angleToIndex(theta) {
      let u = (theta + Math.PI) / (2 * Math.PI); // 0..1
      let ix = Math.floor(u * N);
      ix = clamp(ix, 0, N - 1);
      return ix;
    }
    function indexToAngle(i) {
      // center of cell i
      return ((i + 0.5) / N) * 2 * Math.PI - Math.PI;
    }

    // Forward kinematics
    function fk(theta1, theta2) {
      const x1 = base.x + L1 * Math.cos(theta1);
      const y1 = base.y + L1 * Math.sin(theta1);
      const x2 = x1 + L2 * Math.cos(theta1 + theta2);
      const y2 = y1 + L2 * Math.sin(theta1 + theta2);
      return {
        base: {x: base.x, y: base.y},
        j1: {x: x1, y: y1},
        ee: {x: x2, y: y2}
      };
    }

    // Distance from point C to segment AB (squared)
    function pointSegDist2(C, A, B) {
      const ABx = B.x - A.x, ABy = B.y - A.y;
      const ACx = C.x - A.x, ACy = C.y - A.y;
      const ab2 = ABx*ABx + ABy*ABy;
      let t = 0;
      if (ab2 > 0) {
        t = (ACx*ABx + ACy*ABy) / ab2;
        t = Math.max(0, Math.min(1, t));
      }
      const Px = A.x + t * ABx;
      const Py = A.y + t * ABy;
      const dx = C.x - Px, dy = C.y - Py;
      return dx*dx + dy*dy;
    }

    function configInCollision(theta1, theta2) {
      const geom = fk(theta1, theta2);
      for (const obs of obstacles) {
        const R = (linkR) + obs.radius;
        const R2 = R * R;
        // link 1
        if (pointSegDist2({x: obs.x, y: obs.y}, geom.base, geom.j1) < R2) return true;
        // link 2
        if (pointSegDist2({x: obs.x, y: obs.y}, geom.j1, geom.ee) < R2) return true;
      }
      return false;
    }

    // Build C-space
    function computeCspace() {
      cObs = new Array(N * N).fill(false);
      for (let iy = 0; iy < N; iy++) {
        const q2 = indexToAngle(iy);
        for (let ix = 0; ix < N; ix++) {
          const q1 = indexToAngle(ix);
          const idx = iy * N + ix;
          cObs[idx] = configInCollision(q1, q2);
        }
      }
    }

    // BFS distance field from goal cell, wrapping edges
    function neighbors4(ix, iy) {
      return [
        {ix: mod(ix+1, N), iy},
        {ix: mod(ix-1, N), iy},
        {ix, iy: mod(iy+1, N)},
        {ix, iy: mod(iy-1, N)}
      ];
    }

    function computeDistanceField() {
      distField = new Array(N * N).fill(Infinity);
      const q = [];
      const gIdx = goalCell.iy * N + goalCell.ix;
      if (!cObs[gIdx]) {
        distField[gIdx] = 0;
        q.push({ix: goalCell.ix, iy: goalCell.iy});
      }
      let head = 0;
      maxDist = 0;
      while (head < q.length) {
        const cur = q[head++];
        const cIdx = cur.iy * N + cur.ix;
        const d = distField[cIdx];
        for (const nb of neighbors4(cur.ix, cur.iy)) {
          const nIdx = nb.iy * N + nb.ix;
          if (!cObs[nIdx] && distField[nIdx] === Infinity) {
            distField[nIdx] = d + 1;
            if (distField[nIdx] > maxDist) maxDist = distField[nIdx];
            q.push(nb);
          }
        }
      }
    }

    function backtrackPathFromStart() {
      pathCells = [];
      pathAngles = [];
      pathCumLen = [];
      totalPathLen = 0;

      const sIdx = startCell.iy * N + startCell.ix;
      if (cObs[sIdx]) {
        // start in obstacle: no path
        updatePhaseTitle();
        return;
      }
      if (distField[sIdx] === Infinity) {
        updatePhaseTitle();
        return;
      }

      let cur = { ix: startCell.ix, iy: startCell.iy };
      pathCells.push({ ...cur });
      pathAngles.push({ q1: indexToAngle(cur.ix), q2: indexToAngle(cur.iy) });
      pathCumLen.push(0);

      while (!(cur.ix === goalCell.ix && cur.iy === goalCell.iy)) {
        const curIdx = cur.iy * N + cur.ix;
        const dcur = distField[curIdx];
        // choose neighbor with strictly lower distance
        let best = null;
        let bestD = Infinity;
        for (const nb of neighbors4(cur.ix, cur.iy)) {
          const nIdx = nb.iy * N + nb.ix;
          const dn = distField[nIdx];
          if (dn < bestD) {
            bestD = dn;
            best = nb;
          }
        }
        if (best == null || bestD >= dcur) {
          // Dead end (shouldn't happen with valid field)
          break;
        }
        // advance
        const prevAng = pathAngles[pathAngles.length - 1];
        const nextAng = { q1: indexToAngle(best.ix), q2: indexToAngle(best.iy) };
        const dth1 = angleDiffShortest(prevAng.q1, nextAng.q1);
        const dth2 = angleDiffShortest(prevAng.q2, nextAng.q2);
        const seg = Math.hypot(dth1, dth2);
        totalPathLen += seg;
        pathCells.push({ ...best });
        pathAngles.push(nextAng);
        pathCumLen.push(totalPathLen);
        cur = best;
        // safety
        if (pathCells.length > N * N + 5) break;
      }
      updatePhaseTitle();
    }

    function updatePhaseTitle() {
      titlePhase.textContent = `robot phase space, path length = ${totalPathLen.toFixed(2)} rads`;
    }

    function fullRecompute() {
      setResolutionFromQ(Q);
      // snap start/goal to valid indices with current N
      startCell = { ix: angleToIndex(startConfig.q1), iy: angleToIndex(startConfig.q2) };
      goalCell  = { ix: angleToIndex(goalConfig.q1),  iy: angleToIndex(goalConfig.q2)  };
      // Recreate fields
      computeCspace();
      computeDistanceField();
      backtrackPathFromStart();
      updateInterpolatedConfigFromP();
      if (phaseSketch) phaseSketch.redraw();
      if (workspaceSketch) workspaceSketch.redraw();
    }

    function recomputeAfterGoalChange() {
      // Called when goal cell changed
      computeDistanceField();
      backtrackPathFromStart();
      updateInterpolatedConfigFromP();
      if (phaseSketch) phaseSketch.redraw();
      if (workspaceSketch) workspaceSketch.redraw();
    }

    function recomputePathOnly() {
      backtrackPathFromStart();
      updateInterpolatedConfigFromP();
      if (phaseSketch) phaseSketch.redraw();
      if (workspaceSketch) workspaceSketch.redraw();
    }

    function updateInterpolatedConfigFromP() {
      if (pathAngles.length === 0) {
        // no path, show start
        currentConfig.q1 = startConfig.q1;
        currentConfig.q2 = startConfig.q2;
        return;
      }
      const L = totalPathLen;
      const t = clamp(P, 0, 1);
      if (L <= 1e-6) {
        currentConfig.q1 = pathAngles[pathAngles.length - 1].q1;
        currentConfig.q2 = pathAngles[pathAngles.length - 1].q2;
        return;
      }
      const targetLen = t * L;
      // find segment
      let idx = 0;
      while (idx < pathCumLen.length && pathCumLen[idx] < targetLen) idx++;
      if (idx === 0) {
        currentConfig.q1 = pathAngles[0].q1;
        currentConfig.q2 = pathAngles[0].q2;
      } else if (idx >= pathCumLen.length) {
        const last = pathAngles[pathAngles.length - 1];
        currentConfig.q1 = last.q1;
        currentConfig.q2 = last.q2;
      } else {
        const prevLen = pathCumLen[idx - 1];
        const segLen = pathCumLen[idx] - prevLen;
        const localT = segLen > 1e-9 ? (targetLen - prevLen) / segLen : 0;
        const a = pathAngles[idx - 1];
        const b = pathAngles[idx];
        currentConfig.q1 = angleLerp(a.q1, b.q1, localT);
        currentConfig.q2 = angleLerp(a.q2, b.q2, localT);
      }
    }

    // ---------------------- Controls wiring ----------------------
    sliderQ.addEventListener('input', (e) => {
      Q = parseInt(e.target.value, 10);
      valueQ.textContent = Q.toString();
      fullRecompute();
    });
    sliderP.addEventListener('input', (e) => {
      P = parseFloat(e.target.value);
      valueP.textContent = P.toFixed(2);
      updateInterpolatedConfigFromP();
      if (workspaceSketch) workspaceSketch.redraw();
      if (phaseSketch) phaseSketch.redraw();
    });
    btnReset.addEventListener('click', () => {
      // Reset sliders
      sliderQ.value = '2';
      sliderP.value = '1';
      valueQ.textContent = '2';
      valueP.textContent = '1.00';
      P = 1.0;
      // Reset obstacles and configs
      obstacles = JSON.parse(JSON.stringify(defaultObstacles));
      startConfig = { ...defaultStartConfig };
      goalConfig  = { ...defaultGoalConfig };
      isDraggingObstacle1 = isDraggingObstacle2 = false;
      isDraggingStart = isDraggingGoal = false;
      fullRecompute();
    });

    // ---------------------- p5 sketches ----------------------
    let workspaceSketch = null;
    let phaseSketch = null;

    // Workspace sketch (robot and obstacles)
    workspaceSketch = new p5((p) => {
      let hoverIdx = -1;

      p.setup = function() {
        const c = p.createCanvas(WORK_W, WORK_H);
        c.parent('canvas-workspace');
        p.noLoop();
      };

      function drawObstacle(obs, isHover) {
        p.push();
        p.noStroke();
        p.fill(170, 110, 60);
        p.circle(obs.x, obs.y, obs.radius * 2);
        // pattern: concentric rings
        p.noFill();
        p.stroke(100, 60, 30, 180);
        for (let r = obs.radius - 3; r > 2; r -= 3) {
          p.circle(obs.x, obs.y, r * 2);
        }
        if (isHover) {
          p.stroke(0, 120, 220);
          p.circle(obs.x, obs.y, (obs.radius + 4) * 2);
        }
        p.pop();
      }

      function drawAngles(theta1, theta2, geom) {
        p.push();
        p.noFill();
        p.stroke(120);
        p.strokeWeight(1.5);
        // theta1 arc around base
        p.arc(geom.base.x, geom.base.y, 60, 60, 0, theta1);
        p.textAlign(p.LEFT, p.CENTER);
        p.noStroke();
        p.fill(120);
        p.text('θ1', geom.base.x + 35, geom.base.y - 20);

        // theta2 arc around joint 1 (relative)
        p.push();
        p.translate(geom.j1.x, geom.j1.y);
        p.rotate(theta1);
        p.stroke(120);
        p.noFill();
        p.arc(0, 0, 48, 48, 0, theta2);
        p.pop();
        p.fill(120);
        p.noStroke();
        p.text('θ2', geom.j1.x + 28, geom.j1.y - 14);
        p.pop();
      }

      function drawRobot(theta1, theta2) {
        const geom = fk(theta1, theta2);

        // Links as capsules
        p.push();
        p.stroke(0);
        p.fill(180);
        p.strokeWeight(1.5);

        // Link 1
        p.push();
        p.translate(geom.base.x, geom.base.y);
        p.rotate(theta1);
        p.rectMode(p.CORNER);
        p.rect(0, -linkR, L1, linkR*2, linkR);
        // base pin
        p.fill(60);
        p.circle(0, 0, 6);
        p.pop();

        // Link 2
        p.push();
        p.translate(geom.j1.x, geom.j1.y);
        p.rotate(theta1 + theta2);
        p.fill(190);
        p.rect(0, -linkR*0.9, L2, linkR*1.8, linkR*0.9);
        p.fill(60);
        p.circle(0, 0, 6);
        // Text on link
        p.push();
        p.fill(40);
        p.noStroke();
        p.translate(L2 * 0.45, -2);
        p.rotate(- (theta1 + theta2) ); // keep text roughly horizontal
        p.textAlign(p.CENTER, p.CENTER);
        p.text('robot arm', 0, 0);
        p.pop();
        p.pop();

        // end effector pin
        p.fill(60);
        p.circle(geom.ee.x, geom.ee.y, 5);

        drawAngles(theta1, theta2, geom);
        p.pop();
      }

      p.draw = function() {
        p.background('#F0F0F0');

        // Draw obstacles
        hoverIdx = -1;
        const m = { x: p.mouseX, y: p.mouseY };
        for (let i = 0; i < obstacles.length; i++) {
          const obs = obstacles[i];
          const d2 = (m.x - obs.x) * (m.x - obs.x) + (m.y - obs.y) * (m.y - obs.y);
          const isHover = d2 <= (obs.radius + 6) * (obs.radius + 6);
          if (isHover) hoverIdx = i;
          drawObstacle(obs, isHover);
        }

        // Draw robot at currentConfig (interpolated along path)
        drawRobot(currentConfig.q1, currentConfig.q2);
      };

      p.mousePressed = function() {
        // Check if pressing on an obstacle
        for (let i = 0; i < obstacles.length; i++) {
          const obs = obstacles[i];
          const d2 = (p.mouseX - obs.x) ** 2 + (p.mouseY - obs.y) ** 2;
          if (d2 <= (obs.radius + 6) ** 2) {
            if (i === 0) isDraggingObstacle1 = true;
            if (i === 1) isDraggingObstacle2 = true;
            break;
          }
        }
      };

      p.mouseDragged = function() {
        let changed = false;
        if (isDraggingObstacle1) {
          obstacles[0].x = clamp(p.mouseX, obstacles[0].radius, WORK_W - obstacles[0].radius);
          obstacles[0].y = clamp(p.mouseY, obstacles[0].radius, WORK_H - obstacles[0].radius);
          changed = true;
        }
        if (isDraggingObstacle2) {
          obstacles[1].x = clamp(p.mouseX, obstacles[1].radius, WORK_W - obstacles[1].radius);
          obstacles[1].y = clamp(p.mouseY, obstacles[1].radius, WORK_H - obstacles[1].radius);
          changed = true;
        }
        if (changed) {
          computeCspace();
          computeDistanceField();
          backtrackPathFromStart();
          updateInterpolatedConfigFromP();
          p.redraw();
          if (phaseSketch) phaseSketch.redraw();
        }
      };

      p.mouseReleased = function() {
        isDraggingObstacle1 = false;
        isDraggingObstacle2 = false;
      };
    });

    // Phase-space sketch (C-space, BFS and path)
    phaseSketch = new p5((p) => {
      const markerR = 7;
      let hoverStart = false;
      let hoverGoal = false;
      let invalidDrag = false; // for showing invalid drop
      let justDragging = false;

      p.setup = function() {
        const c = p.createCanvas(PHASE_W, PHASE_H);
        c.parent('canvas-phase-space');
        p.noLoop();
      };

      function drawGrid() {
        p.noStroke();
        // distance-based grayscale and obstacles in red
        for (let iy = 0; iy < N; iy++) {
          for (let ix = 0; ix < N; ix++) {
            const idx = iy * N + ix;
            const x = ix * cellSize;
            const y = iy * cellSize;

            if (cObs[idx]) {
              p.fill('#CC0000');
            } else {
              let br = 10;
              if (distField[idx] !== Infinity) {
                const v = distField[idx];
                const b = p.map(v, 0, Math.max(1, maxDist), 255, 30, true);
                br = b;
              }
              p.fill(br);
            }
            p.rect(x, y, cellSize, cellSize);
          }
        }

        // Axes labels
        p.fill(60);
        p.noStroke();
        p.textAlign(p.CENTER, p.CENTER);
        p.text('θ1', p.width / 2, p.height - 8);
        p.push();
        p.translate(8, p.height / 2);
        p.rotate(-Math.PI / 2);
        p.text('θ2', 0, 0);
        p.pop();

        // Draw path as thick green polyline
        if (pathCells.length > 1) {
          p.stroke(0, 255, 0);
          p.strokeWeight(Math.max(2, cellSize * 0.3));
          p.noFill();
          p.beginShape();
          for (const cell of pathCells) {
            const cx = (cell.ix + 0.5) * cellSize;
            const cy = (cell.iy + 0.5) * cellSize;
            p.vertex(cx, cy);
          }
          p.endShape();
        }

        // Draw traversal marker along the path
        if (pathCells.length > 0) {
          // find interpolated pixel from currentConfig
          const x = (angleToIndex(currentConfig.q1) + 0.5) * cellSize;
          const y = (angleToIndex(currentConfig.q2) + 0.5) * cellSize;
          p.noStroke();
          p.fill(40, 120, 255);
          p.circle(x, y, Math.max(4, cellSize * 0.4));
        }
      }

      function drawMarker(cell, isHover, isInvalid=false) {
        const cx = (cell.ix + 0.5) * cellSize;
        const cy = (cell.iy + 0.5) * cellSize;
        p.push();
        p.stroke(255);
        if (isInvalid) {
          p.fill(220, 30, 30);
          p.stroke(255);
        } else {
          p.fill(0, 200, 0);
          p.stroke(255);
        }
        p.strokeWeight(1.5);
        const r = markerR + (isHover ? 2 : 0);
        p.circle(cx, cy, r * 2);
        // crosshair
        p.stroke(255);
        p.line(cx - r, cy, cx + r, cy);
        p.line(cx, cy - r, cx, cy + r);
        p.pop();
      }

      function mouseToCell(mx, my) {
        const ix = clamp(Math.floor(mx / cellSize), 0, N - 1);
        const iy = clamp(Math.floor(my / cellSize), 0, N - 1);
        return { ix, iy };
      }

      p.draw = function() {
        p.background(240);
        drawGrid();

        // Hover status
        const ms = mouseToCell(p.mouseX, p.mouseY);
        const sCx = (startCell.ix + 0.5) * cellSize;
        const sCy = (startCell.iy + 0.5) * cellSize;
        const gCx = (goalCell.ix + 0.5) * cellSize;
        const gCy = (goalCell.iy + 0.5) * cellSize;
        hoverStart = p.dist(p.mouseX, p.mouseY, sCx, sCy) <= markerR + 4;
        hoverGoal = p.dist(p.mouseX, p.mouseY, gCx, gCy) <= markerR + 4;

        // Draw start and goal markers
        const startInvalid = cObs[startCell.iy * N + startCell.ix];
        const goalInvalid = cObs[goalCell.iy * N + goalCell.ix];
        drawMarker(startCell, hoverStart, startInvalid || (isDraggingStart && invalidDrag));
        drawMarker(goalCell, hoverGoal, goalInvalid || (isDraggingGoal && invalidDrag));
      };

      p.mousePressed = function() {
        const sCx = (startCell.ix + 0.5) * cellSize;
        const sCy = (startCell.iy + 0.5) * cellSize;
        const gCx = (goalCell.ix + 0.5) * cellSize;
        const gCy = (goalCell.iy + 0.5) * cellSize;

        const overStart = p.dist(p.mouseX, p.mouseY, sCx, sCy) <= markerR + 4;
        const overGoal  = p.dist(p.mouseX, p.mouseY, gCx, gCy) <= markerR + 4;
        if (overStart) {
          isDraggingStart = true;
          justDragging = true;
        } else if (overGoal) {
          isDraggingGoal = true;
          justDragging = true;
        }
      };

      p.mouseDragged = function() {
        if (!isDraggingStart && !isDraggingGoal) return;
        const cell = mouseToCell(p.mouseX, p.mouseY);
        invalidDrag = cObs[cell.iy * N + cell.ix];

        if (isDraggingStart) {
          // Show as being dragged (even if invalid)
          startCell = cell;
          startConfig.q1 = indexToAngle(startCell.ix);
          startConfig.q2 = indexToAngle(startCell.iy);
          // Update path if valid
          if (!invalidDrag) {
            recomputePathOnly();
          } else {
            // still redraw to show red marker
            phaseSketch.redraw();
            // also update workspace to show robot at temp start config while dragging
            currentConfig.q1 = startConfig.q1;
            currentConfig.q2 = startConfig.q2;
            workspaceSketch.redraw();
          }
        }
        if (isDraggingGoal) {
          goalCell = cell;
          goalConfig.q1 = indexToAngle(goalCell.ix);
          goalConfig.q2 = indexToAngle(goalCell.iy);
          if (!invalidDrag) {
            recomputeAfterGoalChange();
          } else {
            phaseSketch.redraw();
            currentConfig.q1 = goalConfig.q1;
            currentConfig.q2 = goalConfig.q2;
            workspaceSketch.redraw();
          }
        }
      };

      p.mouseReleased = function() {
        if (justDragging) {
          // If released on invalid, revert to nearest previous valid cell
          if (invalidDrag) {
            if (isDraggingStart) {
              // revert using current angles mapped back to indices on existing path start (if any)
              startConfig = { ...defaultStartConfig }; // fallback
              startCell = { ix: angleToIndex(startConfig.q1), iy: angleToIndex(startConfig.q2) };
            }
            if (isDraggingGoal) {
              goalConfig = { ...defaultGoalConfig };
              goalCell = { ix: angleToIndex(goalConfig.q1), iy: angleToIndex(goalConfig.q2) };
            }
            fullRecompute();
          } else {
            // already recomputed during drag
            updateInterpolatedConfigFromP();
            workspaceSketch.redraw();
          }
        }
        isDraggingStart = false;
        isDraggingGoal = false;
        invalidDrag = false;
        justDragging = false;
      };
    });

    // ---------------------- Initialization ----------------------
    function init() {
      // Slider initial values
      valueQ.textContent = sliderQ.value;
      valueP.textContent = parseFloat(sliderP.value).toFixed(2);

      // Initialize resolution and compute data
      setResolutionFromQ(Q);
      startCell = { ix: angleToIndex(startConfig.q1), iy: angleToIndex(startConfig.q2) };
      goalCell  = { ix: angleToIndex(goalConfig.q1),  iy: angleToIndex(goalConfig.q2)  };

      computeCspace();
      computeDistanceField();
      backtrackPathFromStart();
      updateInterpolatedConfigFromP();

      // Initial draws
      if (phaseSketch) phaseSketch.redraw();
      if (workspaceSketch) workspaceSketch.redraw();
    }

    // Run initialization after the sketches are ready
    window.addEventListener('load', init);
  </script>
</body>
</html>
