  }
</style>
</head>
<body>
  <div id="main-container">
    <div id="control-panel">
      <div class="group">
        <h4>problem</h4>
        <div class="row">
          <label>dimension</label>
          <div>
            <button id="btn-2d" class="btn active">2D</button>
            <button id="btn-3d" class="btn">3D</button>
          </div>
        </div>
        <div class="row">
          <label>random seed</label>
          <input type="range" id="slider-seed" min="0" max="1000" step="1" value="525">
          <span id="seed-value" class="value-box">525</span>
        </div>
        <div class="row">
          <label>number</label>
          <input type="range" id="slider-number" min="5" max="150" step="1" value="40">
          <span id="number-value" class="value-box">40</span>
        </div>
      </div>

      <div class="group">
        <h4>method</h4>
        <div class="row">
          <button id="btn-prim" class="btn active">Prim</button>
          <button id="btn-kruskal" class="btn">Kruskal</button>
        </div>
      </div>

      <div class="group">
        <h4>process</h4>
        <div class="row">
          <button id="btn-play" class="btn">►</button>
          <button id="btn-pause" class="btn" disabled>||</button>
          <button id="btn-reset" class="btn">|◄</button>
        </div>
        <div class="hint">Press Play to add one MST edge per step.</div>
      </div>
    </div>

    <div id="canvas-container">
      <div class="canvas-frame" aria-hidden="true"></div>
      <!-- p5.js canvas will be injected here -->
    </div>
  </div>

<script>
/* ---------- State ---------- */
let dimension = '2D';           // '2D' or '3D'
let algorithm = 'prim';         // 'prim' or 'kruskal'
let animationState = 'stopped'; // 'stopped' | 'playing' | 'paused'
let seed = 525;
let numPoints = 40;

let points = [];
let mstSteps = [];   // Array of {i, j, w}
let currentStep = 0;
let isFinished = false;

let canvas = null;
let cubeSize = 400; // for 3D bounding box
let frameTicker = 0;
const framesPerStep = 10;

/* ---------- UI Helpers ---------- */
function setActive(button, groupButtons){
  groupButtons.forEach(b => b.classList.remove('active'));
  button.classList.add('active');
}
function updateProcessButtons(){
  const btnPlay  = document.getElementById('btn-play');
  const btnPause = document.getElementById('btn-pause');
  const btnReset = document.getElementById('btn-reset');

  if(isFinished){
    btnPlay.disabled = true;
    btnPause.disabled = true;
    btnReset.disabled = false;
    return;
  }

  if(animationState === 'playing'){
    btnPlay.disabled = true;
    btnPause.disabled = false;
    btnReset.disabled = false;
  }else if(animationState === 'paused'){
    btnPlay.disabled = false;
    btnPause.disabled = true;
    btnReset.disabled = false;
  }else{ // stopped (but not finished)
    btnPlay.disabled = false;
    btnPause.disabled = true;
    btnReset.disabled = false;
  }
}
function updateValueDisplays(){
  document.getElementById('seed-value').textContent = seed;
  document.getElementById('number-value').textContent = numPoints;
}

/* ---------- Point Generation ---------- */
function generatePoints(){
  points = [];
  randomSeed(seed); // p5 seeded randomness
  if(dimension === '2D'){
    const margin = 32;
    for(let i=0;i<numPoints;i++){
      const x = random(margin, width - margin);
      const y = random(margin, height - margin);
      points.push({x, y});
    }
  }else{
    const usable = Math.min(width, height) * 0.8;
    cubeSize = usable;
    const half = usable/2 - 18;
    for(let i=0;i<numPoints;i++){
      const x = random(-half, half);
      const y = random(-half, half);
      const z = random(-half, half);
      points.push({x, y, z});
    }
  }
}

/* ---------- Distance ---------- */
function distBetween(a, b){
  if(dimension === '2D'){
    const dx = a.x - b.x, dy = a.y - b.y;
    return Math.hypot(dx, dy);
  }else{
    const dx = a.x - b.x, dy = a.y - b.y, dz = a.z - b.z;
    return Math.hypot(dx, dy, dz);
  }
}

/* ---------- Algorithms ---------- */
function computePrimSteps(){
  const n = points.length;
  if(n <= 1) return [];
  const visited = new Array(n).fill(false);
  visited[0] = true;
  const steps = [];
  for(let step=0; step < n-1; step++){
    let best = null; // {i, j, w}
    for(let i=0;i<n;i++){
      if(!visited[i]) continue;
      for(let j=0;j<n;j++){
        if(visited[j]) continue;
        const w = distBetween(points[i], points[j]);
        if(!best || w < best.w){
          best = {i, j, w};
        }
      }
    }
    if(best){
      steps.push(best);
      visited[best.j] = true;
    }else{
      break;
    }
  }
  return steps;
}

function computeKruskalSteps(){
  const n = points.length;
  if(n <= 1) return [];
  const edges = [];
  for(let i=0;i<n;i++){
    for(let j=i+1;j<n;j++){
      edges.push({i, j, w: distBetween(points[i], points[j])});
    }
  }
  edges.sort((a,b)=>a.w-b.w);

  // Disjoint Set Union (Union-Find)
  const parent = Array.from({length:n}, (_,i)=>i);
  const rank = new Array(n).fill(0);
  function find(x){
    if(parent[x] !== x) parent[x] = find(parent[x]);
    return parent[x];
  }
  function unite(a,b){
    let ra = find(a), rb = find(b);
    if(ra === rb) return false;
    if(rank[ra] < rank[rb]) [ra, rb] = [rb, ra];
    parent[rb] = ra;
    if(rank[ra] === rank[rb]) rank[ra]++;
    return true;
  }

  const steps = [];
  for(const e of edges){
    if(unite(e.i, e.j)){
      steps.push(e);
      if(steps.length === n-1) break;
    }
  }
  return steps;
}

function recomputeMST(){
  if(algorithm === 'prim'){
    mstSteps = computePrimSteps();
  }else{
    mstSteps = computeKruskalSteps();
  }
  currentStep = 0;
  isFinished = mstSteps.length === 0;
}

/* ---------- Resets ---------- */
function fullReset() {
  animationState = 'stopped';
  createOrRecreateCanvas(); // ensure correct renderer
  generatePoints();
  recomputeMST();
  updateProcessButtons();
}
function partialReset() {
  animationState = 'stopped';
  recomputeMST();
  updateProcessButtons();
}

/* ---------- Canvas Management ---------- */
function createOrRecreateCanvas(){
  const container = document.getElementById('canvas-container');
  const w = container.clientWidth - 48; // leave frame inset
  const h = container.clientHeight - 48;
  if(canvas) canvas.remove();
  if(dimension === '3D'){
    canvas = createCanvas(w, h, WEBGL);
  }else{
    canvas = createCanvas(w, h);
  }
  canvas.parent('canvas-container');
}

/* ---------- Drawing ---------- */
function draw2D(){
  background('#E0E0E0');

  // Draw edges (MST progress)
  stroke('red');
  strokeWeight(2);
  for(let k=0; k<currentStep; k++){
    const e = mstSteps[k];
    if(!e) break;
    const a = points[e.i], b = points[e.j];
    line(a.x, a.y, b.x, b.y);
  }

  // Draw points
  noStroke();
  fill('blue');
  for(const p of points){
    circle(p.x, p.y, 9);
  }
}

function draw3D(){
  background('#E0E0E0');
  orbitControl();
  // Box wireframe
  push();
  noFill();
  stroke(130);
  strokeWeight(1);
  box(cubeSize);
  pop();

  // Edges
  stroke('red');
  strokeWeight(3);
  for(let k=0; k<currentStep; k++){
    const e = mstSteps[k];
    if(!e) break;
    const a = points[e.i], b = points[e.j];
    line(a.x, a.y, a.z, b.x, b.y, b.z);
  }

  // Points as spheres
  ambientLight(255);
  noStroke();
  ambientMaterial(40, 96, 235);
  for(const p of points){
    push();
    translate(p.x, p.y, p.z);
    sphere(6, 12, 8);
    pop();
  }
}

/* ---------- Animation Loop ---------- */
function advanceIfPlaying(){
  if(animationState !== 'playing' || isFinished) return;
  frameTicker++;
  if(frameTicker % framesPerStep === 0){
    if(currentStep < mstSteps.length){
      currentStep++;
    }
    if(currentStep >= mstSteps.length){
      animationState = 'stopped';
      isFinished = true;
      updateProcessButtons();
    }
  }
}

/* ---------- p5 global callbacks ---------- */
function setup(){
  // Initial canvas (2D by default); will be recreated on fullReset
  createOrRecreateCanvas();
}
function draw(){
  if(dimension === '3D') draw3D(); else draw2D();
  advanceIfPlaying();
}
function windowResized(){
  // Keep canvas sized to container; no data regeneration
  createOrRecreateCanvas();
}

/* ---------- DOM wiring ---------- */
window.addEventListener('DOMContentLoaded', () => {
  // Default UI values
  document.getElementById('btn-2d').classList.add('active');
  document.getElementById('btn-prim').classList.add('active');
  updateValueDisplays();
  fullReset();

  // Dimension buttons
  const btn2D = document.getElementById('btn-2d');
  const btn3D = document.getElementById('btn-3d');
  btn2D.addEventListener('click', () => {
    if(dimension === '2D') return;
    dimension = '2D';
    setActive(btn2D, [btn2D, btn3D]);
    fullReset();
  });
  btn3D.addEventListener('click', () => {
    if(dimension === '3D') return;
    dimension = '3D';
    setActive(btn3D, [btn2D, btn3D]);
    fullReset();
  });

  // Sliders
  const sliderSeed = document.getElementById('slider-seed');
  const sliderNumber = document.getElementById('slider-number');
  sliderSeed.addEventListener('input', () => {
    seed = +sliderSeed.value;
    updateValueDisplays();
    fullReset();
  });
  sliderNumber.addEventListener('input', () => {
    numPoints = +sliderNumber.value;
    updateValueDisplays();
    fullReset();
  });

  // Algorithm buttons
  const btnPrim = document.getElementById('btn-prim');
  const btnKruskal = document.getElementById('btn-kruskal');
  btnPrim.addEventListener('click', () => {
    if(algorithm === 'prim') return;
    algorithm = 'prim';
    setActive(btnPrim, [btnPrim, btnKruskal]);
    partialReset();
  });
  btnKruskal.addEventListener('click', () => {
    if(algorithm === 'kruskal') return;
    algorithm = 'kruskal';
    setActive(btnKruskal, [btnPrim, btnKruskal]);
    partialReset();
  });

  // Process controls
  document.getElementById('btn-play').addEventListener('click', () => {
    if(isFinished) return;
    animationState = 'playing';
    updateProcessButtons();
  });
  document.getElementById('btn-pause').addEventListener('click', () => {
    if(animationState !== 'playing') return;
    animationState = 'paused';
    updateProcessButtons();
  });
  document.getElementById('btn-reset').addEventListener('click', () => {
    animationState = 'stopped';
    currentStep = 0;
    isFinished = (mstSteps.length === 0);
    updateProcessButtons();
  });
});
</script>
</body>
</html>
