<script>
(function(){
  const selectMethod = document.getElementById('select-method');
  const slider1 = document.getElementById('slider-num1');
  const slider2 = document.getElementById('slider-num2');
  const btnYes = document.getElementById('btn-int-yes');
  const btnNo = document.getElementById('btn-int-no');
  const out = document.getElementById('output-display');

  // Helper: format numbers (trim trailing zeros)
  function fmt(n){
    if (!isFinite(n)) return String(n);
    const s = n.toFixed(6);
    return s.replace(/\.?0+$/,'');
  }

  // Helper: count decimal places safely from a fixed string
  function decimalPlaces(n){
    const s = fmt(n);
    const idx = s.indexOf('.');
    return idx === -1 ? 0 : (s.length - idx - 1);
  }

  // Helper: transform to integer string and factor
  function toIntStrAndFactor(n){
    const s = fmt(n); // e.g., "16.1" or "2"
    const dp = s.indexOf('.');
    if (dp === -1){
      return { intStr: s.replace(/^(-)?0+(?=\d)/,'$1') || '0', factor: 1 };
    }else{
      const digits = s.length - dp - 1;
      const intStr = s.replace('.','');
      const factor = Math.pow(10, digits);
      return { intStr, factor };
    }
  }

  function clearOutput(){
    out.innerHTML = '';
  }
  function addLine(text){
    const p = document.createElement('p');
    p.textContent = text;
    out.appendChild(p);
  }

  function updateDisplay(){
    const method = selectMethod.value;
    const num1 = parseFloat(slider1.value);
    const num2 = parseFloat(slider2.value);

    const a = toIntStrAndFactor(num1);
    const b = toIntStrAndFactor(num2);

    const productInts = (parseInt(a.intStr,10) * parseInt(b.intStr,10));
    const productFactors = a.factor * b.factor;
    const roundedProduct = Math.round(num1) * Math.round(num2);
    const result = num1 * num2;

    clearOutput();

    if(method === 'estimation'){
      addLine(`start with: ${fmt(num1)} × ${fmt(num2)}`);
      addLine(`convert to: ${a.intStr}. × ${b.intStr}. = ${productInts}.`);
      addLine(`estimate as: ${Math.round(num1)} × ${Math.round(num2)} = ${roundedProduct}`);
      addLine(`result is: ${fmt(result)}`);
    }else{ // place values
      addLine(`start with: ${fmt(num1)} × ${fmt(num2)}`);

      const leftPart = (a.factor === 1) ? `${fmt(num1)}` : `(${a.intStr}. ÷ ${a.factor})`;
      const rightPart = (b.factor === 1) ? `${fmt(num2)}` : `(${b.intStr}. ÷ ${b.factor})`;
      addLine(`rewrite as: ${leftPart} × ${rightPart}`);

      addLine(`= (${a.intStr}. × ${b.intStr}.) ÷ (${a.factor} × ${b.factor})`);
      addLine(`= ${productInts}. ÷ ${productFactors}`);
      addLine(`= ${fmt(result)}`);
    }
  }

  // Integer toggle behavior
  function setIntegerMode(isInt){
    if(isInt){
      slider2.step = '1';
      slider2.value = String(Math.round(parseFloat(slider2.value)));
      btnYes.classList.add('active'); btnYes.classList.remove('inactive');
      btnNo.classList.remove('active'); btnNo.classList.add('inactive');
      btnYes.setAttribute('aria-pressed','true');
      btnNo.setAttribute('aria-pressed','false');
    }else{
      slider2.step = '0.1';
      btnNo.classList.add('active'); btnNo.classList.remove('inactive');
      btnYes.classList.remove('active'); btnYes.classList.add('inactive');
      btnNo.setAttribute('aria-pressed','true');
      btnYes.setAttribute('aria-pressed','false');
    }
    updateDisplay();
  }

  // Listeners
  selectMethod.addEventListener('change', updateDisplay);
  slider1.addEventListener('input', updateDisplay);
  slider2.addEventListener('input', updateDisplay);
  btnYes.addEventListener('click', ()=>setIntegerMode(true));
  btnNo.addEventListener('click', ()=>setIntegerMode(false));

  // Initialize
  updateDisplay();
})();
</script>
</body>
</html>
