    border:1px solid #aaa;
    background:#eee;
    border-radius:4px;
    cursor:pointer;
  }
  #control-panel button:disabled {
    opacity:0.45;
    cursor:not-allowed;
  }
  #control-panel button.active {
    background:#d6f5d6;
    border-color:#7ec27e;
  }
  #visualization-area {
    position:relative;
    padding:8px;
  }
  #three-js-canvas-container, #plotly-chart-container {
    width:100%;
    height:460px;
    border-top:1px solid #eee;
    border-bottom:1px solid #eee;
    background:#ffffff;
  }
  #plotly-chart-container { display:none; }
  #description-area {
    display:flex;
    justify-content:space-between;
    gap:10px;
    padding:8px 14px 14px 14px;
  }
  #label-left, #label-right {
    font-size:14px;
    margin:0;
  }
</style>
</head>
<body>
<div id="demo-container">
  <div id="control-panel">
    <select id="select-vibration-mode">
      <option value="symmetric-stretch" selected>symmetric stretch</option>
      <option value="asymmetric-stretch">stretch (triply degenerate)</option>
      <option value="bending-e">bend (doubly degenerate)</option>
      <option value="bending-f2">bend (triply degenerate)</option>
      <option value="ir-spectrum">IR spectrum</option>
    </select>
    <span>run</span>
    <button id="btn-play">►</button>
    <button id="btn-pause">||</button>
    <button id="btn-reset">|◄</button>
  </div>

  <div id="visualization-area">
    <div id="three-js-canvas-container"></div>
    <div id="plotly-chart-container"></div>
  </div>

  <div id="description-area">
    <p id="label-left" style="color:blue;">stretching A₁ Raman active 2916 cm⁻¹</p>
    <p id="label-right" style="color:blue;">no change in the molecular dipole moment</p>
  </div>
</div>

<script>
(() => {
  // DOM references
  const modeSelect = document.getElementById('select-vibration-mode');
  const btnPlay = document.getElementById('btn-play');
  const btnPause = document.getElementById('btn-pause');
  const btnReset = document.getElementById('btn-reset');
  const threeContainer = document.getElementById('three-js-canvas-container');
  const plotContainer = document.getElementById('plotly-chart-container');
  const descArea = document.getElementById('description-area');
  const labelLeft = document.getElementById('label-left');
  const labelRight = document.getElementById('label-right');

  // Global state
  let renderer, scene;
  let camLeft, camRight, controlsLeft, controlsRight, activeControls = null;
  let carbon, H = [], bonds = [];
  let moleculeGroup, vectorsGroup, netArrow;
  let animationId = null, playing = false, t = 0, lastTime = 0;
  let spectrumPlotted = false;

  // Constants for geometry
  const bondLen = 1.35; // initial C-H distance
  const H_dirs = [
    new THREE.Vector3( 1,  1,  1).normalize(),
    new THREE.Vector3( 1, -1, -1).normalize(),
    new THREE.Vector3(-1,  1, -1).normalize(),
    new THREE.Vector3(-1, -1,  1).normalize()
  ];
  const H_init = H_dirs.map(v => v.clone().multiplyScalar(bondLen));
  const perpAxis = new THREE.Vector3(0.0, 1.0, 0.12).normalize();
  const H_perp = H_dirs.map(v => {
    let p = new THREE.Vector3().crossVectors(v, perpAxis);
    if (p.length() < 1e-3) p = new THREE.Vector3().crossVectors(v, new THREE.Vector3(1,0,0));
    return p.normalize();
  });

  // Utility: set UI enabled/disabled for animation controls
  function setControlsEnabled(enabled) {
    btnPlay.disabled = !enabled;
    btnPause.disabled = !enabled;
    btnReset.disabled = !enabled;
  }

  // Initialize three.js scene
  function initThree() {
    scene = new THREE.Scene();

    renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true, preserveDrawingBuffer: true });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.setSize(threeContainer.clientWidth, threeContainer.clientHeight);
    renderer.setScissorTest(true);
    threeContainer.appendChild(renderer.domElement);

    // Cameras
    camLeft  = new THREE.PerspectiveCamera(45, 0.5 * threeContainer.clientWidth / threeContainer.clientHeight, 0.1, 100);
    camRight = new THREE.PerspectiveCamera(45, 0.5 * threeContainer.clientWidth / threeContainer.clientHeight, 0.1, 100);
    camLeft.position.set(4, 3.5, 4.5);
    camRight.position.set(4, 3.5, 4.5);

    // Controls for each camera (toggle which is active based on pointer)
    controlsLeft  = new THREE.OrbitControls(camLeft,  renderer.domElement);
    controlsRight = new THREE.OrbitControls(camRight, renderer.domElement);
    controlsLeft.target.set(0,0,0);
    controlsRight.target.set(0,0,0);
    controlsLeft.enableDamping = controlsRight.enableDamping = true;
    activeControls = controlsLeft;

    renderer.domElement.addEventListener('pointerdown', (ev) => {
      const rect = renderer.domElement.getBoundingClientRect();
      const x = ev.clientX - rect.left;
      if (x < rect.width/2) {
        activeControls = controlsLeft;
      } else {
        activeControls = controlsRight;
      }
    });

    // Lights
    const amb = new THREE.AmbientLight(0xffffff, 0.7);
    const dir = new THREE.DirectionalLight(0xffffff, 0.7);
    dir.position.set(5,5,6);
    scene.add(amb, dir);

    // Groups on separate layers so each viewport shows only its content
    moleculeGroup = new THREE.Group();
    vectorsGroup = new THREE.Group();
    moleculeGroup.layers.set(0); // seen by camLeft
    vectorsGroup.layers.set(1);  // seen by camRight
    scene.add(moleculeGroup, vectorsGroup);

    // Build methane molecule (left viewport)
    const matC = new THREE.MeshLambertMaterial({ color: 0x111111 });
    const matH = new THREE.MeshLambertMaterial({ color: 0xe9f2ff, emissive: 0x0, reflectivity: 0.2 });
    carbon = new THREE.Mesh(new THREE.SphereGeometry(0.55, 48, 48), matC);
    moleculeGroup.add(carbon);
    for (let i=0;i<4;i++) {
      const h = new THREE.Mesh(new THREE.SphereGeometry(0.28, 32, 32), matH);
      h.position.copy(H_init[i]);
      H.push(h);
      moleculeGroup.add(h);

      const cyl = createBondCylinder(new THREE.Vector3(0,0,0), H_init[i]);
      bonds.push(cyl);
      moleculeGroup.add(cyl);
    }

    // Vector arrows (right viewport)
    const arrowColor = 0x222222;
    for (let i=0;i<4;i++) {
      const dirV = H_init[i].clone().normalize();
      const len = H_init[i].length();
      const arrow = new THREE.ArrowHelper(dirV, new THREE.Vector3(0,0,0), len, arrowColor, 0.18, 0.1);
      vectorsGroup.add(arrow);
    }
    netArrow = new THREE.ArrowHelper(new THREE.Vector3(1,0,0), new THREE.Vector3(0,0,0), 0.01, 0xff2b2b, 0.25, 0.15);
    netArrow.visible = false;
    vectorsGroup.add(netArrow);

    window.addEventListener('resize', onResize);
    onResize();
    resetPositions();
    renderOnce();
  }

  function createBondCylinder(a, b) {
    const dir = new THREE.Vector3().subVectors(b, a);
    const len = dir.length();
    const geom = new THREE.CylinderGeometry(0.07, 0.07, len, 24, 1, false);
    const mat = new THREE.MeshBasicMaterial({ color: 0x808080 });
    const cyl = new THREE.Mesh(geom, mat);
    alignCylinder(cyl, a, b);
    return cyl;
  }

  function alignCylinder(cyl, a, b) {
    const dir = new THREE.Vector3().subVectors(b, a);
    const len = dir.length();
    cyl.scale.set(1, 1, 1); // reset scale
    cyl.position.copy(a).addScaledVector(dir, 0.5);
    cyl.lookAt(b);
    // Cylinder default is aligned with Y; rotate to align with Z then use lookAt trick
    cyl.rotation.x += Math.PI / 2;
    cyl.geometry.dispose();
    cyl.geometry = new THREE.CylinderGeometry(0.07, 0.07, len, 24, 1, false);
  }

  function onResize() {
    if (!renderer) return;
    const w = threeContainer.clientWidth;
    const h = threeContainer.clientHeight;
    renderer.setSize(w, h);
    const aspectHalf = (w/2) / h;
    camLeft.aspect = aspectHalf;
    camRight.aspect = aspectHalf;
    camLeft.updateProjectionMatrix();
    camRight.updateProjectionMatrix();
    renderOnce();
  }

  // Animation controls
  function play() {
    if (playing) return;
    playing = true;
    btnPlay.classList.add('active');
    lastTime = performance.now();
    loop();
  }
  function pause() {
    if (!playing) return;
    playing = false;
    btnPlay.classList.remove('active');
    if (animationId) cancelAnimationFrame(animationId);
    animationId = null;
  }
  function resetPositions() {
    pause();
    t = 0;
    // reset H positions
    for (let i=0;i<4;i++) {
      H[i].position.copy(H_init[i]);
      alignCylinder(bonds[i], new THREE.Vector3(0,0,0), H[i].position);
      // update arrows
      updateArrowFromBond(i, H[i].position);
    }
    updateNetDipole();
    renderOnce();
  }

  function loop(now) {
    animationId = requestAnimationFrame(loop);
    const dt = Math.min(0.05, (now - lastTime) / 1000 || 0.016);
    lastTime = now;
    t += dt;
    updateFrame();
    renderOnce();
  }

  // Rendering with two viewports and layers
  function renderOnce() {
    if (!renderer) return;
    const w = renderer.domElement.width / renderer.getPixelRatio();
    const h = renderer.domElement.height / renderer.getPixelRatio();
    renderer.setClearColor(0xffffff, 1);

    // Left viewport: molecule (layer 0)
    renderer.setViewport(0, 0, w/2, h);
    renderer.setScissor(0, 0, w/2, h);
    camLeft.layers.set(0);
    scene.background = null;
    activeControls?.update();
    renderer.render(scene, camLeft);

    // Right viewport: vectors (layer 1)
    renderer.setViewport(w/2, 0, w/2, h);
    renderer.setScissor(w/2, 0, w/2, h);
    camRight.layers.set(1);
    renderer.render(scene, camRight);
  }

  // Update logic per mode
  function updateFrame() {
    const mode = modeSelect.value;
    const s = Math.sin(getOmega(mode) * t);
    const A = getAmplitude(mode);

    for (let i=0;i<4;i++) {
      const init = H_init[i];
      const radial = H_dirs[i];
      const tang = H_perp[i];

      let disp = new THREE.Vector3();

      if (mode === 'symmetric-stretch') {
        disp.addScaledVector(radial, A * s);
      } else if (mode === 'asymmetric-stretch') {
        const coeff = [1.0, -1.0, 0.35, -0.35][i];
        disp.addScaledVector(radial, A * coeff * s);
      } else if (mode === 'bending-e') {
        const coeff = [1, -1, -1, 1][i];
        disp.addScaledVector(tang, A * coeff * s);
      } else if (mode === 'bending-f2') {
        const coeff = [1.0, 0.35, -0.6, -0.2][i];
        disp.addScaledVector(tang, A * coeff * s);
      }
      const newPos = init.clone().add(disp);
      H[i].position.copy(newPos);
      alignCylinder(bonds[i], new THREE.Vector3(0,0,0), newPos);
      updateArrowFromBond(i, newPos);
    }
    updateNetDipole();
  }

  function getOmega(mode) {
    // Arbitrary angular speeds for smooth animation
    switch (mode) {
      case 'symmetric-stretch': return 2.2;
      case 'asymmetric-stretch': return 2.8;
      case 'bending-e': return 1.6;
      case 'bending-f2': return 1.9;
      default: return 2.0;
    }
  }
  function getAmplitude(mode) {
    switch (mode) {
      case 'symmetric-stretch': return 0.28;
      case 'asymmetric-stretch': return 0.32;
      case 'bending-e': return 0.40;
      case 'bending-f2': return 0.42;
      default: return 0.3;
    }
  }

  // Vector helpers update
  function updateArrowFromBond(index, vecToH) {
    const arrow = vectorsGroup.children[index]; // first 4 are individual arrows
    const dir = vecToH.clone().normalize();
    const len = vecToH.length();
    arrow.setDirection(dir);
    arrow.setLength(len, 0.18, 0.1);
  }
  function updateNetDipole() {
    let sum = new THREE.Vector3();
    for (let i=0;i<4;i++) {
      sum.add(H[i].position);
    }
    const mag = sum.length();
    const threshold = 0.15;
    if (mag > threshold) {
      netArrow.visible = true;
      netArrow.setDirection(sum.clone().normalize());
      netArrow.setLength(mag, 0.25, 0.15);
    } else {
      netArrow.visible = false;
      netArrow.setLength(0.01);
    }
  }

  // Mode label updates
  function updateModeLabels(mode) {
    if (mode === 'symmetric-stretch') {
      labelLeft.textContent = 'stretching A₁ Raman active 2916 cm⁻¹';
      labelRight.textContent = 'no change in the molecular dipole moment';
      labelLeft.style.color = labelRight.style.color = 'blue';
    } else if (mode === 'asymmetric-stretch') {
      labelLeft.textContent = 'stretching F₂ IR active 3019 cm⁻¹';
      labelRight.textContent = 'change in the molecular dipole moment';
      labelLeft.style.color = labelRight.style.color = 'red';
    } else if (mode === 'bending-e') {
      labelLeft.textContent = 'bending E Raman active 1534 cm⁻¹';
      labelRight.textContent = 'no change in the molecular dipole moment';
      labelLeft.style.color = labelRight.style.color = 'blue';
    } else if (mode === 'bending-f2') {
      labelLeft.textContent = 'bending F₂ IR active 1306 cm⁻¹';
      labelRight.textContent = 'change in the molecular dipole moment';
      labelLeft.style.color = labelRight.style.color = 'red';
    }
  }

  // Select change logic
  function onModeChange() {
    const v = modeSelect.value;
    if (v === 'ir-spectrum') {
      threeContainer.style.display = 'none';
      descArea.style.display = 'none';
      plotContainer.style.display = 'block';
      setControlsEnabled(false);
      pause();
      if (!spectrumPlotted) {
        createIRPlot();
        spectrumPlotted = true;
      }
    } else {
      plotContainer.style.display = 'none';
      threeContainer.style.display = 'block';
      descArea.style.display = 'flex';
      setControlsEnabled(true);
      updateModeLabels(v);
      resetPositions();
    }
  }

  // IR spectrum plot with images rendered offscreen via three.js
  function createIRPlot() {
    const n = 2400;
    const x = new Array(n);
    const y = new Array(n);
    const xmin = 400, xmax = 4000;
    const dx = (xmax - xmin) / (n - 1);
    for (let i=0;i<n;i++) {
      x[i] = xmin + i*dx;
      y[i] = 0.95 + (Math.random()*0.02 - 0.01);
    }
    // Gaussian dip helper
    function addGaussianDip(center, width, depth) {
      for (let i=0;i<n;i++) {
        const v = Math.exp(-0.5 * Math.pow((x[i]-center)/width, 2));
        y[i] -= depth * v;
      }
    }
    // Main IR-active peaks (F2 bending and F2 stretching)
    addGaussianDip(1306, 24, 0.80);
    addGaussianDip(3019, 18, 0.85);

    // Rotational fine structure: clusters of narrow dips near each peak
    function addFine(center, span, count) {
      for (let k=0;k<count;k++) {
        const c = center + (Math.random()*2-1) * span;
        const w = 3 + Math.random()*2;
        const d = 0.05 + Math.random()*0.06;
        addGaussianDip(c, w, d);
      }
    }
    addFine(1306, 130, 32);
    addFine(3019, 140, 36);

    const trace = {
      x, y,
      mode: 'lines',
      line: { color: '#2b6cb0', width: 1.2 },
      hovertemplate: 'wavenumber %{x:.0f} cm⁻¹<br>Transmittance %{y:.2f}<extra></extra>'
    };

    // Offscreen molecule renders for images
    Promise.all([
      renderMoleculeImage('f2-bend', 160),
      renderMoleculeImage('f2-stretch', 160)
    ]).then(([imgBend, imgStretch]) => {
      const layout = {
        margin: { l: 55, r: 22, t: 24, b: 48 },
        xaxis: { title: 'wavenumber (cm⁻¹)', range: [xmin, xmax] },
        yaxis: { title: 'Transmittance', range: [0, 1.1] },
        annotations: [
          {
            x: 1306, y: 0.18, ax: -60, ay: 40,
            text: 'ν F₂ 1306 cm⁻¹', arrowhead: 3, arrowsize:1, arrowwidth:1.2,
            font: { color: 'red' }, showarrow: true, arrowcolor: 'red'
          },
          {
            x: 3019, y: 0.22, ax: -55, ay: 40,
            text: 'ν F₂ 3019 cm⁻¹', arrowhead: 3, arrowsize:1, arrowwidth:1.2,
            font: { color: 'red' }, showarrow: true, arrowcolor: 'red'
          }
        ],
        images: [
          {
            source: imgBend, xref: 'x', yref:'y',
            x: 1500, y: 0.5, sizex: 260, sizey: 0.3, xanchor:'center', yanchor:'middle', layer:'above'
          },
          {
            source: imgStretch, xref:'x', yref:'y',
            x: 3300, y: 0.6, sizex: 260, sizey: 0.3, xanchor:'center', yanchor:'middle', layer:'above'
          }
        ]
      };
      Plotly.newPlot(plotContainer, [trace], layout, { displayModeBar: false, responsive: true });
    });
  }

  // Render small methane image offscreen; pose can be 'f2-bend' or 'f2-stretch'
  function renderMoleculeImage(pose, size=140) {
    return new Promise((resolve) => {
      const r = new THREE.WebGLRenderer({ antialias:true, alpha:true, preserveDrawingBuffer:true });
      r.setSize(size, size);
      const sc = new THREE.Scene();
      const cam = new THREE.PerspectiveCamera(40, 1, 0.1, 100);
      cam.position.set(3.2, 2.8, 4.0); cam.lookAt(0,0,0);
      sc.add(new THREE.AmbientLight(0xffffff, 0.8));
      const d = new THREE.DirectionalLight(0xffffff, 0.7);
      d.position.set(5,6,7); sc.add(d);

      const g = new THREE.Group();
      const cMat = new THREE.MeshLambertMaterial({ color: 0x111111 });
      const hMat = new THREE.MeshLambertMaterial({ color: 0xe9f2ff });
      const c = new THREE.Mesh(new THREE.SphereGeometry(0.55, 32, 32), cMat);
      g.add(c);
      const spheres = [];
      for (let i=0;i<4;i++) {
        const s = new THREE.Mesh(new THREE.SphereGeometry(0.28, 24, 24), hMat);
        s.position.copy(H_init[i]);
        spheres.push(s);
        g.add(s);
        const bond = new THREE.Mesh(new THREE.CylinderGeometry(0.07, 0.07, bondLen, 16, 1, false), new THREE.MeshBasicMaterial({ color: 0x808080 }));
        // align
        const a = new THREE.Vector3(0,0,0);
        const b = s.position.clone();
        bond.position.copy(a).addScaledVector(b, 0.5);
        bond.lookAt(b); bond.rotation.x += Math.PI/2;
        bond.geometry.dispose();
        bond.geometry = new THREE.CylinderGeometry(0.07, 0.07, b.length(), 16, 1, false);
        g.add(bond);
      }

      if (pose === 'f2-stretch') {
        spheres[0].position.addScaledVector(H_dirs[0], 0.35);
        spheres[1].position.addScaledVector(H_dirs[1], -0.25);
      } else if (pose === 'f2-bend') {
        spheres[0].position.addScaledVector(H_perp[0], 0.40);
        spheres[2].position.addScaledVector(H_perp[2], -0.25);
        spheres[3].position.addScaledVector(H_perp[3], -0.15);
      }

      sc.add(g);
      r.render(sc, cam);
      const url = r.domElement.toDataURL('image/png');
      // clean
      r.dispose();
      resolve(url);
    });
  }

  // Event listeners
  modeSelect.addEventListener('change', onModeChange);
  btnPlay.addEventListener('click', play);
  btnPause.addEventListener('click', pause);
  btnReset.addEventListener('click', resetPositions);

  // Initialize
  initThree();
  updateModeLabels('symmetric-stretch');

  // Expose keyboard shortcuts (optional)
  window.addEventListener('keydown', (e) => {
    if (plotContainer.style.display !== 'none') return;
    if (e.code === 'Space') { e.preventDefault(); playing ? pause() : play(); }
    if (e.key === 'r' || e.key === 'R') resetPositions();
  });
})();
</script>
</body>
</html>
