<body>
  <div id="main-container">
    <!-- Control Panel Section -->
    <div class="control-panel">
      <label for="slider-time">time (seconds)</label>
      <input id="slider-time" type="range" min="0" max="100" step="0.1" value="9.8"/>
      <span id="span-time-value">9.8</span>
      <button id="btn-reset" title="Reset to t = 0">+</button>
    </div>

    <!-- Material Visualization Section -->
    <div class="viz-card">
      <canvas id="canvas-material-bar" width="600" height="80"></canvas>
    </div>

    <!-- Plot Area Section -->
    <div class="viz-card">
      <div id="div-plot"></div>
    </div>
  </div>

  <!-- Plotly.js CDN -->
  <script src="https://cdn.plot.ly/plotly-2.27.1.min.js"></script>
  <script>
    // Constants for the physical-to-canvas mapping
    const TOTAL_MM = 16;          // visualized length in mm
    const GLASS_MM = 6;           // glass extent
    const canvas = document.getElementById('canvas-material-bar');
    const ctx = canvas.getContext('2d');
    const W = canvas.width;
    const H = canvas.height;
    const MM2PX = W / TOTAL_MM;

    // Bar geometry on the canvas
    const barY = 10;              // top of the bar
    const barH = 35;              // height of the bar
    const axisY = 65;             // y of the axis line

    // Stefan distance function (mm)
    function stefanDistance(t){
      return 0.155 * Math.sqrt(t);
    }

    // Draw the glass-water-ice bar and axis
    function drawMaterialBar(t){
      const s = stefanDistance(t); // in mm
      const waterMM = Math.max(0, Math.min(10, s)); // clamp for safety
      const glassW = GLASS_MM * MM2PX;
      const waterW = waterMM * MM2PX;
      const iceX = (GLASS_MM + waterMM) * MM2PX;

      // Clear
      ctx.clearRect(0, 0, W, H);

      // Glass gradient (red to white)
      const grad = ctx.createLinearGradient(0, 0, glassW, 0);
      grad.addColorStop(0, '#FF0000');
      grad.addColorStop(1, '#FFFFFF');
      ctx.fillStyle = grad;
      ctx.fillRect(0, barY, glassW, barH);

      // Melted water (light blue)
      ctx.fillStyle = '#00BFFF';
      ctx.fillRect(glassW, barY, waterW, barH);

      // Ice block (dark blue)
      ctx.fillStyle = '#003366';
      ctx.fillRect(iceX, barY, W - iceX, barH);

      // Axis line
      ctx.strokeStyle = '#000000';
      ctx.lineWidth = 1;
      ctx.beginPath();
      ctx.moveTo(0, axisY);
      ctx.lineTo(W, axisY);
      ctx.stroke();

      // Axis labels at 0, 5, 10, 15 mm
      ctx.fillStyle = '#000';
      ctx.font = '12px Arial';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'top';
      [0,5,10,15].forEach(mm => {
        const x = mm * MM2PX;
        // optional small ticks
        ctx.beginPath();
        ctx.moveTo(x, axisY);
        ctx.lineTo(x, axisY + 5);
        ctx.stroke();
        ctx.fillText(String(mm), x, axisY + 7);
      });
    }

    // Plot initialization with Plotly
    function initPlot(t0){
      const xs = [];
      const ys = [];
      const tMax = 100;
      const step = 0.1;
      for(let t = 0; t <= tMax + 1e-9; t += step){
        xs.push(+t.toFixed(1));
        ys.push(stefanDistance(t));
      }

      const trace = {
        x: xs,
        y: ys,
        mode: 'lines',
        type: 'scatter',
        line: { color: '#FFA500', width: 3 },
        hovertemplate: 't=%{x:.1f}s<br>s=%{y:.3f}mm<extra></extra>'
      };

      const layout = {
        title: 'Stefan distance versus time',
        margin: {l: 60, r: 20, t: 40, b: 50},
        xaxis: { title: 'time (s)', range: [0, 100], zeroline: false },
        yaxis: { title: 'distance (mm)', range: [0, 1.6], zeroline: false },
        shapes: [{
          type: 'line',
          xref: 'x',
          yref: 'y',
          x0: t0,
          x1: t0,
          y0: 0,
          y1: stefanDistance(t0),
          line: { color: '#000000', width: 2 }
        }]
      };

      Plotly.newPlot('div-plot', [trace], layout, {displayModeBar: false, responsive: true});
    }

    // Master update: span, canvas, and plot indicator line
    function updateAll(t){
      const tNum = Number(t);
      const s = stefanDistance(tNum);
      document.getElementById('span-time-value').textContent = tNum.toFixed(1);
      drawMaterialBar(tNum);

      // Update indicator line on the plot
      Plotly.relayout('div-plot', {
        'shapes[0].x0': tNum,
        'shapes[0].x1': tNum,
        'shapes[0].y1': s
      });
    }

    // Setup interactions and initial rendering
    document.addEventListener('DOMContentLoaded', () => {
      const slider = document.getElementById('slider-time');
      const btnReset = document.getElementById('btn-reset');

      const initialT = Number(slider.value); // 9.8
      // Initialize the plot and the material bar
      initPlot(initialT);
      updateAll(initialT);

      // Slider interaction
      slider.addEventListener('input', (e) => {
        updateAll(e.target.value);
      });

      // Reset button -> t = 0
      btnReset.addEventListener('click', () => {
        slider.value = 0;
        slider.dispatchEvent(new Event('input', {bubbles:true}));
      });
    });
  </script>
</body>
</html>
