  <script>
    // Global state for geolocation
    let userLat = 51.5;     // default: London
    let userLon = 0.0;      // default: Greenwich

    // DOM elements
    const slider = document.getElementById('date-slider');
    const dateDisplay = document.getElementById('date-display');
    const todayBtn = document.getElementById('today-button');
    const localCanvas = document.getElementById('local-clock-canvas');
    const solarCanvas = document.getElementById('solar-clock-canvas');
    const localDigital = document.getElementById('local-time-digital');
    const solarDigital = document.getElementById('solar-time-digital');
    const offsetDiv = document.getElementById('offset-display');

    // Set up canvases for HiDPI
    const LOCAL_FACE_COLOR = '#E6E6FA'; // lavender
    const SOLAR_FACE_COLOR = '#FAFAD2'; // light goldenrod yellow

    function setupCanvasForHiDPI(canvas, cssSize = 300) {
      const ratio = window.devicePixelRatio || 1;
      canvas.width = Math.floor(cssSize * ratio);
      canvas.height = Math.floor(cssSize * ratio);
      canvas.style.width = cssSize + 'px';
      canvas.style.height = cssSize + 'px';
      const ctx = canvas.getContext('2d');
      ctx.setTransform(ratio, 0, 0, ratio, 0, 0);
      return ctx;
    }
    const localCtx = setupCanvasForHiDPI(localCanvas, 300);
    const solarCtx = setupCanvasForHiDPI(solarCanvas, 300);

    // Utility: format time HH:MM:SS
    function two(n) { return (n < 10 ? '0' : '') + n; }
    function fmtTime(d) { return `${two(d.getHours())}:${two(d.getMinutes())}:${two(d.getSeconds())}`; }

    // Date display helpers (use a constant non-leap year for mapping slider day -> human date)
    const DAYS = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    const MONTHS = ['January','February','March','April','May','June','July','August','September','October','November','December'];
    const BASE_YEAR = 2021; // non-leap year

    function dayOfYearToDate(doy) {
      const base = new Date(Date.UTC(BASE_YEAR, 0, 1)); // Jan 1
      const target = new Date(base.getTime() + doy * 86400000);
      return target;
    }
    function updateDateLabelFromSlider() {
      const d = parseInt(slider.value, 10);
      const dt = dayOfYearToDate(d);
      const label = `${DAYS[dt.getUTCDay()]}, ${two(dt.getUTCDate())} ${MONTHS[dt.getUTCMonth()]}`;
      dateDisplay.textContent = label;
    }

    // "Today" -> set slider to current day-of-year (0..364)
    function currentDayOfYearClamped() {
      const now = new Date();
      const start = new Date(now.getFullYear(), 0, 1);
      const diff = Math.floor((now - start) / 86400000);
      return Math.max(0, Math.min(364, diff));
    }
    todayBtn.addEventListener('click', () => {
      slider.value = currentDayOfYearClamped();
      updateDateLabelFromSlider();
    });
    slider.addEventListener('input', updateDateLabelFromSlider);

    // Geolocation
    function initGeolocation() {
      if (!navigator.geolocation) return;
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          userLat = pos.coords.latitude;
          userLon = pos.coords.longitude;
        },
        () => {
          // keep defaults (London)
        },
        { enableHighAccuracy: false, timeout: 5000, maximumAge: 600000 }
      );
    }

    // Equation of Time (minutes), with B in radians
    function equationOfTimeMinutes(doy) {
      const Bdeg = (360 / 365.24) * (doy - 81);
      const B = Bdeg * Math.PI / 180;
      return 9.87 * Math.sin(2 * B) - 7.53 * Math.cos(B) - 1.5 * Math.sin(B);
    }

    // Compute solar time from local time + location + slider date
    function computeSolarTime(localTime, lonDeg, doy) {
      const eot = equationOfTimeMinutes(doy);                 // minutes
      const utcMs = localTime.getTime() + localTime.getTimezoneOffset() * 60000;
      const offsetMinutes = eot + 4 * lonDeg;                 // minutes (east positive)
      return new Date(utcMs + offsetMinutes * 60000);
    }

    // Drawing: analog clock on a given canvas context
    function drawClock(ctx, w, h, timeObj, faceColor) {
      const cx = w / 2;
      const cy = h / 2;
      const radius = Math.min(w, h) * 0.46;

      ctx.clearRect(0, 0, w, h);

      // Face background
      ctx.save();
      ctx.beginPath();
      ctx.arc(cx, cy, radius, 0, Math.PI * 2);
      ctx.fillStyle = faceColor;
      ctx.fill();
      ctx.lineWidth = 6;
      ctx.strokeStyle = '#000';
      ctx.stroke();
      ctx.restore();

      // Ticks
      ctx.save();
      ctx.translate(cx, cy);
      for (let i = 0; i < 60; i++) {
        const angle = (i / 60) * Math.PI * 2;
        const isHour = i % 5 === 0;
        const r1 = radius * (isHour ? 0.85 : 0.92);
        const r2 = radius * 0.98;
        ctx.beginPath();
        ctx.moveTo(r1 * Math.cos(angle), r1 * Math.sin(angle));
        ctx.lineTo(r2 * Math.cos(angle), r2 * Math.sin(angle));
        ctx.lineWidth = isHour ? 3 : 1.2;
        ctx.strokeStyle = '#000';
        ctx.stroke();
      }

      // Numbers 1..12
      ctx.fillStyle = '#000';
      ctx.font = '20px sans-serif';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      for (let n = 1; n <= 12; n++) {
        const angle = (n / 12) * Math.PI * 2;
        const tx = (radius * 0.72) * Math.sin(angle);
        const ty = (-radius * 0.72) * Math.cos(angle);
        // rotate 90 degrees to align 12 at top
        ctx.fillText(String(n), tx, ty);
      }
      ctx.restore();

      // Hands
      const s = timeObj.getSeconds() + timeObj.getMilliseconds() / 1000;
      const m = timeObj.getMinutes() + s / 60;
      const hhh = (timeObj.getHours() % 12) + m / 60;

      // Convert to angles (0 at top, clockwise)
      const angS = (s / 60) * Math.PI * 2 - Math.PI / 2;
      const angM = (m / 60) * Math.PI * 2 - Math.PI / 2;
      const angH = (hhh / 12) * Math.PI * 2 - Math.PI / 2;

      function drawHand(angle, length, width, color) {
        ctx.save();
        ctx.translate(cx, cy);
        ctx.rotate(angle);
        ctx.beginPath();
        ctx.moveTo(-width * 0.5, 0);
        ctx.lineTo(length, 0);
        ctx.lineWidth = width;
        ctx.lineCap = 'round';
        ctx.strokeStyle = color;
        ctx.stroke();
        ctx.restore();
      }

      // Hour, Minute, Second
      drawHand(angH, radius * 0.5, 6, '#000');
      drawHand(angM, radius * 0.72, 4, '#000');

      // Second hand (thin red)
      ctx.save();
      ctx.translate(cx, cy);
      ctx.rotate(angS);
      ctx.beginPath();
      ctx.moveTo(-radius * 0.15, 0);
      ctx.lineTo(radius * 0.85, 0);
      ctx.lineWidth = 2;
      ctx.strokeStyle = '#C40000';
      ctx.stroke();
      ctx.restore();

      // Center pin
      ctx.beginPath();
      ctx.arc(cx, cy, 5, 0, Math.PI * 2);
      ctx.fillStyle = '#C40000';
      ctx.fill();
      ctx.lineWidth = 2;
      ctx.strokeStyle = '#000';
      ctx.stroke();
    }

    // Main update loop
    function updateAll() {
      const localNow = new Date();
      const doy = parseInt(slider.value, 10);

      // Compute solar time
      const solarNow = computeSolarTime(localNow, userLon, doy);

      // Draw clocks
      drawClock(localCtx, localCanvas.width / (window.devicePixelRatio || 1), localCanvas.height / (window.devicePixelRatio || 1), localNow, LOCAL_FACE_COLOR);
      drawClock(solarCtx, solarCanvas.width / (window.devicePixelRatio || 1), solarCanvas.height / (window.devicePixelRatio || 1), solarNow, SOLAR_FACE_COLOR);

      // Digital
      localDigital.textContent = fmtTime(localNow);
      solarDigital.textContent = fmtTime(solarNow);

      // Offset (in minutes)
      const offsetMin = Math.round((solarNow.getTime() - localNow.getTime()) / 60000);
      offsetDiv.textContent = `offset of solar time from local time: ${offsetMin} minutes`;
    }

    // Initialize
    function init() {
      updateDateLabelFromSlider();
      initGeolocation();
      updateAll(); // initial paint
      setInterval(updateAll, 1000);
    }

    // Start after DOM is ready
    if (document.readyState === 'loading') {
      document.addEventListener('DOMContentLoaded', init);
    } else {
      init();
    }
  </script>
</body>
