</style>
</head>
<body>

<!-- Main container for the demo -->
<div id="demo-container">
  <!-- Control panel section -->
  <div id="control-panel">
      <div>
          <label for="input-steps">steps</label>
          <input type="number" id="input-steps" min="0" max="6" step="1" value="2">
      </div>
      <div>
          <label for="checkbox-zoom">zoom</label>
          <input type="checkbox" id="checkbox-zoom">
      </div>
      <div>
          <label for="select-color">color</label>
          <select id="select-color">
              <option value="DarkRainbow" selected>DarkRainbow</option>
              <option value="SiennaTones">SiennaTones</option>
              <option value="RustTones">RustTones</option>
              <option value="FuchsiaTones">FuchsiaTones</option>
              <option value="Grays">Grays</option>
          </select>
      </div>
  </div>
  <!-- Visualization canvas container -->
  <div id="canvas-container"></div>
</div>

<script>
/*
  Notes for viewers:
  The implementation below follows the requested interaction model,
  uses p5.js, and renders a recursive tiling made of rhombi.
  Each rhombus is replaced by 7 scaled children (scale 1/sqrt(7)).
  The placement pattern produces a Gosper-like star/flowsnake texture.
  Zoom mode magnifies by (sqrt(7))^steps around the center, as required.
*/

let palettes = {
  DarkRainbow: ['#1E4363', '#2E6341'],               // dark blue, dark green
  SiennaTones: ['#A0522D', '#CD853F', '#D2B48C'],    // sienna, peru, tan
  RustTones:   ['#B7410E', '#000033', '#FFFFFF'],    // rust, very dark blue, white
  FuchsiaTones:['#C71585', '#000000', '#FFFFFF'],    // medium violet red, black, white
  Grays:       ['#404040', '#808080', '#C0C0C0']     // dark gray, gray, light gray
};

// DOM elements
const elSteps   = document.getElementById('input-steps');
const elZoom    = document.getElementById('checkbox-zoom');
const elPalette = document.getElementById('select-color');
const canvasContainer = document.getElementById('canvas-container');

// p5 globals/state
let cnv;
let baseSize = 32;          // logical base size for a single rhombus
const sqrt7 = Math.sqrt(7);
// Subdivision rotation hint (~19.1066 deg)
const phiDeg = Math.atan(Math.sqrt(3)/5) * 180 / Math.PI;

function setup(){
  const w = Math.max(360, Math.min(window.innerWidth - 48, 900));
  cnv = createCanvas(w, Math.floor(w * 0.9));
  cnv.parent('canvas-container');
  angleMode(DEGREES);
  noLoop();

  // Input limits
  elSteps.min = 0; elSteps.max = 6; elSteps.step = 1;
  if(!elSteps.value) elSteps.value = 2;

  // Events -> redraw
  elSteps.addEventListener('input', clampAndRedraw);
  elZoom.addEventListener('change', () => redraw());
  elPalette.addEventListener('change', () => redraw());
  window.addEventListener('resize', onResize);
}

function onResize(){
  const w = Math.max(360, Math.min(window.innerWidth - 48, 900));
  resizeCanvas(w, Math.floor(w * 0.9));
  redraw();
}

function clampAndRedraw(){
  let v = parseInt(elSteps.value, 10);
  if(isNaN(v)) v = 2;
  v = Math.max(0, Math.min(6, v));
  elSteps.value = v;
  redraw();
}

// Utility: vectors from angle and length
function vecFrom(angleDeg, len){
  return createVector(len * cos(angleDeg), len * sin(angleDeg));
}

// Draw a single rhombus given center, size, orientation angle
function drawRhombus(cx, cy, size, angleDeg, fillColor, lineColor){
  // equal vectors with 60° between them
  const u = vecFrom(angleDeg, size);
  const v = vecFrom(angleDeg + 60, size);

  // vertices around center
  const p1 = p5.Vector.add(p5.Vector.add(createVector(cx, cy), u), v);
  const p2 = p5.Vector.add(p5.Vector.add(createVector(cx, cy), u), p5.Vector.mult(v, -1));
  const p3 = p5.Vector.add(p5.Vector.add(createVector(cx, cy), p5.Vector.mult(u, -1)), p5.Vector.mult(v, -1));
  const p4 = p5.Vector.add(p5.Vector.add(createVector(cx, cy), p5.Vector.mult(u, -1)), v);

  noStroke();
  fill(fillColor);
  beginShape();
  vertex(p1.x, p1.y);
  vertex(p2.x, p2.y);
  vertex(p3.x, p3.y);
  vertex(p4.x, p4.y);
  endShape(CLOSE);

  // faint diagonal across the obtuse-angle pair
  stroke(lineColor);
  strokeWeight(max(0.6, size * 0.05));
  const alphaLine = 26;
  const c = color(red(lineColor), green(lineColor), blue(lineColor), alphaLine);
  stroke(c);
  line(p2.x, p2.y, p4.x, p4.y);
}

// Orientation class for color selection (0, 120, 240 classes)
function orientationClass(angleDeg){
  let a = ((angleDeg % 360) + 360) % 360;
  // Snap to nearest of 0/120/240
  const idx = Math.round(a/120) % 3;
  return idx;
}

// Recursive tiling: replace one rhombus by 7 smaller ones (scale 1/sqrt(7))
// This placement is designed to produce a Gosper-like star while honoring the scale rule.
function drawTiling(level, cx, cy, size, angleDeg, palette){
  if(level <= 0){
    const idx = orientationClass(angleDeg);
    const col = palette[idx % palette.length];
    // choose a soft inner-line color that works for both light/dark fills
    const lineCol = '#ffffff';
    drawRhombus(cx, cy, size, angleDeg, col, lineCol);
    return;
  }

  const childSize = size / sqrt7;

  // Basis vectors of parent (for displacements)
  const e0 = vecFrom(angleDeg, size * 0.86);   // ring radius factor
  const e60 = vecFrom(angleDeg + 60, size * 0.86);
  const eM = p5.Vector.sub(e60, e0);           // 60° - 0° direction

  // Child positions (center + 6 around a hex ring) + a central one
  const centers = [
    {off: createVector(0,0), ang: angleDeg},                         // center
    {off: e0,                ang: angleDeg + 0 +  phiDeg},           // +e0
    {off: e60,               ang: angleDeg + 60 - phiDeg},           // +e60
    {off: p5.Vector.mult(e0, -1),  ang: angleDeg + 180 + phiDeg},    // -e0
    {off: p5.Vector.mult(e60,-1),  ang: angleDeg + 120 - phiDeg},    // -e60
    {off: eM,                ang: angleDeg + 300 - phiDeg},          //  e60 - e0
    {off: p5.Vector.mult(eM,-1),   ang: angleDeg + 240 + phiDeg}     // -(e60 - e0)
  ];

  for(const c of centers){
    drawTiling(level-1, cx + c.off.x, cy + c.off.y, childSize, c.ang, palette);
  }
}

// Central configuration for step 0: six rhombi (two of each orientation) forming a hexagon ring
function drawInitial(level, palette){
  const s = baseSize;
  // Offsets for the 6 around-center rhombi
  const d = s * 2.35;
  const a0 = vecFrom(0, d);
  const a60 = vecFrom(60, d);
  const a60m0 = p5.Vector.sub(a60, a0);

  const items = [
    {pos:a0,                 ang:0},
    {pos:a60,                ang:120},
    {pos:a60m0,              ang:240},
    {pos:p5.Vector.mult(a0, -1),  ang:0},
    {pos:p5.Vector.mult(a60,-1),  ang:120},
    {pos:p5.Vector.mult(a60m0,-1),ang:240},
  ];

  // Draw/expand each
  for(const it of items){
    drawTiling(level, it.pos.x, it.pos.y, s, it.ang, palette);
  }
}

// Optional central hexagon (used by RustTones and FuchsiaTones as per plan)
function drawCentralHexagon(paletteName){
  if(paletteName !== 'RustTones' && paletteName !== 'FuchsiaTones') return;
  const pal = palettes[paletteName];
  const fillCol = pal[2] || '#FFFFFF';
  const strokeCol = pal[0] || '#000000';
  const r = baseSize * 0.9;

  push();
  noStroke();
  fill(fillCol);
  beginShape();
  for(let i=0;i<6;i++){
    const ang = -90 + i*60;
    vertex(r*cos(ang), r*sin(ang));
  }
  endShape(CLOSE);
  stroke(strokeCol);
  strokeWeight(2);
  noFill();
  beginShape();
  for(let i=0;i<6;i++){
    const ang = -90 + i*60;
    vertex( (r+0.5)*cos(ang), (r+0.5)*sin(ang) );
  }
  endShape(CLOSE);
  pop();
}

function draw(){
  background(255);

  const steps = Math.max(0, Math.min(6, parseInt(elSteps.value || '2', 10)));
  const useZoom = !!elZoom.checked;
  const palName = elPalette.value || 'DarkRainbow';
  const palette = palettes[palName] || palettes.DarkRainbow;

  // Center the drawing
  push();
  translate(width/2, height/2);

  // Choose scale
  // 1) Fit entire current fractal (unchecked)
  // 2) Zoom: magnify central pattern by (sqrt(7))^steps as requested
  const margin = 0.92;
  const fit0Extent = baseSize * 7.0; // approximate diameter of step 0 composition
  let sc;
  if(useZoom){
    // Base scale to fit step 0 then magnify by (sqrt(7))^steps
    const baseFit = margin * min(width, height) / fit0Extent;
    sc = baseFit * Math.pow(sqrt7, steps);
  }else{
    // Heuristic fit for current step count (keeps whole structure visible)
    const extent = baseSize * (7.0 + 2.9 * steps); // grows with level
    sc = margin * min(width, height) / extent;
  }
  scale(sc);

  // Draw the current level starting from the 6-rhombi ring
  drawInitial(steps, palette);

  // Optional central hexagon overlay for specific palettes
  drawCentralHexagon(palName);

  pop();

  // Border for visual clarity (inside #canvas-container padding)
  noFill();
  stroke(230);
  rect(4, 4, width-8, height-8);
}
</script>
</body>
</html>
