    font-family: Arial, Helvetica, sans-serif;
    margin:0;
    background:#fafafa;
    color:#333;
  }
  #container{
    max-width: 900px;
    margin: 14px auto;
    padding: 0 10px;
  }
  #controls{
    display:flex;
    flex-wrap:wrap;
    align-items:center;
    gap:12px;
    padding:10px;
    background:#fff;
    border:1px solid #ddd;
    border-radius:8px;
  }
  #controls button{
    padding:6px 12px;
    border:1px solid #bbb;
    border-radius:16px;
    background:#eee;
    cursor:pointer;
  }
  #controls button.active{
    background:#ddd;
    border-color:#999;
    box-shadow: inset 0 1px 2px rgba(0,0,0,0.2);
  }
  #controls label{
    user-select:none;
  }
  #controls .alpha-label{ color: var(--alpha); font-weight:600; }
  #controls .beta-label{ color: var(--beta); font-weight:600; }
  #visualization{
    position:relative;
    margin-top:10px;
    background:#fff;
    border:1px solid #ddd;
    border-radius:8px;
    padding:10px;
  }
  #canvas-container{
    position:relative;
    width:100%;
    height:auto;
  }
  /* Info boxes */
  #info-box-main, #info-box-alpha, #info-box-beta{
    position:absolute;
    min-width:180px;
    max-width:260px;
    padding:8px 10px;
    background:#fff;
    border:2px solid #000;
    border-radius:6px;
    box-shadow: 0 2px 6px rgba(0,0,0,0.15);
    font-size:14px;
  }
  #info-box-main{
    left:24px;
    top:20px;
  }
  #info-box-alpha{
    left:24px;
    top:20px;
    border-color: var(--alpha);
  }
  #info-box-beta{
    right:24px;
    top:20px;
    border-color: var(--beta);
  }
  .title{
    font-weight:700;
    margin-bottom:2px;
  }
  .hidden{
    display:none !important;
  }
  .mj-green{ color: var(--green); }
  .mj-blue{ color: var(--blue); }
  .mj-red{ color: var(--red); }
  .mj-alpha{ color: var(--alpha); }
  .mj-beta{ color: var(--beta); }

  /* helper small legend color for checkboxes */
  #controls input[type="checkbox"]{
    transform: translateY(1px);
  }
</style>
</head>
<body>
<div id="container">
  <div id="controls">
    <button id="btn-diagram" class="active">diagram</button>
    <button id="btn-phases">phases</button>

    <span style="flex:0 0 8px"></span>

    <input type="checkbox" id="check-grid-lines">
    <label for="check-grid-lines">grid lines</label>

    <input type="checkbox" id="check-tie-lines" checked>
    <label for="check-tie-lines">tie lines</label>

    <input type="checkbox" id="check-alpha-phase" checked>
    <label for="check-alpha-phase" class="alpha-label">alpha phase</label>

    <input type="checkbox" id="check-beta-phase" checked>
    <label for="check-beta-phase" class="beta-label">beta phase</label>
  </div>

  <div id="visualization">
    <div id="canvas-container"></div>

    <div id="info-box-main">
      <div class="title">mass fractions</div>
      <div id="main-math"></div>
    </div>

    <div id="info-box-alpha" class="hidden">
      <div class="title">mass fractions</div>
      <div id="alpha-math"></div>
      <div class="mj-alpha" style="margin-top:4px;">alpha phase</div>
    </div>

    <div id="info-box-beta" class="hidden">
      <div class="title">mass fractions</div>
      <div id="beta-math"></div>
      <div class="mj-beta" style="margin-top:4px;">beta phase</div>
    </div>
  </div>
</div>

<script>
/* ------------------------------
   State variables and constants
--------------------------------*/
let currentView = 'diagram'; // 'diagram' or 'phases'

let showGridLines = false;
let showTieLines = true;
let showAlphaPhase = true;
let showBetaPhase = true;

// user-point composition (barycentric)
let userPoint = { xA: 0.42, xB: 0.47, xC: 0.11 }; // default

// Geometry for triangle
let PA, PB, PC; // pixels for vertices
let canvasW = 760, canvasH = 600;
let sideLen, triHeight, marginTop, centerX;

// Precomputed curves
let alphaCurvePoints = []; // [{xA,xB,xC}, ...]
let betaCurvePoints  = [];
let tieLines = []; // [{alpha:{}, beta:{}}, ...]
let envelopePolyPixels = []; // for point-in-polygon

// Colors
const colA = '#008800', colB = '#0000FF', colC = '#FF0000';
const colAlpha = '#FFA500', colBeta = '#FF00FF';

/* --------------------------------
   Utility: MathJax content update
----------------------------------*/
function formatFrac(x){ return x.toFixed(2); }

function updateInfoBoxes(){
  // main
  document.getElementById('main-math').innerHTML =
    '\\(\\begin{aligned}' +
    '\\color{#008800}{x_A}&= ' + formatFrac(userPoint.xA) + '\\\\' +
    '\\color{#0000FF}{x_B}&= ' + formatFrac(userPoint.xB) + '\\\\' +
    '\\color{#FF0000}{x_C}&= ' + formatFrac(userPoint.xC) +
    '\\end{aligned}\\)';

  // Determine alpha/beta compositions if applicable
  const inside = isPointInEnvelope(baryToPixel(userPoint));
  if(currentView === 'diagram'){
    // alpha
    if(showAlphaPhase && inside){
      const {alphaComp, betaComp} = computePhaseEndpointsFor(userPoint);
      document.getElementById('alpha-math').innerHTML =
        '\\(\\begin{aligned}' +
        '\\color{#008800}{x_A^{\\alpha}}&= ' + formatFrac(alphaComp.xA) + '\\\\' +
        '\\color{#0000FF}{x_B^{\\alpha}}&= ' + formatFrac(alphaComp.xB) + '\\\\' +
        '\\color{#FF0000}{x_C^{\\alpha}}&= ' + formatFrac(alphaComp.xC) +
        '\\end{aligned}\\)';
      document.getElementById('info-box-alpha').classList.remove('hidden');
    }else{
      document.getElementById('info-box-alpha').classList.add('hidden');
    }

    // beta
    if(showBetaPhase && inside){
      const {alphaComp, betaComp} = computePhaseEndpointsFor(userPoint);
      document.getElementById('beta-math').innerHTML =
        '\\(\\begin{aligned}' +
        '\\color{#008800}{x_A^{\\beta}}&= ' + formatFrac(betaComp.xA) + '\\\\' +
        '\\color{#0000FF}{x_B^{\\beta}}&= ' + formatFrac(betaComp.xB) + '\\\\' +
        '\\color{#FF0000}{x_C^{\\beta}}&= ' + formatFrac(betaComp.xC) +
        '\\end{aligned}\\)';
      document.getElementById('info-box-beta').classList.remove('hidden');
    }else{
      document.getElementById('info-box-beta').classList.add('hidden');
    }

    // main info visible in diagram view
    document.getElementById('info-box-main').classList.remove('hidden');
  }else{
    // phases view hides all boxes
    document.getElementById('info-box-main').classList.add('hidden');
    document.getElementById('info-box-alpha').classList.add('hidden');
    document.getElementById('info-box-beta').classList.add('hidden');
  }

  if(window.MathJax && MathJax.typeset){
    MathJax.typeset();
  }
}

/* --------------------------------
   p5.js sketch
----------------------------------*/
let pg; // p5 graphics will use default global

function setup(){
  // Prepare geometry
  const cont = document.getElementById('canvas-container');
  // keep some margins around
  canvasW = Math.min(820, Math.max(620, cont.clientWidth - 4));
  canvasH = 620;
  const cnv = createCanvas(canvasW, canvasH);
  cnv.parent('canvas-container');
  pixelDensity(1);

  marginTop = 70;
  sideLen = Math.min(canvasW*0.75, canvasH*0.7);
  triHeight = sideLen * Math.sqrt(3)/2;
  centerX = canvasW/2;

  // Vertices for equilateral triangle
  PA = createVector(centerX, marginTop);
  PB = createVector(centerX - sideLen/2, marginTop + triHeight);
  PC = createVector(centerX + sideLen/2, marginTop + triHeight);

  // Precompute phase envelope and tie lines
  buildEnvelopeAndTies();

  noLoop();
  updateInfoBoxes();
  redraw();
}

function draw(){
  clear();
  background(255);

  // Recompute envelope polygon pixels for point-in-polygon each frame
  buildEnvelopePixels();

  drawTriangleAndAxes();

  if(currentView === 'diagram'){
    if(showGridLines) drawGridLines();
    drawEnvelope();
    if(showTieLines) drawAllTieLines();
    drawUserPointAndProjections();

    // Alpha/Beta inside region
    const inside = isPointInEnvelope(baryToPixel(userPoint));
    if(inside){
      const {alphaComp, betaComp} = computePhaseEndpointsFor(userPoint);
      const alphaP = baryToPixel(alphaComp);
      const betaP = baryToPixel(betaComp);
      // dashed black line: alpha - user - beta
      stroke(0); strokeWeight(1.6);
      drawDashedLine(alphaP.x, alphaP.y, mouseX= baryToPixel(userPoint).x, mouseY= baryToPixel(userPoint).y, 6, 6);
      drawDashedLine(baryToPixel(userPoint).x, baryToPixel(userPoint).y, betaP.x, betaP.y, 6, 6);

      if(showAlphaPhase){
        // alpha dot
        noStroke(); fill(colAlpha);
        circle(alphaP.x, alphaP.y, 8);
        // dashed projections (parallel to sides)
        stroke(colAlpha); strokeWeight(1.5);
        drawPhaseProjections(alphaP);
      }
      if(showBetaPhase){
        noStroke(); fill(colBeta);
        circle(betaP.x, betaP.y, 8);
        stroke(colBeta); strokeWeight(1.5);
        drawPhaseProjections(betaP);
      }
    }
  }else{
    // phases view shading
    drawPhasesView();
  }
}

/* --------------------------------
   Drawing helpers
----------------------------------*/

function drawTriangleAndAxes(){
  // Outer triangle
  stroke(0); strokeWeight(3);
  noFill();
  triangle(PA.x, PA.y, PB.x, PB.y, PC.x, PC.y);

  // Vertex labels
  noStroke();
  textAlign(CENTER, BOTTOM);
  fill(colA);
  textSize(18);
  text('A', PA.x, PA.y - 8);

  textAlign(RIGHT, BOTTOM);
  fill(colB);
  text('B', PB.x - 8, PB.y + 4);

  textAlign(LEFT, BOTTOM);
  fill(colC);
  text('C', PC.x + 8, PC.y + 4);

  // Axis ticks and numbers 0.1..0.9
  textSize(13);
  // Base BC (xC along base in red)
  for(let v=0.1; v<1.0; v+=0.1){
    const p = pLerp(PB, PC, v);
    // tick
    stroke(0); strokeWeight(1);
    const tickLen = 6;
    line(p.x, p.y, p.x, p.y + tickLen);
    // number
    noStroke(); fill(colC);
    textAlign(CENTER, TOP);
    text(v.toFixed(1), p.x, p.y + tickLen + 4);
  }
  // Side AB (xB in blue)
  for(let v=0.1; v<1.0; v+=0.1){
    const p = pLerp(PB, PA, v);
    // Draw short tick perpendicular outward
    const n = perpOutward(PB, PA);
    stroke(0); line(p.x, p.y, p.x + n.x*6, p.y + n.y*6);
    noStroke(); fill(colB);
    textAlign(RIGHT, CENTER);
    text(v.toFixed(1), p.x + n.x*16, p.y + n.y*16);
  }
  // Side AC (xA in green)
  for(let v=0.1; v<1.0; v+=0.1){
    const p = pLerp(PC, PA, v);
    const n = perpOutward(PC, PA);
    stroke(0); line(p.x, p.y, p.x + n.x*6, p.y + n.y*6);
    noStroke(); fill(colA);
    textAlign(LEFT, CENTER);
    text(v.toFixed(1), p.x + n.x*16, p.y + n.y*16);
  }
}

function drawGridLines(){
  stroke(200); strokeWeight(1);
  // xA constant
  for(let t=0.1; t<1.0; t+=0.1){
    const p1 = baryToPixel({xA:t, xB:1-t, xC:0});
    const p2 = baryToPixel({xA:t, xB:0,   xC:1-t});
    line(p1.x, p1.y, p2.x, p2.y);
  }
  // xB constant
  for(let t=0.1; t<1.0; t+=0.1){
    const p1 = baryToPixel({xA:1-t, xB:t, xC:0});
    const p2 = baryToPixel({xA:0,   xB:t, xC:1-t});
    line(p1.x, p1.y, p2.x, p2.y);
  }
  // xC constant
  for(let t=0.1; t<1.0; t+=0.1){
    const p1 = baryToPixel({xA:1-t, xB:0,   xC:t});
    const p2 = baryToPixel({xA:0,   xB:1-t, xC:t});
    line(p1.x, p1.y, p2.x, p2.y);
  }
}

function drawEnvelope(){
  noFill();
  // alpha side (orange)
  stroke(colAlpha); strokeWeight(3);
  beginShape();
  alphaCurvePoints.forEach(c => {
    const p = baryToPixel(c);
    vertex(p.x, p.y);
  });
  endShape();
  // beta side (magenta)
  stroke(colBeta); strokeWeight(3);
  beginShape();
  betaCurvePoints.forEach(c => {
    const p = baryToPixel(c);
    vertex(p.x, p.y);
  });
  endShape();
}

function drawAllTieLines(){
  stroke(160); strokeWeight(1.3);
  tieLines.forEach(t=>{
    const a = baryToPixel(t.alpha);
    const b = baryToPixel(t.beta);
    line(a.x, a.y, b.x, b.y);
  });
}

function drawUserPointAndProjections(){
  const p = baryToPixel(userPoint);
  // user dot
  noStroke(); fill(0);
  circle(p.x, p.y, 9);

  // projection lines parallel to sides with arrowheads
  const baseVec = p5.Vector.sub(PC, PB).normalize(); // parallel to BC
  const abVec   = p5.Vector.sub(PA, PB).normalize(); // along AB
  const acVec   = p5.Vector.sub(PA, PC).normalize(); // along AC

  // green (parallel to BC)
  stroke(colA); strokeWeight(3);
  drawArrow(p.x, p.y, p.x + baseVec.x*110, p.y + baseVec.y*110, colA);

  // blue (parallel to AC direction)
  stroke(colB); strokeWeight(3);
  const dirB = acVec.copy().mult(-1); // roughly toward left-bottom
  drawArrow(p.x, p.y, p.x + dirB.x*80, p.y + dirB.y*80, colB);

  // red (parallel to AB direction)
  stroke(colC); strokeWeight(3);
  const dirC = abVec.copy().mult(-1); // roughly toward right-bottom
  drawArrow(p.x, p.y, p.x + dirC.x*80, p.y + dirC.y*80, colC);
}

function drawPhaseProjections(pointPix){
  // dashed lines parallel to each side from point
  const baseVec = p5.Vector.sub(PC, PB).normalize();
  const abVec   = p5.Vector.sub(PA, PB).normalize();
  const acVec   = p5.Vector.sub(PA, PC).normalize();

  drawDashedLine(pointPix.x, pointPix.y,
                 pointPix.x + baseVec.x*70, pointPix.y + baseVec.y*70, 5, 5);
  drawDashedLine(pointPix.x, pointPix.y,
                 pointPix.x - abVec.x*60, pointPix.y - abVec.y*60, 5, 5);
  drawDashedLine(pointPix.x, pointPix.y,
                 pointPix.x - acVec.x*60, pointPix.y - acVec.y*60, 5, 5);
}

function drawPhasesView(){
  // fill triangle with light red (one phase)
  noStroke();
  fill(getCSS('--light-red'));
  beginShape();
  vertex(PA.x, PA.y); vertex(PB.x, PB.y); vertex(PC.x, PC.y);
  endShape(CLOSE);

  // fill envelope area with light blue (two phases)
  fill(getCSS('--light-blue'));
  beginShape();
  // alpha then beta reversed to make a closed dome
  alphaCurvePoints.forEach(c=>{
    const p = baryToPixel(c);
    vertex(p.x, p.y);
  });
  for(let i=betaCurvePoints.length-1;i>=0;i--){
    const p = baryToPixel(betaCurvePoints[i]);
    vertex(p.x, p.y);
  }
  endShape(CLOSE);

  // boundary with colored segments
  noFill();
  stroke(colAlpha); strokeWeight(3);
  beginShape();
  alphaCurvePoints.forEach(c=>{ const p=baryToPixel(c); vertex(p.x,p.y); });
  endShape();

  stroke(colBeta); strokeWeight(3);
  beginShape();
  betaCurvePoints.forEach(c=>{ const p=baryToPixel(c); vertex(p.x,p.y); });
  endShape();

  // labels
  noStroke(); textSize(20);
  fill(30);
  textAlign(CENTER, CENTER);
  // label centers
  const centerInside = baryToPixel({xA:0.25, xB:0.35, xC:0.40});
  const centerOutside = baryToPixel({xA:0.55, xB:0.30, xC:0.15});
  text('two phases', centerInside.x, centerInside.y);
  text('one phase', centerOutside.x, centerOutside.y);

  // alpha/beta labels along curves (approximate)
  fill(colAlpha);
  const aLabelPos = baryToPixel(alphaCurvePoints[Math.floor(alphaCurvePoints.length*0.2)]);
  textAlign(LEFT, CENTER);
  text('alpha phase', aLabelPos.x - 40, aLabelPos.y);
  fill(colBeta);
  const bLabelPos = baryToPixel(betaCurvePoints[Math.floor(betaCurvePoints.length*0.7)]);
  textAlign(LEFT, CENTER);
  text('beta phase', bLabelPos.x, bLabelPos.y + 8);

  // plait point
  const plaitComp = alphaCurvePoints[alphaCurvePoints.length-1]; // same as betaCurvePoints[beta last]
  const plaitPix  = baryToPixel(plaitComp);
  fill(0); noStroke();
  circle(plaitPix.x, plaitPix.y, 9);
  fill(30);
  textAlign(LEFT, BOTTOM);
  text('plait point', plaitPix.x - 60, plaitPix.y - 10);
  // curved arrow (bezier)
  stroke(0); strokeWeight(2); noFill();
  const fromX = plaitPix.x - 40, fromY = plaitPix.y - 30;
  bezier(fromX, fromY, fromX+10, fromY+15, plaitPix.x-30, plaitPix.y-10, plaitPix.x-6, plaitPix.y-6);
  // arrow head
  drawArrow(plaitPix.x-10, plaitPix.y-10, plaitPix.x-2, plaitPix.y-2, '#000');

  // draw user point in this view
  const pp = baryToPixel(userPoint);
  noStroke(); fill(0);
  circle(pp.x, pp.y, 9);
}

/* --------------------------------
   Envelope and tie-lines creation
----------------------------------*/
function buildEnvelopeAndTies(){
  alphaCurvePoints = [];
  betaCurvePoints = [];
  tieLines = [];

  // Symmetric construction:
  // alpha(u): from near B (AB edge) to plait point
  // beta(u):  from near C (AC edge) to plait point
  // u in [0,1]
  const steps = 20;
  for(let k=0;k<=steps;k++){
    const u = k/steps; // 0..1
    // alpha curve
    const a = {
      xA: 0.08 + 0.32*u,
      xB: 0.92 - 0.62*u,
      xC: 0.30*u
    };
    // beta curve
    const b = {
      xA: 0.08 + 0.32*u,
      xB: 0.30*u,
      xC: 0.92 - 0.62*u
    };
    alphaCurvePoints.push(a);
    betaCurvePoints.push(b);
  }

  // Build tie lines at selected u values
  for(let u=0.1; u<1.0; u+=0.1){
    const a = alphaAt(u);
    const b = betaAt(u);
    tieLines.push({alpha:a, beta:b});
  }
}

// Envelope polygon pixels (alpha forward + beta backward)
function buildEnvelopePixels(){
  envelopePolyPixels = [];
  alphaCurvePoints.forEach(c => { const p=baryToPixel(c); envelopePolyPixels.push([p.x,p.y]); });
  for(let i=betaCurvePoints.length-1;i>=0;i--){
    const p = baryToPixel(betaCurvePoints[i]);
    envelopePolyPixels.push([p.x,p.y]);
  }
}

/* --------------------------------
   Phase interpolation utilities
----------------------------------*/
function alphaAt(u){
  u = constrain(u, 0, 1);
  return {
    xA: 0.08 + 0.32*u,
    xB: 0.92 - 0.62*u,
    xC: 0.30*u
  };
}
function betaAt(u){
  u = constrain(u, 0, 1);
  return {
    xA: 0.08 + 0.32*u,
    xB: 0.30*u,
    xC: 0.92 - 0.62*u
  };
}
function computePhaseEndpointsFor(comp){
  // Based on symmetry: each tie line has constant xA,
  // u is determined by xA
  const u = (comp.xA - 0.08) / 0.32;
  const uc = constrain(u, 0, 1);
  const alphaComp = alphaAt(uc);
  const betaComp  = betaAt(uc);
  return {alphaComp, betaComp};
}

/* --------------------------------
   Conversions and geometry helpers
----------------------------------*/

// Pixel = xA*PA + xB*PB + xC*PC
function baryToPixel(b){
  return {
    x: b.xA*PA.x + b.xB*PB.x + b.xC*PC.x,
    y: b.xA*PA.y + b.xB*PB.y + b.xC*PC.y
  };
}

// Barycentric coordinates from pixel (general triangle)
function pixelToBary(px, py){
  const x = px, y = py;
  const x0 = PA.x, y0 = PA.y;
  const x1 = PB.x, y1 = PB.y;
  const x2 = PC.x, y2 = PC.y;

  const denom = ( (y1 - y2)*(x0 - x2) + (x2 - x1)*(y0 - y2) );
  const l0 = ( (y1 - y2)*(x - x2) + (x2 - x1)*(y - y2) ) / denom;
  const l1 = ( (y2 - y0)*(x - x2) + (x0 - x2)*(y - y2) ) / denom;
  const l2 = 1 - l0 - l1;
  return { xA:l0, xB:l1, xC:l2 };
}

function isInsideTriangle(b){
  const e = -1e-6;
  return b.xA >= e && b.xB >= e && b.xC >= e &&
         b.xA <= 1+1e-6 && b.xB <= 1+1e-6 && b.xC <= 1+1e-6;
}

// Ray-casting for point in polygon (pixel coordinates)
function pointInPolygon(pt, poly){
  // poly: array of [x,y]
  let inside = false;
  for(let i=0, j=poly.length-1; i<poly.length; j=i++){
    const xi = poly[i][0], yi = poly[i][1];
    const xj = poly[j][0], yj = poly[j][1];
    const intersect = ((yi>pt.y) !== (yj>pt.y)) &&
      (pt.x < (xj - xi) * (pt.y - yi) / (yj - yi + 1e-12) + xi);
    if(intersect) inside = !inside;
  }
  return inside;
}
function isPointInEnvelope(pt){
  return pointInPolygon({x:pt.x, y:pt.y}, envelopePolyPixels);
}

// vector helpers
function pLerp(a, b, t){ return createVector(lerp(a.x,b.x,t), lerp(a.y,b.y,t)); }
function perpOutward(p1, p2){
  // Normal vector pointing outward of triangle for side p1->p2
  const v = p5.Vector.sub(p2, p1).normalize();
  let n = createVector(-v.y, v.x); // rotate 90
  // Choose outward direction by comparing with centroid
  const mid = pLerp(p1, p2, 0.5);
  const centroid = createVector((PA.x+PB.x+PC.x)/3, (PA.y+PB.y+PC.y)/3);
  const toCentroid = p5.Vector.sub(centroid, mid);
  // If pointing toward centroid, flip to outward
  if(n.dot(toCentroid) > 0) n.mult(-1);
  return n;
}

function drawArrow(x1,y1,x2,y2,color='#000'){
  // line
  stroke(color);
  line(x1,y1,x2,y2);
  // arrow head
  push();
  translate(x2,y2);
  const ang = Math.atan2(y2-y1, x2-x1);
  rotate(ang);
  noStroke(); fill(color);
  const size = 7;
  triangle(0,0, -size, size*0.6, -size, -size*0.6);
  pop();
}

function drawDashedLine(x1,y1,x2,y2, seg=6, gap=6){
  const dx = x2-x1, dy = y2-y1;
  const dist = Math.hypot(dx,dy);
  const steps = Math.floor(dist/(seg+gap));
  const vx = dx/dist, vy = dy/dist;
  for(let i=0;i<steps;i++){
    const sx = x1 + (seg+gap)*i*vx;
    const sy = y1 + (seg+gap)*i*vy;
    const ex = sx + seg*vx;
    const ey = sy + seg*vy;
    line(sx,sy,ex,ey);
  }
}

