<script>
/* ========================= Utilities & PRNG ========================= */
function mulberry32(seed){
  let t = seed >>> 0;
  return function(){
    t += 0x6D2B79F5;
    let r = Math.imul(t ^ t >>> 15, 1 | t);
    r ^= r + Math.imul(r ^ r >>> 7, 61 | r);
    return ((r ^ r >>> 14) >>> 0) / 4294967296;
  }
}
function makeRNGFromSeed(seedVal){
  let s = parseInt(seedVal, 10);
  if (isNaN(s)) s = 0;
  return mulberry32(s);
}
function formatNumber(x){
  return (Math.round(x * 1e6) / 1e6).toFixed(6);
}

/* ========================= Geometry helpers ========================= */
function buildDistance(points){
  const n = points.length;
  const d = Array.from({length:n}, ()=>Array(n).fill(0));
  for(let i=0;i<n;i++){
    for(let j=i+1;j<n;j++){
      const dx = points[i].x - points[j].x;
      const dy = points[i].y - points[j].y;
      const dist = Math.hypot(dx, dy);
      d[i][j] = d[j][i] = dist;
    }
  }
  return d;
}
function tourLength(tour, dist){
  const n = tour.length;
  let L = 0;
  for(let i=0;i<n;i++){
    const a = tour[i], b = tour[(i+1)%n];
    L += dist[a][b];
  }
  return L;
}
function nearestNeighbor(dist){
  const n = dist.length;
  const visited = new Array(n).fill(false);
  const tour = [0];
  visited[0] = true;
  for(let k=1;k<n;k++){
    const last = tour[tour.length-1];
    let best = -1, bestD = Infinity;
    for(let i=0;i<n;i++){
      if(!visited[i] && dist[last][i] < bestD){
        bestD = dist[last][i]; best = i;
      }
    }
    visited[best] = true;
    tour.push(best);
  }
  return tour;
}
function reverseSegment(tour, i, j){
  while(i < j){
    [tour[i], tour[j]] = [tour[j], tour[i]];
    i++; j--;
  }
}

/* ========================= Local Search Variants ========================= */
function solveTwoOpt(initialTour, dist, timeLimitMs=400){
  const n = initialTour.length;
  const tour = initialTour.slice();
  const start = performance.now();
  let improved = true;
  const EPS = 1e-12;
  while(improved && performance.now() - start < timeLimitMs){
    improved = false;
    outer: for(let i=0;i<n-1;i++){
      for(let k=i+2;k<n - (i===0?1:0);k++){
        const a = tour[i], b = tour[(i+1)%n];
        const c = tour[k], d = tour[(k+1)%n];
        const delta = (dist[a][c] + dist[b][d]) - (dist[a][b] + dist[c][d]);
        if(delta < -EPS){
          reverseSegment(tour, i+1, k);
          improved = true;
          break outer;
        }
        if(performance.now() - start >= timeLimitMs) break outer;
      }
    }
  }
  return tour;
}

// Or-Opt: move segment of length 1..3 to a different position
function solveOrOpt(initialTour, dist, maxSegLen=3, timeLimitMs=550){
  let tour = initialTour.slice();
  const n = tour.length;
  const start = performance.now();
  const EPS = 1e-12;
  while(performance.now() - start < timeLimitMs){
    let any = false;
    for(let len=1; len<=maxSegLen; len++){
      // keep first city anchored at index 0 and avoid wrap segments
      for(let i=1; i+len<=n-1; i++){
        const prev = tour[i-1], first = tour[i], last = tour[i+len-1], next = tour[i+len];
        for(let j=0; j<n-1; j++){
          if(j>=i-1 && j<=i+len-1) continue; // cannot insert into its own segment neighborhood
          const jNode = tour[j], jNext = tour[j+1];
          const removed = dist[prev][first] + dist[last][next] + dist[jNode][jNext];
          const added   = dist[prev][next] + dist[jNode][first] + dist[last][jNext];
          const delta = added - removed;
          if(delta < -EPS){
            // apply move: remove segment [i..i+len-1]
            const seg = tour.splice(i, len);
            // after removal, indices after i shift; adjust j accordingly
            let insertPos = j < i ? j+1 : j - len + 1;
            tour.splice(insertPos, 0, ...seg);
            any = true;
            break;
          }
          if(performance.now() - start >= timeLimitMs) break;
        }
        if(any || performance.now() - start >= timeLimitMs) break;
      }
      if(any || performance.now() - start >= timeLimitMs) break;
    }
    if(!any) break; // local optimum
  }
  return tour;
}

// "2.5-Opt" (OrZweig surrogate): 2-Opt followed by best insertion (Or-Opt with len=1) loops
function solveOrZweig(initialTour, dist){
  let t = solveTwoOpt(initialTour, dist, 300);
  t = solveOrOpt(t, dist, 1, 400);
  t = solveTwoOpt(t, dist, 200);
  return t;
}

// "3-Opt" surrogate: combine 2-Opt + Or-Opt (len up to 3) with extra passes and longer budget
function solve3Opt(initialTour, dist){
  let t = initialTour.slice();
  t = solveTwoOpt(t, dist, 500);
  t = solveOrOpt(t, dist, 3, 800);
  t = solveTwoOpt(t, dist, 300);
  return t;
}

/* ========================= Christofides (approx) ========================= */
function primMST(dist){
  const n = dist.length;
  const inMST = Array(n).fill(false);
  const key = Array(n).fill(Infinity);
  const parent = Array(n).fill(-1);
  key[0] = 0;

  for(let count=0; count<n-1; count++){
    let u = -1, best = Infinity;
    for(let v=0; v<n; v++){
      if(!inMST[v] && key[v] < best){ best = key[v]; u = v; }
    }
    if(u === -1) break;
    inMST[u] = true;
    for(let v=0; v<n; v++){
      if(!inMST[v] && dist[u][v] < key[v]){
        key[v] = dist[u][v];
        parent[v] = u;
      }
    }
  }
  const edges = [];
  for(let v=1; v<n; v++){
    if(parent[v] !== -1) edges.push([parent[v], v]);
  }
  return edges;
}
function oddDegreeVertices(n, edges){
  const deg = Array(n).fill(0);
  edges.forEach(([u,v])=>{ deg[u]++; deg[v]++; });
  const odd = [];
  for(let i=0;i<n;i++) if(deg[i] % 2 === 1) odd.push(i);
  return odd;
}
// Greedy minimum-weight perfect matching on odd set (approximate)
function greedyPerfectMatching(odd, dist){
  odd = odd.slice();
  const edges = [];
  while(odd.length > 1){
    let i = 0, a = odd[i];
    // find nearest b to a
    let bestJ = 1, best = Infinity;
    for(let j=1;j<odd.length;j++){
      const b = odd[j];
      if(dist[a][b] < best){ best = dist[a][b]; bestJ = j; }
    }
    const b = odd[bestJ];
    edges.push([a,b]);
    // remove paired vertices
    if(bestJ > i){ odd.splice(bestJ,1); odd.splice(i,1); }
    else { odd.splice(i,1); odd.splice(bestJ,1); }
  }
  return edges;
}
function buildMultigraph(n, edgesA, edgesB){
  const adj = Array.from({length:n}, ()=>new Map());
  function add(u,v){
    const m1 = adj[u]; const m2 = adj[v];
    m1.set(v, (m1.get(v)||0)+1);
    m2.set(u, (m2.get(u)||0)+1);
  }
  edgesA.forEach(([u,v])=>add(u,v));
  edgesB.forEach(([u,v])=>add(u,v));
  return adj;
}
function hierholzer(adj){
  const n = adj.length;
  const stk = [0];
  const path = [];
  const local = adj.map(m=>new Map(m)); // copy
  while(stk.length){
    const v = stk[stk.length-1];
    const it = local[v].keys();
    let next = it.next();
    while(!next.done && local[v].get(next.value) === 0) next = it.next();
    if(!next.done){
      const u = next.value;
      // consume one undirected edge
      local[v].set(u, local[v].get(u)-1);
      local[u].set(v, local[u].get(v)-1);
      stk.push(u);
    }else{
      path.push(stk.pop());
    }
  }
  return path.reverse();
}
function eulerToHamiltonian(euler){
  const seen = new Set();
  const tour = [];
  for(const v of euler){
    if(!seen.has(v)){
      seen.add(v); tour.push(v);
    }
  }
  // ensure starting at 0
  let idx0 = tour.indexOf(0);
  if(idx0 > 0){
    const rotated = tour.slice(idx0).concat(tour.slice(0,idx0));
    return rotated;
  }
  return tour;
}
function christofides(points, dist){
  const n = points.length;
  if(n <= 2) return Array.from({length:n}, (_,i)=>i);
  const mstEdges = primMST(dist);
  const odds = oddDegreeVertices(n, mstEdges);
  const matchEdges = greedyPerfectMatching(odds, dist);
  const adj = buildMultigraph(n, mstEdges, matchEdges);
  const euler = hierholzer(adj);
  let tour = eulerToHamiltonian(euler);
  // small 2-opt polish for the Hamiltonian path
  tour = solveTwoOpt(tour, dist, 200);
  return tour;
}

/* ========================= p5 sketches ========================= */
function createTourSketch(wrapperId, lineColor){
  const sketch = (p)=>{
    let pts = [], tour = [];
    const margin = 40;
    function getSize(){
      const wrapper = document.getElementById(wrapperId);
      const w = Math.max(320, wrapper.clientWidth || 400);
      const h = Math.round(w*0.7);
      return {w, h};
    }
    function mapX(x){ return margin + x*(p.width - 2*margin); }
    function mapY(y){ return p.height - (margin + y*(p.height - 2*margin)); }

    p.updateData = function(points, order){
      pts = points ? points.slice() : [];
      tour = order ? order.slice() : [];
      p.redraw();
    };

    p.setup = function(){
      const s = getSize();
      const cnv = p.createCanvas(s.w, s.h);
      cnv.parent(wrapperId);
      p.noLoop();
      p.pixelDensity(1);
    };
    p.windowResized = function(){
      const s = getSize();
      p.resizeCanvas(s.w, s.h);
      p.redraw();
    };
    p.drawAxes = function(){
      p.stroke(180); p.strokeWeight(1); p.noFill();
      // frame
      p.rect(margin, margin, p.width-2*margin, p.height-2*margin);
      // ticks and labels
      p.textSize(11); p.fill(100); p.noStroke();
      for(let t=0.2;t<=1.0+1e-9;t+=0.2){
        const x = mapX(t), y0 = p.height - margin, y1 = margin;
        p.stroke(200); p.line(x, y0-5, x, y0+5); p.noStroke();
        p.text(t.toFixed(1), x-8, y0+18);

        const y = mapY(t);
        p.stroke(200); p.line(margin-5, y, margin+5, y); p.noStroke();
        p.text(t.toFixed(1), margin-28, y+4);
      }
    };
    p.drawTour = function(){
      if(!pts.length || !tour.length) return;
      // points
      p.noStroke(); p.fill(220,0,0);
      for(const pt of pts){
        p.circle(mapX(pt.x), mapY(pt.y), 7);
      }
      // path
      p.noFill(); p.stroke(lineColor); p.strokeWeight(2);
      for(let i=0;i<tour.length;i++){
        const a = pts[tour[i]];
        const b = pts[tour[(i+1)%tour.length]];
        p.line(mapX(a.x), mapY(a.y), mapX(b.x), mapY(b.y));
      }
    };
    p.draw = function(){
      p.background(255);
      p.drawAxes();
      p.drawTour();
    };
  };
  return new p5(sketch);
}

/* ========================= Main app state and logic ========================= */
const state = {
  points: [],
  dist: [],
  tours: {}, // name -> {tour, length, time}
  leftSketch: null,
  rightSketch: null
};

function generatePoints(N, seed){
  const rng = makeRNGFromSeed(seed);
  const pts = [];
  for(let i=0;i<N;i++){
    pts.push({x:rng(), y:rng()});
  }
  return pts;
}

function runAllAlgorithms(){
  const N = parseInt(document.getElementById('slider-points').value, 10);
  const seed = document.getElementById('seed-input').value;
  state.points = generatePoints(N, seed);
  state.dist = buildDistance(state.points);

  const initial = nearestNeighbor(state.dist);

  // Helper to time a solver
  function run(name, fn){
    const t0 = performance.now();
    const tour = fn();
    const L = tourLength(tour, state.dist);
    const t1 = performance.now();
    state.tours[name] = { tour, length: L, time: (t1 - t0)/1000.0 };
  }

  run('OrZweig', ()=>solveOrZweig(initial, state.dist));
  run('OrOpt',   ()=>solveOrOpt(initial, state.dist, 3, 600));
  run('TwoOpt',  ()=>solveTwoOpt(initial, state.dist, 450));
  run('CCA',     ()=>christofides(state.points, state.dist));
  run('3-Opt',   ()=>solve3Opt(initial, state.dist));

  // Update results table
  document.getElementById('timing-orzweig').textContent = formatNumber(state.tours['OrZweig'].time);
  document.getElementById('result-orzweig').textContent  = formatNumber(state.tours['OrZweig'].length);

  document.getElementById('timing-oropt').textContent = formatNumber(state.tours['OrOpt'].time);
  document.getElementById('result-oropt').textContent  = formatNumber(state.tours['OrOpt'].length);

  document.getElementById('timing-twoopt').textContent = formatNumber(state.tours['TwoOpt'].time);
  document.getElementById('result-twoopt').textContent  = formatNumber(state.tours['TwoOpt'].length);

  document.getElementById('timing-cca').textContent = formatNumber(state.tours['CCA'].time);
  document.getElementById('result-cca').textContent  = formatNumber(state.tours['CCA'].length);

  document.getElementById('timing-3opt').textContent = formatNumber(state.tours['3-Opt'].time);
  document.getElementById('result-3opt').textContent  = formatNumber(state.tours['3-Opt'].length);

  // Winner Logic
  const eps = 1e-9;
  const minMath = Math.min(state.tours['OrZweig'].length, state.tours['OrOpt'].length, state.tours['TwoOpt'].length, state.tours['CCA'].length);
  const three = state.tours['3-Opt'].length;
  let resultText = 'Tie';
  if(three + eps < minMath) resultText = '3-Opt wins';
  else if(minMath + eps < three) resultText = 'Mathematica wins';
  else resultText = 'Tie';
  document.getElementById('winner-display').textContent = resultText;

  // Redraw plots with current selection and 3-Opt
  updatePlotsOnly();
}

function updatePlotsOnly(){
  const selected = document.querySelector('input[name="math-method"]:checked').value;
  document.getElementById('left-plot-title').textContent = selected;
  if(state.leftSketch && state.rightSketch && state.points.length){
    const leftTour = state.tours[selected] ? state.tours[selected].tour : [];
    const rightTour = state.tours['3-Opt'] ? state.tours['3-Opt'].tour : [];
    state.leftSketch.updateData(state.points, leftTour);
    state.rightSketch.updateData(state.points, rightTour);
  }
}

/* ========================= Event bindings ========================= */
function setupUI(){
  document.getElementById('btn-random-set').addEventListener('click', ()=>{
    const randSeed = Math.floor(1 + Math.random()*99999);
    document.getElementById('seed-input').value = String(randSeed);
    runAllAlgorithms();
  });
  document.getElementById('slider-points').addEventListener('change', ()=>{
    runAllAlgorithms();
  });
  document.getElementById('btn-reset-seed').addEventListener('click', ()=>{
    document.getElementById('seed-input').value = '0';
    runAllAlgorithms();
  });
  document.querySelectorAll('input[name="math-method"]').forEach(rb=>{
    rb.addEventListener('change', ()=>{
      // Only redraw left plot using pre-computed tours
      updatePlotsOnly();
    });
  });
}

/* ========================= Boot ========================= */
window.addEventListener('DOMContentLoaded', ()=>{
  setupUI();
  // create sketches
  state.leftSketch = createTourSketch('canvas-left-wrapper', '#0072B2');
  state.rightSketch = createTourSketch('canvas-right-wrapper', '#E69F00');
  // Initial computation
  runAllAlgorithms();
});
</script>
</body>
</html>
