<script>
/* ---------- Core automaton and utilities ---------- */
const SCALE = 2;            // 256x256 logical grid -> 512x512 canvas
const CANVAS_SIZE = 512;    // square canvas
const MOD = 65536;          // 16-bit

function bitReverse15(n) {
  // reverse lowest 15 bits of n
  let rev = 0;
  for (let i = 0; i < 15; i++) {
    rev = (rev << 1) | ((n >> i) & 1);
  }
  return rev & 0x7FFF;
}

function f(n) {
  const br = bitReverse15(n >> 1);       // floor(n/2) then reverse 15 bits
  return (n + br) & 0xFFFF;              // modulo 65536
}

function nToGridXY(n) {
  return { x: n & 255, y: (n >> 8) & 255 };
}

// Map grid coordinates (0..255) to canvas pixels.
// Flip Y so that larger logical y appear lower on canvas (more "cartesian").
function gridToCanvas(x, y) {
  return {
    x: x * SCALE + SCALE / 2,
    y: (255 - y) * SCALE + SCALE / 2
  };
}

function nToCanvasXY(n) {
  const g = nToGridXY(n);
  return gridToCanvas(g.x, g.y);
}

/* ---------- Global state ---------- */
let initVal = 462;
let maxSteps = 9;
let showCycle4 = false;
let showCycle2 = false;
let showCycle1 = false;
let showAllPoints = false;
let modifyInit = false; // tracked only

let sequence = [];   // integers n_0..n_{maxSteps+1}
let seqPoints = [];  // corresponding canvas coordinates

// Precomputed overlays
let allMapPoints = null;   // [{x,y}, ...] of f(n) mapped to canvas
let fixedPointDots = null; // fixed points f(n)=n mapped to canvas

/* ---------- Sequence generation and cycle detection ---------- */
function computeSequence(n0, steps) {
  const seq = [n0];
  let n = n0;
  for (let i = 0; i <= steps; i++) {
    n = f(n);
    seq.push(n);
  }
  return seq;
}

function firstRepeatInfo(seq) {
  const seen = new Map();
  for (let i = 0; i < seq.length; i++) {
    const val = seq[i];
    if (seen.has(val)) {
      const prev = seen.get(val);
      return { found: true, k: i - prev, j: i };
    }
    seen.set(val, i);
  }
  return { found: false };
}

function updateInfoText() {
  const infoEl = document.getElementById('info-text');
  const info = firstRepeatInfo(sequence);
  if (info.found) {
    infoEl.textContent = `result of initial condition ${initVal} repeats in ${info.k} steps`;
  } else {
    infoEl.textContent = `result of initial condition ${initVal} does not repeat in ${maxSteps + 1} steps`;
  }
}

/* ---------- Precomputations for overlays ---------- */
function precomputeAllMapPoints() {
  if (allMapPoints) return;
  allMapPoints = new Array(MOD);
  for (let n = 0; n < MOD; n++) {
    const nn = f(n);
    allMapPoints[n] = nToCanvasXY(nn);
  }
}

function precomputeFixedPoints() {
  if (fixedPointDots) return;
  fixedPointDots = [];
  for (let n = 0; n < MOD; n++) {
    if (f(n) === n) {
      fixedPointDots.push(nToCanvasXY(n));
    }
  }
}

/* ---------- UI bindings ---------- */
function bindUI() {
  const sInit = document.getElementById('slider-initialization');
  const vInit = document.getElementById('value-initialization');
  const sSteps = document.getElementById('slider-max-steps');
  const vSteps = document.getElementById('value-max-steps');

  sInit.addEventListener('input', () => {
    initVal = parseInt(sInit.value, 10);
    vInit.textContent = `+ ${initVal}`;
    regenerate();
  });

  sSteps.addEventListener('input', () => {
    maxSteps = parseInt(sSteps.value, 10);
    vSteps.textContent = `+ ${maxSteps}`;
    regenerate();
  });

  document.getElementById('checkbox-all-points').addEventListener('change', (e) => {
    showAllPoints = e.target.checked;
    if (showAllPoints) precomputeAllMapPoints();
    redrawSketch();
  });

  document.getElementById('checkbox-cycle1').addEventListener('change', (e) => {
    showCycle1 = e.target.checked;
    if (showCycle1) precomputeFixedPoints();
    redrawSketch();
  });

  document.getElementById('checkbox-cycle2').addEventListener('change', (e) => {
    showCycle2 = e.target.checked;
    redrawSketch();
  });

  document.getElementById('checkbox-cycle4').addEventListener('change', (e) => {
    showCycle4 = e.target.checked;
    redrawSketch();
  });

  document.getElementById('checkbox-modify').addEventListener('change', (e) => {
    modifyInit = e.target.checked; // tracked only
    redrawSketch();
  });
}

/* ---------- p5 sketch ---------- */
let sketchP5;

function regenerate() {
  sequence = computeSequence(initVal, maxSteps);
  seqPoints = sequence.map(n => nToCanvasXY(n));
  updateInfoText();
  redrawSketch();
}

function redrawSketch() {
  if (sketchP5) sketchP5.redraw();
}

function startSketch() {
  new p5((p) => {
    sketchP5 = p;

    p.setup = function() {
      const c = p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
      c.parent('canvas-container');
      p.pixelDensity(1);
      p.noLoop();
      regenerate();
    };

    p.draw = function() {
      // Background
      p.background('#e0f0e0');

      // "All map points" overlay
      if (showAllPoints && allMapPoints) {
        p.stroke('#808080');
        p.strokeWeight(1);
        for (let i = 0; i < allMapPoints.length; i++) {
          const pt = allMapPoints[i];
          p.point(pt.x, pt.y);
        }
      }

      // Automaton path polyline
      if (seqPoints && seqPoints.length > 1) {
        p.noFill();
        p.stroke('#800080');
        p.strokeWeight(2);
        p.beginShape();
        for (let i = 0; i < seqPoints.length; i++) {
          p.vertex(seqPoints[i].x, seqPoints[i].y);
        }
        p.endShape();

        // Start (red) and end (blue) points
        const r = 4;
        p.noStroke();
        p.fill('#FF0000');
        p.circle(seqPoints[0].x, seqPoints[0].y, r);
        p.fill('#0000FF');
        p.circle(seqPoints[seqPoints.length - 1].x, seqPoints[seqPoints.length - 1].y, r);
      }

      // "Length 1 cycle points" overlay (fixed points)
      if (showCycle1 && fixedPointDots) {
        p.stroke('#00FFFF');
        p.strokeWeight(3);
        for (const pt of fixedPointDots) {
          p.point(pt.x, pt.y);
        }
      }

      // "Length 2 cycles" overlay (hardcoded pairs)
      if (showCycle2) {
        const pairs = [
          [10922, 21845], [13107, 26214], [13653, 27306],
          [15339, 30678], [21844, 43691], [26213, 52427],
          [27305, 54611], [30677, 61355], [43690, 54613]
        ];
        p.stroke('#008000');
        p.strokeWeight(3);
        for (const [n1, n2] of pairs) {
          const p1 = nToCanvasXY(n1);
          const p2 = nToCanvasXY(n2);
          p.line(p1.x, p1.y, p2.x, p2.y);
        }
      }

      // "Length 4 cycle" overlay (hardcoded cycle and constructions)
      if (showCycle4) {
        const cycVals = [43690, 54612, 32768, 49152];
        const cycPts = cycVals.map(n => nToCanvasXY(n));
        // Cycle path
        p.stroke('#008000');
        p.strokeWeight(3);
        for (let i = 0; i < cycPts.length; i++) {
          const a = cycPts[i];
          const b = cycPts[(i + 1) % cycPts.length];
          p.line(a.x, a.y, b.x, b.y);
        }
        // Bounding box
        let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;
        for (const pt of cycPts) {
          if (pt.x < minX) minX = pt.x;
          if (pt.y < minY) minY = pt.y;
          if (pt.x > maxX) maxX = pt.x;
          if (pt.y > maxY) maxY = pt.y;
        }
        p.noFill();
        p.stroke('#FFA500');
        p.strokeWeight(3);
        p.rect(minX, minY, maxX - minX, maxY - minY);

        // Perpendicular bisectors of the four sides
        p.stroke('#008000');
        p.strokeWeight(3);
        for (let i = 0; i < cycPts.length; i++) {
          const a = cycPts[i];
          const b = cycPts[(i + 1) % cycPts.length];
          const mx = (a.x + b.x) / 2;
          const my = (a.y + b.y) / 2;
          const vx = b.x - a.x;
          const vy = b.y - a.y;
          // Perpendicular vector
          let pxv = -vy, pyv = vx;
          const len = Math.hypot(pxv, pyv) || 1;
          // Draw a long portion of the bisector
          const L = 140;
          pxv = (pxv / len) * L;
          pyv = (pyv / len) * L;
          p.line(mx - pxv, my - pyv, mx + pxv, my + pyv);
        }
      }
    };
  });
}

/* ---------- Initialize ---------- */
bindUI();
startSketch();
</script>
</body>
</html>
