        <label for="stage-3">3</label>

        <input type="radio" name="stage" id="stage-4" value="4">
        <label for="stage-4">4</label>

        <input type="radio" name="stage" id="stage-5" value="5" checked>
        <label for="stage-5">5</label>

        <input type="radio" name="stage" id="stage-6" value="6">
        <label for="stage-6">6</label>

