        { name: 'Mu Tel',    raH: 18.997, decD: -52.89 }
      ],
      edges: [[0,1],[1,2]]
    }
  };

  // Families -> constellations
  const FAMILIES = {
    'Zodiac': ['Cygnus', 'Capricornus', 'Aries'],
    'Ursa Major Family': ['Ursa Major', 'Ursa Minor'],
    'Perseus Family': ['Cassiopeia', 'Perseus', 'Andromeda'],
    'Hercules Family': ['Hercules', 'Lyra', 'Aquila'],
    'Orion Family': ['Orion', 'Canis Major'],
    'Bayer Group': ['Hydra', 'Crater', 'Corvus'],
    'La Caille Family': ['Pyxis', 'Antlia', 'Telescopium']
  };

  // Constellation visualization objects (updated dynamically)
  const constellationMaterial = new THREE.LineBasicMaterial({ color: 0xff3030, transparent: true, opacity: 0.9 });
  const constellationGeometry = new THREE.BufferGeometry();
  const constellationLines = new THREE.LineSegments(constellationGeometry, constellationMaterial);
  constellationLines.visible = false;
  scene.add(constellationLines);

  const constellationPointMaterial = new THREE.PointsMaterial({ color: 0xffdd55, size: 0.14, sizeAttenuation: true });
  const constellationPointGeometry = new THREE.BufferGeometry();
  const constellationPoints = new THREE.Points(constellationPointGeometry, constellationPointMaterial);
  constellationPoints.visible = false;
  scene.add(constellationPoints);

  function getAllConstellationNames() {
    const names = new Set();
    Object.keys(FAMILIES).forEach(f => FAMILIES[f].forEach(n => names.add(n)));
    return Array.from(names).sort();
  }

  function populateConstellationSelect(family) {
    // Always include "None"
    selectConstellation.innerHTML = '';
    const noneOption = document.createElement('option');
    noneOption.textContent = 'None';
    noneOption.value = 'None';
    selectConstellation.appendChild(noneOption);

    let names = [];
    if (family === 'all') {
      names = getAllConstellationNames();
      selectConstellation.value = 'None';
    } else {
      names = FAMILIES[family] ? [...FAMILIES[family]] : [];
    }
    names.forEach(name => {
      const opt = document.createElement('option');
      opt.value = name;
      opt.textContent = name;
      selectConstellation.appendChild(opt);
    });

    if (family === 'all') {
      selectConstellation.value = 'None';
    } else {
      // Special default: when "Zodiac" is selected, default to "Cygnus" (per requirement)
      if (family === 'Zodiac' && names.includes('Cygnus')) {
        selectConstellation.value = 'Cygnus';
      } else if (names.length > 0) {
        selectConstellation.value = names[0];
      } else {
        selectConstellation.value = 'None';
      }
    }
    updateConstellationSelection();
  }

  function updateConstellationSelection() {
    const sel = selectConstellation.value;
    if (!sel || sel === 'None' || !CONSTELLATIONS[sel]) {
      constellationLines.visible = false;
      constellationPoints.visible = false;
      // Clear existing geometry
      constellationGeometry.setAttribute('position', new THREE.BufferAttribute(new Float32Array(0), 3));
      constellationPointGeometry.setAttribute('position', new THREE.BufferAttribute(new Float32Array(0), 3));
      return;
    }
    const c = CONSTELLATIONS[sel];
    // Build points for stars
    const pts = new Float32Array(c.stars.length * 3);
    c.stars.forEach((s, i) => {
      const v = radecHoursToVec3(s.raH, s.decD, R);
      pts[i*3] = v.x; pts[i*3+1] = v.y; pts[i*3+2] = v.z;
    });
    constellationPointGeometry.setAttribute('position', new THREE.BufferAttribute(pts, 3));
    constellationPointGeometry.computeBoundingSphere();

    // Build line segments
    const segCount = c.edges.length;
    const linePts = new Float32Array(segCount * 2 * 3);
    for (let i = 0; i < segCount; i++) {
      const [a, b] = c.edges[i];
      const va = new THREE.Vector3(pts[a*3], pts[a*3+1], pts[a*3+2]);
      const vb = new THREE.Vector3(pts[b*3], pts[b*3+1], pts[b*3+2]);
      linePts[i*6]   = va.x; linePts[i*6+1] = va.y; linePts[i*6+2] = va.z;
      linePts[i*6+3] = vb.x; linePts[i*6+4] = vb.y; linePts[i*6+5] = vb.z;
    }
    constellationGeometry.setAttribute('position', new THREE.BufferAttribute(linePts, 3));
    constellationGeometry.computeBoundingSphere();

    constellationLines.visible = true;
    constellationPoints.visible = true;
  }

  // UI bindings
  selectFamily.addEventListener('change', () => {
    populateConstellationSelect(selectFamily.value);
  });
  selectConstellation.addEventListener('change', () => {
    updateConstellationSelection();
  });

  checkSphere.addEventListener('change', () => celestialSphere.visible = checkSphere.checked);
  checkStars.addEventListener('change', () => starCloud.visible = checkStars.checked);
  checkEquator.addEventListener('change', () => equatorLine.visible = checkEquator.checked);
  checkEcliptic.addEventListener('change', () => eclipticLine.visible = checkEcliptic.checked);
  checkZenith.addEventListener('change', () => zenithGroup.visible = checkZenith.checked);
  checkAries.addEventListener('change', () => ariesMesh.visible = checkAries.checked);

  sliderRA.addEventListener('input', updateZenithFromSliders);
  sliderDec.addEventListener('input', updateZenithFromSliders);

  // Initial population (default family: Zodiac, default constellation: Cygnus)
  selectFamily.value = 'Zodiac';
  populateConstellationSelect('Zodiac');

  // Resize handling
  function onResize() {
    const w = container.clientWidth;
    const h = container.clientHeight;
    camera.aspect = w / h;
    camera.updateProjectionMatrix();
    renderer.setSize(w, h, false);
    labelRenderer.setSize(w, h);
  }
  window.addEventListener('resize', onResize);
  const ro = new ResizeObserver(onResize);
  ro.observe(container);
  onResize();

  // Animation loop
  function animate() {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
    labelRenderer.render(scene, camera);
  }
  animate();

  // Set initial visibility from checkboxes
  celestialSphere.visible = checkSphere.checked;
  starCloud.visible = checkStars.checked;
  equatorLine.visible = checkEquator.checked;
  eclipticLine.visible = checkEcliptic.checked;
  zenithGroup.visible = checkZenith.checked;
  ariesMesh.visible = checkAries.checked;

</script>
</body>
