<!-- Plotly.js CDN -->
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

<script>
/* ---------- Global state ---------- */
const CONST = { fs: 32000, N: 2048, f: 1000, A: 1 };
let storedSpectrum = null; // single-sided normalized magnitude (length N/2)
let storedFreq = null;     // frequency vector for first N/2 bins

/* ---------- Radix-2 in-place FFT ---------- */
function fft(real, imag) {
  const n = real.length;
  const levels = Math.log2(n) | 0;
  if ((1 << levels) !== n) throw new Error("FFT length must be a power of two");

  // Bit-reversed permutation
  for (let i = 0; i < n; i++) {
    let j = 0;
    for (let k = 0; k < levels; k++) j = (j << 1) | ((i >>> k) & 1) & 1;
    if (j > i) {
      [real[i], real[j]] = [real[j], real[i]];
      [imag[i], imag[j]] = [imag[j], imag[i]];
    }
  }

  // Cooley-Tukey
  for (let size = 2; size <= n; size <<= 1) {
    const halfsize = size >>> 1;
    const tablestep = (2 * Math.PI) / size;
    for (let i = 0; i < n; i += size) {
      for (let j = 0; j < halfsize; j++) {
        const angle = j * tablestep;
        const cos = Math.cos(angle);
        const sin = Math.sin(angle);
        const tre =  real[i + j + halfsize] * cos + imag[i + j + halfsize] * sin;
        const tim = -real[i + j + halfsize] * sin + imag[i + j + halfsize] * cos;
        real[i + j + halfsize] = real[i + j] - tre;
        imag[i + j + halfsize] = imag[i + j] - tim;
        real[i + j] += tre;
        imag[i + j] += tim;
      }
    }
  }
  // in-place; no return needed
}

/* ---------- UI Elements ---------- */
const elC1 = document.getElementById('slider-c1');
const elC2 = document.getElementById('slider-c2');
const elC1Val = document.getElementById('c1-value-display');
const elC2Val = document.getElementById('c2-value-display');
const btnResetC1 = document.getElementById('reset-c1');
const btnResetC2 = document.getElementById('reset-c2');

const elClip = document.getElementById('checkbox-clipping');
const levelGroup = document.getElementById('level-control-group');
const elLevel = document.getElementById('slider-level');
const elLevelVal = document.getElementById('level-value-display');

const btnLinear = document.getElementById('btn-linear');
const btnDecibel = document.getElementById('btn-decibel');

/* ---------- Initialize controls (defaults) ---------- */
function initControls() {
  // Sliders defaults and bounds
  elC1.min = -2; elC1.max = 2; elC1.step = 0.1; elC1.value = 0;
  elC2.min = -2; elC2.max = 2; elC2.step = 0.1; elC2.value = 0;

  elLevel.min = 0; elLevel.max = 1.5; elLevel.step = 0.05; elLevel.value = 0.7;

  // Displays
  elC1Val.textContent = '0';
  elC2Val.textContent = '0';
  elLevelVal.textContent = '0.7';

  // Clipping defaults
  elClip.checked = false;
  levelGroup.style.display = 'none';

  // Scale buttons
  btnLinear.classList.add('active');
  btnDecibel.classList.remove('active');
}

/* ---------- Plot Layouts ---------- */
const signalLayout = {
  title: 'signal',
  xaxis: { title: 'time[s]', range: [0, 0.005], autorange: false, zeroline: false },
  yaxis: { title: '', range: [-2, 2], autorange: false, zeroline: true, zerolinecolor: '#88a' },
  margin: { l: 40, r: 20, b: 40, t: 40 },
  showlegend: false
};
const spectrumBaseLayout = {
  title: 'magnitude spectrum (DFT)',
  xaxis: { title: 'frequency[Hz]', range: [0, 8000], autorange: false },
  yaxis: { title: '', autorange: true },
  margin: { l: 40, r: 20, b: 40, t: 40 },
  bargap: 0.8,
  showlegend: false
};

/* ---------- Core Update Function ---------- */
function updateSimulation() {
  // Constants
  const f = CONST.f;
  const A = CONST.A;
  const N = CONST.N;
  const fs = CONST.fs;

  // Read control values
  const c1 = parseFloat(elC1.value);
  const c2 = parseFloat(elC2.value);
  const clipping = elClip.checked;
  const level = parseFloat(elLevel.value);

  // Time vector
  const t = new Array(N);
  for (let i = 0; i < N; i++) t[i] = i / fs;

  // Input and output signals
  const twoPiF = 2 * Math.PI * f;
  const x = new Array(N);
  const y = new Array(N);
  for (let i = 0; i < N; i++) {
    const xi = A * Math.sin(twoPiF * t[i]);
    x[i] = xi;
    let yi = xi + c1 * (xi * xi) + c2 * (xi * xi * xi);
    if (clipping) yi = Math.max(-level, Math.min(level, yi));
    y[i] = yi;
  }

  // Update Signal Plot
  const signalTrace = { x: t, y: y, type: 'scatter', mode: 'lines', line: { color: 'blue', width: 2 } };
  Plotly.react('plot-signal', [signalTrace], signalLayout, {responsive: true});

  // FFT
  const real = y.slice();
  const imag = new Array(N).fill(0);
  fft(real, imag);

  // Single-sided normalized magnitude
  const half = N >> 1;
  const normMag = new Array(half);
  const mag0 = Math.hypot(real[0], imag[0]) / N;
  normMag[0] = mag0;
  for (let k = 1; k < half; k++) {
    normMag[k] = (2 * Math.hypot(real[k], imag[k])) / N;
  }

  // Store spectrum and frequency vector
  storedSpectrum = normMag;
  if (!storedFreq || storedFreq.length !== half) {
    storedFreq = new Array(half);
    for (let k = 0; k < half; k++) storedFreq[k] = (k * fs) / N;
  }

  // Update displays
  elC1Val.textContent = Number(c1).toFixed(1);
  elC2Val.textContent = Number(c2).toFixed(1);
  elLevelVal.textContent = Number(level).toFixed(2);

  // Render spectrum with current scale
  updateSpectrumScale();
}

/* ---------- Spectrum scale update ---------- */
function updateSpectrumScale() {
  if (!storedSpectrum || !storedFreq) return;

  const isLinear = btnLinear.classList.contains('active');
  let yData, yLayout;

  if (isLinear) {
    yData = storedSpectrum.slice();
    const ymax = Math.max(1.1, Math.max(...yData) * 1.1);
    yLayout = { yaxis: { title: '', range: [0, ymax], autorange: false } };
  } else { // decibel
    const eps = 1e-12;
    yData = storedSpectrum.map(v => {
      const db = 20 * Math.log10(Math.max(v, eps));
      return Math.max(-100, db);
    });
    yLayout = { yaxis: { title: '', range: [-100, 0], autorange: false } };
  }

  const spectrumTrace = {
    x: storedFreq,
    y: yData,
    type: 'bar',
    marker: { color: '#6c8fd6' },
    width: 5
  };
  const layout = Object.assign({}, spectrumBaseLayout, yLayout);

  Plotly.react('plot-spectrum', [spectrumTrace], layout, {responsive: true});
}

/* ---------- Event bindings ---------- */
function bindEvents() {
  elC1.addEventListener('input', updateSimulation);
  elC2.addEventListener('input', updateSimulation);

  btnResetC1.addEventListener('click', () => { elC1.value = 0; updateSimulation(); });
  btnResetC2.addEventListener('click', () => { elC2.value = 0; updateSimulation(); });

  elClip.addEventListener('change', () => {
    levelGroup.style.display = elClip.checked ? 'flex' : 'none';
    updateSimulation();
  });
  elLevel.addEventListener('input', updateSimulation);

  btnLinear.addEventListener('click', () => {
    if (!btnLinear.classList.contains('active')) {
      btnLinear.classList.add('active');
      btnDecibel.classList.remove('active');
      updateSpectrumScale();
    }
  });
  btnDecibel.addEventListener('click', () => {
    if (!btnDecibel.classList.contains('active')) {
      btnDecibel.classList.add('active');
      btnLinear.classList.remove('active');
      updateSpectrumScale();
    }
  });

  window.addEventListener('resize', () => {
    Plotly.Plots.resize('plot-signal');
    Plotly.Plots.resize('plot-spectrum');
  });
}

/* ---------- Boot ---------- */
document.addEventListener('DOMContentLoaded', () => {
  initControls();
  bindEvents();
  updateSimulation(); // initial render
});
</script>
</body>
</html>
