      <div class="checkboxes">
        <label><input id="checkbox-injected" type="checkbox" checked> <span class="dot red"></span> injected</label>
        <label><input id="checkbox-refracted" type="checkbox" checked> <span class="dot green"></span> refracted</label>
        <label><input id="checkbox-reflected" type="checkbox" checked> <span class="dot blue"></span> reflected</label>
        <label><input id="checkbox-ejected" type="checkbox" checked> <span class="dot black"></span> ejected</label>
        <label><input id="checkbox-normals" type="checkbox"> normals</label>
        <label><input id="checkbox-intersections" type="checkbox"> intersections</label>
        <label><input id="checkbox-beam-top" type="checkbox" checked> beam top half</label>
        <label><input id="checkbox-beam-bottom" type="checkbox" checked> beam bottom half</label>
      </div>
    </div>

    <div id="canvas-container">
      <!-- p5.js canvas will be injected here -->
      <canvas id="p5-canvas"></canvas>
    </div>
  </div>

<script>
(function() {
  // Utility: update slider display values
  function updateLabels() {
    document.getElementById('val-ray-count').textContent =
      document.getElementById('slider-ray-count').value;

    document.getElementById('val-beam-width').textContent =
      (+document.getElementById('slider-beam-width').value).toFixed(2);

    document.getElementById('val-incident-angle').textContent =
      document.getElementById('slider-incident-angle').value + '°';

    document.getElementById('val-refraction-ratio').textContent =
      (+document.getElementById('slider-refraction-ratio').value).toFixed(2);
  }

  // p5 sketch in instance mode
  const sketch = (p) => {
    let scaleFactor = 1; // pixels per world unit
    const viewR = 2.5;   // half-range of world coords in both axes
    const canvasMargin = 16;

    function worldStroke(px) { p.strokeWeight(px/scaleFactor); }
    function worldCircle(x, y, r, col) {
      p.push();
      p.fill(col || '#000');
      p.noStroke();
      p.circle(x, y, 2*r);
      p.pop();
    }
    function drawDashedAlong(p0, dir, length, dash=0.06, gap=0.04) {
      // dir should be unit in world coords
      let t = 0;
      p.push();
      p.stroke(0,0,0,120);
      worldStroke(1.2);
      while (t < length) {
        const t2 = Math.min(length, t + dash);
        const a = {x: p0.x + dir.x*t,  y: p0.y + dir.y*t};
        const b = {x: p0.x + dir.x*t2, y: p0.y + dir.y*t2};
        p.line(a.x, a.y, b.x, b.y);
        t = t2 + gap;
      }
      p.pop();
    }
    function toScreen(pt) {
      const x = p.width/2 + pt.x * scaleFactor;
      const y = p.height/2 - pt.y * scaleFactor;
      return {x, y};
    }

    function vecAdd(a,b){ return {x:a.x+b.x, y:a.y+b.y}; }
    function vecSub(a,b){ return {x:a.x-b.x, y:a.y-b.y}; }
    function vecMul(a,s){ return {x:a.x*s, y:a.y*s}; }
    function dot(a,b){ return a.x*b.x + a.y*b.y; }
    function len(a){ return Math.hypot(a.x, a.y); }
    function norm(a){ const L = len(a)||1; return {x:a.x/L, y:a.y/L}; }

    function intersectRayCircle(o, d, r=1, eps=1e-6) {
      // Ray: o + t d, with t >= 0, d normalized. Solve ||o + t d||^2 = r^2
      const b = dot(o, d);
      const c = dot(o,o) - r*r;
      const disc = b*b - c;
      if (disc < 0) return null;
      const s = Math.sqrt(disc);
      const t1 = -b - s;
      const t2 = -b + s;
      let t = Infinity;
      if (t1 > eps) t = Math.min(t, t1);
      if (t2 > eps) t = Math.min(t, t2);
      if (!isFinite(t)) return null;
      return t;
    }

    function reflect(I, N) {
      const k = 2 * dot(I, N);
      return norm(vecSub(I, vecMul(N, k)));
    }

    function refract(I, N, eta) {
      // I, N are unit, N is outward normal. eta = n1/n2
      const cosi = -dot(N, I);
      const k = 1 - eta*eta*(1 - cosi*cosi);
      if (k < 0) return null; // Total internal reflection
      const T = vecAdd(vecMul(I, eta), vecMul(N, eta*cosi - Math.sqrt(k)));
      return norm(T);
    }

    function drawAxesAndSphere() {
      p.push();
      p.translate(p.width/2, p.height/2);
      p.scale(scaleFactor, -scaleFactor);

      // Axes
      p.stroke('#999');
      worldStroke(1);
      p.line(-viewR, 0, viewR, 0);
      p.line(0, -viewR, 0, viewR);

      // Ticks at -2,-1,1,2
      const ticks = [-2, -1, 1, 2];
      p.stroke('#777');
      worldStroke(1);
      for (const t of ticks) {
        // x-axis ticks
        p.line(t, -0.05, t, 0.05);
        // y-axis ticks
        p.line(-0.05, t, 0.05, t);
      }

      // Sphere
      p.noFill();
      p.stroke('#000');
      worldStroke(1.8);
      p.circle(0, 0, 2); // diameter = 2 (radius 1)
      p.pop();

      // Labels draw in screen space to avoid flipping
      p.push();
      p.fill('#333');
      p.noStroke();
      p.textAlign(p.CENTER, p.CENTER);
      p.textSize(12);
      for (const t of [ -2, -1, 1, 2 ]) {
        // x labels
        let s = toScreen({x:t, y:0});
        p.text(String(t), s.x, s.y + 14);
        // y labels
        s = toScreen({x:0, y:t});
        p.text(String(t), s.x - 14, s.y);
      }
      p.pop();
    }

    function getControls() {
      return {
        N: parseInt(document.getElementById('slider-ray-count').value, 10),
        w: parseFloat(document.getElementById('slider-beam-width').value),
        angleDeg: parseFloat(document.getElementById('slider-incident-angle').value),
        nRatio: parseFloat(document.getElementById('slider-refraction-ratio').value),
        showInjected: document.getElementById('checkbox-injected').checked,
        showRefracted: document.getElementById('checkbox-refracted').checked,
        showReflected: document.getElementById('checkbox-reflected').checked,
        showEjected: document.getElementById('checkbox-ejected').checked,
        showNormals: document.getElementById('checkbox-normals').checked,
        showIntersections: document.getElementById('checkbox-intersections').checked,
        beamTop: document.getElementById('checkbox-beam-top').checked,
        beamBottom: document.getElementById('checkbox-beam-bottom').checked,
      };
    }

    function drawRaySegments() {
      const cfg = getControls();
      const a = cfg.angleDeg * Math.PI / 180;
      const u = {x: Math.cos(a), y: Math.sin(a)};                 // beam direction
      const nperp = {x: -Math.sin(a), y:  Math.cos(a)};           // "up" relative to beam
      const Lstart = 3.2; // start distance behind origin

      // Prepare transforms
      p.push();
      p.translate(p.width/2, p.height/2);
      p.scale(scaleFactor, -scaleFactor);

      const N = Math.max(1, cfg.N);
      const offsets = [];
      if (N === 1) {
        offsets.push(0);
      } else {
        for (let i = 0; i < N; i++) {
          const t = (i/(N-1)) - 0.5; // -0.5 .. 0.5
          offsets.push(t * cfg.w);
        }
      }

      const rayWeight = 1.6; // px

      for (let i = 0; i < offsets.length; i++) {
        const off = offsets[i];
        // Beam half filtering
        const isTop = off > 0.000001;
        const isBottom = off < -0.000001;
        const center = Math.abs(off) <= 0.000001;
        if (!center) {
          if (isTop && !cfg.beamTop) continue;
          if (isBottom && !cfg.beamBottom) continue;
        } else {
          if (!cfg.beamTop && !cfg.beamBottom) continue;
        }

        // Starting point of this ray
        const p0 = vecAdd(vecMul(u, -Lstart), vecMul(nperp, off));
        const d0 = u; // already unit

        // First intersection (entry)
        const t1 = intersectRayCircle(p0, d0, 1);
        let P1 = null;
        if (t1 !== null) P1 = vecAdd(p0, vecMul(d0, t1));

        // Draw injected segment
        if (cfg.showInjected) {
          p.push();
          p.stroke('#ff0000');
          worldStroke(rayWeight);
          const tail = p0;
          const head = P1 ? P1 : vecAdd(p0, vecMul(d0, 6.0));
          p.line(tail.x, tail.y, head.x, head.y);
          p.pop();
        }

        if (!P1) continue; // Missed the sphere

        const N1 = norm(P1); // outward normal
        // Refraction into sphere: n1=1, n2=nRatio
        const T1 = refract(d0, N1, 1.0/cfg.nRatio);
        if (!T1) continue; // (shouldn't happen for entering)

        // Second intersection (from inside)
        const pInside = vecAdd(P1, vecMul(T1, 1e-6));
        const t2 = intersectRayCircle(pInside, T1, 1);
        if (t2 === null) continue;
        const P2 = vecAdd(pInside, vecMul(T1, t2));

        if (cfg.showRefracted) {
          p.push();
          p.stroke('#00cc00'); // slightly darker for visibility
          worldStroke(rayWeight);
          p.line(P1.x, P1.y, P2.x, P2.y);
          p.pop();
        }

        const N2 = norm(P2);
        const R = reflect(T1, N2); // internal reflection direction

        // Third intersection after reflection
        const pRef = vecAdd(P2, vecMul(R, 1e-6));
        const t3 = intersectRayCircle(pRef, R, 1);
        if (t3 === null) continue;
        const P3 = vecAdd(pRef, vecMul(R, t3));

        if (cfg.showReflected) {
          p.push();
          p.stroke('#0000ff');
          worldStroke(rayWeight);
          p.line(P2.x, P2.y, P3.x, P3.y);
          p.pop();
        }

        // Exit refraction (sphere -> air)
        const N3 = norm(P3);
        const E = refract(R, N3, cfg.nRatio); // eta = n_in/n_out = nRatio
        if (E && cfg.showEjected) {
          p.push();
          p.stroke('#000000');
          worldStroke(rayWeight);
          const far = vecAdd(P3, vecMul(E, 6.0));
          p.line(P3.x, P3.y, far.x, far.y);
          p.pop();
        }

        // Intersections
        if (cfg.showIntersections) {
          const rSmall = 0.04;
          p.noStroke();
          p.fill('#ff0000'); p.circle(P1.x, P1.y, 2*rSmall);
          p.fill('#00cc00'); p.circle(P2.x, P2.y, 2*rSmall);
          p.fill('#0000ff'); p.circle(P3.x, P3.y, 2*rSmall);
        }

        // Normals (dashed)
        if (cfg.showNormals) {
          const lenN = 0.22;
          drawDashedAlong(P1, norm(P1), lenN);
          drawDashedAlong(P2, norm(P2), lenN);
          drawDashedAlong(P3, norm(P3), lenN);
        }
      }

      p.pop();
    }

    p.setup = function() {
      // Create canvas sized to container while keeping aspect
      const container = document.getElementById('canvas-container');
      const W = Math.max(640, container.clientWidth - canvasMargin*2);
      const H = Math.max(440, container.clientHeight - canvasMargin*2);
      const cnv = p.createCanvas(W, H);
      cnv.parent('canvas-container');
      cnv.id('p5-canvas');

      scaleFactor = Math.min(p.width, p.height) / (2*viewR); // px per unit so range [-viewR, viewR] fits

      p.noLoop();
      updateLabels();
      p.redraw();
    };

    p.windowResized = function() {
      const container = document.getElementById('canvas-container');
      const W = Math.max(640, container.clientWidth - canvasMargin*2);
      const H = Math.max(440, container.clientHeight - canvasMargin*2);
      p.resizeCanvas(W, H);
      scaleFactor = Math.min(p.width, p.height) / (2*viewR);
      p.redraw();
    };

    p.draw = function() {
      p.background(255);
      drawAxesAndSphere();
      drawRaySegments();
    };
  };

  const p5Instance = new p5(sketch);

  // Wire up control events to update values and redraw
  function attachListeners() {
    const panel = document.getElementById('control-panel');
    const inputs = panel.querySelectorAll('input');
    inputs.forEach(inp => {
      inp.addEventListener('input', () => { updateLabels(); p5Instance.redraw(); });
      inp.addEventListener('change', () => { updateLabels(); p5Instance.redraw(); });
    });
  }
  attachListeners();
  updateLabels();
})();
</script>
</body>
</html>
