<script>
/* ----------------------------- Global state ----------------------------- */
let params = {
  size: 18, // total vertices, even
  degreeMax: 3
};

let graph = null;          // { n, U:[1..n], V:[n+1..2n], adjU:Map, adjV:Map, edges:[{u,v}] }
let history = [];          // array of state objects for each step
let currentStep = 0;

let p5canvas = null;
let canvasSize = { w: 1000, h: 520 };

/* --------------------------- Utility functions -------------------------- */
function randInt(a,b){ return Math.floor(Math.random()*(b-a+1))+a; }
function shuffle(arr){ for(let i=arr.length-1;i>0;i--){ const j = Math.floor(Math.random()*(i+1)); [arr[i],arr[j]]=[arr[j],arr[i]]; } return arr; }

function makeEmptyMatching(n){
  const matchU = new Array(n+1).fill(null); // 1..n
  const matchV = new Array(2*n+1).fill(null); // n+1..2n indices used
  return {matchU, matchV};
}
function matchingPairsFrom(matchU){
  const pairs = [];
  for(let u=1; u<matchU.length; u++){
    const v = matchU[u];
    if(v!=null) pairs.push([u,v]);
  }
  return pairs;
}
/* Minimum vertex cover from current matching (Kőnig's theorem) */
function computeMinVertexCover(graph, matchU, matchV){
  const n = graph.n;
  const visitedU = new Array(n+1).fill(false);
  const visitedV = new Array(2*n+1).fill(false);

  // all free U vertices start
  const q = [];
  for(let u=1; u<=n; u++){
    if(matchU[u]==null){ visitedU[u]=true; q.push({side:'U', id:u}); }
  }
  while(q.length){
    const item = q.shift();
    if(item.side==='U'){
      const u = item.id;
      const neigh = graph.adjU.get(u) || [];
      for(const v of neigh){
        if(!visitedV[v] && matchU[u] !== v){
          visitedV[v] = true;
          q.push({side:'V', id:v});
        }
      }
    }else{
      const v = item.id;
      const u2 = matchV[v];
      if(u2!=null && !visitedU[u2]){
        visitedU[u2] = true;
        q.push({side:'U', id:u2});
      }
    }
  }
  // cover = (U - visitedU) union (visitedV)
  const cover = new Set();
  for(let u=1; u<=n; u++) if(!visitedU[u]) cover.add(u);
  for(let v=n+1; v<=2*n; v++) if(visitedV[v]) cover.add(v);
  return cover;
}

/* ---------------------------- Graph generation -------------------------- */
function generateGraph(size, degreeMax){
  const n = Math.floor(size/2);
  const U = Array.from({length:n}, (_,i)=>i+1);
  const V = Array.from({length:n}, (_,i)=>n+i+1);

  const adjU = new Map();
  const adjV = new Map();
  for(let u of U){ adjU.set(u, []); }
  for(let v of V){ adjV.set(v, []); }

  // Ensure each lower (V) node has degree in [1, degreeMax]
  for(const v of V){
    const deg = Math.min(n, randInt(1, Math.max(1, degreeMax)));
    const candidates = shuffle([...U]).slice(0, deg);
    for(const u of candidates){
      if(!adjU.get(u).includes(v)){
        adjU.get(u).push(v);
        adjV.get(v).push(u);
      }
    }
  }

  // Optionally sprinkle a few extra edges for variety
  const extraEdges = Math.floor(n * 0.4);
  for(let k=0;k<extraEdges;k++){
    const u = randInt(1,n);
    const v = n + randInt(1,n);
    if(!adjU.get(u).includes(v)){
      adjU.get(u).push(v);
      adjV.get(v).push(u);
    }
  }

  const edges = [];
  for(const [u, vs] of adjU.entries()){
    for(const v of vs){ edges.push({u, v}); }
  }

  return { n, U, V, adjU, adjV, edges };
}

/* ---------------------- BFS augmenting path + history -------------------- */
function runAlgorithmWithHistory(g){
  history = [];
  const n = g.n;
  const {matchU, matchV} = makeEmptyMatching(n);

  let augmentedAny = true;
  let safety = 0; // safety to avoid infinite loops in degenerate cases
  while(augmentedAny && safety < 200){
    safety++;
    augmentedAny = false;
    // try each free U as a root sequentially
    const freeUs = [];
    for(let u=1; u<=n; u++) if(matchU[u]==null) freeUs.push(u);
    let foundInThisPass = false;

    for(const root of freeUs){
      const bfsRes = bfsFromRoot(g, root, matchU, matchV);
      // push the steps recorded during BFS
      for(const step of bfsRes.steps){
        recordSnapshot(step.matching, step.bfsTree, step.highlightedPath);
      }
      if(bfsRes.found){
        // augment matching
        const path = bfsRes.path;
        for(const [u,v] of path){
          if(matchU[u]===v){
            matchU[u]=null;
            matchV[v]=null;
          }else{
            matchU[u]=v;
            matchV[v]=u;
          }
        }
        augmentedAny = true;
        foundInThisPass = true;
        // after augmentation snapshot (tree cleared)
        const bfsTree = {root:null, edges:[], depths:{}, nodesU:[], nodesV:[]};
        recordSnapshot(matchingPairsFrom(matchU), bfsTree, path); // show the path one more time over updated matching
        break; // restart from new free set (classic augmenting approach)
      }
    }
    if(!foundInThisPass) break; // no augmenting paths from any free root
  }

  // Final snapshot (ensure we have at least one and show final cover)
  recordSnapshot(matchingPairsFrom(matchU), {root:null, edges:[], depths:{}, nodesU:[], nodesV:[]}, []);
}

/* BFS from a single root, recording step-by-step growth of the tree */
function bfsFromRoot(g, root, matchU, matchV){
  const steps = [];
  const visitedU = new Array(g.n+1).fill(false);
  const visitedV = new Array(2*g.n+1).fill(false);
  const parent = {}; // parent[nodeId] = nodeId of opposite side
  const depths = {};
  const nodesU = new Set();
  const nodesV = new Set();
  const treeEdges = [];

  const q = [];
  visitedU[root] = true;
  depths[root] = 0;
  nodesU.add(root);
  q.push(root);

  // initial snapshot containing only the root
  steps.push({
    matching: matchingPairsFrom(matchU),
    bfsTree: {
      root,
      edges:[...treeEdges],
      depths:{...depths},
      nodesU:[...nodesU],
      nodesV:[...nodesV]
    },
    highlightedPath: []
  });

  while(q.length){
    const u = q.shift();
    const neigh = g.adjU.get(u) || [];
    // Explore unmatched edges U -> V
    for(const v of neigh){
      if(visitedV[v]) continue;
      visitedV[v] = true;
      parent[v] = u;
      depths[v] = depths[u] + 1;
      nodesV.add(v);
      treeEdges.push([u, v]);

      steps.push({
        matching: matchingPairsFrom(matchU),
        bfsTree: {
          root,
          edges:[...treeEdges],
          depths:{...depths},
          nodesU:[...nodesU],
          nodesV:[...nodesV]
        },
        highlightedPath: []
      });

      if(matchV[v]==null){
        // Found free V -> construct augmenting path
        const path = buildAugPath(parent, v);
        steps.push({
          matching: matchingPairsFrom(matchU),
          bfsTree: {
            root,
            edges:[...treeEdges],
            depths:{...depths},
            nodesU:[...nodesU],
            nodesV:[...nodesV]
          },
          highlightedPath: path
        });
        return {found:true, path, steps};
      }else{
        const u2 = matchV[v];
        if(!visitedU[u2]){
          visitedU[u2] = true;
          parent[u2] = v;
          depths[u2] = depths[v] + 1;
          nodesU.add(u2);
          treeEdges.push([v, u2]);

          steps.push({
            matching: matchingPairsFrom(matchU),
            bfsTree: {
              root,
              edges:[...treeEdges],
              depths:{...depths},
              nodesU:[...nodesU],
              nodesV:[...nodesV]
            },
            highlightedPath: []
          });

          q.push(u2);
        }
      }
    }
  }

  // No augmenting path from this root; keep the built tree as final from this attempt
  return {found:false, path:[], steps};
}

function buildAugPath(parent, freeV){
  const path = [];
  let v = freeV;
  while(true){
    const u = parent[v];
    path.unshift([u, v]);
    if(parent[u] == null) break;
    v = parent[u];
  }
  return path;
}

/* -------------------------- History & snapshots -------------------------- */
function recordSnapshot(matchingPairs, bfsTree, highlightedPath){
  const matchUMap = new Array(graph.n+1).fill(null);
  for(const [u,v] of matchingPairs){ matchUMap[u]=v; }
  const matchVMap = new Array(2*graph.n+1).fill(null);
  for(const [u,v] of matchingPairs){ matchVMap[v]=u; }

  const cover = computeMinVertexCover(graph, matchUMap, matchVMap);
  const coverArr = Array.from(cover.values());

  history.push({
    matching: matchingPairs.map(p=>[p[0], p[1]]),
    cover: coverArr,
    bfsTree: bfsTree ? JSON.parse(JSON.stringify(bfsTree)) : {root:null, edges:[], depths:{}, nodesU:[], nodesV:[]},
    highlightedPath: highlightedPath ? highlightedPath.map(p=>[p[0],p[1]]) : [],
    statusText: `${matchingPairs.length} edges in matching (blue); ${coverArr.length} vertices in cover (green)`
  });
}

/* ----------------------------- UI management ---------------------------- */
function setActiveButton(groupId, buttonId){
  const group = document.getElementById(groupId);
  for(const btn of group.querySelectorAll('button')) btn.classList.remove('active');
  document.getElementById(buttonId).classList.add('active');
}

function readActive(groupId, prefix){
  const group = document.getElementById(groupId);
  const active = group.querySelector('button.active');
  if(!active) return null;
  const id = active.id; // e.g., size-18
  return parseInt(id.replace(prefix+'-',''), 10);
}

function populateStepControls(){
  const cont = document.getElementById('step-controls');
  cont.innerHTML = '';
  for(let i=0;i<history.length;i++){
    const b = document.createElement('button');
    b.id = `step-${i+1}`;
    b.textContent = String(i+1);
    if(i===0) b.classList.add('active');
    b.addEventListener('click', ()=>{
      setCurrentStep(i);
    });
    cont.appendChild(b);
  }
}

function setCurrentStep(i){
  currentStep = Math.max(0, Math.min(i, history.length-1));
  // update active button
  const cont = document.getElementById('step-controls');
  for(const btn of cont.querySelectorAll('button')) btn.classList.remove('active');
  const active = document.getElementById(`step-${currentStep+1}`);
  if(active) active.classList.add('active');

  // update status text
  const st = document.getElementById('status-text');
  st.textContent = history[currentStep]?.statusText || '';

  // redraw
  if(window.redraw) window.redraw();
}

/* ------------------------------- p5 drawing ------------------------------ */
function setupP5(){
  // Remove placeholder canvas (we'll create our own and assign the id)
  const existing = document.getElementById('main-canvas');
  if(existing) existing.remove();

  const wrapper = document.getElementById('canvas-wrapper');
  canvasSize.w = wrapper.clientWidth;
  canvasSize.h = wrapper.clientHeight;

  p5canvas = new p5((p)=>{
    p.setup = function(){
      const cnv = p.createCanvas(canvasSize.w, canvasSize.h);
      cnv.id('main-canvas');
      cnv.parent('canvas-wrapper');
      p.noLoop();
      p.pixelDensity(1);
      p.textFont('system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif');
      p.textSize(14);
    };

    p.windowResized = function(){
      const w = wrapper.clientWidth;
      const h = wrapper.clientHeight;
      p.resizeCanvas(w, h);
      setTimeout(()=>p.redraw(), 0);
    };

    p.draw = function(){
      p.clear();
      const W = p.width, H = p.height;
      // Draw subtle panel
      p.push();
      p.noStroke();
      p.fill(255);
      p.rect(4, 4, W-8, H-8, 8);
      p.pop();

      if(!history.length || !graph) return;
      const state = history[currentStep];

      const leftW = Math.floor(W*0.36);
      const rightW = W - leftW - 20;
      const leftX = 10;
      const rightX = leftX + leftW + 10;
      const topY = 20;
      const bottomY = H - 40;

      // Draw partitions positions
      const n = graph.n;
      const posU = {};
      const posV = {};
      // evenly spaced on the right area
      for(let i=1;i<=n;i++){
        const x = rightX + (i-0.5)*rightW/n;
        posU[i] = {x, y: topY+40};
      }
      for(let j=1;j<=n;j++){
        const x = rightX + (j-0.5)*rightW/n;
        const id = n + j;
        posV[id] = {x, y: bottomY};
      }

      // Draw all default edges (thin gray)
      p.push();
      p.stroke('#808080'); p.strokeWeight(1);
      for(const e of graph.edges){
        const a = posU[e.u];
        const b = posV[e.v];
        if(!a || !b) continue;
        p.line(a.x, a.y, b.x, b.y);
      }
      p.pop();

      // Draw highlighted path (red medium)
      if(state.highlightedPath && state.highlightedPath.length){
        p.push();
        p.stroke('#FF0000'); p.strokeWeight(3);
        for(const [u,v] of state.highlightedPath){
          const a = posU[u], b = posV[v];
          if(a && b) p.line(a.x, a.y, b.x, b.y);
        }
        p.pop();
      }

      // Draw matching edges on top (thick blue)
      p.push();
      p.stroke('#A0A0FF'); p.strokeWeight(5); p.strokeCap(p.ROUND);
      for(const [u,v] of state.matching){
        const a = posU[u], b = posV[v];
        if(a && b) p.line(a.x, a.y, b.x, b.y);
      }
      p.pop();

      // Vertex rendering (right side)
      const coverSet = new Set(state.cover || []);
      p.textAlign(p.CENTER, p.BOTTOM);
      for(let u=1; u<=n; u++){
        const pos = posU[u];
        const inCover = coverSet.has(u);
        p.fill(inCover ? '#00C000' : '#333333');
        if(state.bfsTree && state.bfsTree.root === u) p.fill('#FF0000');
        p.stroke(0,60); p.strokeWeight(1);
        p.circle(pos.x, pos.y, 14);
        p.noStroke();
        p.fill(0,140);
        p.text(String(u), pos.x, pos.y - 10);
      }
      p.textAlign(p.CENTER, p.TOP);
      for(let j=1; j<=n; j++){
        const id = n + j;
        const pos = posV[id];
        const inCover = coverSet.has(id);
        p.fill(inCover ? '#00C000' : '#333333');
        p.stroke(0,60); p.strokeWeight(1);
        p.circle(pos.x, pos.y, 14);
        p.noStroke();
        p.fill(0,140);
        p.text(String(id), pos.x, pos.y + 10);
      }

      // Draw BFS tree on left area
      drawBFSTree(p, state.bfsTree, {x:leftX, y:topY, w:leftW-10, h:bottomY-topY+10});
    };

  });
}

function drawBFSTree(p, tree, rect){
  if(!tree || (!tree.root && (!tree.nodesU || !tree.nodesU.length) && (!tree.nodesV || !tree.nodesV.length))) return;
  const depths = tree.depths || {};
  const nodes = [];
  const include = new Set();
  if(tree.root!=null) include.add(tree.root);
  for(const u of (tree.nodesU||[])) include.add(u);
  for(const v of (tree.nodesV||[])) include.add(v);

  for(const id of include){
    nodes.push({id, depth: depths[id] ?? 0});
  }
  if(nodes.length===0) return;

  // Build levels
  const byDepth = new Map();
  let maxDepth = 0;
  for(const n of nodes){
    maxDepth = Math.max(maxDepth, n.depth||0);
    if(!byDepth.has(n.depth)) byDepth.set(n.depth, []);
    byDepth.get(n.depth).push(n.id);
  }
  // Compute positions: x by depth, y by order in that depth
  const positions = {};
  for(let d=0; d<=maxDepth; d++){
    const lvl = byDepth.get(d) || [];
    const m = lvl.length;
    for(let i=0;i<m;i++){
      const x = rect.x + 30 + (rect.w-60) * (d/(Math.max(1,maxDepth)));
      const y = rect.y + 30 + (rect.h-60) * ((i+1)/(m+1));
      positions[lvl[i]] = {x,y};
    }
  }

  // Draw edges
  p.push();
  p.stroke('#FF0000'); p.strokeWeight(1.5);
  for(const [a,b] of (tree.edges||[])){
    const pa = positions[a], pb = positions[b];
    if(pa && pb) p.line(pa.x, pa.y, pb.x, pb.y);
  }
  p.pop();

  // Draw nodes (orange squares)
  p.push();
  p.rectMode(p.CENTER);
  for(const id of Object.keys(positions)){
    const pos = positions[id];
    p.stroke(0,120); p.fill('#FFA500');
    p.rect(pos.x, pos.y, 22, 22, 4);
    p.noStroke(); p.fill('#8080C0'); p.textAlign(p.CENTER, p.CENTER);
    p.text(String(id), pos.x, pos.y);
  }
  p.pop();
}

/* -------------------------- Orchestration (init) ------------------------- */
function regenerate(){
  const size = readActive('size-controls','size') || params.size;
  const degree = readActive('degree-controls','degree') || params.degreeMax;

  params.size = size;
  params.degreeMax = degree;

  graph = generateGraph(size, degree);
  runAlgorithmWithHistory(graph);
  populateStepControls();
  setCurrentStep(0);
}

function wireControls(){
  // Size group
  document.querySelectorAll('#size-controls button').forEach(btn=>{
    btn.addEventListener('click', ()=>{
      setActiveButton('size-controls', btn.id);
      regenerate();
    });
  });
  // Degree group
  document.querySelectorAll('#degree-controls button').forEach(btn=>{
    btn.addEventListener('click', ()=>{
      setActiveButton('degree-controls', btn.id);
      regenerate();
    });
  });
  // Reset button
  document.getElementById('reset-button').addEventListener('click', ()=>{
    regenerate(); // uses current active settings
  });
}

/* ------------------------------ Bootstrapping ---------------------------- */
document.addEventListener('DOMContentLoaded', ()=>{
  setupP5();
  wireControls();
  // defaults already active per HTML: size-18, degree-3
  regenerate();
});
</script>
</body>
</html>
