<script>
(function(){
  // --- Elements
  const plotDiv = document.getElementById('plot-container');

  const slider = document.getElementById('slider-elements');
  const display = document.getElementById('display-elements');

  const scaleLinear = document.getElementById('radio-scale-linear');
  const scaleLog = document.getElementById('radio-scale-logarithmic');

  const rangeFixed = document.getElementById('radio-range-fixed');
  const rangeAuto = document.getElementById('radio-range-automatic');

  const checkBubble = document.getElementById('check-bubble');
  const checkSelection = document.getElementById('check-selection');
  const checkInsertion = document.getElementById('check-insertion');

  const checkMerge = document.getElementById('check-merge');
  const checkQuick = document.getElementById('check-quick');
  const checkHeap = document.getElementById('check-heap');

  const checkBogo = document.getElementById('check-bogo');

  // --- Helpers
  function factorial(n){
    // iterative factorial for stability
    let f = 1;
    for (let i = 2; i <= n; i++) f *= i;
    return f;
  }

  function factorialSeq(n){
    const seq = new Array(n);
    let f = 1;
    for (let i = 1; i <= n; i++){
      f *= i;
      seq[i] = f; // 1-based for convenience
    }
    return seq;
  }

  function genX(n){
    return Array.from({length:n}, (_,i)=>i+1);
  }

  function currentScale(){
    return scaleLog.checked ? 'log' : 'linear';
  }

  function insertionVisible(){
    return checkInsertion.checked || checkBogo.checked;
  }

  function baseLayout(){
    return {
      title: {text:'time complexity'},
      margin: {t:44, r:16, l:54, b:60},
      xaxis: {title:'number of elements'},
      yaxis: {title:'operations', type: currentScale()},
      legend: {orientation:'h', y:-0.18, x:0.5, xanchor:'center'},
    };
  }

  function applyRange(layout){
    if (rangeFixed.checked){
      layout.xaxis.autorange = false;
      layout.xaxis.range = [0, 20];
      layout.yaxis.autorange = false;

      if (currentScale() === 'log'){
        // Log axis range is specified in powers of 10; 10^0 .. 10^log10(500)
        layout.yaxis.range = [0, Math.log10(500)];
      } else {
        layout.yaxis.range = [0, 500];
      }
    } else {
      layout.xaxis.autorange = true;
      layout.yaxis.autorange = true;
      delete layout.xaxis.range;
      delete layout.yaxis.range;
    }
    return layout;
  }

  function buildTraces(n){
    const x = genX(n);
    const facts = factorialSeq(n);

    const yBubble = x.map(v => v*v);
    const ySelection = x.map(v => 0.25 * v*v);
    const yInsertion = x.map(v => v * facts[v]); // same curve for insertion & bogo
    const yMerge = x.map(v => v * Math.log2 ? v * Math.log2(v) : v * (Math.log(v)/Math.LN2));
    const yQuick = x.map(v => 1.2 * (Math.log2 ? v*Math.log2(v) : v*(Math.log(v)/Math.LN2)));
    const yHeap  = x.map(v => 1.5 * (Math.log2 ? v*Math.log2(v) : v*(Math.log(v)/Math.LN2)));

    const traces = [
      {name:'bubble', x, y:yBubble, type:'scatter', mode:'lines',
       line:{color:'green', width:5}, visible: checkBubble.checked},
      {name:'selection', x, y:ySelection, type:'scatter', mode:'lines',
       line:{color:'orange', width:3, dash:'dot'}, visible: checkSelection.checked},
      {name:'insertion', x, y:yInsertion, type:'scatter', mode:'lines',
       line:{color:'red', width:2}, visible: insertionVisible()},
      {name:'merge', x, y:yMerge, type:'scatter', mode:'lines',
       line:{color:'blue', width:2}, visible: checkMerge.checked},
      {name:'quick', x, y:yQuick, type:'scatter', mode:'lines',
       line:{color:'purple', width:2}, visible: checkQuick.checked},
      {name:'heap', x, y:yHeap, type:'scatter', mode:'lines',
       line:{color:'cyan', width:2}, visible: checkHeap.checked},
      // Bogo trace (not shown in legend); uses same data but stays hidden, while checkbox toggles the insertion trace.
      {name:'bogo', x, y:yInsertion, type:'scatter', mode:'lines',
       line:{color:'red', width:2}, visible:false, showlegend:false}
    ];

    return traces;
  }

  function updateDisplay(){
    display.textContent = '+ ' + slider.value;
  }

  function render(){
    const n = Number(slider.value);
    const traces = buildTraces(n);
    const layout = applyRange(baseLayout());
    Plotly.react(plotDiv, traces, layout, {responsive:true, displayModeBar:false});
  }

  // --- Event wiring
  slider.addEventListener('input', () => { updateDisplay(); render(); });
  scaleLinear.addEventListener('change', render);
  scaleLog.addEventListener('change', render);
  rangeFixed.addEventListener('change', render);
  rangeAuto.addEventListener('change', render);

  [checkBubble, checkSelection, checkInsertion, checkMerge, checkQuick, checkHeap, checkBogo]
    .forEach(el => el.addEventListener('change', render));

  // --- Initial render
  updateDisplay();
  render();

})();
</script>
