    border: 1px solid #ccc;
    height: 420px;
  }
</style>
</head>
<body>
  <div id="main-container">
    <div id="control-panel">
      <label for="slider-xD">distillate composition</label>
      <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84"/>
      <span id="label-xD">0.84</span>
    </div>
    <div id="plot"></div>
  </div>

<script>
  // Core update function
  function updatePlot(xD) {
    const a = 9;
    const b = -0.6;

    const yOf = (x) => {
      const denom = 1 + (a - 1) * x + b * x * (1 - x);
      return (a * x) / denom;
    };

    const rOf = (x, xD) => {
      const y = yOf(x);
      const denom = (y - x);
      if (!isFinite(denom) || Math.abs(denom) < 1e-9) return NaN;
      return (xD - y) / denom;
    };

    const x_vals = [];
    const r_vals = [];
    const dx = 0.001;
    const xEnd = Math.max(0, Math.min(1, xD));
    for (let x = 0.001; x <= xEnd; x += dx) {
      const r = rOf(x, xD);
      if (isFinite(r) && r >= 0 && r < 5) {
        x_vals.push(x);
        r_vals.push(r);
      }
    }

    // Find local maximum (tangent pinch point)
    let pinch_x = [];
    let pinch_y = [];
    for (let i = 1; i < r_vals.length - 1; i++) {
      if (r_vals[i - 1] < r_vals[i] && r_vals[i] > r_vals[i + 1]) {
        pinch_x = [x_vals[i]];
        pinch_y = [r_vals[i]];
        break;
      }
    }

    const curveTrace = {
      x: x_vals,
      y: r_vals,
      mode: 'lines',
      type: 'scatter',
      line: { color: '#8B0000', width: 2.5 },
      hoverinfo: 'x+y'
    };

    const pinchTrace = {
      x: pinch_x,
      y: pinch_y,
      mode: 'markers',
      type: 'scatter',
      marker: { color: 'royalblue', size: 10 },
      hoverinfo: 'x+y'
    };

    const layout = {
      xaxis: {
        range: [0, 1.0],
        dtick: 0.2,
        gridcolor: '#e0e0e0',
        zeroline: false
      },
      yaxis: {
        range: [0, 3.0],
        dtick: 0.5,
        gridcolor: '#e0e0e0',
        zeroline: false
      },
      showlegend: false,
      margin: { l: 40, r: 20, b: 40, t: 20 },
      plot_bgcolor: 'white',
      paper_bgcolor: 'white'
    };

    Plotly.react('plot', [curveTrace, pinchTrace], layout, {displayModeBar: false});
  }

  // Initialize
  document.addEventListener('DOMContentLoaded', () => {
    const slider = document.getElementById('slider-xD');
    const label = document.getElementById('label-xD');

    const initVal = parseFloat(slider.value || 0.84);
    label.textContent = initVal.toFixed(2);
    updatePlot(initVal);

    slider.addEventListener('input', (e) => {
      const val = parseFloat(e.target.value);
      label.textContent = val.toFixed(2);
      updatePlot(val);
    });
  });
</script>
</body>
</html>
