<!-- p5.js CDN -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.9.2/lib/p5.min.js"></script>
<script>
/* ---------- State ---------- */
let sceneObjects = []; // {type, value, x, y, rotation, bbox:{w,h}}
let selectedTool = null; // e.g., { type:'atom', value:'H' } or { type:'charge', value:'+' }
let selectedButtonEl = null;

let activeObject = null;
let interactionMode = null; // 'translate' or 'rotate'
let dragOffset = {dx:0, dy:0};
let rotateStart = {angle:0, rotation:0};

let canvasEl = null;
let containerEl = null;

/* ---------- UI Wiring ---------- */
function setupToolbar() {
  const setSelected = (btn) => {
    if (selectedButtonEl) selectedButtonEl.classList.remove('selected');
    selectedButtonEl = btn;
    if (btn) btn.classList.add('selected');
  };

  // Charge buttons
  const chargeBtns = [
    {id:'btn-charge-minus', value:'-'},
    {id:'btn-charge-plus',  value:'+'},
    {id:'btn-charge-2',     value:'2'},
  ];
  chargeBtns.forEach(({id, value}) => {
    const btn = document.getElementById(id);
    btn.addEventListener('click', () => {
      selectedTool = { type:'charge', value };
      setSelected(btn);
    });
  });

  // Elements
  const elements = ['H','B','C','N','O','F','P','S','Cl','Se','Br','I','Li','Na','K','Be','Mg','Ca','Cr','Mn','Fe','Co','Cu','Zn','Al','Si'];
  elements.forEach(sym => {
    const id = `btn-element-${sym}`;
    const btn = document.getElementById(id);
    if (btn) {
      btn.addEventListener('click', () => {
        selectedTool = { type:'atom', value:sym };
        setSelected(btn);
      });
    }
  });

  // Add button (structures)
  document.getElementById('btn-add').addEventListener('click', () => {
    const sel = document.getElementById('select-structure');
    const value = sel.value;
    const center = { x: width/2, y: height/2 };
    const bounds = getStructureBounds(value);
    sceneObjects.push({
      type:'structure',
      value,
      x: center.x, y: center.y,
      rotation: 0,
      bbox: {...bounds}
    });
  });

  // Undo
  document.getElementById('btn-undo').addEventListener('click', () => {
    if (sceneObjects.length > 0) sceneObjects.pop();
  });
}

/* ---------- p5 Sketch ---------- */
function setup() {
  containerEl = document.getElementById('canvas-container');
  const dims = getCanvasSizeFromContainer();
  const c = createCanvas(dims.w, dims.h);
  c.parent(containerEl);
  c.id('drawing-canvas');
  pixelDensity(1);
  textFont('Helvetica, Arial, sans-serif');
  textAlign(CENTER, CENTER);

  canvasEl = c.elt;

  setupToolbar();
}

function getCanvasSizeFromContainer() {
  // Fit inside container with its padding taken by CSS; p5 handles actual size
  const rect = containerEl.getBoundingClientRect();
  return { w: Math.max(200, Math.floor(rect.width - 0)), h: Math.max(200, Math.floor(rect.height - 0)) };
}

function windowResized() {
  if (!containerEl) return;
  const dims = getCanvasSizeFromContainer();
  resizeCanvas(dims.w, dims.h);
}

function draw() {
  background(255);

  // Draw all objects
  noFill();
  stroke(0);
  sceneObjects.forEach(obj => {
    push();
    translate(obj.x, obj.y);
    rotate(obj.rotation || 0);
    drawObject(obj);
    pop();
  });

  // Ghost preview if a tool is selected and mouse is within canvas
  if (selectedTool && isMouseInCanvas()) {
    push();
    translate(mouseX, mouseY);
    rotate(0);
    stroke(0, 120);
    fill(0, 120);
    drawPreview(selectedTool);
    pop();
  }

  // Optional: subtle crosshair when rotating (not required)
}

function drawPreview(tool) {
  if (tool.type === 'atom' || tool.type === 'charge') {
    textSize(16);
    noStroke();
    fill(0, 140);
    text(tool.value, 0, 0);
  }
}

/* Drawing helpers */
function drawObject(obj) {
  if (obj.type === 'atom' || obj.type === 'charge') {
    // Draw as text label
    textSize(16);
    fill(0);
    noStroke();
    text(obj.value, 0, 0);
    // If bbox not set, create one based on current text width
    if (!obj.bbox) {
      const w = Math.max(18, textWidth(obj.value) + 6);
      obj.bbox = {w, h: 20};
    }
    // Optionally visualize bbox for debugging:
    // noFill(); stroke(220,0,0,60); rectMode(CENTER); rect(0,0,obj.bbox.w,obj.bbox.h);
  } else if (obj.type === 'structure') {
    stroke(0);
    noFill();
    switch (obj.value) {
      case 'bond': drawBond(); break;
      case 'water': drawWater(); break;
      case 'cyclopropyl_ethane': drawCyclopropylEthane(); break;
      case 'borazine': drawBorazine(); break;
      case 'isopropylamine': drawIsopropylamine(); break;
      default: drawBond();
    }
  }
}

/* --- Structures --- */
function drawBond() {
  strokeWeight(1);
  line(-30, 0, 30, 0);
}

function drawWater() {
  strokeWeight(1);
  // Bonds
  line(0, 0, -24, -24);
  line(0, 0, 24, -24);
  // Atoms
  noStroke();
  fill(0);
  textSize(14);
  text('O', 0, 6);
  text('H', -24, -28);
  text('H', 24, -28);
}

function drawCyclopropylEthane() {
  strokeWeight(1);
  // Cyclopropyl triangle (left)
  line(-70, -16, -70, 16);
  line(-70, -16, -50, 0);
  line(-70, 16, -50, 0);
  // Link to ethyl
  line(-50, 0, -18, 0);
  line(-18, 0, 12, 12);
  line(12, 12, 36, 24);
}

function drawBorazine() {
  strokeWeight(1);
  const R = 40;
  const verts = [];
  for (let i=0;i<6;i++){
    const a = -PI/2 + i * PI/3;
    verts.push({x: R*cos(a), y: R*sin(a)});
  }
  // ring
  for (let i=0;i<6;i++){
    const a = verts[i];
    const b = verts[(i+1)%6];
    line(a.x, a.y, b.x, b.y);
  }
  // labels and hydrogens
  noStroke();
  fill(0);
  textSize(12);
  for (let i=0;i<6;i++){
    const v = verts[i];
    const label = (i%2===0) ? 'B' : 'N';
    text(label, v.x*0.75, v.y*0.75); // slightly inner
    // outward hydrogen
    const ang = -PI/2 + i*PI/3;
    const hx = (R+14)*cos(ang);
    const hy = (R+14)*sin(ang);
    const bx = (R)*cos(ang);
    const by = (R)*sin(ang);
    stroke(0);
    line(bx,by,hx,hy);
    noStroke();
    text('H', hx + 10*cos(ang), hy + 10*sin(ang));
  }
}

function drawIsopropylAmineSkeleton() {
  strokeWeight(1);
  // central carbon at (0,0); left carbon chain
  line(-30, -10, 0, 0);    // left bond
  line(-60, -20, -30, -10);// leftmost bond
  // isopropyl down branch
  line(0, 0, 0, 22);
  // amine to the right
  line(0, 0, 28, -4);
}
function drawIsopropylamine() {
  drawIsopropylAmineSkeleton();
  // "N" near the right end with two hydrogens
  noStroke();
  fill(0);
  textSize(12);
  text('N', 45, -8);
  stroke(0);
  line(45, -8, 45, -28); // to H up
  line(45, -8, 62, 2);   // to H right
  noStroke();
  text('H', 45, -34);
  text('H', 70, 4);
}

/* Bounds for structures (approximate) */
function getStructureBounds(value) {
  switch(value) {
    case 'bond': return {w: 70, h: 24};
    case 'water': return {w: 80, h: 70};
    case 'cyclopropyl_ethane': return {w: 160, h: 80};
    case 'borazine': return {w: 150, h: 150};
    case 'isopropylamine': return {w: 160, h: 110};
    default: return {w: 80, h: 80};
  }
}

/* ---------- Interaction / Hit-testing ---------- */
function isMouseInCanvas() {
  return mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height;
}

function mousePressed() {
  if (!isMouseInCanvas()) return;

  // If placing new item from toolbar
  if (selectedTool) {
    const obj = {
      type: selectedTool.type,
      value: selectedTool.value,
      x: mouseX, y: mouseY,
      rotation: 0,
      bbox: null
    };
    // Predefine bbox for labels to speed hit-test later
    if (obj.type === 'atom' || obj.type === 'charge') {
      textSize(16);
      const w = Math.max(18, textWidth(obj.value) + 6);
      obj.bbox = {w, h: 20};
    }
    sceneObjects.push(obj);

    // reset selected tool and visual state
    selectedTool = null;
    if (selectedButtonEl) selectedButtonEl.classList.remove('selected');
    selectedButtonEl = null;
    return;
  }

  // Otherwise attempt to pick existing object for manipulation
  for (let i = sceneObjects.length - 1; i >= 0; i--) {
    const obj = sceneObjects[i];
    if (hitTest(mouseX, mouseY, obj)) {
      activeObject = obj;
      const distToCenter = dist(mouseX, mouseY, obj.x, obj.y);
      if (distToCenter < 25) {
        interactionMode = 'rotate';
        rotateStart.angle = Math.atan2(mouseY - obj.y, mouseX - obj.x);
        rotateStart.rotation = obj.rotation || 0;
      } else {
        interactionMode = 'translate';
        dragOffset.dx = mouseX - obj.x;
        dragOffset.dy = mouseY - obj.y;
      }
      return; // stop at first hit (top-most)
    }
  }
}

function mouseDragged() {
  if (!activeObject) return;
  if (interactionMode === 'translate') {
    activeObject.x = mouseX - dragOffset.dx;
    activeObject.y = mouseY - dragOffset.dy;
  } else if (interactionMode === 'rotate') {
    const currentAngle = Math.atan2(mouseY - activeObject.y, mouseX - activeObject.x);
    activeObject.rotation = rotateStart.rotation + (currentAngle - rotateStart.angle);
  }
}

function mouseReleased() {
  activeObject = null;
  interactionMode = null;
}

function hitTest(px, py, obj) {
  const rot = obj.rotation || 0;
  // Transform point into object's local space (inverse transform)
  const dx = px - obj.x;
  const dy = py - obj.y;
  const cosr = Math.cos(-rot);
  const sinr = Math.sin(-rot);
  const lx = dx * cosr - dy * sinr;
  const ly = dx * sinr + dy * cosr;

  // Bounding box sizes
  let w=60, h=40;
  if (obj.type === 'structure') {
    const bb = obj.bbox || getStructureBounds(obj.value);
    w = bb.w; h = bb.h;
  } else if (obj.type === 'atom' || obj.type === 'charge') {
    if (!obj.bbox) {
      textSize(16);
      const tw = Math.max(18, textWidth(obj.value) + 6);
      obj.bbox = {w: tw, h: 20};
    }
    w = obj.bbox.w; h = obj.bbox.h;
  }
  return Math.abs(lx) <= w/2 && Math.abs(ly) <= h/2;
}
</script>
</body>
</html>
