        <option value="-2.0943951023931953">-2π/3</option>
        <option value="-1.5707963267948966" selected>-π/2</option>
        <option value="-1.0471975511965976">-π/3</option>
        <option value="0">0</option>
        <option value="1.0471975511965976">π/3</option>
        <option value="1.5707963267948966">π/2</option>
        <option value="2.0943951023931953">2π/3</option>
        <option value="3.141592653589793">π</option>
      </select>
    </div>
  </div>

  <!-- Formula Display -->
  <div id="formula-display"></div>

  <!-- Visualization Canvas -->
  <div id="canvas-container"></div>
</div>

<script>
  // ---------- Global State ----------
  const state = {
    A: 1.5,
    f: 0.3,
    lambda: 4.5,
    t: 2.5,
    phi0: -Math.PI/2,
    k: 0,
    omega: 0
  };
  const PPU = 30;        // pixels per unit for world scaling
  const X_MAX = 16;      // show x from 0..16
  const Y_RANGE = 3.2;   // show y from -Y_RANGE..Y_RANGE
  const vScalePx = 15;   // velocity vector pixel scaling
  const aScalePx = 5;    // acceleration vector pixel scaling

  // DOM Elements
  const ampSlider = document.getElementById('slider-amplitude');
  const frqSlider = document.getElementById('slider-frequency');
  const wavSlider = document.getElementById('slider-wavelength');
  const timeSlider = document.getElementById('slider-time');
  const phaseSelect = document.getElementById('select-phase');

  const ampVal = document.getElementById('value-amplitude');
  const frqVal = document.getElementById('value-frequency');
  const wavVal = document.getElementById('value-wavelength');
  const timeVal = document.getElementById('value-time');
  const formulaDiv = document.getElementById('formula-display');

  function fmt(n, d=2){
    return Number(n).toFixed(d);
  }
  function approxEqual(a,b,eps=1e-9){ return Math.abs(a-b) < eps; }

  function phaseToTex(phi){
    const sgn = phi < 0 ? '-' : '+';
    const aph = Math.abs(phi);
    // match against provided discrete options (π, 2π/3, π/2, π/3, 0)
    if (approxEqual(aph, 0)) return '0';
    if (approxEqual(aph, Math.PI)) return '\\pi';
    if (approxEqual(aph, 2*Math.PI/3)) return '\\tfrac{2\\pi}{3}';
    if (approxEqual(aph, Math.PI/2)) return '\\tfrac{\\pi}{2}';
    if (approxEqual(aph, Math.PI/3)) return '\\tfrac{\\pi}{3}';
    // fallback numeric radians
    return fmt(phi, 2);
  }

  function updateParamsFromControls(){
    state.A = parseFloat(ampSlider.value);
    state.f = parseFloat(frqSlider.value);
    state.lambda = parseFloat(wavSlider.value);
    state.t = parseFloat(timeSlider.value);
    state.phi0 = parseFloat(phaseSelect.value);

    state.k = 2*Math.PI / state.lambda;
    state.omega = 2*Math.PI * state.f;

    // Update value readouts (2-4 decimals)
    ampVal.textContent = fmt(state.A, 2);
    frqVal.textContent = fmt(state.f, 2);
    wavVal.textContent = fmt(state.lambda, 2);
    timeVal.textContent = fmt(state.t, 2);

    updateFormula();
    if (typeof redraw === 'function') redraw(); // p5 redraw
  }

  function updateFormula(){
    const A = fmt(state.A);
    const k = fmt(state.k);
    const w = fmt(state.omega);

    let latex;
    if (approxEqual(state.phi0, Math.PI/2)) {
      latex = `\\(${A}\\sin(${k}x - ${w}t)\\)`;
    } else if (approxEqual(state.phi0, -Math.PI/2)) {
      latex = `\\(-${A}\\sin(${k}x - ${w}t)\\)`;
    } else if (approxEqual(state.phi0, Math.PI)) {
      latex = `\\(-${A}\\cos(${k}x - ${w}t)\\)`;
    } else if (approxEqual(state.phi0, 0)) {
      latex = `\\(${A}\\cos(${k}x - ${w}t)\\)`;
    } else {
      // Full form: A cos(ωt - kx + φ0)
      const sign = state.phi0 >= 0 ? '+' : '-';
      const phiTex = phaseToTex(state.phi0 >= 0 ? state.phi0 : -state.phi0);
      latex = `\\(${A}\\cos(${w}t - ${k}x ${sign} ${phiTex})\\)`;
    }
    formulaDiv.innerHTML = latex;
    if (window.MathJax && MathJax.typesetPromise) {
      MathJax.typesetPromise([formulaDiv]);
    }
  }

  // Listen to UI changes
  ['input','change'].forEach(evt=>{
    ampSlider.addEventListener(evt, updateParamsFromControls);
    frqSlider.addEventListener(evt, updateParamsFromControls);
    wavSlider.addEventListener(evt, updateParamsFromControls);
    timeSlider.addEventListener(evt, updateParamsFromControls);
  });
  phaseSelect.addEventListener('change', updateParamsFromControls);

  // ---------- p5 Sketch ----------
  let cnv, canvasW, canvasH;
  let originPx = {x: 80, y: 280}; // will be updated to vertically center

  function worldToPixel(x, y){
    return {
      x: originPx.x + x*PPU,
      y: originPx.y - y*PPU
    };
  }

  function setup(){
    const cc = document.getElementById('canvas-container');
    canvasW = Math.max(720, Math.min(1024, cc.clientWidth - 16));
    canvasH = 460;
    cnv = createCanvas(canvasW, canvasH);
    cnv.parent('canvas-container');
    pixelDensity(1);
    noLoop();
    updateGeometry();
    updateParamsFromControls();
  }

  function windowResized(){
    const cc = document.getElementById('canvas-container');
    const newW = Math.max(720, Math.min(1024, cc.clientWidth - 16));
    const newH = 460;
    resizeCanvas(newW, newH);
    canvasW = newW; canvasH = newH;
    updateGeometry();
    redraw();
  }

  function updateGeometry(){
    originPx.x = 60; // left margin
    originPx.y = Math.round(canvasH*0.65); // vertically centered-ish for grid
  }

  function drawAxes(){
    push();
    // World transform: translate to origin, then scale units to pixels and invert y
    translate(originPx.x, originPx.y);
    scale(PPU, -PPU);

    stroke(0); strokeWeight(1/PPU*1.2); fill(0);

    // x-axis
    line(0,0, X_MAX+0.8, 0);
    // y-axis (draw several units up/down)
    const yMin = -Y_RANGE, yMax = Y_RANGE;
    line(0, yMin-0.6, 0, yMax+0.6);

    // x-axis arrowhead
    const ah = 0.18;
    triangle(X_MAX+0.8, 0, X_MAX+0.8-ah, +ah/2, X_MAX+0.8-ah, -ah/2);
    // y-axis arrowhead
    triangle(0, yMax+0.6, -ah/2, yMax+0.6-ah, +ah/2, yMax+0.6-ah);

    pop();

    // axis labels in screen space
    push();
    noStroke();
    fill(0);
    textSize(14);
    text('x', originPx.x + (X_MAX+1.1)*PPU, originPx.y - 6);
    text('y', originPx.x - 12, originPx.y - (Y_RANGE+1.1)*PPU);
    pop();
  }

  function drawGridAndTicks(){
    // dashed vertical grid lines and ticks from x=1..16
    const ctx = drawingContext;

    // Grid lines
    ctx.save();
    ctx.setLineDash([6, 6]);
    stroke(120, 200, 120, 130);
    strokeWeight(1);
    for (let xi = 1; xi <= X_MAX; xi++){
      const p1 = worldToPixel(xi, -Y_RANGE);
      const p2 = worldToPixel(xi,  Y_RANGE);
      line(p1.x, p1.y, p2.x, p2.y);
    }
    ctx.restore();

    // Ticks and numeric labels
    stroke(0); strokeWeight(1);
    fill(0);
    textSize(12);
    for (let xi = 1; xi <= X_MAX; xi++){
      // tick 6 px tall
      const px = originPx.x + xi*PPU;
      line(px, originPx.y-3, px, originPx.y+3);

      // number label slightly above axis
      noStroke();
      textAlign(CENTER, BOTTOM);
      text(xi.toString(), px, originPx.y - 6);
      stroke(0);
    }
  }

  function drawWave(){
    // draw y = A cos(ω t - k x + φ0)
    const A = state.A, k = state.k, w = state.omega, t = state.t, phi = state.phi0;

    noFill();
    stroke(40, 90, 200);
    strokeWeight(2);

    beginShape();
    for (let px = originPx.x; px <= originPx.x + (X_MAX)*PPU; px++){
      const x = (px - originPx.x) / PPU;
      const y = A * Math.cos(w*t - k*x + phi);
      const p = worldToPixel(x,y);
      vertex(p.x, p.y);
    }
    endShape();
  }

  function drawPoint(x, y){
    const p = worldToPixel(x,y);
    noStroke();
    fill(0);
    circle(p.x, p.y, 6);
  }

  function drawArrow(baseX, baseY, vectorYWorld, colorRGB, labelText, headPx=8){
    // Draw vertical arrow from (baseX, baseY) to (baseX, baseY + vectorYWorld)
    const start = worldToPixel(baseX, baseY);
    const end = worldToPixel(baseX, baseY + vectorYWorld);

    stroke(colorRGB[0], colorRGB[1], colorRGB[2]);
    strokeWeight(2);
    line(start.x, start.y, end.x, end.y);

    // Arrowhead in screen space (triangle at tip)
    const dir = Math.sign(vectorYWorld) || 1; // 1 up, -1 down in world coords
    const tip = {x:end.x, y:end.y};
    const left = {x: tip.x - 5, y: tip.y + dir*headPx};
    const right= {x: tip.x + 5, y: tip.y + dir*headPx};

    noStroke();
    fill(colorRGB[0], colorRGB[1], colorRGB[2]);
    triangle(tip.x, tip.y, left.x, left.y, right.x, right.y);

    // Label near the tip
    fill(colorRGB[0], colorRGB[1], colorRGB[2]);
    textSize(14);
    textAlign(LEFT, CENTER);
    const labelOffsetX = 6;
    const labelOffsetY = dir < 0 ? 10 : -10;
    text(labelText, tip.x + labelOffsetX, tip.y + labelOffsetY);
  }

  function drawVectorsAndPoints(){
    const A = state.A, k = state.k, w = state.omega, t = state.t, phi = state.phi0;

    for (let xi = 1; xi <= X_MAX; xi++){
      const y  = A * Math.cos(w*t - k*xi + phi);
      const v  = -A * w * Math.sin(w*t - k*xi + phi);
      const a  = -A * w * w * Math.cos(w*t - k*xi + phi);

      drawPoint(xi, y);

      // Convert v and a to world-lengths based on pixel scale
      const vWorldLen = (v * vScalePx) / PPU;
      const aWorldLen = (a * aScalePx) / PPU;

      drawArrow(xi, y, vWorldLen, [220,40,40], "v⃗", 8);    // red
      drawArrow(xi, y, aWorldLen, [0,0,0], "a⃗", 8);        // black
    }
  }

  function draw(){
    background(255);

    // Axes, grid, wave, vectors
    drawAxes();
    drawGridAndTicks();
    drawWave();
    drawVectorsAndPoints();
  }

  // Initialize after DOM is ready for initial MathJax
  document.addEventListener('DOMContentLoaded', () => {
    updateParamsFromControls();
  });
</script>
</body>
</html>
