<body>
  <div id="app">
    <!-- Control Panel -->
    <div class="control-panel">
      <label for="slider-eccentricity">eccentricity ε</label>
      <input type="range" id="slider-eccentricity" min="0" max="1.5" step="0.01" value="0" />
      <span id="value-eccentricity">0</span>
    </div>

    <!-- Visualization Area -->
    <div class="viz-area">
      <h3>Kepler orbits</h3>
      <div id="canvas-container">
