  <script>
    // DOM references and state
    const slider = document.getElementById('slider-eccentricity');
    const valueEps = document.getElementById('value-eccentricity');
    const energyValue = document.getElementById('energy-value');
    const canvasContainer = document.getElementById('canvas-container');

    let eps = parseFloat(slider.value); // eccentricity ε
    const p = 2; // semi-latus rectum

    // Format helpers
    function updateUIFromSlider() {
      eps = parseFloat(slider.value);
      valueEps.textContent = eps.toFixed(1); // at least one decimal place
      updateEnergyDisplay();
      if (typeof redraw === 'function') redraw(); // p5 redraw
    }

    function updateEnergyDisplay() {
      const E = -0.25 * (1 - eps * eps);
      if (Math.abs(eps) < 1e-12) {
        energyValue.innerHTML = "\\(-\\frac{1}{4}\\)";
        if (window.MathJax && MathJax.typeset) MathJax.typeset();
      } else {
        energyValue.textContent = E.toFixed(2);
      }
    }

    slider.addEventListener('input', updateUIFromSlider);

    // p5.js sketch (global mode as requested)
    let originX, originY, unit; // pixels coordinates: origin and unit scale
    const CANVAS_W = 700;
    const CANVAS_H = 500;

    function setup() {
      const cnv = createCanvas(CANVAS_W, CANVAS_H);
      cnv.parent('canvas-container');
      // Initial coordinate transform parameters
      originX = width * 0.7;
      originY = height / 2;
      unit = 30; // pixels per world unit ~ 30

      noLoop();
      updateEnergyDisplay();
      redraw(); // draw initial
      if (window.MathJax && MathJax.typeset) MathJax.typeset(); // ensure initial fraction renders
    }

    function drawAxes() {
      push();
      translate(originX, originY);
      scale(unit, -unit);

      // 1-pixel strokes independent of scale
      const hair = 1 / unit;
      stroke(0);
      strokeWeight(hair);
      noFill();

      // Axes ranges
      const xmin = -15, xmax = 5, ymin = -7, ymax = 7;
      // Draw axes
      line(xmin, 0, xmax, 0); // x-axis
      line(0, ymin, 0, ymax); // y-axis

      // Tick marks at integer intervals
      const tickSmall = 0.10;
      const tickBig = 0.18;

      for (let x = Math.ceil(xmin); x <= Math.floor(xmax); x++) {
        if (x === 0) continue;
        const len = (x % 5 === 0) ? tickBig : tickSmall;
        line(x, -len, x, len);
      }
      for (let y = Math.ceil(ymin); y <= Math.floor(ymax); y++) {
        if (y === 0) continue;
        const len = (y % 5 === 0) ? tickBig : tickSmall;
        line(-len, y, len, y);
      }

      // Central body (6 px diameter => convert to world units)
      const rpx = 6 / unit;
      fill(0);
      noStroke();
      ellipse(0, 0, rpx, rpx);

      pop();

      // Axis labels (draw in screen coordinates for upright text)
      const xLabel = worldToScreen(xmax - 0.2, -0.35);
      const yLabel = worldToScreen(0.4, ymax - 0.2);
      push();
      noStroke();
      fill(0);
      textSize(12);
      textAlign(LEFT, TOP);
      text("Global'x", xLabel.x, xLabel.y);
      text("Global'y", yLabel.x, yLabel.y);
      pop();
    }

    function drawOrbit() {
      push();
      translate(originX, originY);
      scale(unit, -unit);

      noFill();
      stroke('#4a80b5');
      strokeWeight(2 / unit);

      const startedShape = { val: false };
      function startIfNeeded() {
        if (!startedShape.val) { beginShape(); startedShape.val = true; }
      }
      function endIfStarted(close = false) {
        if (startedShape.val) {
          if (close) endShape(CLOSE);
          else endShape();
          startedShape.val = false;
        }
      }

      const MAX_R = 50; // safety to prevent huge jumps

      if (Math.abs(eps) < 1e-12) {
        // Circle: radius 2 centered at (-2, 0)
        startIfNeeded();
        for (let t = 0; t <= TWO_PI + 0.0001; t += 0.01) {
          const x = 2 * Math.cos(t) - 2;
          const y = 2 * Math.sin(t);
          vertex(x, y);
        }
        endIfStarted(true);
      } else if (eps > 0 && eps < 1) {
        // Ellipse using polar equation r = p / (1 + e cosθ)
        startIfNeeded();
        for (let t = 0; t <= TWO_PI + 0.0001; t += 0.01) {
          const denom = 1 + eps * Math.cos(t);
          const r = p / denom;
          const x = r * Math.cos(t);
          const y = r * Math.sin(t);
          vertex(x, y);
        }
        endIfStarted();
      } else if (Math.abs(eps - 1) < 1e-12) {
        // Parabola: limit theta to finite range
        let prevLarge = false;
        for (let t = -2.2; t <= 2.2; t += 0.01) {
          const denom = 1 + eps * Math.cos(t);
          if (denom <= 0) { endIfStarted(); prevLarge = true; continue; }
          const r = p / denom;
          if (!isFinite(r) || r > MAX_R) { endIfStarted(); prevLarge = true; continue; }
          const x = r * Math.cos(t);
          const y = r * Math.sin(t);
          if (prevLarge) { prevLarge = false; startIfNeeded(); }
          vertex(x, y);
        }
        endIfStarted();
      } else if (eps > 1) {
        // Hyperbola: theta valid where 1 + e cosθ > 0 -> θ ∈ (-acos(-1/e), acos(-1/e))
        const tMax = Math.acos(-1 / eps);
        let prevBreak = true;
        for (let t = -tMax + 0.0001; t <= tMax - 0.0001; t += 0.005) {
          const denom = 1 + eps * Math.cos(t);
          if (denom <= 0) { endIfStarted(); prevBreak = true; continue; }
          const r = p / denom;
          if (!isFinite(r) || r > MAX_R) { endIfStarted(); prevBreak = true; continue; }
          const x = r * Math.cos(t);
          const y = r * Math.sin(t);
          if (prevBreak) { startIfNeeded(); prevBreak = false; }
          vertex(x, y);
        }
        endIfStarted();
      }
      pop();
    }

    function worldToScreen(x, y) {
      // Convert world coordinate (x, y) to canvas pixel coordinates
      return { x: originX + x * unit, y: originY - y * unit };
    }

    function draw() {
      background('#f0f0f0');

      // Axes and orbit
      drawAxes();
      drawOrbit();
    }

    // Initial UI sync and typesetting on load
    document.addEventListener('DOMContentLoaded', () => {
      // Ensure initial fraction renders even before first slider movement
      if (window.MathJax && MathJax.typeset) MathJax.typeset();
    });
  </script>
</body>
