<script>
(function() {
  // Global constants
  const N = 64;                 // number of basis states
  const NUM_SOL = 2;            // number of solutions
  const MAX_CONFLICT = Math.log2(N); // 6 for N=64
  const I = math.complex(0, 1); // imaginary unit

  // DOM references
  const btnUnstructured = document.getElementById('btn-unstructured');
  const btnAdiabatic = document.getElementById('btn-adiabatic');
  const btnDiscrete = document.getElementById('btn-discrete-adiabatic');
  const btnHeuristic = document.getElementById('btn-heuristic');
  const sliderTotal = document.getElementById('slider-total-steps');
  const sliderCurrent = document.getElementById('slider-current-step');
  const displayTotal = document.getElementById('display-total-steps');
  const displayCurrent = document.getElementById('display-current-step');
  const labelCurrent = document.getElementById('label-current-step');

  const btnConflicts = document.getElementById('btn-conflicts');
  const btnEigen = document.getElementById('btn-eigenvalues');

  // App state and precomputed items
  const app = {
    algorithm: 'heuristic',
    plotMode: 'conflicts',
    totalSteps: 10,
    currentStep: 0,
    solutions: [],
    highlightIndex: null, // a non-solution index to color red at step 0
    conflicts: new Array(N).fill(0),
    psi0: null,  // initial uniform state (vector)
    matrices: {}, // cache for static matrices
    unitaryCacheKey: null, // to invalidate unitary cache when total steps or algorithm change
    unitaryCache: {} // for discrete/heuristic U_k steps
  };

  // Utility functions
  function randInt(max) { return Math.floor(Math.random() * max); }

  function pickSolutions() {
    const s = new Set();
    while (s.size < NUM_SOL) s.add(randInt(N));
    return Array.from(s);
  }

  function hamming(a, b) {
    let x = a ^ b;
    let d = 0;
    while (x) { d += x & 1; x >>= 1; }
    return d;
  }

  function computeConflicts(solutions) {
    const C = new Array(N);
    for (let i = 0; i < N; i++) {
      if (solutions.includes(i)) { C[i] = 0; continue; }
      let best = Infinity;
      for (const w of solutions) best = Math.min(best, hamming(i, w));
      C[i] = best;
    }
    return C;
  }

  function uniformState(n) {
    const amp = 1 / Math.sqrt(n);
    return math.matrix(Array(n).fill(math.complex(amp, 0)));
  }

  function identity(n) {
    const M = Array.from({length: n}, () => Array(n).fill(0));
    for (let i = 0; i < n; i++) M[i][i] = 1;
    return math.matrix(M);
  }

  function projectorUniform(n) {
    const v = 1 / n;
    const M = Array.from({length: n}, () => Array(n).fill(v));
    return math.matrix(M); // |s><s| where |s> uniform
  }

  function diag(arr) {
    const n = arr.length;
    const M = Array.from({length: n}, () => Array(n).fill(0));
    for (let i = 0; i < n; i++) M[i][i] = arr[i];
    return math.matrix(M);
  }

  function toComplexMatrix(M) {
    // Convert real numbers to complex numbers in matrix
    const A = M.toArray().map(row => row.map(x => math.complex(x, 0)));
    return math.matrix(A);
  }

  function normalizeVec(vec) {
    const arr = vec.toArray();
    let norm2 = 0;
    for (const c of arr) norm2 += math.abs(c) ** 2;
    const norm = Math.sqrt(norm2) || 1;
    return math.matrix(arr.map(c => math.divide(c, norm)));
  }

  function vecToReIm(vec) {
    const arr = vec.toArray();
    const re = arr.map(c => math.re(c));
    const im = arr.map(c => math.im(c));
    return {re, im};
  }

  function probFromVec(vec) {
    const arr = vec.toArray();
    return arr.map(c => math.abs(c) ** 2);
  }

  function sum(arr) { return arr.reduce((a,b)=>a+b,0); }

  function colorForConflict(c, maxC=MAX_CONFLICT) {
    // HSL interpolation from blue (low) to orange (high)
    const t = Math.max(0, Math.min(1, c / maxC));
    const hueBlue = 210; // blue
    const hueOrange = 30; // orange
    const hue = hueBlue + (hueOrange - hueBlue) * t;
    return `hsl(${hue}, 70%, 50%)`;
  }

  function setActiveButton(clicked, group) {
    group.forEach(btn => btn.classList.toggle('active', btn === clicked));
  }

  // Build static problem and matrices
  function initializeProblem() {
    app.solutions = pickSolutions();
    const nonSolutions = Array.from({length: N}, (_, i) => i).filter(i => !app.solutions.includes(i));
    app.highlightIndex = nonSolutions[0];

    app.conflicts = computeConflicts(app.solutions);
    app.psi0 = uniformState(N);

    // Static matrices
    app.matrices.I = identity(N);
    app.matrices.P0 = projectorUniform(N); // |s><s|
    app.matrices.H0 = math.multiply(-1, app.matrices.P0); // -P0
    app.matrices.Hp = diag(app.conflicts); // diag(C(i))

    // Grover components
    // Oracle U_w: -1 on solution indices, +1 elsewhere
    const Uw = Array.from({length: N}, () => Array(N).fill(0));
    for (let i = 0; i < N; i++) {
      const val = app.solutions.includes(i) ? -1 : 1;
      Uw[i][i] = val;
    }
    app.matrices.Uw = toComplexMatrix(math.matrix(Uw));

    // Diffusion U_s = 2*P0 - I
    const Us_real = math.subtract(math.multiply(2, app.matrices.P0), app.matrices.I);
    app.matrices.Us = toComplexMatrix(Us_real);

    // Grover operator G = U_s * U_w
    app.matrices.G = math.multiply(app.matrices.Us, app.matrices.Uw);

    // Clear cache
    app.unitaryCache = {};
    app.unitaryCacheKey = null;
  }

  // Algorithms
  function stateGrover(k) {
    let psi = app.psi0;
    for (let t = 0; t < k; t++) {
      psi = math.multiply(app.matrices.G, psi);
      psi = normalizeVec(psi);
    }
    return psi;
  }

  function H_of_s(s) {
    // H(s) = (1-s) H0 + s Hp
    const A = math.multiply(1 - s, app.matrices.H0);
    const B = math.multiply(s, app.matrices.Hp);
    return math.add(A, B); // real matrix
  }

  function groundState(H) {
    // Use math.eigs to get eigen pairs; choose the smallest real eigenvalue
    const res = math.eigs(H);
    const values = res.values;
    const vectors = res.vectors; // columns are eigenvectors
    let minIdx = 0;
    let minVal = Infinity;
    for (let i = 0; i < values.length; i++) {
      const v = values[i];
      const realPart = (typeof v === 'number') ? v : math.re(v);
      if (realPart < minVal) { minVal = realPart; minIdx = i; }
    }
    const col = vectors.subset(math.index(math.range(0, vectors.size()[0]), minIdx)).toArray();
    // Convert to complex vector
    const cvec = math.matrix(col.map(x => math.complex(x, 0)));
    return normalizeVec(cvec);
  }

  function stateAdiabatic(k, T) {
    const s = (T === 0) ? 0 : k / T;
    const H = H_of_s(s);
    return groundState(H);
  }

  function unitaryForStep(t, T) {
    const cacheKey = `${T}|${t}`;
    if (app.unitaryCacheKey !== `alg-${app.algorithm}|T-${T}`) {
      app.unitaryCache = {};
      app.unitaryCacheKey = `alg-${app.algorithm}|T-${T}`;
    }
    if (app.unitaryCache[cacheKey]) return app.unitaryCache[cacheKey];

    const s = T ? t / T : 0;
    const H = H_of_s(s); // real
    const Hc = toComplexMatrix(H); // convert to complex
    // U = expm(-i * H)
    const minus_i_H = math.multiply(math.complex(0, -1), Hc);
    const U = math.expm(minus_i_H);
    app.unitaryCache[cacheKey] = U;
    return U;
  }

  function stateDiscreteLike(k, T) {
    let psi = app.psi0;
    for (let t = 0; t < k; t++) {
      const U = unitaryForStep(t, T);
      psi = math.multiply(U, psi);
      psi = normalizeVec(psi);
    }
    return psi;
  }

  function getStateVector() {
    const k = app.currentStep;
    const T = app.totalSteps;
    switch (app.algorithm) {
      case 'unstructured': return stateGrover(k);
      case 'adiabatic': return stateAdiabatic(k, T);
      case 'discrete-adiabatic': return stateDiscreteLike(k, T);
      case 'heuristic': return stateDiscreteLike(k, T);
      default: return app.psi0;
    }
  }

  // Probability plot helpers
  function probabilityByConflict(psi) {
    const p = probFromVec(psi);
    const buckets = new Map();
    for (let i = 0; i < N; i++) {
      const c = app.conflicts[i];
      buckets.set(c, (buckets.get(c) || 0) + p[i]);
    }
    const xs = Array.from({length: MAX_CONFLICT + 1}, (_, i) => i);
    const ys = xs.map(x => buckets.get(x) || 0);
    return {x: xs, y: ys};
  }

  function groverEigenProjections(psi) {
    const M = app.solutions.length;
    const rest = N - M;
    // Components in {|w>, |r>} basis
    let sumW = math.complex(0,0);
    let sumR = math.complex(0,0);
    const arr = psi.toArray();
    for (let i = 0; i < N; i++) {
      if (app.solutions.includes(i)) sumW = math.add(sumW, arr[i]);
      else sumR = math.add(sumR, arr[i]);
    }
    const alpha = math.divide(sumW, Math.sqrt(M)); // <w|psi>
    const beta  = math.divide(sumR, Math.sqrt(rest)); // <r|psi>
    const v = [alpha, beta];

    // 2x2 Grover operator in {|w>,|r>} basis
    const a = 2*M/N - 1;
    const b = 2*Math.sqrt(M*rest)/N;
    const d = 2*rest/N - 1;
    const G2 = math.matrix([[ -a,  b],
                            [ -b,  d]]);
    const res = math.eigs(G2);
    const vals = res.values;           // complex eigenvalues
    const V = res.vectors.toArray();   // columns are eigenvectors
    // Build eigenvectors as arrays [v0, v1] (complex)
    const evecs = [ [math.complex(V[0][0], 0), math.complex(V[1][0], 0)],
                    [math.complex(V[0][1], 0), math.complex(V[1][1], 0)] ];
    // Normalize eigenvectors
    function normalize2(vec) {
      const n = Math.sqrt(math.abs(vec[0])**2 + math.abs(vec[1])**2) || 1;
      return [math.divide(vec[0], n), math.divide(vec[1], n)];
    }
    const e0 = normalize2(evecs[0]);
    const e1 = normalize2(evecs[1]);

    function projProb(e, v) {
      const conj0 = math.conj(e[0]), conj1 = math.conj(e[1]);
      const p = math.add(math.multiply(conj0, v[0]), math.multiply(conj1, v[1]));
      return math.abs(p)**2;
    }
    let p0 = projProb(e0, v);
    let p1 = projProb(e1, v);
    const total = p0 + p1 || 1;
    p0 /= total; p1 /= total;

    const angles = vals.map(ev => {
      const c = (typeof ev === 'number') ? math.complex(ev, 0) : ev;
      return Math.atan2(math.im(c), math.re(c));
    });

    return { angles: angles, probs: [p0, p1] };
  }

  // Plotting
  function plotAmplitudes(psi) {
    const {re, im} = vecToReIm(psi);
    const colors = re.map((_, i) =>
      app.solutions.includes(i) ? '#000000' : colorForConflict(app.conflicts[i])
    );

    const nonSolIdx = [];
    const nonSolX = [];
    const nonSolY = [];
    const nonSolColor = [];
    for (let i = 0; i < N; i++) {
      if (!app.solutions.includes(i)) {
        nonSolIdx.push(i);
        nonSolX.push(re[i]);
        nonSolY.push(im[i]);
        nonSolColor.push(colors[i]);
      }
    }

    const solX = app.solutions.map(i => re[i]);
    const solY = app.solutions.map(i => im[i]);

    const traces = [
      {
        type: 'scattergl',
        mode: 'markers',
        x: nonSolX,
        y: nonSolY,
        marker: { color: nonSolColor, size: 8, line: {width: 0} },
        hoverinfo: 'x+y',
        name: 'states'
      },
      {
        type: 'scattergl',
        mode: 'markers',
        x: solX,
        y: solY,
        marker: { color: '#000', size: 12, line: { color: '#000', width: 1 } },
        hoverinfo: 'x+y',
        name: 'solutions'
      }
    ];

    if (app.currentStep === 0 && app.highlightIndex != null) {
      traces.push({
        type: 'scattergl',
        mode: 'markers',
        x: [re[app.highlightIndex]],
        y: [im[app.highlightIndex]],
        marker: { color: 'red', size: 10, line: {color: '#800', width: 1} },
        name: 'highlight'
      });
    }

    // symmetric axes around zero
    const maxAbs = Math.max(
      0.1,
      ...re.map(v => Math.abs(v)),
      ...im.map(v => Math.abs(v))
    );
    const r = maxAbs * 1.25;

    Plotly.react('plot-amplitudes', traces, {
      margin: {l: 45, r: 10, t: 10, b: 45},
      xaxis: { title: 'Re', range: [-r, r], zeroline: true, mirror: true, showline: true },
      yaxis: { title: 'Im', range: [-r, r], zeroline: true, mirror: true, showline: true, scaleratio: 1 },
      showlegend: false
    }, {displayModeBar: false, responsive: true});
  }

  function plotProbability(psi) {
    const mode = app.plotMode;
    if (mode === 'conflicts' || (mode === 'eigenvalues' && app.algorithm !== 'unstructured')) {
      const agg = probabilityByConflict(psi);
      Plotly.react('plot-probability', [{
        type: 'bar',
        x: agg.x,
        y: agg.y,
        marker: { color: '#4f7bd9', line: {color: '#2e4a94', width: 1} }
      }], {
        margin: {l: 50, r: 10, t: 10, b: 45},
        xaxis: { title: (mode === 'conflicts') ? 'number of conflicts' : 'eigenvalue' },
        yaxis: { title: 'probability', range: [0, 1] },
        bargap: 0.2
      }, {displayModeBar: false, responsive: true});
    } else {
      // Unstructured eigenvalue mode: projection onto eigenvectors of G
      const proj = groverEigenProjections(psi);
      Plotly.react('plot-probability', [{
        type: 'bar',
        x: proj.angles,
        y: proj.probs,
        marker: { color: '#4f7bd9', line: {color: '#2e4a94', width: 1} }
      }], {
        margin: {l: 50, r: 10, t: 10, b: 45},
        xaxis: { title: 'arg(eigenvalue)' },
        yaxis: { title: 'probability', range: [0, 1] },
        bargap: 0.4
      }, {displayModeBar: false, responsive: true});
    }
  }

  function update() {
    // Read control state
    app.totalSteps = parseInt(sliderTotal.value, 10);
    app.currentStep = parseInt(sliderCurrent.value, 10);

    // Compute state vector
    const psi = getStateVector();

    // Update plots
    plotAmplitudes(psi);
    plotProbability(psi);
  }

  // Control bindings
  function setAlgorithm(alg) {
    app.algorithm = alg;
    // Highlight active algorithm button
    setActiveButton(
      { 'unstructured': btnUnstructured, 'adiabatic': btnAdiabatic,
        'discrete-adiabatic': btnDiscrete, 'heuristic': btnHeuristic }[alg],
      [btnUnstructured, btnAdiabatic, btnDiscrete, btnHeuristic]
    );

    // Set default total steps for the algorithm
    const defaults = {
      'unstructured': 5,
      'adiabatic': 200,
      'discrete-adiabatic': 200,
      'heuristic': 10
    };
    sliderTotal.value = defaults[alg];
    displayTotal.textContent = sliderTotal.value;

    // Update current step slider
    sliderCurrent.max = sliderTotal.value;
    sliderCurrent.value = 0;
    displayCurrent.textContent = '0';
    labelCurrent.textContent = `current step (0 to ${sliderTotal.value})`;

    // Invalidate unitary cache if needed
    app.unitaryCache = {};
    app.unitaryCacheKey = null;

    update();
  }

  btnUnstructured.addEventListener('click', () => setAlgorithm('unstructured'));
  btnAdiabatic.addEventListener('click', () => setAlgorithm('adiabatic'));
  btnDiscrete.addEventListener('click', () => setAlgorithm('discrete-adiabatic'));
  btnHeuristic.addEventListener('click', () => setAlgorithm('heuristic'));

  sliderTotal.addEventListener('input', () => {
    const newMax = parseInt(sliderTotal.value, 10);
    displayTotal.textContent = newMax;
    sliderCurrent.max = newMax;
    if (parseInt(sliderCurrent.value, 10) > newMax) {
      sliderCurrent.value = newMax;
    }
    displayCurrent.textContent = sliderCurrent.value;
    labelCurrent.textContent = `current step (0 to ${newMax})`;

    // Invalidate caches because T changed
    app.unitaryCache = {};
    app.unitaryCacheKey = null;

    update();
  });

  sliderCurrent.addEventListener('input', () => {
    displayCurrent.textContent = sliderCurrent.value;
    update();
  });

  btnConflicts.addEventListener('click', () => {
    app.plotMode = 'conflicts';
    setActiveButton(btnConflicts, [btnConflicts, btnEigen]);
    update();
  });
  btnEigen.addEventListener('click', () => {
    app.plotMode = 'eigenvalues';
    setActiveButton(btnEigen, [btnConflicts, btnEigen]);
    update();
  });

  // Initialize
  function init() {
    // Defaults set in HTML for heuristic, steps=10, current=0, conflicts mode
    initializeProblem();
    // Ensure displays
    displayTotal.textContent = sliderTotal.value;
    displayCurrent.textContent = sliderCurrent.value;
    labelCurrent.textContent = `current step (0 to ${sliderTotal.value})`;
    // First draw
    update();
  }

  init();
})();
</script>
</body>
</html>
