  <script>
    // Utility helpers
    const $ = (id) => document.getElementById(id);
    function seq(start, end, step){
      const arr = [];
      if (step <= 0) step = 1;
      const n = Math.floor((end - start) / step + 0.000001);
      for(let i=0;i<=n;i++) arr.push(start + i*step);
      // Ensure it ends exactly at 'end' if off by rounding
      if (arr[arr.length-1] !== end) arr.push(end);
      return arr;
    }
    function clamp(v, lo, hi){ return Math.max(lo, Math.min(hi, v)); }

    // Models
    function f_sat_Antoine_bar(Tc){
      // Antoine-like: f_sat(T) in bar, T in °C
      return Math.pow(10, 8.65 - 5818 / (230 + Tc));
    }
    function Tsat_from_P_bar(Pbar){
      // Inverse of the above, returns T (°C)
      return 5818 / (8.65 - Math.log10(Pbar)) - 230;
    }
    function Psat_MPa(TK){
      // P^sat(T) in MPa, Antoine-like in Kelvin
      return Math.pow(10, 10.83 - 5047 / TK);
    }
    function fVapor(P_MPa, T_K, realGas){
      if(realGas){
        return P_MPa * Math.exp(-25 * P_MPa / T_K);
      } else {
        return P_MPa;
      }
    }

    // Elements
    const selectPlot = $("select-plot");
    const pressureSlider = $("slider-pressure");
    const tempSlider = $("slider-temperature");
    const valuePressure = $("value-pressure");
    const valueTemp = $("value-temperature");
