        groupPressure.style.display = "flex";
        groupTemp.style.display = "none";
        checkboxContainer.style.display = "none";
      } else {
        groupPressure.style.display = "none";
        groupTemp.style.display = "flex";
        checkboxContainer.style.display = "flex";
