        const f_left = T_left.map(t => f_sat_Antoine_bar(t));

        // Stable vapor (y = P from Tsat to 400)
        const f_right_const = T_right.map(_ => Pbar);

        // Metastable liquid (f_sat from Tsat to 400)
        const f_right_meta = T_right.map(t => f_sat_Antoine_bar(t));

        // Metastable vapor (y = P from 280 to Tsat)
        const f_left_const = T_left.map(_ => Pbar);

        const traces = [
          {
            x: T_left, y: f_left,
            mode: "lines",
            line: {color:"#0000FF", width:3},
            name: "stable liquid",
            hovertemplate: "T=%{x:.0f}°C<br>f=%{y:.3f} bar<extra></extra>"
