        const f_sat = fVapor(Psat, T, isReal);
        const f_right_const = P_right.map(_ => f_sat);
        const f_right_meta = P_right.map(p => fVapor(p, T, isReal));
        const f_left_const = P_left.map(_ => f_sat);

        const traces = [
          {
            x: P_left, y: f_left,
            mode:"lines",
            line:{color:"#0000FF", width:3},
            name:"stable vapor",
            hovertemplate:"P=%{x:.2f} MPa<br>f=%{y:.3f} MPa<extra></extra>"
          },
          {
