              x:[Ps1, Ps1], y:[0, f_sat],
              mode:"lines",
              line:{color:"#000000", width:2, dash:"dash"},
              name:"indicator"
            }
        ];

        const ann = [
          {
            x:0.5, y:0.25, text:"vapor",
            showarrow:false, font:{size:14, color:"#000"},
            xanchor:"center", yanchor:"middle"
          },
          {
            x:2.7, y: clamp(f_sat, 0, 3.0),
            text:"liquid", showarrow:false,
            font:{size:14, color:"#000"},
            xanchor:"center", yanchor:"bottom"
          },
          {
            x:Ps1, y: clamp(f_sat + 0.08, 0, 3.0),
            text:"P<sup>sat</sup>",
            showarrow:false, font:{size:14, color:"#000"},
            xanchor:"center", yanchor:"bottom"
          }
        ];

        const layout = {
          paper_bgcolor:"#ffffff",
          plot_bgcolor:"#ffffff",
          margin:{l:70, r:20, t:20, b:70},
          xaxis:{
            title:{text:"pressure (MPa)", font:{size:14}},
            range:[P_lo, P_hi],
            showgrid:true, gridcolor:"#e0e0e0",
            zeroline:false, tickfont:{size:12}
          },
          yaxis:{
            title:{text:"fugacity (MPa)", font:{size:14}},
            range:[0, 3.0],
            showgrid:true, gridcolor:"#e0e0e0",
            zeroline:false, tickfont:{size:12}
          },
          showlegend:false,
          annotations: ann
        };

        Plotly.react(plotDiv, traces, layout, {displayModeBar:false, responsive:true});
      }
    }

    // Initialize displays and first plot
    valuePressure.textContent = Number(pressureSlider.value).toFixed(2);
    valueTemp.textContent = String(Math.round(Number(tempSlider.value)));
    updatePlot();
  </script>
