<!-- MathJax (loaded but not strictly needed for simple text) -->
<script defer src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

<script>
(function() {
    // Remove the placeholder canvas so p5 can create its own with the same id
    const placeholder = document.getElementById('canvas-bloch');
    if (placeholder && placeholder.parentElement) {
        placeholder.parentElement.removeChild(placeholder);
    }

    // ------------------- Global state (model, shared with p5 and Plotly) -------------------
    const state = {
        // Geometry
        R: 160,                 // pixels
        margin: 30,             // bottom margin for canvas
        theta: 0.44879,         // default π/7
        // Vectors in pixels (world coordinates with origin at bottom-center, y up)
        a: {x: 0, y: 0},
        b: {x: 0, y: 0},
        c: {x: 0, y: 0},        // user-movable
        d_opt: {x: 0, y: 0},    // best d for current c
        // Optional "optimal pair" (for the green dot)
        c_pair: {x: 0, y: 0},
        d_pair: {x: 0, y: 0},
        dragging: false,
        // UI flags
        showMetricCircle: false,
        showMetricRegion: false,
        showFirstOrderVec: false,
        showFirstOrderRegion: false,
        showOptimalPair: false,
        showOptimalRegion: false
    };

    // Utility functions (math on short objects)
    function v(x, y){ return {x:x, y:y}; }
    function add(a,b){ return v(a.x+b.x, a.y+b.y); }
    function sub(a,b){ return v(a.x-b.x, a.y-b.y); }
    function mul(a,s){ return v(a.x*s, a.y*s); }
    function dot(a,b){ return a.x*b.x + a.y*b.y; }
    function mag(a){ return Math.hypot(a.x, a.y); }
    function norm(a){ const m = mag(a); return m>0? v(a.x/m, a.y/m) : v(1,0); }
    function perp(a){ return v(-a.y, a.x); }

    // Initialize model vectors from theta
    function updateAB() {
        const R = state.R;
        const t = state.theta;
        state.a = v( R*Math.sin(t), R*Math.cos(t) );
        state.b = v(-R*Math.sin(t), R*Math.cos(t) );
    }

    // Optimal pair (toy but consistent with green dot coordinates)
    // Choose c_pair parallel to a, and d_pair parallel to b, with same shrink factor s = 1 - d
    // where 0.5 * d^2 = e = (sin(2θ)/2)^2 -> d = sin(2θ)/sqrt(2)
    function updateOptimalPair() {
        const e = Math.sin(2*state.theta)/Math.SQRT2; // d
        const s = Math.max(0, 1 - e);
        const aU = norm(state.a), bU = norm(state.b);
        state.c_pair = mul(aU, state.R * s);
        state.d_pair = mul(bU, state.R * s);
    }

    // Compute d_opt for current c as the closest point on the ellipse:
    // (d_parallel / A)^2 + (d_perp / B)^2 = 1, with A = sqrt(1 - ||c||^2), B = 1
    // Ellipse is aligned with basis {u = ĉ, v = u⊥}
    function computeDopt() {
        const R = state.R;
        const cN = mag(state.c) / R;
        // Numerical safety
        const A = Math.max(Math.sqrt(Math.max(0, 1 - cN*cN)), 1e-6);
        const B = 1.0;

        // Basis relative to c
        const u = norm(state.c);
        const vperp = perp(u);

        // b in normalized coordinates
        const bN = mul(state.b, 1/R);
        const bp = dot(bN, u);
        const bo = dot(bN, vperp);

        // Check if b is inside ellipse (then closest is itself)
        const inside = (bp*bp)/(A*A) + (bo*bo)/(B*B) <= 1.0;
        let xp, yp;
        if (inside) {
            xp = bp; yp = bo;
        } else {
            // Newton iteration on parameterization p(t) = (A cos t, B sin t)
            let t = Math.atan2(bo/B, bp/A);
            for (let k=0; k<40; k++) {
                const ct = Math.cos(t), st = Math.sin(t);
                const g = (-A*st)*(A*ct - bp) + (B*ct)*(B*st - bo);
                const gp = (B*B - A*A)*(ct*ct - st*st) + A*bp*ct + B*bo*st;
                const step = g / (Math.abs(gp) > 1e-10 ? gp : (gp>=0?1e-10:-1e-10));
                t -= step;
                if (Math.abs(step) < 1e-9) break;
            }
            xp = A * Math.cos(t);
            yp = B * Math.sin(t);
        }
        // Convert back to world coords and pixels
        const resN = add( mul(u, xp), mul(vperp, yp) );
        state.d_opt = mul(resN, R);
    }

    // Error metrics (normalized length units)
    function errorPair() {
        const R = state.R;
        const aN = mul(state.a, 1/R);
        const bN = mul(state.b, 1/R);
        const cN = mul(state.c, 1/R);
        const dN = mul(state.d_opt, 1/R);
        const Dca = 0.5 * ( (aN.x - cN.x)**2 + (aN.y - cN.y)**2 );
        const Ddb = 0.5 * ( (bN.x - dN.x)**2 + (bN.y - dN.y)**2 );
        return {Dca, Ddb};
    }

    // Boundary curve for the trade-off
    function boundary(theta, n=200) {
        const s2 = Math.sin(2*theta);
        const xmax = s2*s2;
        const xs = [], ys = [];
        for (let i=0; i<=n; i++) {
            const x = xmax * (i/n);
            const y = (s2 - Math.sqrt(x))**2;
            xs.push(x); ys.push(y);
        }
        return {xs, ys};
    }

    // ------------------- Plotly (right panel) -------------------
    function initPlot() {
        const B = boundary(state.theta);
        const err = errorPair();
        const eopt = (Math.sin(2*state.theta)/2)**2;

        const traces = [
            {
                x: B.xs, y: B.ys,
                mode: 'lines',
                line: {color: 'rgb(70,130,180)', width: 2},
                name: 'boundary'
            },
            {
                x: [err.Dca], y: [err.Ddb],
                mode: 'markers',
                marker: {color: 'rgb(220,60,60)', size: 10},
                name: 'current',
                hovertemplate: 'D(C,A)=%{x:.3f}<br>D(D,B)=%{y:.3f}<extra></extra>'
            },
            {
                x: [eopt], y: [eopt],
                mode: 'markers',
                marker: {color: 'rgb(46,160,67)', size: 9},
                name: 'optimal',
                visible: state.showOptimalPair ? true : 'legendonly',
                hovertemplate: 'optimal (%{x:.3f}, %{y:.3f})<extra></extra>'
            }
        ];

        const layout = {
            margin: {l: 50, r: 10, t: 10, b: 45},
            xaxis: {title: 'D(C, A)', range: [0, 1.5], zeroline: false},
            yaxis: {title: 'D(D, B)', range: [0, 1.5], zeroline: false},
            showlegend: false
        };
        Plotly.newPlot('plot-error', traces, layout, {responsive: true});
    }

    function updatePlot() {
        const B = boundary(state.theta);
        const err = errorPair();
        const eopt = (Math.sin(2*state.theta)/2)**2;

        const update = {
            x: [B.xs, [err.Dca], [eopt]],
            y: [B.ys, [err.Ddb], [eopt]]
        };
        const vis = state.showOptimalPair ? true : 'legendonly';
        const updateTraces = [0,1,2];
        Plotly.restyle('plot-error', {x: [B.xs], y: [B.ys]}, [0]);
        Plotly.restyle('plot-error', {x: [[err.Dca]], y: [[err.Ddb]]}, [1]);
        Plotly.restyle('plot-error', {x: [[eopt]], y: [[eopt]], visible: [vis]}, [2]);
    }

    // ------------------- UI bindings -------------------
    const sliderTheta = document.getElementById('slider-theta');
    const spanTheta = document.getElementById('span-theta-value');
    const spanSinSq = document.getElementById('span-sinsq-theta-value');

    function updateThetaUI() {
        const t = parseFloat(sliderTheta.value);
        state.theta = t;
        // Display numeric radians
        spanTheta.textContent = t.toFixed(5);
        spanSinSq.textContent = Math.sin(t)**2 <= 1 ? (Math.sin(t)**2).toFixed(6) : '—';
    }

    function onThetaChange() {
        updateThetaUI();
        updateAB();
        updateOptimalPair();
        computeDopt();
        updatePlot();
        if (p5inst) p5inst.redraw();
    }

    sliderTheta.addEventListener('input', onThetaChange);

    // Checkboxes
    function bindCheckbox(id, key, onChange=()=>{}) {
        const el = document.getElementById(id);
        el.checked = false;
        el.addEventListener('change', () => {
            state[key] = el.checked;
            onChange();
            if (p5inst) p5inst.redraw();
            updatePlot();
        });
    }

    bindCheckbox('checkbox-metric-circle', 'showMetricCircle');
    bindCheckbox('checkbox-metric-region', 'showMetricRegion');
    bindCheckbox('checkbox-first-order-vec', 'showFirstOrderVec');
    bindCheckbox('checkbox-first-order-region', 'showFirstOrderRegion');
    bindCheckbox('checkbox-optimal-pair', 'showOptimalPair', () => {});
    bindCheckbox('checkbox-optimal-region', 'showOptimalRegion');

    // ------------------- p5 sketch (left panel) -------------------
    let p5inst = new p5((p)=>{
        let cnv;
        p.setup = function() {
            // Create canvas and set the id as required
            const container = document.getElementById('canvas-container');
            const W = container.clientWidth > 420 ? 420 : Math.max(320, container.clientWidth);
            const H = 420;
            cnv = p.createCanvas(W, H);
            cnv.id('canvas-bloch');
            cnv.parent('canvas-container');
            p.pixelDensity(1);

            // Initial model state
            updateAB();
            // default c
            state.c = v(state.R*0.3, Math.max(0, state.R*0.7));
            clampCToSemicircle();
            updateOptimalPair();
            computeDopt();

            p.noLoop();
        };

        p.windowResized = function() {
            const container = document.getElementById('canvas-container');
            const W = container.clientWidth > 420 ? 420 : Math.max(320, container.clientWidth);
            const H = 420;
            p.resizeCanvas(W, H);
            p.redraw();
            Plotly.Plots.resize('plot-error');
        };

        // Convert mouse position to world coordinates (origin at bottom center, y up)
        function mouseToWorld() {
            const ox = p.width/2;
            const oy = p.height - state.margin;
            const xw = p.mouseX - ox;
            const yw = oy - p.mouseY;
            return v(xw, yw);
        }

        function clampCToSemicircle() {
            // clamp magnitude to R and y >= 0
            let c = state.c;
            const m = mag(c);
            if (m > state.R) {
                const u = norm(c);
                c = mul(u, state.R);
            }
            if (c.y < 0) c.y = 0;
            state.c = c;
        }

        // Arrow drawing helper
        function drawArrow(base, tip, col, weight=2, dashed=false, label='') {
            p.push();
            p.stroke(col);
            p.fill(col);
            p.strokeWeight(weight);
            if (dashed) {
                p.drawingContext.setLineDash([6, 6]);
            } else {
                p.drawingContext.setLineDash([]);
            }
            // line
            p.line(base.x, base.y, tip.x, tip.y);
            // arrow head
            const dir = sub(tip, base);
            const L = mag(dir);
            if (L > 0) {
                const u = mul(dir, 1/L);
                const head = 8, wing = 5;
                const left = add(tip, add( mul(u, -head), mul(perp(u),  wing) ));
                const right= add(tip, add( mul(u, -head), mul(perp(u), -wing) ));
                p.noStroke(); p.fill(col);
                p.triangle(tip.x, tip.y, left.x, left.y, right.x, right.y);
            }
            if (label) {
                p.noStroke();
                p.fill(col);
                const off = mul(norm(sub(tip, base)), 12);
                p.textSize(13);
                // place label slightly beyond tip
                p.push();
                p.scale(1, -1); // back to screen coords for text
                p.text(label,
                    tip.x + off.x,
                    -(tip.y + off.y) - 4
                );
                p.pop();
            }
            p.pop();
        }

        function dashedArc(radius, col) {
            p.push();
            p.noFill();
            p.stroke(col);
            p.strokeWeight(2);
            p.drawingContext.setLineDash([8,6]);
            p.arc(0, 0, 2*radius, 2*radius, 0, Math.PI);
            p.pop();
        }

        function metricCircle(center, radius, col) {
            p.push();
            p.noFill();
            p.stroke(col);
            p.strokeWeight(2);
            p.drawingContext.setLineDash([6,6]);
            p.circle(center.x, center.y, 2*radius);
            p.pop();
        }

        p.draw = function() {
            p.background(250);
            p.push();
            // Set world transform: origin at bottom center, y up
            p.translate(p.width/2, p.height - state.margin);
            p.scale(1, -1);

            // Base line and semicircle
            p.noFill();
            p.stroke(0); p.strokeWeight(4);
            p.arc(0, 0, 2*state.R, 2*state.R, 0, Math.PI);
            p.strokeWeight(2);
            p.line(-state.R-20, 0, state.R+20, 0);

            // Auxiliary dashed arcs (regions)
            if (state.showMetricRegion || state.showFirstOrderRegion) {
                dashedArc(mag(state.c), 'rgba(60,160,60,0.7)'); // green
                dashedArc(mag(state.d_opt), 'rgba(230,150,90,0.6)'); // orange
            }
            if (state.showOptimalPair && state.showOptimalRegion) {
                dashedArc(mag(state.c_pair), 'rgba(60,160,60,0.35)');
                dashedArc(mag(state.d_pair), 'rgba(230,150,90,0.35)');
            }

            // Vectors
            drawArrow(v(0,0), state.b, 'rgb(210,60,60)', 3, false, 'b');
            drawArrow(v(0,0), state.a, 'rgb(50,100,220)', 3, false, 'a');

            drawArrow(v(0,0), state.c, 'rgb(40,150,70)', 3, false, 'c');

            // First-order optimizing d vector (from b tip to d_opt tip)
            if (state.showFirstOrderVec) {
                drawArrow(state.b, state.d_opt, 'rgb(230,150,70)', 2, true, 'd_err');
            }

            // Metric circle centered at a with radius ||c-a||
            if (state.showMetricCircle) {
                const r = mag(sub(state.c, state.a));
                metricCircle(state.a, r, 'rgba(70,130,180,0.8)');
            }

            // Optimal pair vectors
            if (state.showOptimalPair) {
                drawArrow(v(0,0), state.c_pair, 'rgba(46,160,67,0.7)', 2, true, 'c_opt');
                drawArrow(v(0,0), state.d_pair, 'rgba(46,160,67,0.7)', 2, true, 'd_opt');
            }

            p.pop();

            // Draw draggable handle for c (on screen coords)
            p.push();
            // map c to screen
            const ox = p.width/2, oy = p.height - state.margin;
            const cx = ox + state.c.x, cy = oy - state.c.y;
            p.noStroke();
            p.fill(40,150,70);
            p.circle(cx, cy, 10);
            p.pop();
        };

        // Interactivity: drag the tip of c
        p.mousePressed = function() {
            const ox = p.width/2, oy = p.height - state.margin;
            const cx = ox + state.c.x, cy = oy - state.c.y;
            const d = Math.hypot(p.mouseX - cx, p.mouseY - cy);
            if (d < 12) {
                state.dragging = true;
            }
        };

        p.mouseDragged = function() {
            if (!state.dragging) return;
            const w = mouseToWorld();
            state.c = v(w.x, w.y);
            clampCToSemicircle();
            computeDopt();
            updatePlot();
            p.redraw();
        };

        p.mouseReleased = function() { state.dragging = false; };
    });

    // ------------------- Initialize UI + Plot -------------------
    updateThetaUI();      // set initial spans
    updateAB();           // set a and b
    updateOptimalPair();  // set optimal pair
    // default c set in p5.setup
    // initialize plot after a short tick to ensure canvas is created
    window.addEventListener('load', () => {
        initPlot();
        updatePlot();
    });

})();
</script>
