<script>
(function() {
  const PLOT_ID = 'plot-container';

  // DOM elements
  const sliderPoints = document.getElementById('slider-points');
  const sliderTime   = document.getElementById('slider-time');
  const labelPoints  = document.getElementById('label-points');
  const labelTime    = document.getElementById('label-time');

  // Initial UI labels
  labelPoints.textContent = String(sliderPoints.value);
  labelTime.textContent   = Number(sliderTime.value).toFixed(3);

  // Heat equation analytical solution via Fourier series
  function T_of_x_t(x, t) {
    // T(x, t) = (8/π²) * Σ_{n=1..∞} (1/n²) * sin(nπ/2) * sin(nπx) * e^{-2 n² π² t}
    const terms = 100; // sufficient for convergence for this demo
    const pi = Math.PI;
    const factor = 8 / (pi * pi);
    let s = 0;
    for (let n = 1; n <= terms; n++) {
      const npi = n * pi;
      const coeff = (1 / (n * n)) * Math.sin(npi / 2);
      if (coeff === 0) continue; // skip even n quickly
      s += coeff * Math.sin(npi * x) * Math.exp(-2 * n * n * pi * pi * t);
    }
    return factor * s;
  }

  // Generate smooth analytical curve
  function makeAnalyticalCurve(t) {
    const nPts = 200;
    const xs = new Array(nPts);
    const ys = new Array(nPts);
    for (let i = 0; i < nPts; i++) {
      const x = i / (nPts - 1);
      xs[i] = x;
      ys[i] = T_of_x_t(x, t);
    }
    return { x: xs, y: ys };
  }

  // Chebyshev-Gauss-Lobatto points mapped to [0, 1]
  function chebyshevLobattoPoints(numPoints) {
    const N = numPoints - 1;
    const xs = [];
    // j = N..0 gives ascending x in [0,1]
    for (let j = N; j >= 0; j--) {
      const xj = (Math.cos(Math.PI * j / N) + 1) / 2;
      xs.push(xj);
    }
    return xs;
  }

  // Build traces based on current controls
  function buildTraces(numPoints, t) {
    const smooth = makeAnalyticalCurve(t);

    // Red marker points at CGL nodes
    const xNodes = chebyshevLobattoPoints(numPoints);
    const yNodes = xNodes.map(x => T_of_x_t(x, t));

    const blueTrace = {
      type: 'scatter',
      mode: 'lines',
      x: smooth.x,
      y: smooth.y,
      line: { color: 'blue', width: 3 },
      hoverinfo: 'x+y',
      name: 'Analytical'
    };

    const redTrace = {
      type: 'scatter',
      mode: 'markers',
      x: xNodes,
      y: yNodes,
      marker: { color: 'red', size: 8 },
      hoverinfo: 'x+y',
      name: 'Collocation'
    };

    return [blueTrace, redTrace];
  }

  const layout = {
    margin: { t: 20, r: 20, b: 40, l: 48 },
    xaxis: {
      title: 'x',
      range: [0, 1],
      tickmode: 'linear',
      dtick: 0.2,
      showgrid: true,
      gridcolor: '#e6e6e6',
      zeroline: false
    },
    yaxis: {
      title: 'T',
      range: [-0.1, 1.1],
      tickmode: 'linear',
      dtick: 0.2,
      showgrid: true,
      gridcolor: '#e6e6e6',
      zeroline: false
    },
    showlegend: false
  };

  const config = {
    responsive: true,
    displayModeBar: false
  };

  function updatePlot() {
    const numPoints = parseInt(sliderPoints.value, 10);
    const t = parseFloat(sliderTime.value);
    const traces = buildTraces(numPoints, t);
    Plotly.react(PLOT_ID, traces, layout, config);
  }

  // Initialize plot on page load
  updatePlot();

  // Slider interactions
  sliderPoints.addEventListener('input', () => {
    labelPoints.textContent = String(sliderPoints.value);
    updatePlot();
  });

  sliderTime.addEventListener('input', () => {
    labelTime.textContent = Number(sliderTime.value).toFixed(3);
    updatePlot();
  });
})();
</script>
</body>
</html>
