<script>
/* ---------- Global State ---------- */
let numRegularPoints = 4;
let numSteinerPoints = 2;
let pointMode = 'steiner'; // 'regular' or 'steiner'
let showAngles = true;

let regularPoints = [];
let steinerPoints = [];

let mstEdges = [];
let totalLength = 0;

let dragging = null; // { type: 'regular'|'steiner', index }
const REG_PT_RADIUS = 8;
const STEINER_OUTER_RADIUS = 8;
const STEINER_INNER_RADIUS = 4;

/* ---------- Helpers ---------- */
function dist2(a, b) {
  const dx = a.x - b.x, dy = a.y - b.y;
  return dx*dx + dy*dy;
}
function formatLen(len) {
  // mimic snapshots: 5 decimal places
  return (+len).toFixed(5).replace(/\.?0+$/,'0'); // keep at least one zero
}
function getAllPoints() {
  // return array [{x,y,type:'regular'|'steiner'}]
  const all = [];
  for (const p of regularPoints) all.push({x: p.x, y: p.y, type: 'regular'});
  for (const p of steinerPoints) all.push({x: p.x, y: p.y, type: 'steiner'});
  return all;
}

/* ---------- Minimum Spanning Tree - Kruskal ---------- */
class DSU {
  constructor(n) {
    this.p = Array.from({length:n}, (_,i)=>i);
    this.r = Array(n).fill(0);
  }
  find(x) {
    if (this.p[x] !== x) this.p[x] = this.find(this.p[x]);
    return this.p[x];
  }
  union(a, b) {
    let ra = this.find(a), rb = this.find(b);
    if (ra === rb) return false;
    if (this.r[ra] < this.r[rb]) [ra, rb] = [rb, ra];
    this.p[rb] = ra;
    if (this.r[ra] === this.r[rb]) this.r[ra]++;
    return true;
  }
}
function computeMST() {
  const pts = getAllPoints();
  const n = pts.length;
  const edges = [];
  for (let i = 0; i < n; i++) {
    for (let j = i+1; j < n; j++) {
      const w = Math.hypot(pts[i].x - pts[j].x, pts[i].y - pts[j].y);
      edges.push({i, j, w});
    }
  }
  edges.sort((a,b)=>a.w - b.w);
  const dsu = new DSU(n);
  mstEdges = [];
  totalLength = 0;
  for (const e of edges) {
    if (dsu.union(e.i, e.j)) {
      mstEdges.push(e);
      totalLength += e.w;
      if (mstEdges.length === n-1) break;
    }
  }
  document.getElementById('text-total-length').textContent = formatLen(totalLength);
}

/* ---------- Placement Utilities ---------- */
function resetRegularPoints(n) {
  numRegularPoints = n;
  const w = width, h = height;
  const cx = w/2, cy = h/2;
  const R = Math.min(w, h) * 0.38;
  regularPoints = [];
  for (let k=0; k<n; k++) {
    const ang = -Math.PI/2 + 2*Math.PI*k/n;
    regularPoints.push({ x: cx + R * Math.cos(ang), y: cy + R * Math.sin(ang) });
  }
}
function setSteinerCount(m) {
  numSteinerPoints = m;
  const cx = width/2, cy = height/2;
  // adjust number, placing new ones at center
  if (steinerPoints.length < m) {
    while (steinerPoints.length < m) steinerPoints.push({x: cx, y: cy});
  } else {
    steinerPoints.length = m;
  }
}
function recenterSteinerPoints() {
  const cx = width/2, cy = height/2;
  for (const p of steinerPoints) { p.x = cx; p.y = cy; }
}

/* ---------- p5 Setup & Draw ---------- */
let canvasRef;
function setup() {
  const container = document.getElementById('canvas-container');
  const w = Math.max(420, container.clientWidth - 24);
  const h = Math.max(420, window.innerHeight - 2*12 - 24);
  canvasRef = createCanvas(w-20, h-20);
  canvasRef.parent('canvas-container');
  canvasRef.id('main-canvas');

  // Initial points
  resetRegularPoints(numRegularPoints);
  setSteinerCount(numSteinerPoints);

  computeMST();
  updateActiveControls();
}
function windowResized() {
  // Keep canvas responsive to container
  const container = document.getElementById('canvas-container');
  const w = Math.max(420, container.clientWidth - 24);
  const h = Math.max(420, window.innerHeight - 2*12 - 24);
  resizeCanvas(w-20, h-20);
  // After resize, reposition regular polygon and recenter Steiner points relatively (optional)
  resetRegularPoints(numRegularPoints);
  recenterSteinerPoints();
  computeMST();
}
function draw() {
  background('#EEEEEE');

  const all = getAllPoints();

  // Light complete graph lines
  stroke('rgba(190,160,130,0.5)');
  strokeWeight(1);
  for (let i = 0; i < all.length; i++) {
    for (let j = i+1; j < all.length; j++) {
      line(all[i].x, all[i].y, all[j].x, all[j].y);
    }
  }

  // MST lines
  stroke('#333333');
  strokeWeight(3);
  for (const e of mstEdges) {
    const a = all[e.i], b = all[e.j];
    line(a.x, a.y, b.x, b.y);
  }

  // Points
  drawPoints();

  // Angles
  if (showAngles) drawSteinerAngles();
}

/* ---------- Rendering Helpers ---------- */
function drawPoints() {
  // Regular points (white fill, blue stroke)
  for (const p of regularPoints) {
    stroke('#0000FF');
    strokeWeight(2);
    fill('#FFFFFF');
    circle(p.x, p.y, REG_PT_RADIUS*2);
  }

  // Steiner points (double circle, red stroke)
  for (const p of steinerPoints) {
    stroke('#FF0000');
    strokeWeight(2);
    fill('#FFFFFF');
    circle(p.x, p.y, STEINER_OUTER_RADIUS*2);
    circle(p.x, p.y, STEINER_INNER_RADIUS*2);
  }
}
function drawSteinerAngles() {
  const all = getAllPoints();
  const nReg = regularPoints.length;

  // Build adjacency list from MST
  const adj = Array.from({length: all.length}, () => []);
  for (const e of mstEdges) {
    adj[e.i].push(e.j);
    adj[e.j].push(e.i);
  }

  textAlign(CENTER, CENTER);
  textSize(13);
  noStroke();
  fill(0);

  for (let s = nReg; s < all.length; s++) {
    const p = all[s];
    const nbrIdxs = adj[s];
    if (nbrIdxs.length < 2) continue;

    // angles of neighbors around p
    let angles = nbrIdxs.map(j => Math.atan2(all[j].y - p.y, all[j].x - p.x));
    angles = angles.map(a => ((a % TWO_PI) + TWO_PI) % TWO_PI).sort((a,b) => a-b);

    for (let k = 0; k < angles.length; k++) {
      const a = angles[k];
      const b = angles[(k+1) % angles.length];
      let diff = b - a;
      if (diff <= 0) diff += TWO_PI;
      const bis = a + diff/2;
      const deg = Math.round(diff * 180 / Math.PI);

      const r = 24;
      const tx = p.x + r * Math.cos(bis);
      const ty = p.y + r * Math.sin(bis);
      text(deg + '°', tx, ty);
    }
  }
}

/* ---------- Interaction: Dragging ---------- */
function mousePressed() {
  if (!isMouseInCanvas()) return;

  const search = (arr, radius) => {
    let best = {idx: -1, d2: Infinity};
    for (let i=0; i<arr.length; i++) {
      const d2v = dist2(arr[i], {x: mouseX, y: mouseY});
      if (d2v < best.d2 && d2v <= radius*radius) best = {idx: i, d2: d2v};
    }
    return best.idx;
  };

  if (pointMode === 'regular') {
    const idx = search(regularPoints, 12);
    if (idx !== -1) dragging = {type: 'regular', index: idx};
  } else {
    const idx = search(steinerPoints, 12);
    if (idx !== -1) dragging = {type: 'steiner', index: idx};
  }
}
function mouseDragged() {
  if (!dragging || !isMouseInCanvas()) return;
  const clamp = (v, min, max) => Math.max(min, Math.min(max, v));
  const p = dragging.type === 'regular'
              ? regularPoints[dragging.index]
              : steinerPoints[dragging.index];
  p.x = clamp(mouseX, 6, width-6);
  p.y = clamp(mouseY, 6, height-6);
  computeMST();
}
function mouseReleased() {
  dragging = null;
}
function isMouseInCanvas() {
  return mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height;
}

/* ---------- UI Wiring ---------- */
function updateActiveControls() {
  // Regular buttons
  document.querySelectorAll('#regular-points-selector button').forEach(btn => btn.classList.remove('active'));
  document.getElementById('btn-regular-' + numRegularPoints).classList.add('active');

  // Steiner buttons
  document.querySelectorAll('#steiner-points-selector button').forEach(btn => btn.classList.remove('active'));
  document.getElementById('btn-steiner-' + numSteinerPoints).classList.add('active');

  // Radios
  document.getElementById('radio-regular').checked = (pointMode === 'regular');
  document.getElementById('radio-steiner').checked = (pointMode === 'steiner');

  // Checkbox
  document.getElementById('checkbox-show-angles').checked = showAngles;
}

function bindControls() {
  // Regular count buttons
  document.getElementById('btn-regular-3').addEventListener('click', () => {
    resetRegularPoints(3);
    recenterSteinerPoints();
    computeMST();
    updateActiveControls();
  });
  document.getElementById('btn-regular-4').addEventListener('click', () => {
    resetRegularPoints(4);
    recenterSteinerPoints();
    computeMST();
    updateActiveControls();
  });
  document.getElementById('btn-regular-5').addEventListener('click', () => {
    resetRegularPoints(5);
    recenterSteinerPoints();
    computeMST();
    updateActiveControls();
  });

  // Steiner count buttons
  document.getElementById('btn-steiner-0').addEventListener('click', () => {
    setSteinerCount(0);
    computeMST();
    updateActiveControls();
  });
  document.getElementById('btn-steiner-1').addEventListener('click', () => {
    setSteinerCount(1);
    computeMST();
    updateActiveControls();
  });
  document.getElementById('btn-steiner-2').addEventListener('click', () => {
    setSteinerCount(2);
    computeMST();
    updateActiveControls();
  });
  document.getElementById('btn-steiner-3').addEventListener('click', () => {
    setSteinerCount(3);
    computeMST();
    updateActiveControls();
  });

  // Point mode radios
  document.getElementById('radio-regular').addEventListener('change', () => {
    pointMode = 'regular';
    updateActiveControls();
  });
  document.getElementById('radio-steiner').addEventListener('change', () => {
    pointMode = 'steiner';
    updateActiveControls();
  });

  // Angles checkbox
  document.getElementById('checkbox-show-angles').addEventListener('change', (e) => {
    showAngles = e.target.checked;
    updateActiveControls();
  });
}

bindControls();
</script>
</body>
</html>
