<!-- p5.js CDN -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.7.0/p5.min.js"></script>
<script>
  // Global state
  const state = {
    groups: ["W","X","Y","Z"],
    means: [0,0,0,0],
    sds:   [1,1,1,1],
    n: 10,
    k: 4,
    gm: 0,
    msBetween: 0,
    msWithin: 0,
    f: 0
  };

  // Hook sliders to update
  const sliderIds = [
    "slider-mean-w","slider-mean-x","slider-mean-y","slider-mean-z",
    "slider-sd-w","slider-sd-x","slider-sd-y","slider-sd-z"
  ];

  function fmt1(x){ return Number(x).toFixed(1); }
  function fmtF(x){
    if (!isFinite(x)) return "∞";
    return (Math.abs(x) < 0.1 ? x.toFixed(5) : x.toFixed(2));
  }

  function readAndCompute(){
    // Read sliders and update span values
    const mIds = ["w","x","y","z"].map(c => "slider-mean-"+c);
    const sIds = ["w","x","y","z"].map(c => "slider-sd-"+c);

    state.means = mIds.map((id,i) => {
      const v = parseFloat(document.getElementById(id).value);
      document.getElementById("value-mean-"+["w","x","y","z"][i]).textContent = fmt1(v);
      return v;
    });
    state.sds = sIds.map((id,i) => {
      const v = parseFloat(document.getElementById(id).value);
      document.getElementById("value-sd-"+["w","x","y","z"][i]).textContent = fmt1(v);
      return v;
    });

    // ANOVA computations
    const n = state.n;
    const k = state.k;
    const means = state.means;
    const sds = state.sds;

    const gm = means.reduce((a,b)=>a+b,0)/k;
    let ssBetween = 0;
    for (let i=0;i<k;i++){
      const d = means[i] - gm;
      ssBetween += n * d * d;
    }
    const msBetween = ssBetween / (k - 1);

    let ssWithin = 0;
    for (let i=0;i<k;i++){
      ssWithin += (n - 1) * sds[i]*sds[i];
    }
    const msWithin = ssWithin / (n*k - k); // k*(n-1)

    const f = msWithin === 0 ? Infinity : (msBetween / msWithin);

    state.gm = gm;
    state.msBetween = msBetween;
    state.msWithin = msWithin;
    state.f = f;
  }

  function updateAndRedraw(){
    readAndCompute();
    if (typeof window.redraw === "function") window.redraw();
  }

  // Attach events after DOM content loads
  window.addEventListener("DOMContentLoaded", () => {
    sliderIds.forEach(id => {
      const el = document.getElementById(id);
      el.addEventListener("input", updateAndRedraw);
    });
  });

  // p5 sketch (global mode, parented to visualization panel)
  let topPlot = null;
  let bottomPlot = null;

  function setup(){
    const panel = document.getElementById("visualization-panel");
    const w = panel.clientWidth;
    const h = panel.clientHeight;
    const cnv = createCanvas(w, h);
    cnv.parent("visualization-panel");
    pixelDensity(1);
    textFont('Helvetica, Arial, sans-serif');
    noLoop();
    computeLayout();
    updateAndRedraw(); // initial render
  }

  function windowResized(){
    const panel = document.getElementById("visualization-panel");
    resizeCanvas(panel.clientWidth, panel.clientHeight);
    computeLayout();
    redraw();
  }

  function computeLayout(){
    const pad = 20;
    const left = 60;
    const right = width - 30;
    const totalH = height - 2*pad;
    const topH = Math.floor(totalH * 0.54);
    const bottomH = totalH - topH - 10;

    topPlot = {
      x: left,
      y: pad + 28, // leave space for F text
      w: right - left,
      h: topH - 28
    };
    bottomPlot = {
      x: left,
      y: pad + topH + 10,
      w: right - left,
      h: bottomH
    };
  }

  // Utility mappers for top and bottom plots
  function yDataToPix(v){
    // Map data range [-10,10] to pixel y in topPlot
    const t = topPlot;
    return map(v, 10, -10, t.y, t.y + t.h, true);
  }
  function xBand(i, k){
    const t = topPlot;
    const step = t.w / k;
    const cx = t.x + step * (i + 0.5);
    const bw = Math.min(56, step * 0.55);
    return {cx, bw};
  }

  function drawAxesTop(){
    const t = topPlot;
    stroke(0);
    strokeWeight(1);
    // y-axis
    line(t.x, t.y, t.x, t.y + t.h);
    // y ticks
    const ticks = [-10,-5,0,5,10];
    textSize(12);
    textAlign(RIGHT, CENTER);
    noFill();
    ticks.forEach(val=>{
      const yy = yDataToPix(val);
      // tick mark
      line(t.x-4, yy, t.x, yy);
      fill(30);
      noStroke();
      text(val, t.x - 8, yy);
      noFill();
      stroke(230);
      line(t.x, yy, t.x + t.w, yy); // faint grid
      stroke(0);
    });

    // x labels
    noStroke();
    textAlign(CENTER, TOP);
    fill(30);
    for (let i=0;i<state.k;i++){
      const {cx} = xBand(i, state.k);
      textStyle(ITALIC);
      text(state.groups[i], cx, t.y + t.h + 6);
    }
    textStyle(NORMAL);
  }

  function drawGroupBars(){
    const k = state.k;
    for (let i=0;i<k;i++){
      const {cx, bw} = xBand(i, k);
      const mean = state.means[i];
      const sd = state.sds[i];

      const topY = yDataToPix(mean + sd);
      const botY = yDataToPix(mean - sd);
      const leftX = cx - bw/2;
      const h = Math.max(2, botY - topY);

      noStroke();
      fill('#F57F29');
      rect(leftX, topY, bw, h, 4);

      // mean line within bar
      stroke(0);
      strokeWeight(2);
      const my = yDataToPix(mean);
      line(leftX+3, my, leftX + bw - 3, my);
    }
  }

  function drawGrandMean(){
    const t = topPlot;
    const y = yDataToPix(state.gm);
    push();
    stroke(0);
    strokeWeight(2);
    drawingContext.setLineDash([8,6]);
    line(t.x, y, t.x + t.w, y);
    drawingContext.setLineDash([]);
    pop();
  }

  function drawFratioText(){
    const t = topPlot;
    noStroke();
    fill(40);
    textAlign(CENTER, BOTTOM);
    textStyle(ITALIC);
    textSize(16);
    text("F ratio = ", t.x + t.w/2, t.y - 24);

    textStyle(NORMAL);
    textSize(16);
    text(fmtF(state.f), t.x + t.w/2, t.y - 6);
  }

  function drawBottomChart(){
    const b = bottomPlot;
    const axisMax = 200; // fixed domain
    const pxPerUnit = b.w / axisMax;

    // Title
    noStroke();
    textAlign(CENTER, TOP);
    textStyle(BOLD);
    fill(35);
    textSize(16);
    text("Analysis of the F-Ratio", b.x + b.w/2, b.y + 2);
    textStyle(NORMAL);

    const chartTop = b.y + 28;
    const chartH = b.h - 34;

    // x-axis line
    stroke(0);
    strokeWeight(1);
    line(b.x, chartTop + chartH - 1, b.x + b.w, chartTop + chartH - 1);

    // x ticks and labels
    const ticks = [0,50,100,150,200];
    textSize(12);
    textAlign(CENTER, TOP);
    noStroke();
    fill(30);
    ticks.forEach(tk=>{
      const xx = b.x + tk * pxPerUnit;
      stroke(200);
      line(xx, chartTop, xx, chartTop + chartH - 1);
      noStroke();
      text(tk, xx, chartTop + chartH + 4);
    });

    // Bars
    const rows = [
      {name: "MS Between", val: state.msBetween, color: "#4691A5"},
      {name: "MS Within",  val: state.msWithin,  color: "#D49D3C"},
      {name: "F-Ratio",    val: state.f,         color: "#8C3A8D"}
    ];

    const barGap = 10;
    const barH = Math.min(22, (chartH - barGap*2)/3);
    let y = chartTop + 4;

    rows.forEach(r=>{
      const w = Math.max(0, Math.min(r.val, axisMax)) * pxPerUnit;

      noStroke();
      fill(r.color);
      rect(b.x, y, w, barH, 4);

      // small color swatch at far left (legend look)
      stroke(r.color);
      strokeWeight(4);
      line(b.x - 14, y + barH/2 - 8, b.x - 14, y + barH/2 + 8);

      // Label to the right of bars
      noStroke();
      fill(40);
      textAlign(LEFT, CENTER);
      textSize(13);
      text(r.name, b.x + b.w + 10, y + barH/2);

      y += barH + barGap;
    });
  }

  function draw(){
    background(255);

    // Top plot
    drawAxesTop();
    drawGrandMean();
    drawGroupBars();
    drawFratioText();

    // Bottom chart
    drawBottomChart();
  }

</script>
</body>
</html>
