      <label for="slider-sigma1">scale σ<sub>1</sub></label>
      <input id="slider-sigma1" type="range" min="0.1" max="0.5" step="0.01" value="0.198">
      <input id="display-sigma1" type="text" readonly>
    </div>
    <div class="slider-row">
      <label for="slider-xi1">shape ξ<sub>1</sub></label>
      <input id="slider-xi1" type="range" min="-0.5" max="0.5" step="0.01" value="-0.05">
      <input id="display-xi1" type="text" readonly>
    </div>

    <h3 style="color: red;">GEV distribution 2</h3>
    <div class="slider-row">
      <label for="slider-mu2">location μ<sub>2</sub></label>
