      <input id="slider-sigma2" type="range" min="0.1" max="0.5" step="0.01" value="0.3">
      <input id="display-sigma2" type="text" readonly>
    </div>
    <div class="slider-row">
      <label for="slider-xi2">shape ξ<sub>2</sub></label>
      <input id="slider-xi2" type="range" min="-0.5" max="0.5" step="0.01" value="-0.05">
