<script>
  // Helper formatting
  function fmt(v) {
    // Display with up to 3 decimals, trim trailing zeros
    return parseFloat(v).toFixed(3).replace(/\.?0+$/,'');
  }

  // GEV PDF
  function gevPdf(x, mu, sigma, xi) {
    if (sigma <= 0) return 0;
    const eps = 1e-8;
    if (Math.abs(xi) < eps) {
      // Gumbel case
      const z = (x - mu) / sigma;
      const t = Math.exp(-z);
      return (1 / sigma) * t * Math.exp(-t);
    } else {
      const z = (x - mu) / sigma;
      const a = 1 + xi * z;
      if (a <= 0) return 0; // outside support
      const t = Math.pow(a, -1 / xi);
      return (1 / sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
    }
  }

  // Return level y_p given period p
  function gevReturnLevel(p, mu, sigma, xi) {
    // p must be > 1
    const eps = 1e-8;
    const q = -Math.log(1 - 1 / p); // positive
    if (Math.abs(xi) < eps) {
      return mu - sigma * Math.log(Math.log(p / (p - 1))); // same as mu - sigma*log(-log(1-1/p))
    } else {
      const power = Math.pow(q, -xi);
      return mu - (sigma / xi) * (1 - power);
    }
  }

  // Generate arrays
  function linspace(start, end, n) {
    const arr = new Array(n);
    const step = (end - start) / (n - 1);
    for (let i = 0; i < n; i++) arr[i] = start + i * step;
    return arr;
  }

  // Read current slider values
  function readParams() {
    return {
      mu1: parseFloat(document.getElementById('slider-mu1').value),
      sigma1: parseFloat(document.getElementById('slider-sigma1').value),
      xi1: parseFloat(document.getElementById('slider-xi1').value),
      mu2: parseFloat(document.getElementById('slider-mu2').value),
      sigma2: parseFloat(document.getElementById('slider-sigma2').value),
      xi2: parseFloat(document.getElementById('slider-xi2').value),
      pmax: parseFloat(document.getElementById('slider-pmax').value)
    };
  }

  // Update readouts next to sliders
  function updateReadouts(values) {
    document.getElementById('display-mu1').value = fmt(values.mu1);
    document.getElementById('display-sigma1').value = fmt(values.sigma1);
    document.getElementById('display-xi1').value = fmt(values.xi1);
    document.getElementById('display-mu2').value = fmt(values.mu2);
    document.getElementById('display-sigma2').value = fmt(values.sigma2);
    document.getElementById('display-xi2').value = fmt(values.xi2);
    document.getElementById('display-pmax').value = fmt(values.pmax);
  }

  // Draw or update plots
  function update() {
    const v = readParams();
    updateReadouts(v);

    // ---------- PDF Plot ----------
    const xMin = 2.5, xMax = 7.0;
    const x = linspace(xMin, xMax, 450);
    const y1 = x.map(xx => gevPdf(xx, v.mu1, v.sigma1, v.xi1));
    const y2 = x.map(xx => gevPdf(xx, v.mu2, v.sigma2, v.xi2));

    const pdfData = [
      {
        x: x, y: y1, type: 'scatter', mode: 'lines',
        line: {color: 'blue', width: 2},
        fill: 'tozeroy', fillcolor: 'rgba(0,0,255,0.2)',
        name: 'GEV 1'
      },
      {
        x: x, y: y2, type: 'scatter', mode: 'lines',
        line: {color: 'red', width: 2},
        fill: 'tozeroy', fillcolor: 'rgba(255,0,0,0.2)',
        name: 'GEV 2'
      }
    ];

    const pdfLayout = {
      title: {text: 'PDFs of GEV Distributions 1 and 2', font: {size: 16}},
      margin: {l: 40, r: 10, t: 40, b: 30},
      xaxis: {range: [xMin, xMax], fixedrange: true, zeroline: false},
      yaxis: {automargin: true, rangemode: 'tozero'},
      legend: {orientation: 'h', x: 0.5, xanchor: 'center', y: 1.02}
    };

    Plotly.react('plot-pdf', pdfData, pdfLayout, {responsive: true, displayModeBar: false});

    // ---------- Return Level Plot ----------
    const pmin = 1.01;
    const pmax = Math.max(10, v.pmax); // ensure at least 10 for visibility
    const p = linspace(pmin, pmax, 250);
    const rl1 = p.map(pp => gevReturnLevel(pp, v.mu1, v.sigma1, v.xi1));
    const rl2 = p.map(pp => gevReturnLevel(pp, v.mu2, v.sigma2, v.xi2));

    const rlData = [
      {x: p, y: rl1, type: 'scatter', mode: 'lines', line: {color: 'blue', width: 2}, name: 'GEV 1'},
      {x: p, y: rl2, type: 'scatter', mode: 'lines', line: {color: 'red', width: 2}, name: 'GEV 2'}
    ];

    const rlLayout = {
      margin: {l: 55, r: 10, t: 10, b: 40},
      xaxis: {title: 'Return Period (years)', range: [0, v.pmax], zeroline: false},
      yaxis: {title: 'Maximum Sea Level (meters)', automargin: true, rangemode: 'tozero'},
      showlegend: false
    };

    Plotly.react('plot-return-level', rlData, rlLayout, {responsive: true, displayModeBar: false});
  }

  // Attach events
  function attachEvents() {
    const ids = [
      'slider-mu1','slider-sigma1','slider-xi1',
      'slider-mu2','slider-sigma2','slider-xi2',
      'slider-pmax'
    ];
    ids.forEach(id => {
      document.getElementById(id).addEventListener('input', update);
    });
  }

  // Initialize on load
  window.addEventListener('DOMContentLoaded', () => {
    attachEvents();
    update();
  });
</script>
</body>
</html>
