<script>
(function(){
  // Global state
  const state = {
    signal: 'triangle',
    a: 2.0,
    b: 4.0,
    T: 2.0,
    tmin: -10,
    tmax: 20
  };

  // UI elements
  const btns = {
    rampdown: document.getElementById('btn-rampdown'),
    tophat: document.getElementById('btn-tophat'),
    stairstep: document.getElementById('btn-stairstep'),
    triangle: document.getElementById('btn-triangle'),
    uh: document.getElementById('btn-uh')
  };
  const sliderA = document.getElementById('slider-a');
  const sliderB = document.getElementById('slider-b');
  const sliderT = document.getElementById('slider-t');
  const valA = document.getElementById('slider-a-value');
  const valB = document.getElementById('slider-b-value');
  const valT = document.getElementById('slider-t-value');

  const checkX = document.getElementById('check-x');
  const checkY = document.getElementById('check-y');
  const checkZ = document.getElementById('check-z');
  const checkW = document.getElementById('check-w');

  function fmt(x){ return (Math.round(x*10)/10).toFixed(1); }

  function setActiveSignal(id){
    Object.values(btns).forEach(b => b.classList.remove('active'));
    btns[id].classList.add('active');
    state.signal = id;
  }

  // Base signal control points (without zero padding)
  function basePoints(name){
    switch(name){
      case 'triangle':
        return [
          [0,0],[3,4],[6,0]
        ];
      case 'tophat':
        return [
          [1,0],[1,4],[5,4],[5,0]
        ];
      case 'stairstep':
        return [
          [1,0],[1,1],[2,1],[2,4],[4,4],[4,1],[5,1],[5,0]
        ];
      case 'rampdown':
        return [
          [0,4],[6,0]
        ];
      case 'uh':
        return [
          [1,0],[1,4],[1.5,4],[1.5,1],[2.5,1],[2.5,4],[3,4],[3,0],
          [null,null],
          [4,0],[4,4],[4.5,4],[4.5,2],[5.5,2],[5.5,4],[6,4],[6,0]
        ];
      default:
        return [[0,0]];
    }
  }

  // Add zero padding outside the shape to ensure x(t)=0 outside its support
  function addZeroPadding(points, tmin, tmax){
    // Determine first and last finite x
    let firstX = null, lastX = null;
    for(const p of points){
      if(p[0] !== null && p[1] !== null){
        if(firstX === null) firstX = p[0];
        lastX = p[0];
      }
    }
    const result = [];
    result.push([tmin, 0]);
    if(firstX === null){
      result.push([tmax, 0]);
      return result;
    }
    // horizontal zero until firstX
    if(firstX > tmin) result.push([firstX, 0]);
    // main points
    for(const p of points) result.push(p);
    // ensure drop to zero at lastX then continue to tmax
    if(lastX !== null) result.push([lastX, 0]);
    result.push([tmax, 0]);
    return result;
  }

  // Transform helper: map t -> f(t)
  function transform(points, mapFn){
    const out = [];
    for(const p of points){
      if(p[0] === null || p[1] === null){
        out.push([null,null]);
      } else {
        out.push([mapFn(p[0]), p[1]]);
      }
    }
    return out;
  }

  // Convert [ [x,y], ... ] to Plotly x[], y[]
  function toXY(points){
    const x = [], y = [];
    for(const p of points){
      if(p[0] === null){
        x.push(null); y.push(null);
      } else {
        x.push(p[0]); y.push(p[1]);
      }
    }
    return {x,y};
  }

  // Marker shapes and annotations for b and T
  function markers(b, T){
    const dotR = 0.18;
    const shapes = [
      {type:'circle', xref:'x', yref:'y', x0: T - dotR, x1: T + dotR, y0: -dotR, y1: dotR, fillcolor:'#d62728', line:{width:0}},
      {type:'circle', xref:'x', yref:'y', x0: b - dotR, x1: b + dotR, y0: -dotR, y1: dotR, fillcolor:'olivedrab', line:{width:0}}
    ];
    const annotations = [
      {xref:'x', yref:'y', x:T, y:0.35, text:'T', showarrow:false, font:{color:'#d62728', size:14}},
      {xref:'x', yref:'y', x:b, y:0.35, text:'b', showarrow:false, font:{color:'olivedrab', size:14}}
    ];
    return {shapes, annotations};
  }

  // Layouts
  function topLayout(b, T){
    const m = markers(b,T);
    return {
      title: 'signal',
      xaxis: {title: 'time (s)', range:[state.tmin, state.tmax], zeroline:true},
      yaxis: {range:[-0.5, 4.5], zeroline:true},
      showlegend: true,
      legend: {x:0.01, y:0.75},
      margin: {l:50, r:20, t:30, b:35},
      shapes: m.shapes,
      annotations: m.annotations
    };
  }
  function bottomLayout(b, T){
    const m = markers(b,T);
    return {
      xaxis: {title: 'time (s)', range:[state.tmin, state.tmax], zeroline:true},
      yaxis: {range:[-0.5, 4.5], zeroline:true},
      showlegend: true,
      legend: {x:0.01, y:0.98},
      margin: {l:50, r:20, t:10, b:35},
      shapes: m.shapes,
      annotations: m.annotations
    };
  }

  // Update visibility from checkboxes without recomputing traces
  function restyleVisibility(){
    const visX = checkX.checked ? true : 'legendonly';
    const visY = checkY.checked ? true : 'legendonly';
    const visZ = checkZ.checked ? true : 'legendonly';
    const visW = checkW.checked ? true : 'legendonly';
    Plotly.restyle('plot-bottom', {visible: visX}, [0]);
    Plotly.restyle('plot-bottom', {visible: visY}, [1]);
    Plotly.restyle('plot-bottom', {visible: visZ}, [2]);
    Plotly.restyle('plot-bottom', {visible: visW}, [3]);
  }

  // Central plot update
  function updatePlots(){
    // Build base and transformed polylines
    const base = addZeroPadding(basePoints(state.signal), state.tmin, state.tmax);
    const x_xy = toXY(base);
    const y_xy = toXY(transform(base, t => (t + state.b)/state.a));
    const z_xy = toXY(transform(base, t => t + state.b));
    const w_xy = toXY(transform(base, t => t / state.a));

    // Top trace (x(t))
    const topTrace = {
      type: 'scatter',
      mode: 'lines',
      x: x_xy.x, y: x_xy.y,
      line: {color: 'rgb(70,130,180)', width: 2.5},
      name: '\\(x(t)\\)'
    };
    const topL = topLayout(state.b, state.T);

    // Bottom traces
    const visX = checkX.checked ? true : 'legendonly';
    const visY = checkY.checked ? true : 'legendonly';
    const visZ = checkZ.checked ? true : 'legendonly';
    const visW = checkW.checked ? true : 'legendonly';

    const bottomData = [
      {type:'scatter', mode:'lines', x:x_xy.x, y:x_xy.y, line:{color:'rgb(70,130,180)', width:2.5}, name:'\\(x(t)\\)', visible: visX},
      {type:'scatter', mode:'lines', x:y_xy.x, y:y_xy.y, line:{color:'red', width:2.5}, name:'\\(y(t) = x(at - b) = x(a(t-T))\\)', visible: visY},
      {type:'scatter', mode:'lines', x:z_xy.x, y:z_xy.y, line:{color:'olivedrab', width:2.5}, name:'\\(z(t) = x(t - b)\\)', visible: visZ},
      {type:'scatter', mode:'lines', x:w_xy.x, y:w_xy.y, line:{color:'saddlebrown', width:2.5}, name:'\\(w(t) = x(at)\\)', visible: visW}
    ];
    const bottomL = bottomLayout(state.b, state.T);

    if(!updatePlots._init){
      Plotly.newPlot('plot-top', [topTrace], topL, {displayModeBar:false, responsive:true});
      Plotly.newPlot('plot-bottom', bottomData, bottomL, {displayModeBar:false, responsive:true});
      updatePlots._init = true;
    } else {
      Plotly.react('plot-top', [topTrace], topL, {displayModeBar:false, responsive:true});
      Plotly.react('plot-bottom', bottomData, bottomL, {displayModeBar:false, responsive:true});
    }
    // Typeset MathJax (legend titles etc.)
    if(window.MathJax && MathJax.typesetPromise){
      MathJax.typesetPromise();
    }
  }

  // Sync UI displays
  function refreshDisplays(){
    valA.textContent = fmt(state.a);
    valB.textContent = fmt(state.b);
    valT.textContent = fmt(state.T);
    sliderA.value = state.a;
    sliderB.value = Math.max(sliderB.min, Math.min(sliderB.max, state.b));
    sliderT.value = state.T;
  }

  // Event listeners
  btns.rampdown.addEventListener('click', ()=>{ setActiveSignal('rampdown'); updatePlots(); });
  btns.tophat.addEventListener('click',   ()=>{ setActiveSignal('tophat');   updatePlots(); });
  btns.stairstep.addEventListener('click',()=>{ setActiveSignal('stairstep');updatePlots(); });
  btns.triangle.addEventListener('click', ()=>{ setActiveSignal('triangle'); updatePlots(); });
  btns.uh.addEventListener('click',       ()=>{ setActiveSignal('uh');       updatePlots(); });

  sliderA.addEventListener('input', () => {
    state.a = parseFloat(sliderA.value);
    state.T = state.a !== 0 ? state.b / state.a : 0;
    refreshDisplays();
    sliderT.value = state.T;
    updatePlots();
  });
  sliderB.addEventListener('input', () => {
    state.b = parseFloat(sliderB.value);
    state.T = state.a !== 0 ? state.b / state.a : 0;
    refreshDisplays();
    sliderT.value = state.T;
    updatePlots();
  });
  sliderT.addEventListener('input', () => {
    state.T = parseFloat(sliderT.value);
    state.b = state.a * state.T;
    // Keep b slider within its bounds but keep T as chosen
    const bClamped = Math.max(parseFloat(sliderB.min), Math.min(parseFloat(sliderB.max), state.b));
    sliderB.value = bClamped;
    state.b = parseFloat(sliderB.value);
    refreshDisplays();
    updatePlots();
  });

  checkX.addEventListener('change', restyleVisibility);
  checkY.addEventListener('change', restyleVisibility);
  checkZ.addEventListener('change', restyleVisibility);
  checkW.addEventListener('change', restyleVisibility);

  // Initial render
  document.addEventListener('DOMContentLoaded', () => {
    // Ensure default states as specified
    sliderA.value = state.a; sliderB.value = state.b; sliderT.value = state.T;
    valA.textContent = fmt(state.a); valB.textContent = fmt(state.b); valT.textContent = fmt(state.T);
    checkX.checked = true; checkY.checked = true; checkZ.checked = true; checkW.checked = false;
    updatePlots();
  });
})();
</script>
</body>
