<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
<script>
(() => {
    // Scene globals
    const canvas = document.getElementById('main-canvas');
    const renderer = new THREE.WebGLRenderer({ canvas, antialias: true, alpha: false });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf0f0f0);

    // Camera and controls
    const camera = new THREE.PerspectiveCamera(50, 1, 0.1, 200);
    camera.position.set(7, -10, 12);
    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.07;
    controls.target.set(3, 2.2, 0.0);
    controls.update();

    // Lights
    const hemi = new THREE.HemisphereLight(0xffffff, 0x444444, 0.9);
    hemi.position.set(0, 1, 0);
    scene.add(hemi);
    const dir = new THREE.DirectionalLight(0xffffff, 0.8);
    dir.position.set(5, -4, 8);
    scene.add(dir);

    // Main group to rebuild
    let mainGroup = new THREE.Group();
    scene.add(mainGroup);

    // UI elements
    const radiosX = Array.from(document.querySelectorAll('input[name="unitsX"]'));
    const radiosY = Array.from(document.querySelectorAll('input[name="unitsY"]'));
    const sliderTrim = document.getElementById('slider-trim');
    const labelTrim = document.getElementById('label-trim-value');
    const checkboxDouble = document.getElementById('checkbox-double');
    const checkboxSphere = document.getElementById('checkbox-sphere');
    const checkboxTriangles = document.getElementById('checkbox-triangles');
    const btnReset = document.getElementById('btn-reset-view');

    // Initialize slider attributes
    sliderTrim.min = "0.65";
    sliderTrim.max = "0.9";
    sliderTrim.step = "0.001";
    sliderTrim.value = "0.8";
    labelTrim.textContent = Number(sliderTrim.value).toFixed(3);

    // Utility: Get selected value of a radio group by name
    function getSelectedValue(name) {
        const el = document.querySelector(`input[name="${name}"]:checked`);
        return el ? parseInt(el.value, 10) : 0;
    }

    // Geometry builder for a single curved triangle (local space)
    function buildCurvedTriangleGeometry(trim, centerColorHex, edgeColorHex, segmentsPerEdge = 48) {
        // Base equilateral triangle centered at origin, vertex radius:
        const Rv = 1.0; // distance from center to vertex
        const v0 = new THREE.Vector3(Rv, 0, 0);
        const v1 = new THREE.Vector3(Rv * Math.cos(2 * Math.PI / 3), Rv * Math.sin(2 * Math.PI / 3), 0);
        const v2 = new THREE.Vector3(Rv * Math.cos(4 * Math.PI / 3), Rv * Math.sin(4 * Math.PI / 3), 0);
        const verts = [v0, v1, v2];

        // Concavity amount mapped from trim slider (lower trim -> deeper concave)
        const bulge = (0.96 - trim) * 1.35; // tuned empirically

        // Quadratic bezier edge points excluding end vertex (to avoid duplicates)
        function bezierPoints(a, b, concavity, segments) {
            const mid = a.clone().add(b).multiplyScalar(0.5);
            const dirToCenter = mid.clone().multiplyScalar(-1).normalize();
            const control = mid.clone().add(dirToCenter.multiplyScalar(concavity));
            const pts = [];
            for (let i = 1; i <= segments; i++) {
                const t = i / (segments + 1);
                const mt = 1 - t;
                const p = new THREE.Vector3(
                    mt * mt * a.x + 2 * mt * t * control.x + t * t * b.x,
                    mt * mt * a.y + 2 * mt * t * control.y + t * t * b.y,
                    0
                );
                pts.push(p);
            }
            return pts;
        }

        // Build boundary loop (ordered)
        const boundary = [];
        for (let e = 0; e < 3; e++) {
            const a = verts[e];
            const b = verts[(e + 1) % 3];
            boundary.push(a.clone());
            const midPts = bezierPoints(a, b, bulge, segmentsPerEdge);
            boundary.push(...midPts);
        }

        // Slight bowl-like curvature for shading (center highest)
        const curvature = 0.18 * (trim - 0.65) / (0.25); // 0..~0.18
        const rMax = Rv;
        function zFromRadius(x, y) {
            const r = Math.sqrt(x * x + y * y);
            const t = Math.min(1, r / rMax);
            // Raise a little towards center
            return curvature * (1 - t * t);
        }

        // Build triangle fan indices (center to boundary segments)
        const positions = [];
        const colors = [];
        const center = new THREE.Vector3(0, 0, zFromRadius(0, 0));
        const centerColor = new THREE.Color(centerColorHex);
        const edgeColor = new THREE.Color(edgeColorHex);

        // push center once
        positions.push(center.x, center.y, center.z);
        colors.push(centerColor.r, centerColor.g, centerColor.b);

        // fan vertices along boundary (also store for outline)
        const outlinePositions = [];
        const outline3 = [];

        for (let i = 0; i < boundary.length; i++) {
            const p = boundary[i];
            const z = zFromRadius(p.x, p.y);
            positions.push(p.x, p.y, z);
            outlinePositions.push(p.x, p.y, z);
            outline3.push(new THREE.Vector3(p.x, p.y, z));

            // color blend by distance
            const r = Math.min(1, Math.sqrt(p.x * p.x + p.y * p.y) / rMax);
            const c = centerColor.clone().lerp(edgeColor, r);
            colors.push(c.r, c.g, c.b);
        }

        // Build indices for triangle fan centered at vertex 0 (center)
        const indices = [];
        const N = boundary.length;
        for (let i = 1; i <= N; i++) {
            // triangle: center(0), i, i+1 (wrap)
            const a = 0;
            const b = i;
            const c = i + 1 > N ? 1 : i + 1;
            indices.push(a, b, c);
        }

        // BufferGeometry (mesh)
        const geom = new THREE.BufferGeometry();
        geom.setIndex(indices);
        geom.setAttribute('position', new THREE.Float32BufferAttribute(positions, 3));
        geom.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
        geom.computeVertexNormals();

        // Outline as LineLoop (only boundary vertices, no center)
        const outlineGeom = new THREE.BufferGeometry();
        outlineGeom.setAttribute('position', new THREE.Float32BufferAttribute(outlinePositions, 3));

        // Flat underlying triangle (planar)
        const flatTriGeom = new THREE.BufferGeometry();
        const flatPos = new Float32Array([
            v0.x, v0.y, 0,
            v1.x, v1.y, 0,
            v2.x, v2.y, 0
        ]);
        flatTriGeom.setAttribute('position', new THREE.BufferAttribute(flatPos, 3));
        flatTriGeom.setIndex([0,1,2]);
        flatTriGeom.computeVertexNormals();

        return {
            vertexRadius: Rv,
            meshGeometry: geom,
            outlineGeometry: outlineGeom,
            flatGeometry: flatTriGeom,
            rawVertices: [v0.clone(), v1.clone(), v2.clone()] // for reference points
        };
    }

    // Materials reused
    const outlineMat = new THREE.LineBasicMaterial({ color: 0x000000 });
    const meshMatPrimary = new THREE.MeshStandardMaterial({
        vertexColors: true, roughness: 0.6, metalness: 0.05, side: THREE.DoubleSide
    });
    const meshMatSecondary = new THREE.MeshStandardMaterial({
        vertexColors: true, roughness: 0.6, metalness: 0.05, side: THREE.DoubleSide
    });
    const triMat = new THREE.MeshBasicMaterial({
        color: 0x800080, transparent: true, opacity: 0.38, side: THREE.DoubleSide
    });
    const pointMat = new THREE.PointsMaterial({
        color: 0x000000, size: 5, sizeAttenuation: false
    });

    // Update scene based on UI
    function updateScene() {
        // Clear and rebuild main group
        scene.remove(mainGroup);
        mainGroup.traverse(o => {
            if (o.geometry) o.geometry.dispose();
            if (o.material) {
                if (Array.isArray(o.material)) o.material.forEach(m => m.dispose());
                else o.material.dispose();
            }
        });
        mainGroup = new THREE.Group();
        scene.add(mainGroup);

        const unitsX = getSelectedValue('unitsX');
        const unitsY = getSelectedValue('unitsY');
        const nx = unitsX + 1; // columns
        const ny = unitsY + 1; // rows
        const trim = parseFloat(sliderTrim.value);
        labelTrim.textContent = trim.toFixed(3);

        // Build base geometries
        const primaryGeom = buildCurvedTriangleGeometry(trim, 0xffc0cb, 0x904090, 40);
        const secondaryGeom = buildCurvedTriangleGeometry(trim, 0x80ffff, 0x008080, 40);

        // Lattice basis vectors (triangular grid)
        const L = 2.2; // grid step
        const b1 = new THREE.Vector3(L, 0, 0);
        const b2 = new THREE.Vector3(L * 0.5, L * Math.sqrt(3) * 0.5, 0);

        const shapesGroup = new THREE.Group();
        mainGroup.add(shapesGroup);

        // Optionally show planar triangles
        const showTriangles = checkboxTriangles.checked;
        const triGroup = new THREE.Group();
        if (showTriangles) mainGroup.add(triGroup);

        // Helper to add one tile (with geometry set)
        function addTile(geomPack, material, pos, rotationZ, addTriangles) {
            const mesh = new THREE.Mesh(geomPack.meshGeometry, material);
            mesh.position.copy(pos);
            mesh.rotation.z = rotationZ;
            shapesGroup.add(mesh);

            const outline = new THREE.LineLoop(geomPack.outlineGeometry, outlineMat);
            outline.position.copy(pos);
            outline.rotation.z = rotationZ;
            shapesGroup.add(outline);

            if (addTriangles) {
                const tmesh = new THREE.Mesh(geomPack.flatGeometry, triMat);
                tmesh.position.copy(pos);
                tmesh.rotation.z = rotationZ;
                triGroup.add(tmesh);

                // Points at triangle vertices
                const ptsGeom = new THREE.BufferGeometry();
                const v = geomPack.rawVertices.map(v => {
                    const p = v.clone().applyAxisAngle(new THREE.Vector3(0,0,1), rotationZ).add(pos);
                    return [p.x, p.y, p.z];
                }).flat();
                ptsGeom.setAttribute('position', new THREE.Float32BufferAttribute(new Float32Array(v), 3));
                const pts = new THREE.Points(ptsGeom, pointMat);
                triGroup.add(pts);
            }
        }

        // Primary lattice
        for (let j = 0; j < ny; j++) {
            for (let i = 0; i < nx; i++) {
                const pos = b1.clone().multiplyScalar(i).add(b2.clone().multiplyScalar(j));
                const rot = ((i + j) % 2 === 0) ? 0 : Math.PI;
                addTile(primaryGeom, meshMatPrimary, pos, rot, showTriangles);
            }
        }

        // Double lattice (interwoven, offset + rotated 180° relative)
        if (checkboxDouble.checked) {
            const offset = b1.clone().add(b2).multiplyScalar(0.5);
            for (let j = 0; j < ny; j++) {
                for (let i = 0; i < nx; i++) {
                    const pos = b1.clone().multiplyScalar(i).add(b2.clone().multiplyScalar(j)).add(offset);
                    const rot = ((i + j) % 2 === 0) ? Math.PI : 0;
                    addTile(secondaryGeom, meshMatSecondary, pos, rot, showTriangles);
                }
            }
        }

        // Reference sphere under the origin tile (tangent to z=0)
        if (checkboxSphere.checked) {
            const sphereRadius = 1.15;
            const sphereGeom = new THREE.SphereGeometry(sphereRadius, 48, 32);
            const sphereMat = new THREE.MeshPhongMaterial({ color: 0xdddddd, shininess: 80 });
            const sphere = new THREE.Mesh(sphereGeom, sphereMat);
            sphere.position.set(0, 0, -sphereRadius); // tangent at (0,0,0)
            mainGroup.add(sphere);
        }

        // Recenter camera target roughly to middle of lattice for convenience
        const mid = b1.clone().multiplyScalar((nx - 1) * 0.5).add(b2.clone().multiplyScalar((ny - 1) * 0.5));
        controls.target.copy(new THREE.Vector3(mid.x, mid.y, 0));
        controls.update();

        render();
    }

    // Event hooks
    [...radiosX, ...radiosY].forEach(r => r.addEventListener('change', updateScene));
    sliderTrim.addEventListener('input', updateScene);
    checkboxDouble.addEventListener('change', updateScene);
    checkboxSphere.addEventListener('change', updateScene);
    checkboxTriangles.addEventListener('change', updateScene);

    // Reset view button
    btnReset.addEventListener('click', () => {
        controls.reset();
    });

    // Handle resize
    function onResize() {
        const container = document.getElementById('canvas-container');
        const width = container.clientWidth;
        const height = container.clientHeight;
        renderer.setSize(width, height, false);
        camera.aspect = width / height;
        camera.updateProjectionMatrix();
        render();
    }
    window.addEventListener('resize', onResize);

    // Initial layout sizing
    onResize();

    // Animation loop (interactive via OrbitControls)
    function animate() {
        requestAnimationFrame(animate);
        controls.update();
        render();
    }
    function render() {
        renderer.render(scene, camera);
    }

    // Build initial scene
    updateScene();
    animate();
})();
</script>
</body>
</html>
