<body>
  <div id="main-container">
    <button id="info-button">+</button>
    <div id="description-panel" style="display: none;">
      <p>
        Interactive demo of time-encoding for analog signals. Use the sliders to set the
        visible time window. Choose a function (blue) and an encoding method (red):
        ASDM is drawn as a square wave produced by an asynchronous sigma–delta modulator,
        while IAF shows integrate-and-fire spike times. Any change recalculates and redraws
        the visualization.
      </p>
    </div>

    <div id="control-panel">
      <div class="control-row">
        <label for="slider-start-time">start time</label>
        <input type="range" id="slider-start-time" min="-5" max="5" step="0.1" value="-1.0">
      </div>
      <div class="control-row">
        <label for="slider-total-time">total time</label>
        <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
      </div>
      <div class="control-row">
        <label>function</label>
        <div id="function-buttons">
          <button id="btn-f">f</button>
          <button id="btn-g" class="active">g</button>
          <button id="btn-sin">sin</button>
          <button id="btn-cos">cos</button>
          <button id="btn-tan">tan</button>
        </div>
      </div>
      <div class="control-row">
        <label>encoding method</label>
        <div id="encoding-buttons">
          <button id="btn-asdm" class="active">ASDM</button>
          <button id="btn-iaf">IAF</button>
        </div>
      </div>
    </div>

    <div id="canvas-container"></div>
  </div>

<script>
  // Global state reflecting required initial values
  const state = {
    startTime: -1.0,
    totalTime: 2.2,
    func: 'g',        // 'f', 'g', 'sin', 'cos', 'tan'
    encoding: 'ASDM'  // 'ASDM', 'IAF'
  };

  // Info button toggles description panel
  document.getElementById('info-button').addEventListener('click', () => {
    const panel = document.getElementById('description-panel');
    panel.style.display = (panel.style.display === 'none' || panel.style.display === '') ? 'block' : 'none';
  });

  // Utility: manage active button styles in a group
  function setActiveInGroup(groupEl, buttonEl) {
    [...groupEl.querySelectorAll('button')].forEach(b => b.classList.remove('active'));
    buttonEl.classList.add('active');
  }

  // Attach listeners for function buttons
  const funcGroup = document.getElementById('function-buttons');
  funcGroup.addEventListener('click', (ev) => {
    if (ev.target.tagName !== 'BUTTON') return;
    const id = ev.target.id;
    const mapping = {
      'btn-f': 'f', 'btn-g': 'g', 'btn-sin': 'sin', 'btn-cos': 'cos', 'btn-tan': 'tan'
    };
    if (mapping[id]) {
      state.func = mapping[id];
      setActiveInGroup(funcGroup, ev.target);
      if (sketchP5) sketchP5.redraw();
    }
  });

  // Attach listeners for encoding buttons
  const encGroup = document.getElementById('encoding-buttons');
  encGroup.addEventListener('click', (ev) => {
    if (ev.target.tagName !== 'BUTTON') return;
    const id = ev.target.id;
    const mapping = { 'btn-asdm': 'ASDM', 'btn-iaf': 'IAF' };
    if (mapping[id]) {
      state.encoding = mapping[id];
      setActiveInGroup(encGroup, ev.target);
      if (sketchP5) sketchP5.redraw();
    }
  });

  // Sliders
  const sliderStart = document.getElementById('slider-start-time');
  const sliderTotal = document.getElementById('slider-total-time');
  sliderStart.addEventListener('input', () => {
    state.startTime = parseFloat(sliderStart.value);
    if (sketchP5) sketchP5.redraw();
  });
  sliderTotal.addEventListener('input', () => {
    state.totalTime = parseFloat(sliderTotal.value);
    if (sketchP5) sketchP5.redraw();
  });

  // p5 instance mode sketch
  let sketchP5 = null;
  const sketch = (p) => {
    let canvas;
    const margin = { top: 14, right: 20, bottom: 36, left: 54 };
    const yMin = -1.2, yMax = 1.2;

    function plotRect() {
      const w = p.width - margin.left - margin.right;
      const h = p.height - margin.top - margin.bottom;
      return { x: margin.left, y: margin.top, w, h };
    }

    function dataToPixel(t, y) {
      const rect = plotRect();
      const xPix = p.map(t, state.startTime, state.startTime + state.totalTime, rect.x, rect.x + rect.w);
      const yPix = p.map(y, yMin, yMax, rect.y + rect.h, rect.y);
      return { x: xPix, y: yPix };
    }

    function drawAxes() {
      const rect = plotRect();
      p.push();
      // Border around plotting area
      p.noFill();
      p.stroke(220);
      p.rect(rect.x, rect.y, rect.w, rect.h);

      // Axis lines (x=0, y=0) if visible
      p.stroke(180);
      p.strokeWeight(1);
      const y0 = dataToPixel(0, 0).y;
      if (y0 >= rect.y && y0 <= rect.y + rect.h) {
        p.line(rect.x, y0, rect.x + rect.w, y0);
      }
      const x0 = dataToPixel(0, 0).x;
      if (x0 >= rect.x && x0 <= rect.x + rect.w) {
        p.line(x0, rect.y, x0, rect.y + rect.h);
      }

      // Ticks every 0.5
      p.fill(100);
      p.textSize(11);
      p.noStroke();

      // Y ticks
      const yStart = Math.ceil(yMin / 0.5) * 0.5;
      for (let y = yStart; y <= yMax + 1e-9; y += 0.5) {
        const py = dataToPixel(state.startTime, y).y;
        p.stroke(210);
        p.line(rect.x, py, rect.x + rect.w, py); // light grid
        p.noStroke();
        if (Math.abs(y) < 1e-6) continue; // 0 labeled on x-axis instead
        p.textAlign(p.RIGHT, p.CENTER);
        p.text(niceNum(y), rect.x - 6, py);
      }

      // X ticks
      p.textAlign(p.CENTER, p.TOP);
      const xMin = state.startTime;
      const xMax = state.startTime + state.totalTime;
      const xStart = Math.ceil(xMin / 0.5) * 0.5;
      for (let t = xStart; t <= xMax + 1e-9; t += 0.5) {
        const px = dataToPixel(t, yMin).x;
        p.stroke(230);
        p.line(px, rect.y, px, rect.y + rect.h); // light grid
        p.noStroke();
        p.text(niceNum(t), px, rect.y + rect.h + 6);
      }

      // Label 0 on both axes if visible and not overlapping
      if (y0 >= rect.y && y0 <= rect.y + rect.h) {
        p.textAlign(p.RIGHT, p.TOP);
        p.text("0", rect.x - 6, y0 + 2);
      }
      if (x0 >= rect.x && x0 <= rect.x + rect.w) {
        p.textAlign(p.CENTER, p.TOP);
        p.text(niceNum(0), x0, rect.y + rect.h + 6);
      }
      p.pop();
    }

    function niceNum(v) {
      // Round to one decimal, avoid -0.0
      const s = (Math.round(v * 10) / 10).toFixed(1);
      return s === "-0.0" ? "0.0" : s;
    }

    function analogFunc(t) {
      const twoPI = Math.PI * 2;
      switch (state.func) {
        case 'f': return 0.5 * t;
        case 'g': return Math.sin(twoPI * t) + 0.3 * Math.cos(3 * twoPI * t);
        case 'sin': return Math.sin(twoPI * t);
        case 'cos': return Math.cos(twoPI * t);
        case 'tan': return Math.tan(Math.PI * t);
        default: return Math.sin(twoPI * t);
      }
    }

    function drawAnalog() {
      const rect = plotRect();
      p.push();
      // Clip to plotting rectangle
      const ctx = p.drawingContext;
      ctx.save();
      ctx.beginPath();
      ctx.rect(rect.x, rect.y, rect.w, rect.h);
      ctx.clip();

      const xMin = state.startTime;
      const xMax = state.startTime + state.totalTime;
      const steps = Math.max(600, Math.floor((xMax - xMin) * 600));
      const dt = (xMax - xMin) / steps;

      p.noFill();
      p.stroke(56, 100, 200);
      p.strokeWeight(2);

      let prev = null;
      for (let i = 0; i <= steps; i++) {
        const t = xMin + i * dt;
        let y = analogFunc(t);

        // Handle tan discontinuities: don't draw across large jumps or infinities
        let valid = Number.isFinite(y) && Math.abs(y) <= 5;
        if (state.func !== 'tan') valid = Number.isFinite(y);

        if (valid) {
          const pt = dataToPixel(t, y);
          if (prev && prev.valid) {
            if (Math.abs(y - prev.yVal) < 2.0) {
              p.line(prev.x, prev.y, pt.x, pt.y);
            }
          }
          prev = { x: pt.x, y: pt.y, yVal: y, valid: true };
        } else {
          prev = { valid: false };
        }
      }

      ctx.restore();
      p.pop();
    }

    function simulateASDM(xMin, xMax) {
      const B = 0.25, delta = 0.05;
      const flips = [];
      let t = xMin;
      let level = B;
      let integral = 0;
      const steps = Math.max(2000, Math.floor((xMax - xMin) * 4000));
      const dt = (xMax - xMin) / steps;

      while (t < xMax) {
        const y = analogFunc(t);
        integral += (y - level) * dt;
        if ((level === B && integral < -delta) || (level === -B && integral > delta)) {
          // approximate better flip time by linear interpolation of integral crossing
          const yNext = analogFunc(t + dt);
          const deriv = (yNext - level); // local slope of (y - level)
          // integral crosses delta at time tau where integral + deriv*(tau - t) * dt? Not precise.
          // Use simple step resolution; sufficient for visualization.
          flips.push(t);
          level = -level;
          integral = 0;
        }
        t += dt;
      }
      return { flips, B };
    }

    function drawASDM() {
      const rect = plotRect();
      const xMin = state.startTime, xMax = state.startTime + state.totalTime;
      const { flips, B } = simulateASDM(xMin, xMax);

      p.push();
      // Clip to plotting rectangle
      const ctx = p.drawingContext;
      ctx.save();
      ctx.beginPath();
      ctx.rect(rect.x, rect.y, rect.w, rect.h);
      ctx.clip();

      p.stroke(220, 30, 30);
      p.strokeWeight(2);
      p.noFill();

      let lastT = xMin;
      let level = B;
      const allTimes = flips.concat([xMax]);
      for (const t of allTimes) {
        // horizontal segment
        const p1 = dataToPixel(lastT, level);
        const p2 = dataToPixel(t, level);
        p.line(p1.x, p1.y, p2.x, p2.y);
        if (t !== xMax) {
          // vertical jump at flip
          const pTop = dataToPixel(t, level);
          level = -level;
          const pBot = dataToPixel(t, level);
          p.line(pTop.x, pTop.y, pBot.x, pBot.y);
          lastT = t;
        }
      }
      ctx.restore();
      p.pop();
    }

    function simulateIAF(xMin, xMax) {
      const theta = 0.05;
      let integral = 0;
      let t = xMin;
      const spikes = [];
      const steps = Math.max(2000, Math.floor((xMax - xMin) * 4000));
      const dt = (xMax - xMin) / steps;

      while (t < xMax) {
        const y = analogFunc(t);
        integral += y * dt;
        if (integral >= theta) {
          spikes.push(t);
          integral = 0;
        }
        t += dt;
      }
      return spikes;
    }

    function drawIAF() {
      const ySpike = 0.3;
      const xMin = state.startTime, xMax = state.startTime + state.totalTime;
      const spikes = simulateIAF(xMin, xMax);

      p.push();
      const rect = plotRect();
      const ctx = p.drawingContext;
      ctx.save();
      ctx.beginPath();
      ctx.rect(rect.x, rect.y, rect.w, rect.h);
      ctx.clip();

      p.stroke(220, 30, 30);
      p.fill(220, 30, 30);
      p.strokeWeight(1);

      for (const ts of spikes) {
        const p0 = dataToPixel(ts, 0);
        const p1 = dataToPixel(ts, ySpike);
        p.line(p0.x, p0.y, p1.x, p1.y);
        p.noStroke();
        p.circle(p1.x, p1.y, 4);
        p.stroke(220, 30, 30);
      }

      ctx.restore();
      p.pop();
    }

    function drawAll() {
      p.background(255);
      drawAxes();
      drawAnalog();
      if (state.encoding === 'ASDM') drawASDM();
      else drawIAF();
    }

    p.setup = function () {
      // Fit canvas to container width with a pleasant aspect ratio
      const cont = document.getElementById('canvas-container');
      const initialW = Math.min(cont.clientWidth - 20, 900);
      const initialH = Math.round(initialW * 0.6);
      canvas = p.createCanvas(Math.max(360, initialW), Math.max(260, initialH));
      canvas.parent('canvas-container');
      p.noLoop();
      drawAll();
    };

    p.draw = function () {
      drawAll();
    };

    p.windowResized = function () {
      // Resize canvas when container width changes
      const cont = document.getElementById('canvas-container');
      const newW = Math.min(cont.clientWidth - 20, 1000);
      const newH = Math.round(newW * 0.6);
      p.resizeCanvas(Math.max(360, newW), Math.max(260, newH), true);
      p.redraw();
    };
  };

  // Create p5 instance
  sketchP5 = new p5(sketch, 'canvas-container');
</script>
</body>
</html>
