              <path d="M2,14 C8,-2 16,-2 24,14 C32,30 40,30 46,14" stroke="#325ad8" stroke-width="2" fill="none"/>
            </svg>
          </button>
          <button id="s-wave-pulse-button" class="icon-btn" title="Pulse">
            <svg viewBox="0 0 48 28" xmlns="http://www.w3.org/2000/svg">
              <path d="M2,22 C10,22 16,4 24,4 C32,4 38,22 46,22" stroke="#325ad8" stroke-width="2" fill="none"/>
            </svg>
          </button>
        </div>
        <label for="s-wave-amplitude-slider">amplitude</label>
        <input type="range" id="s-wave-amplitude-slider" min="0" max="0.3" step="0.01" value="0.1">
        <label for="s-wave-wavelength-slider">wavelength</label>
        <input type="range" id="s-wave-wavelength-slider" min="0.5" max="5.0" step="0.1" value="2.5">
        <label>direction</label>
        <div id="s-wave-direction-selector" class="toggle-group">
          <button id="s-wave-dir-xy-button" class="active">x-y</button>
          <button id="s-wave-dir-z-button">z</button>
        </div>
      </fieldset>

      <!-- Viewpoint -->
      <fieldset>
        <legend>viewpoint</legend>
        <div id="viewpoint-selector" class="toggle-group">
          <button id="viewpoint-top-button" class="active">Top</button>
          <button id="viewpoint-default-button">Default</button>
        </div>
      </fieldset>
    </div>

    <div id="visualization-container"></div>
  </div>

<script>
(() => {
  // DOM elements
  const timeSlider = document.getElementById('time-slider');
  const timeReadout = document.getElementById('time-readout');
  const resetBtn = document.getElementById('reset-button');
  const playPauseBtn = document.getElementById('play-pause-button');
  const stepBtn = document.getElementById('step-button');

  const plotPointsSlider = document.getElementById('plot-points-slider');

  const pSineBtn = document.getElementById('p-wave-sine-button');
  const pPulseBtn = document.getElementById('p-wave-pulse-button');
  const pAmpSlider = document.getElementById('p-wave-amplitude-slider');
  const pWlSlider = document.getElementById('p-wave-wavelength-slider');

  const sSineBtn = document.getElementById('s-wave-sine-button');
  const sPulseBtn = document.getElementById('s-wave-pulse-button');
  const sAmpSlider = document.getElementById('s-wave-amplitude-slider');
  const sWlSlider = document.getElementById('s-wave-wavelength-slider');
  const sDirXYBtn = document.getElementById('s-wave-dir-xy-button');
  const sDirZBtn = document.getElementById('s-wave-dir-z-button');

  const topBtn = document.getElementById('viewpoint-top-button');
  const defaultBtn = document.getElementById('viewpoint-default-button');

  // State
  let isPlaying = false;
  let time = 0;

  let pWaveType = 'sine'; // 'sine' | 'pulse'
  let sWaveType = 'sine'; // 'sine' | 'pulse'
  let sDirection = 'xy';  // 'xy' | 'z'

  // Three.js essentials
  const container = document.getElementById('visualization-container');
  const renderer = new THREE.WebGLRenderer({antialias: true});
  renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
  container.appendChild(renderer.domElement);

  const scene = new THREE.Scene();
  scene.background = new THREE.Color(0xE0E0E0);

  // Lights
  const ambient = new THREE.AmbientLight(0xffffff, 0.6);
  const dir = new THREE.DirectionalLight(0xffffff, 0.8);
  dir.position.set(1, 1, 1);
  scene.add(ambient, dir);

  // Cameras
  let perspectiveCamera, orthoCamera, activeCamera;
  function setupCameras() {
    const w = container.clientWidth || 800;
    const h = container.clientHeight || 600;
    const aspect = w / Math.max(1, h);

    // Perspective
    if (!perspectiveCamera) {
      perspectiveCamera = new THREE.PerspectiveCamera(45, aspect, 0.1, 1000);
    } else {
      perspectiveCamera.aspect = aspect;
    }
    perspectiveCamera.position.set(10, 10, 10);
    perspectiveCamera.lookAt(0,0,0);
    perspectiveCamera.updateProjectionMatrix();

    // Ortho
    const frustumSize = 12;
    const halfH = frustumSize / 2;
    const halfW = halfH * aspect;
    if (!orthoCamera) {
      orthoCamera = new THREE.OrthographicCamera(-halfW, halfW, halfH, -halfH, 0.1, 1000);
    } else {
      orthoCamera.left = -halfW;
      orthoCamera.right = halfW;
      orthoCamera.top = halfH;
      orthoCamera.bottom = -halfH;
    }
    orthoCamera.position.set(0, 0, 20);
    orthoCamera.up.set(0, 1, 0);
    orthoCamera.lookAt(0,0,0);
    orthoCamera.updateProjectionMatrix();
  }

  function resizeRenderer() {
    const w = container.clientWidth;
    const h = container.clientHeight;
    renderer.setSize(w, h, false);
    setupCameras();
  }
  window.addEventListener('resize', resizeRenderer);

  // Grid mesh variables
  let planeMesh = null;
  let wireframe = null;
  let geometry = null;
  let basePositions = null; // Float32Array of original XY (and Z=0)

  const planeMaterial = new THREE.MeshLambertMaterial({
    side: THREE.DoubleSide,
    vertexColors: true
  });
  const wireMaterial = new THREE.LineBasicMaterial({ color: 0x556B2F, linewidth: 1 });

  function buildGrid(segments) {
    // Clean old
    if (planeMesh) {
      scene.remove(planeMesh);
      geometry.dispose();
      planeMaterial.needsUpdate = true;
    }
    if (wireframe) {
      scene.remove(wireframe);
    }

    const size = 10; // 10x10 units
    geometry = new THREE.PlaneGeometry(size, size, segments, segments);

    // Store base positions
    const posAttr = geometry.attributes.position;
    basePositions = new Float32Array(posAttr.array.length);
    basePositions.set(posAttr.array);

    // Colors
    const count = posAttr.count;
    const colors = new Float32Array(count * 3);
    geometry.setAttribute('color', new THREE.BufferAttribute(colors, 3));

    // Mesh and wireframe
    planeMesh = new THREE.Mesh(geometry, planeMaterial);
    scene.add(planeMesh);

    const wfGeo = new THREE.WireframeGeometry(geometry);
    wireframe = new THREE.LineSegments(wfGeo, wireMaterial);
    scene.add(wireframe);

    // Initial update
    updateGeometry();
  }

  // Wave functions
  function waveFunc(type, u) {
    if (type === 'sine') {
      return Math.sin(2 * Math.PI * u);
    } else {
      // gaussian pulse exp(-u^2)
      return Math.exp(-u * u);
    }
  }

  function clamp(x, a, b){ return Math.max(a, Math.min(b, x)); }

  function updateGeometry() {
    if (!geometry || !basePositions) return;
    const pos = geometry.attributes.position.array;
    const col = geometry.attributes.color.array;

    const p_amp = parseFloat(pAmpSlider.value);
    const p_wl  = parseFloat(pWlSlider.value);
    const s_amp = parseFloat(sAmpSlider.value);
    const s_wl  = parseFloat(sWlSlider.value);

    const vertCount = geometry.attributes.position.count;

    let maxScale = Math.max(0.05, p_amp + s_amp);

    for (let i = 0; i < vertCount; i++) {
      const idx = i * 3;
      const x0 = basePositions[idx + 0];
      const y0 = basePositions[idx + 1];

      const r = Math.hypot(x0, y0);
      const invR = r > 1e-8 ? 1 / r : 0.0;

      // P-wave (longitudinal)
      const uP = (r / p_wl) - time;
      const mP = p_amp * waveFunc(pWaveType, uP);
      const rx = (r > 1e-8) ? (x0 * invR) : 0.0;
      const ry = (r > 1e-8) ? (y0 * invR) : 0.0;
      const pdx = mP * rx;
      const pdy = mP * ry;
      const pdz = 0;

      // S-wave (transverse)
      const uS = (r / s_wl) - time;
      const mS = s_amp * waveFunc(sWaveType, uS);
      let sdx=0, sdy=0, sdz=0;
      if (sDirection === 'z') {
        sdz = mS;
      } else {
        // perpendicular in-plane
        const tx = (r > 1e-8) ? (-y0 * invR) : 0.0;
        const ty = (r > 1e-8) ? ( x0 * invR) : 0.0;
        sdx = mS * tx;
        sdy = mS * ty;
      }

      const nx = x0 + pdx + sdx;
      const ny = y0 + pdy + sdy;
      const nz = 0 + pdz + sdz;

      pos[idx + 0] = nx;
      pos[idx + 1] = ny;
      pos[idx + 2] = nz;

      // Color from vertical displacement nz (red-white-blue)
      let v = clamp(nz / maxScale, -1, 1);
      let rCol, gCol, bCol;
      if (v >= 0) {
        rCol = 1.0;
        gCol = 1.0 - v;
        bCol = 1.0 - v;
      } else {
        rCol = 1.0 + v; // v negative
        gCol = 1.0 + v;
        bCol = 1.0;
      }
      col[idx + 0] = rCol;
      col[idx + 1] = gCol;
      col[idx + 2] = bCol;
    }

    geometry.attributes.position.needsUpdate = true;
    geometry.attributes.color.needsUpdate = true;
    geometry.computeVertexNormals();

    // Keep wireframe synced (it shares geometry, normals not needed)
    timeReadout.textContent = time.toFixed(2);
  }

  // Controls behavior
  playPauseBtn.addEventListener('click', () => {
    isPlaying = !isPlaying;
    playPauseBtn.textContent = isPlaying ? '❚❚' : '►';
  });

  resetBtn.addEventListener('click', () => {
    isPlaying = false;
    playPauseBtn.textContent = '►';
    time = 0;
    timeSlider.value = String(time);
    updateGeometry();
  });

  stepBtn.addEventListener('click', () => {
    isPlaying = false;
    playPauseBtn.textContent = '►';
    time = Math.min(parseFloat(timeSlider.max), time + 0.1);
    timeSlider.value = String(time.toFixed(1));
    updateGeometry();
  });

  timeSlider.addEventListener('input', () => {
    time = parseFloat(timeSlider.value);
    updateGeometry();
  });

  plotPointsSlider.addEventListener('change', () => {
    buildGrid(parseInt(plotPointsSlider.value, 10));
  });

  // Wave type toggles
  function activate(btnA, btnB){ btnA.classList.add('active'); btnB.classList.remove('active'); }

  pSineBtn.addEventListener('click', () => { pWaveType = 'sine'; activate(pSineBtn, pPulseBtn); updateGeometry(); });
  pPulseBtn.addEventListener('click', () => { pWaveType = 'pulse'; activate(pPulseBtn, pSineBtn); updateGeometry(); });
  sSineBtn.addEventListener('click', () => { sWaveType = 'sine'; activate(sSineBtn, sPulseBtn); updateGeometry(); });
  sPulseBtn.addEventListener('click', () => { sWaveType = 'pulse'; activate(sPulseBtn, sSineBtn); updateGeometry(); });

  // Sliders for amps/wavelengths
  [pAmpSlider, pWlSlider, sAmpSlider, sWlSlider].forEach(sl => {
    sl.addEventListener('input', updateGeometry);
  });

  // Direction toggles
  sDirXYBtn.addEventListener('click', () => {
    sDirection = 'xy';
    activate(sDirXYBtn, sDirZBtn);
    updateGeometry();
  });
  sDirZBtn.addEventListener('click', () => {
    sDirection = 'z';
    activate(sDirZBtn, sDirXYBtn);
    updateGeometry();
  });

  // Viewpoint toggles
  function setActiveCamera(cam) {
    activeCamera = cam;
  }
  topBtn.addEventListener('click', () => {
    activate(topBtn, defaultBtn);
    setActiveCamera(orthoCamera);
  });
  defaultBtn.addEventListener('click', () => {
    activate(defaultBtn, topBtn);
    setActiveCamera(perspectiveCamera);
  });

  // Init
  resizeRenderer();
  buildGrid(parseInt(plotPointsSlider.value, 10));
  setActiveCamera(orthoCamera); // default Top active

  // Animation loop
  let last = performance.now();
  function animate(now) {
    requestAnimationFrame(animate);
    const dt = (now - last) / 1000;
    last = now;

    if (isPlaying) {
      time += dt;
      if (time > parseFloat(timeSlider.max)) time = 0;
      timeSlider.value = String(time);
      updateGeometry();
    }
    renderer.render(scene, activeCamera);
  }
  animate(performance.now());
})();
</script>
</body>
</html>
