<!-- p5.js CDN script -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

<script>
/* ---------------------------
   State and utilities
---------------------------- */
const state = {
    // coefficients (golden ratio example initially)
    a: 1,
    b: -1,
    c: -1,
    // controls
    z: 1.5,
    plotRange: 4,       // half-extent from center to edge (world units)
    // feature toggles
    showLabels: false,
    showCircle: false,
    showAxes: false,
    showGrid: false,
    // derived geometry
    O: {x:0, y:0},
    L1: {x:0, y:0},
    L2: {x:0, y:0},
    L3: {x:0, y:0},
    L1p: {x:0, y:0},
    L2p: {x:0, y:0},
    C: {x:0, y:0},     // circle center
    r: 0,              // circle radius
    // view
    viewCenter: {x:0, y:0},
    // results
    Pz: 0,
    errLen: 0
};

// DOM elements
const elZ = document.getElementById('slider-z');
const elZLabel = document.getElementById('label-z');
const elRange = document.getElementById('slider-plot-range');
const elRangeLabel = document.getElementById('label-plot-range');
const elCheckLabels = document.getElementById('check-show-labels');
const elCheckCircle = document.getElementById('check-show-circle');
const elCheckAxes = document.getElementById('check-show-axes');
const elCheckGrid = document.getElementById('check-show-grid-lines');
const elBtnNew = document.getElementById('btn-new-example');
const elBtnGolden = document.getElementById('btn-golden-ratio');

const elFormulaText = document.getElementById('formula-text');
const elValuePz = document.getElementById('value-pz');
const elValueErr = document.getElementById('value-l3l2');
const elValueZ = document.getElementById('value-z');

function fmt(x) {
    // Format to 2-4 decimals, trim trailing zeros
    const absx = Math.abs(x);
    let d = 3;
    if (absx >= 100) d = 2;
    else if (absx < 0.01) d = 4;
    const s = x.toFixed(d);
    return s.replace(/(\.\d*?[1-9])0+$/,'$1').replace(/\.0+$/,'');
}

function polynomialToTeX(a,b,c) {
    const term = (coef, sym, power) => {
        if (coef === 0) return '';
        const sign = coef > 0 ? '+' : '-';
        const absC = Math.abs(coef);
        let coefStr = '';
        if (absC !== 1 || sym === '') coefStr = absC.toString();
        const symStr = sym ? sym + (power === 1 ? '' : '^' + power) : '';
        return `${sign} ${coefStr}${symStr}`.trim();
    };
    let parts = [];
    // a z^2
    if (state.a !== 0) {
        const sign = a < 0 ? '-' : '';
        const absA = Math.abs(a);
        parts.push(`${sign}${absA === 1 ? '' : absA}z^2`);
    }
    // b z
    if (b !== 0) {
        const sign = b > 0 ? '+ ' : '- ';
        const absB = Math.abs(b);
        parts.push(`${sign}${absB === 1 ? '' : absB}z`);
    }
    // c
    if (c !== 0) {
        const sign = c > 0 ? '+ ' : '- ';
        parts.push(`${sign}${Math.abs(c)}`);
    }
    let s = parts.join(' ').trim();
    // Clean leading "+ "
    s = s.replace(/^\+ /,'');
    if (s === '') s = '0';
    return s;
}

function updateFormulaDisplay() {
    elFormulaText.textContent = polynomialToTeX(state.a, state.b, state.c);
    if (window.MathJax && MathJax.typesetPromise) {
        MathJax.typesetPromise([document.getElementById('formula-display')]);
    }
}

function computeBaseGeometry() {
    const {a,b,c} = state;
    state.O = {x:0, y:0};
    state.L1 = {x:a, y:0};
    state.L2 = {x:a, y:b};
    state.L3 = {x:a - c, y:b};
    // circle
    const Cx = (a - c)/2;
    const Cy = b/2;
    const d = Math.hypot((a - c), b);
    state.C = {x: Cx, y: Cy};
    state.r = d / 2;
}

function computeTestPath() {
    const {a,b,z} = state;
    state.L1p = {x:a, y: -z * a};
    state.L2p = {x: (a + b*z + a*z*z), y: b};
}

function computeValues() {
    const {a,b,c,z,L2p,L3} = state;
    state.Pz = a*z*z + b*z + c;
    // |L3 L2'|, same y so abs diff in x equals |P(z)|
    state.errLen = Math.abs(state.Pz);
}

function computeViewCenter() {
    // Center on bounding box of O, L1, L2, L3 and circle
    const pts = [state.O, state.L1, state.L2, state.L3];
    let minX = Math.min(...pts.map(p=>p.x));
    let maxX = Math.max(...pts.map(p=>p.x));
    let minY = Math.min(...pts.map(p=>p.y));
    let maxY = Math.max(...pts.map(p=>p.y));
    // include circle bbox
    minX = Math.min(minX, state.C.x - state.r);
    maxX = Math.max(maxX, state.C.x + state.r);
    minY = Math.min(minY, state.C.y - state.r);
    maxY = Math.max(maxY, state.C.y + state.r);
    state.viewCenter = {x:(minX+maxX)/2, y:(minY+maxY)/2};
    // return suggested half-range to fit bbox
    const halfW = (maxX - minX) / 2;
    const halfH = (maxY - minY) / 2;
    const needed = Math.max(halfW, halfH, 1e-6);
    return needed * 1.2 + 0.2; // small margin
}

function updateUIValues() {
    elZLabel.textContent = fmt(state.z);
    elRangeLabel.textContent = fmt(state.plotRange);
    elValuePz.textContent = fmt(state.Pz);
    elValueErr.textContent = fmt(state.errLen);
    elValueZ.textContent = fmt(state.z);
}

/* ---------------------------
   Interactions
---------------------------- */
function update(redrawNow = true) {
    computeBaseGeometry();
    computeTestPath();
    computeValues();
    updateUIValues();
    if (redrawNow && window.redraw) window.redraw();
}

elZ.addEventListener('input', () => {
    state.z = parseFloat(elZ.value);
    update();
});

elRange.addEventListener('input', () => {
    state.plotRange = parseFloat(elRange.value);
    elRangeLabel.textContent = fmt(state.plotRange);
    if (window.redraw) window.redraw();
});

elCheckLabels.addEventListener('change', () => {
    state.showLabels = elCheckLabels.checked;
    if (window.redraw) window.redraw();
});
elCheckCircle.addEventListener('change', () => {
    state.showCircle = elCheckCircle.checked;
