function randomInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

function generateRandomA() {
    // a in [-3,-1] U [1,3]
    const sign = Math.random() < 0.5 ? -1 : 1;
    return sign * randomInt(1,3);
}

elBtnNew.addEventListener('click', () => {
