function getCanvasSize() {
    const rect = document.getElementById('canvas-container').getBoundingClientRect();
    // keep subtle margins inside visualization area
    const w = Math.max(300, Math.floor(rect.width - 2));
    const h = Math.max(260, Math.floor(rect.height - 2));
    return {w, h};
}

function windowResized() {
    const sz = getCanvasSize();
    resizeCanvas(sz.w, sz.h, true);
    redraw();
}

function draw() {
    background(255);

    // world-to-screen parameters
    const cx = width/2;
    const cy = height/2;
    const s = Math.min(width, height) / (2*state.plotRange); // pixels per unit

    // world extents visible
    const xMin = state.viewCenter.x - state.plotRange;
    const xMax = state.viewCenter.x + state.plotRange;
    const yMin = state.viewCenter.y - state.plotRange;
    const yMax = state.viewCenter.y + state.plotRange;

    function toScreen(pt) {
        return {
            x: cx + (pt.x - state.viewCenter.x) * s,
            y: cy - (pt.y - state.viewCenter.y) * s
        };
    }
    function drawLine(p1, p2, col='#000', sw=2) {
        const a = toScreen(p1), b = toScreen(p2);
        stroke(col); strokeWeight(sw); line(a.x, a.y, b.x, b.y);
    }
    function drawPoint(p, col='#000', rPx=6) {
        const q = toScreen(p);
        noStroke(); fill(col); circle(q.x, q.y, rPx);
    }
    function drawCircleWorld(center, radius, col='#000', sw=2) {
        const q = toScreen(center);
        noFill(); stroke(col); strokeWeight(sw);
        const dPx = 2 * radius * s;
        circle(q.x, q.y, dPx);
    }
    function drawTextWorld(textStr, p, offsetPx={x:6,y:-6}) {
        const q = toScreen(p);
        resetMatrix();
        textSize(12);
        fill(0);
        noStroke();
        text(textStr, q.x + offsetPx.x, q.y + offsetPx.y);
        // restore no matrix for later world conversions (we do manual conversion anyway)
    }

    // Grid
    if (state.showGrid) {
        const step = niceStep(state.plotRange);
        stroke(220); strokeWeight(1);
        // vertical lines
        for (let x = Math.ceil((xMin)/step)*step; x <= xMax; x += step) {
            const p1 = toScreen({x, y:yMin});
            const p2 = toScreen({x, y:yMax});
            line(p1.x, p1.y, p2.x, p2.y);
        }
        // horizontal lines
        for (let y = Math.ceil((yMin)/step)*step; y <= yMax; y += step) {
            const p1 = toScreen({x:xMin, y});
            const p2 = toScreen({x:xMax, y});
            line(p1.x, p1.y, p2.x, p2.y);
        }
    }

    // Axes and ticks
    if (state.showAxes) {
        // axes lines
        stroke('#000'); strokeWeight(2);
        // x-axis (y=0)
        if (0 >= yMin && 0 <= yMax) {
            drawLine({x:xMin,y:0},{x:xMax,y:0}, '#000', 2);
        }
        // y-axis (x=0)
        if (0 >= xMin && 0 <= xMax) {
            drawLine({x:0,y:yMin},{x:0,y:yMax}, '#000', 2);
        }
        // ticks and numeric labels
        const step = niceStep(state.plotRange);
        textSize(12); fill(0); noStroke();
        // x ticks
        if (0 >= yMin && 0 <= yMax) {
            for (let x = Math.ceil(xMin/step)*step; x <= xMax; x += step) {
                const p0 = toScreen({x, y:0});
                stroke(0); strokeWeight(2); line(p0.x, p0.y-4, p0.x, p0.y+4);
                noStroke(); textAlign(CENTER, TOP);
                text(fmt(x), p0.x, p0.y+6);
            }
        }
        // y ticks
        if (0 >= xMin && 0 <= xMax) {
            for (let y = Math.ceil(yMin/step)*step; y <= yMax; y += step) {
                const p0 = toScreen({x:0, y});
                stroke(0); strokeWeight(2); line(p0.x-4, p0.y, p0.x+4, p0.y);
                noStroke(); textAlign(LEFT, CENTER);
                text(fmt(y), p0.x+6, p0.y-1);
            }
        }
    }

    // Circle (optional)
    if (state.showCircle) {
        drawCircleWorld(state.C, state.r, '#000', 2);
    }

    // Coefficient path: O -> L1 -> L2 -> L3
    drawLine(state.O, state.L1, '#000', 2);
    drawLine(state.L1, state.L2, '#000', 2);
    drawLine(state.L2, state.L3, '#000', 2);
    drawPoint(state.O, '#000');
    drawPoint(state.L1, '#000');
    drawPoint(state.L2, '#000');
    drawPoint(state.L3, '#000');

    // Test path and error
    drawLine(state.O, state.L1p, '#000', 2);
    drawLine(state.L1p, state.L2p, '#d000d0', 3);
    drawLine(state.L2p, state.L3, '#e02121', 4);
    drawPoint(state.L1p, '#000');
    drawPoint(state.L2p, '#000');

    // Labels
    if (state.showLabels) {
        drawTextWorld('O', state.O);
        drawTextWorld('L1', state.L1);
        drawTextWorld('L2', state.L2);
        drawTextWorld('L3', state.L3);
        drawTextWorld("L1'", state.L1p, {x:6,y:14});
        drawTextWorld("L2'", state.L2p, {x:6,y:-6});
        if (state.showCircle) drawTextWorld('C', state.C, {x:6,y:-6});
        // label z near vertical from L1' to (a,0)
        const midZ = {x: state.L1p.x, y: (state.L1p.y + 0)/2};
        drawTextWorld('z', midZ, {x:-12, y:-4});
    }
}

// choose a "nice" tick step given the half-range
function niceStep(plotRange) {
    // aim for ~10 major ticks over 2*plotRange
    const desired = (2*plotRange) / 10;
    const pow10 = Math.pow(10, Math.floor(Math.log10(desired)));
    const base = desired / pow10;
    let step;
    if (base >= 5) step = 5 * pow10;
    else if (base >= 2) step = 2 * pow10;
    else step = 1 * pow10;
    return step;
}

/* ---------------------------
   Initialization
---------------------------- */
document.addEventListener('DOMContentLoaded', () => {
